import React, { useState, useEffect, useRef } from 'react';
import {
  ChevronRight, ChevronLeft, Star, Sparkles,
  Palette, Box, Sun, Zap, Code, MousePointer2,
  ArrowRight, Play, Heart, User, Circle, Image as ImageIcon,
  Upload, Trash2, X, RefreshCw
} from 'lucide-react';
import { toast } from 'react-toastify';
import DOMPurify from 'dompurify';
import { uploadImage, getPageImages, deleteImage, validateImageFile, compressImage } from '../../imageService';
import '../../../styles/ElementPropertiesPanel.css';

const ICON_PRESETS = [
  { name: 'Star', icon: '<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2"><polygon points="12 2 15.09 8.26 22 9.27 17 14.14 18.18 21.02 12 17.77 5.82 21.02 7 14.14 2 9.27 8.91 8.26 12 2"></polygon></svg>' },
  { name: 'Heart', icon: '<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2"><path d="M20.84 4.61a5.5 5.5 0 0 0-7.78 0L12 5.67l-1.06-1.06a5.5 5.5 0 0 0-7.78 7.78l1.06 1.06L12 21.23l7.78-7.78 1.06-1.06a5.5 5.5 0 0 0 0-7.78z"></path></svg>' },
  { name: 'Arrow Right', icon: '<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2"><line x1="5" y1="12" x2="19" y2="12"></line><polyline points="12 5 19 12 12 19"></polyline></svg>' },
  { name: 'Play', icon: '<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2"><polygon points="5 3 19 12 5 21 5 3"></polygon></svg>' },
  { name: 'User', icon: '<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2"><path d="M20 21v-2a4 4 0 0 0-4-4H8a4 4 0 0 0-4 4v2"></path><circle cx="12" cy="7" r="4"></circle></svg>' },
  { name: 'Facebook', imageUrl: 'https://cdn-icons-png.flaticon.com/512/124/124010.png' },
  { name: 'Zalo', imageUrl: 'https://cdn-icons-png.flaticon.com/512/124/124034.png' },
  { name: 'Messenger', imageUrl: 'https://cdn-icons-png.flaticon.com/512/5968/5968850.png' },
];

const COLOR_PRESETS = [
  '#000000', '#ffffff', '#f3f4f6', '#1f2937', '#374151',
  '#667eea', '#764ba2', '#2563eb', '#3b82f6', '#06b6d4',
  '#10b981', '#22c55e', '#84cc16', '#eab308', '#f59e0b',
  '#ef4444', '#dc2626', '#ec4899', '#a855f7', '#8b5cf6'
];

const HOVER_EFFECTS = [
  { name: 'None', value: 'none' },
  { name: 'Scale Up', value: 'scale', transform: 'scale(1.2)' },
  { name: 'Scale Down', value: 'scaleDown', transform: 'scale(0.9)' },
  { name: 'Rotate', value: 'rotate', transform: 'rotate(15deg)' },
  { name: 'Slide Right', value: 'slideRight', transform: 'translateX(5px)' },
  { name: 'Slide Up', value: 'slideUp', transform: 'translateY(-5px)' },
  { name: 'Pulse', value: 'pulse', animation: 'pulse 0.6s ease infinite' },
];

const ANIMATION_PRESETS = [
  { name: 'None', value: 'none' },
  { name: 'Spin', value: 'spin', animation: 'spin 1s linear infinite' },
  { name: 'Bounce', value: 'bounce', animation: 'bounce 1s ease infinite' },
  { name: 'Pulse', value: 'pulse', animation: 'pulse 1s ease infinite' },
  { name: 'Swing', value: 'swing', animation: 'swing 1s ease infinite' },
  { name: 'Tada', value: 'tada', animation: 'tada 1s ease infinite' },
  { name: 'Wobble', value: 'wobble', animation: 'wobble 1s ease infinite' },
];

const IconPropertiesPanel = ({ selectedElement, onUpdateElement, isCollapsed, onToggle, pageId }) => {
  const [activeTab, setActiveTab] = useState('design');
  const [showSvgEditor, setShowSvgEditor] = useState(false);
  const [showImageEditor, setShowImageEditor] = useState(false);
  const [showUploadModal, setShowUploadModal] = useState(false);
  const [images, setImages] = useState([]);
  const [loading, setLoading] = useState(false);
  const [uploading, setUploading] = useState(false);
  const [progress, setProgress] = useState(0);
  const fileInputRef = useRef(null);

  // Initialize editor based on element type
  useEffect(() => {
    if (selectedElement?.json?.componentData?.imageUrl) {
      setShowImageEditor(true);
      setShowSvgEditor(false);
    } else {
      setShowSvgEditor(true);
      setShowImageEditor(false);
    }
  }, [selectedElement]);

  // Load images when modal opens
  useEffect(() => {
    if (showUploadModal) {
      loadImages();
    }
  }, [showUploadModal, pageId]);

  const loadImages = async () => {
    setLoading(true);
    try {
      const response = await getPageImages(pageId);
      setImages(response.data || []);
    } catch (error) {
      console.error('Error loading images:', error);
      toast.error(`Lỗi tải ảnh: ${error.message}`, { position: 'bottom-right' });
      setImages([]);
    } finally {
      setLoading(false);
    }
  };

  // Handle file upload
  const handleFileSelect = async (e) => {
    const files = Array.from(e.target.files);
    if (files.length === 0) return;

    setUploading(true);
    setProgress(0);

    try {
      for (const file of files) {
        const validation = validateImageFile(file);
        if (!validation.valid) {
          toast.warn(`${file.name}: ${validation.error}`, { position: 'bottom-right' });
          continue;
        }

        const compressedFile = await compressImage(file);
        const response = await uploadImage(compressedFile, pageId, (progress) => {
          setProgress(progress);
        });

        if (response.success) {
          setImages(prev => [...prev, response.data]);
          handleComponentDataChange('imageUrl', response.data.url);
          toast.success(`Tải lên ${file.name} thành công`, { position: 'bottom-right' });
        }
      }
      setShowUploadModal(false);
    } catch (error) {
      console.error('Upload error:', error);
      toast.error(`Lỗi tải ảnh: ${error.message}`, { position: 'bottom-right' });
    } finally {
      setUploading(false);
      setProgress(0);
      if (fileInputRef.current) fileInputRef.current.value = '';
    }
  };

  // Handle image deletion
  const handleDeleteImage = async (imageKey) => {
    if (!window.confirm('Bạn có chắc chắn muốn xóa ảnh này?')) return;

    try {
      const success = await deleteImage(imageKey);
      if (success) {
        setImages(prev => prev.filter(img => img.key !== imageKey));
        if (componentData.imageUrl === images.find(img => img.key === imageKey)?.url) {
          handleComponentDataChange('imageUrl', '');
        }
        toast.success('Xóa ảnh thành công', { position: 'bottom-right' });
      }
    } catch (error) {
      console.error('Error deleting image:', error);
      toast.error(`Lỗi xóa ảnh: ${error.message}`, { position: 'bottom-right' });
    }
  };

  // Update styles
  const handleStyleChange = (property, value) => {
    const updated = {
      ...selectedElement,
      json: {
        ...selectedElement.json,
        styles: {
          ...styles,
          [property]: value,
        },
      },
    };
    onUpdateElement(updated);
  };

  // Update component data
  const handleComponentDataChange = (key, value) => {
    const updated = {
      ...selectedElement,
      json: {
        ...selectedElement.json,
        componentData: {
          ...componentData,
          [key]: key === 'icon' ? DOMPurify.sanitize(value) : value,
          ...(key === 'imageUrl' ? { icon: undefined } : key === 'icon' ? { imageUrl: undefined } : {}),
        },
      },
    };
    onUpdateElement(updated);
  };

  // Update size
  const handleSizeChange = (dimension, value) => {
    const parsedValue = parseInt(value);
    if (isNaN(parsedValue) || parsedValue < 0) {
      toast.error('Kích thước phải là số dương', { position: 'bottom-right' });
      return;
    }
    const updated = {
      ...selectedElement,
      json: {
        ...selectedElement.json,
        size: {
          ...size,
          [dimension]: parsedValue,
        },
      },
    };
    onUpdateElement(updated);
  };

  // Handle hover effects
  const handleHoverEffectChange = (effect) => {
    const hoverEffect = HOVER_EFFECTS.find(e => e.value === effect);
    const newStyles = { ...styles };
    if (!hoverEffect || effect === 'none') {
      delete newStyles[':hover'];
    } else {
      newStyles[':hover'] = {
        transform: hoverEffect.transform || 'none',
        animation: hoverEffect.animation || 'none',
      };
    }
    const updated = {
      ...selectedElement,
      json: { ...selectedElement.json, styles: newStyles },
    };
    onUpdateElement(updated);
  };

  const { componentData = {}, styles = {}, size = {} } = selectedElement.json;

  // Collapsed state
  if (isCollapsed) {
    return (
        <div style={{
          width: '48px',
          height: '100%',
          background: '#ffffff',
          borderLeft: '1px solid #e5e7eb',
          display: 'flex',
          alignItems: 'center',
          padding: '12px 0'
        }}>
          <button
              onClick={onToggle}
              style={{
                width: '32px',
                height: '32px',
                border: 'none',
                background: 'transparent',
                cursor: 'pointer',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                borderRadius: '6px',
                transition: 'background 0.2s'
              }}
              onMouseEnter={(e) => e.currentTarget.style.background = '#f3f4f6'}
              onMouseLeave={(e) => e.currentTarget.style.background = 'transparent'}
          >
            <ChevronLeft size={18} />
          </button>
        </div>
    );
  }

  // No selected element or not an icon
  if (!selectedElement || !selectedElement.json || selectedElement.json.type !== 'icon') {
    return (
        <div style={{
          width: '320px',
          height: '100%',
          background: '#ffffff',
          borderLeft: '1px solid #e5e7eb',
          display: 'flex',
          flexDirection: 'column'
        }}>
          <div style={{
            padding: '16px',
            borderBottom: '1px solid #e5e7eb',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'space-between'
          }}>
            <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
              <Star size={16} color="#2563eb" />
              <h3 style={{ margin: 0, fontSize: '14px', fontWeight: '600' }}>Thuộc tính Icon</h3>
            </div>
            <button
                onClick={onToggle}
                style={{
                  border: 'none',
                  background: 'transparent',
                  cursor: 'pointer',
                  padding: '4px',
                  display: 'flex',
                  alignItems: 'center'
                }}
            >
              <ChevronRight size={18} />
            </button>
          </div>
          <div style={{
            flex: 1,
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            justifyContent: 'center',
            color: '#9ca3af',
            padding: '40px'
          }}>
            <Star size={48} strokeWidth={1.5} />
            <p style={{ margin: '16px 0 0', fontSize: '14px' }}>Chọn một icon để chỉnh sửa</p>
          </div>
        </div>
    );
  }

  // Design tab
  const renderDesignTab = () => (
      <div style={{ padding: '16px', overflowY: 'auto', flex: 1 }}>
        {/* Icon Type Selector */}
        <div style={{ marginBottom: '24px' }}>
          <h4 style={{ margin: '0 0 12px', fontSize: '13px', fontWeight: '600', display: 'flex', alignItems: 'center', gap: '6px' }}>
            <Code size={14} />
            Loại Icon
          </h4>
          <select
              value={componentData.imageUrl ? 'image' : 'svg'}
              onChange={(e) => {
                if (e.target.value === 'svg') {
                  setShowImageEditor(false);
                  setShowSvgEditor(true);
                  handleComponentDataChange('imageUrl', '');
                } else {
                  setShowSvgEditor(false);
                  setShowImageEditor(true);
                  handleComponentDataChange('icon', '');
                }
              }}
              style={{
                width: '100%',
                padding: '8px 12px',
                fontSize: '13px',
                border: '1px solid #e5e7eb',
                borderRadius: '6px',
                background: '#fff',
                cursor: 'pointer'
              }}
          >
            <option value="svg">SVG</option>
            <option value="image">Hình ảnh</option>
          </select>
        </div>

        {/* SVG Editor */}
        {showSvgEditor && (
            <div style={{ marginBottom: '24px' }}>
              <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', marginBottom: '12px' }}>
                <h4 style={{ margin: 0, fontSize: '13px', fontWeight: '600', display: 'flex', alignItems: 'center', gap: '6px' }}>
                  <Code size={14} />
                  Icon SVG
                </h4>
                <button
                    onClick={() => setShowSvgEditor(!showSvgEditor)}
                    style={{
                      padding: '4px 8px',
                      fontSize: '11px',
                      background: showSvgEditor ? '#2563eb' : '#f3f4f6',
                      color: showSvgEditor ? '#fff' : '#374151',
                      border: 'none',
                      borderRadius: '4px',
                      cursor: 'pointer'
                    }}
                >
                  {showSvgEditor ? 'Ẩn Editor' : 'Sửa SVG'}
                </button>
              </div>

              {showSvgEditor && (
                  <>
                    <div style={{
                      padding: '20px',
                      background: '#f9fafb',
                      borderRadius: '8px',
                      marginBottom: '12px',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center'
                    }}>
                      {componentData.icon ? (
                          <div
                              style={{
                                width: '64px',
                                height: '64px',
                                color: styles.color || '#000'
                              }}
                              dangerouslySetInnerHTML={{ __html: DOMPurify.sanitize(componentData.icon) }}
                          />
                      ) : (
                          <Star size={32} color="#9ca3af" />
                      )}
                    </div>

                    <div style={{ marginBottom: '12px' }}>
                      <label style={{ display: 'block', fontSize: '12px', fontWeight: '500', marginBottom: '6px', color: '#374151' }}>
                        Mã SVG
                      </label>
                      <textarea
                          value={componentData.icon || ''}
                          onChange={(e) => handleComponentDataChange('icon', e.target.value)}
                          placeholder="Dán mã SVG tại đây..."
                          rows={8}
                          style={{
                            width: '100%',
                            padding: '8px',
                            fontSize: '11px',
                            fontFamily: 'monospace',
                            border: '1px solid #e5e7eb',
                            borderRadius: '6px',
                            resize: 'vertical',
                            boxSizing: 'border-box'
                          }}
                      />
                    </div>

                    <div>
                      <label style={{ display: 'block', fontSize: '12px', fontWeight: '500', marginBottom: '8px', color: '#374151' }}>
                        Icon có sẵn
                      </label>
                      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(5, 1fr)', gap: '8px' }}>
                        {ICON_PRESETS.filter(preset => preset.icon).map((preset) => (
                            <button
                                key={preset.name}
                                onClick={() => handleComponentDataChange('icon', preset.icon)}
                                style={{
                                  padding: '12px',
                                  border: componentData.icon === preset.icon ? '2px solid #2563eb' : '1px solid #e5e7eb',
                                  borderRadius: '6px',
                                  background: '#fff',
                                  cursor: 'pointer',
                                  transition: 'all 0.2s',
                                  display: 'flex',
                                  alignItems: 'center',
                                  justifyContent: 'center'
                                }}
                                title={preset.name}
                                onMouseEnter={(e) => {
                                  if (componentData.icon !== preset.icon) {
                                    e.currentTarget.style.borderColor = '#93c5fd';
                                    e.currentTarget.style.background = '#f0f9ff';
                                  }
                                }}
                                onMouseLeave={(e) => {
                                  if (componentData.icon !== preset.icon) {
                                    e.currentTarget.style.borderColor = '#e5e7eb';
                                    e.currentTarget.style.background = '#fff';
                                  }
                                }}
                            >
                              <div style={{ width: '20px', height: '20px' }} dangerouslySetInnerHTML={{ __html: preset.icon }} />
                            </button>
                        ))}
                      </div>
                    </div>
                  </>
              )}
            </div>
        )}

        {/* Image Editor */}
        {showImageEditor && (
            <div style={{ marginBottom: '24px' }}>
              <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', marginBottom: '12px' }}>
                <h4 style={{ margin: 0, fontSize: '13px', fontWeight: '600', display: 'flex', alignItems: 'center', gap: '6px' }}>
                  <ImageIcon size={14} />
                  Hình ảnh
                </h4>
                <button
                    onClick={() => setShowImageEditor(!showImageEditor)}
                    style={{
                      padding: '4px 8px',
                      fontSize: '11px',
                      background: showImageEditor ? '#2563eb' : '#f3f4f6',
                      color: showImageEditor ? '#fff' : '#374151',
                      border: 'none',
                      borderRadius: '4px',
                      cursor: 'pointer'
                    }}
                >
                  {showImageEditor ? 'Ẩn Editor' : 'Sửa Hình ảnh'}
                </button>
              </div>

              {showImageEditor && (
                  <>
                    <div style={{
                      padding: '20px',
                      background: '#f9fafb',
                      borderRadius: '8px',
                      marginBottom: '12px',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center'
                    }}>
                      {componentData.imageUrl ? (
                          <img
                              src={componentData.imageUrl}
                              alt={componentData.alt || 'Icon'}
                              style={{ width: '64px', height: '64px', objectFit: 'contain' }}
                          />
                      ) : (
                          <ImageIcon size={32} color="#9ca3af" />
                      )}
                    </div>

                    <button
                        onClick={() => setShowUploadModal(true)}
                        style={{
                          width: '100%',
                          padding: '10px',
                          background: '#3b82f6',
                          color: '#ffffff',
                          border: 'none',
                          borderRadius: '6px',
                          cursor: 'pointer',
                          fontSize: '13px',
                          fontWeight: '600',
                          display: 'flex',
                          alignItems: 'center',
                          justifyContent: 'center',
                          gap: '8px',
                          transition: 'all 0.2s'
                        }}
                        onMouseEnter={(e) => e.currentTarget.style.background = '#2563eb'}
                        onMouseLeave={(e) => e.currentTarget.style.background = '#3b82f6'}
                    >
                      <Upload size={16} />
                      Chọn từ thư viện
                    </button>

                    <div style={{ marginTop: '12px' }}>
                      <label style={{ display: 'block', fontSize: '12px', fontWeight: '500', marginBottom: '6px', color: '#374151' }}>
                        URL Hình ảnh
                      </label>
                      <input
                          type="text"
                          value={componentData.imageUrl || ''}
                          onChange={(e) => handleComponentDataChange('imageUrl', e.target.value)}
                          placeholder="https://example.com/image.jpg"
                          style={{
                            width: '100%',
                            padding: '8px 12px',
                            fontSize: '13px',
                            border: '1px solid #e5e7eb',
                            borderRadius: '6px',
                            outline: 'none',
                            transition: 'border-color 0.2s'
                          }}
                          onFocus={(e) => e.target.style.borderColor = '#2563eb'}
                          onBlur={(e) => e.target.style.borderColor = '#e5e7eb'}
                      />
                    </div>

                    <div style={{ marginTop: '12px' }}>
                      <label style={{ display: 'block', fontSize: '12px', fontWeight: '500', marginBottom: '6px', color: '#374151' }}>
                        Văn bản thay thế (Alt Text)
                      </label>
                      <input
                          type="text"
                          value={componentData.alt || ''}
                          onChange={(e) => handleComponentDataChange('alt', e.target.value)}
                          placeholder="Mô tả hình ảnh"
                          style={{
                            width: '100%',
                            padding: '8px 12px',
                            fontSize: '13px',
                            border: '1px solid #e5e7eb',
                            borderRadius: '6px',
                            outline: 'none',
                            transition: 'border-color 0.2s'
                          }}
                          onFocus={(e) => e.target.style.borderColor = '#2563eb'}
                          onBlur={(e) => e.target.style.borderColor = '#e5e7eb'}
                      />
                    </div>

                    <button
                        onClick={() => fileInputRef.current?.click()}
                        style={{
                          width: '100%',
                          padding: '10px',
                          background: '#3b82f6',
                          color: '#ffffff',
                          border: 'none',
                          borderRadius: '6px',
                          cursor: 'pointer',
                          fontSize: '13px',
                          fontWeight: '600',
                          display: 'flex',
                          alignItems: 'center',
                          justifyContent: 'center',
                          gap: '8px',
                          marginTop: '12px',
                          transition: 'all 0.2s'
                        }}
                        onMouseEnter={(e) => e.currentTarget.style.background = '#2563eb'}
                        onMouseLeave={(e) => e.currentTarget.style.background = '#3b82f6'}
                    >
                      <Upload size={16} />
                      Tải ảnh lên
                    </button>
                    <input
                        ref={fileInputRef}
                        type="file"
                        accept="image/*"
                        multiple
                        onChange={handleFileSelect}
                        style={{ display: 'none' }}
                    />

                    <div style={{ marginTop: '12px' }}>
                      <label style={{ display: 'block', fontSize: '12px', fontWeight: '500', marginBottom: '8px', color: '#374151' }}>
                        Hình ảnh có sẵn
                      </label>
                      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(5, 1fr)', gap: '8px' }}>
                        {ICON_PRESETS.filter(preset => preset.imageUrl).map((preset) => (
                            <button
                                key={preset.name}
                                onClick={() => handleComponentDataChange('imageUrl', preset.imageUrl)}
                                style={{
                                  padding: '12px',
                                  border: componentData.imageUrl === preset.imageUrl ? '2px solid #2563eb' : '1px solid #e5e7eb',
                                  borderRadius: '6px',
                                  background: '#fff',
                                  cursor: 'pointer',
                                  transition: 'all 0.2s',
                                  display: 'flex',
                                  alignItems: 'center',
                                  justifyContent: 'center'
                                }}
                                onMouseEnter={(e) => {
                                  if (componentData.imageUrl !== preset.imageUrl) {
                                    e.currentTarget.style.borderColor = '#93c5fd';
                                    e.currentTarget.style.background = '#f0f9ff';
                                  }
                                }}
                                onMouseLeave={(e) => {
                                  if (componentData.imageUrl !== preset.imageUrl) {
                                    e.currentTarget.style.borderColor = '#e5e7eb';
                                    e.currentTarget.style.background = '#fff';
                                  }
                                }}
                            >
                              <img src={preset.imageUrl} alt={preset.name} style={{ width: '20px', height: '20px', objectFit: 'contain' }} />
                            </button>
                        ))}
                      </div>
                    </div>
                  </>
              )}
            </div>
        )}

        {/* Colors */}
        <div style={{ marginBottom: '24px' }}>
          <h4 style={{ margin: '0 0 12px', fontSize: '13px', fontWeight: '600', display: 'flex', alignItems: 'center', gap: '6px' }}>
            <Palette size={14} />
            Màu sắc
          </h4>
          <div style={{ marginBottom: '16px' }}>
            <label style={{ display: 'block', fontSize: '12px', fontWeight: '500', marginBottom: '6px', color: '#374151' }}>
              Màu Icon
            </label>
            <div style={{ display: 'flex', gap: '8px', marginBottom: '8px' }}>
              <input
                  type="color"
                  value={styles.color || '#000000'}
                  onChange={(e) => handleStyleChange('color', e.target.value)}
                  style={{
                    width: '48px',
                    height: '36px',
                    border: '1px solid #e5e7eb',
                    borderRadius: '6px',
                    cursor: componentData.imageUrl ? 'not-allowed' : 'pointer',
                    opacity: componentData.imageUrl ? 0.5 : 1
                  }}
                  disabled={componentData.imageUrl}
              />
              <input
                  type="text"
                  value={styles.color || '#000000'}
                  onChange={(e) => {
                    const value = e.target.value;
                    if (/^#[0-9A-Fa-f]{6}$/.test(value)) {
                      handleStyleChange('color', value);
                    } else {
                      toast.error('Vui lòng nhập mã màu hex hợp lệ (VD: #FFFFFF)', { position: 'bottom-right' });
                    }
                  }}
                  style={{
                    flex: 1,
                    padding: '0 12px',
                    fontSize: '13px',
                    border: '1px solid #e5e7eb',
                    borderRadius: '6px',
                    opacity: componentData.imageUrl ? 0.5 : 1
                  }}
                  disabled={componentData.imageUrl}
              />
            </div>
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(10, 1fr)', gap: '6px' }}>
              {COLOR_PRESETS.map((color) => (
                  <div
                      key={color}
                      onClick={() => !componentData.imageUrl && handleStyleChange('color', color)}
                      style={{
                        width: '100%',
                        paddingBottom: '100%',
                        background: color,
                        borderRadius: '4px',
                        cursor: componentData.imageUrl ? 'not-allowed' : 'pointer',
                        border: styles.color === color && !componentData.imageUrl ? '2px solid #2563eb' : '1px solid #e5e7eb',
                        position: 'relative',
                        opacity: componentData.imageUrl ? 0.5 : 1
                      }}
                      title={color}
                  />
              ))}
            </div>
          </div>
        </div>

        {/* Size */}
        <div style={{ marginBottom: '24px' }}>
          <h4 style={{ margin: '0 0 12px', fontSize: '13px', fontWeight: '600', display: 'flex', alignItems: 'center', gap: '6px' }}>
            <Box size={14} />
            Kích thước
          </h4>
          <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '12px' }}>
            <div>
              <label style={{ display: 'block', fontSize: '12px', fontWeight: '500', marginBottom: '6px', color: '#374151' }}>
                Chiều rộng
              </label>
              <div style={{ position: 'relative' }}>
                <input
                    type="number"
                    value={size.width || 50}
                    onChange={(e) => handleSizeChange('width', e.target.value)}
                    style={{
                      width: '100%',
                      padding: '8px 32px 8px 12px',
                      fontSize: '13px',
                      border: '1px solid #e5e7eb',
                      borderRadius: '6px',
                      boxSizing: 'border-box',
                      outline: 'none'
                    }}
                    min="0"
                />
                <span style={{
                  position: 'absolute',
                  right: '12px',
                  top: '50%',
                  transform: 'translateY(-50%)',
                  fontSize: '12px',
                  color: '#9ca3af'
                }}>px</span>
              </div>
            </div>
            <div>
              <label style={{ display: 'block', fontSize: '12px', fontWeight: '500', marginBottom: '6px', color: '#374151' }}>
                Chiều cao
              </label>
              <div style={{ position: 'relative' }}>
                <input
                    type="number"
                    value={size.height || 50}
                    onChange={(e) => handleSizeChange('height', e.target.value)}
                    style={{
                      width: '100%',
                      padding: '8px 32px 8px 12px',
                      fontSize: '13px',
                      border: '1px solid #e5e7eb',
                      borderRadius: '6px',
                      boxSizing: 'border-box',
                      outline: 'none'
                    }}
                    min="0"
                />
                <span style={{
                  position: 'absolute',
                  right: '12px',
                  top: '50%',
                  transform: 'translateY(-50%)',
                  fontSize: '12px',
                  color: '#9ca3af'
                }}>px</span>
              </div>
            </div>
          </div>
        </div>

        {/* Effects */}
        <div style={{ marginBottom: '24px' }}>
          <h4 style={{ margin: '0 0 12px', fontSize: '13px', fontWeight: '600', display: 'flex', alignItems: 'center', gap: '6px' }}>
            <Sun size={14} />
            Hiệu ứng
          </h4>
          <div style={{ marginBottom: '16px' }}>
            <label style={{ display: 'block', fontSize: '12px', fontWeight: '500', marginBottom: '6px', color: '#374151' }}>
              Bóng (Drop Shadow)
            </label>
            <input
                type="text"
                value={styles.filter || ''}
                onChange={(e) => handleStyleChange('filter', e.target.value)}
                placeholder="drop-shadow(0 4px 8px rgba(0,0,0,0.2))"
                style={{
                  width: '100%',
                  padding: '8px 12px',
                  fontSize: '13px',
                  border: '1px solid #e5e7eb',
                  borderRadius: '6px',
                  boxSizing: 'border-box',
                  outline: 'none'
                }}
                onFocus={(e) => e.target.style.borderColor = '#2563eb'}
                onBlur={(e) => e.target.style.borderColor = '#e5e7eb'}
            />
          </div>
          <div>
            <label style={{ display: 'block', fontSize: '12px', fontWeight: '500', marginBottom: '6px', color: '#374151' }}>
              Độ mờ (Opacity)
            </label>
            <div style={{ display: 'flex', alignItems: 'center', gap: '12px' }}>
              <input
                  type="range"
                  min="0"
                  max="1"
                  step="0.1"
                  value={styles.opacity || 1}
                  onChange={(e) => handleStyleChange('opacity', parseFloat(e.target.value))}
                  style={{ flex: 1 }}
              />
              <span style={{ fontSize: '12px', color: '#6b7280', minWidth: '32px' }}>
              {((styles.opacity || 1) * 100).toFixed(0)}%
            </span>
            </div>
          </div>
        </div>
      </div>
  );

  // Interaction tab
  const renderInteractionTab = () => (
      <div style={{ padding: '16px', overflowY: 'auto', flex: 1 }}>
        {/* Hover Effects */}
        <div style={{ marginBottom: '24px' }}>
          <h4 style={{ margin: '0 0 12px', fontSize: '13px', fontWeight: '600', display: 'flex', alignItems: 'center', gap: '6px' }}>
            <MousePointer2 size={14} />
            Hiệu ứng Hover
          </h4>
          <select
              value={styles[':hover']?.transform ? HOVER_EFFECTS.find(e => e.transform === styles[':hover'].transform)?.value || 'none' : 'none'}
              onChange={(e) => handleHoverEffectChange(e.target.value)}
              style={{
                width: '100%',
                padding: '8px 12px',
                fontSize: '13px',
                border: '1px solid #e5e7eb',
                borderRadius: '6px',
                background: '#fff',
                cursor: 'pointer'
              }}
          >
            {HOVER_EFFECTS.map((effect) => (
                <option key={effect.value} value={effect.value}>
                  {effect.name}
                </option>
            ))}
          </select>
        </div>

        {/* Click Action */}
        <div style={{ marginBottom: '24px' }}>
          <h4 style={{ margin: '0 0 12px', fontSize: '13px', fontWeight: '600', display: 'flex', alignItems: 'center', gap: '6px' }}>
            <Zap size={14} />
            Hành động khi Click
          </h4>
          <div style={{ marginBottom: '12px' }}>
            <label style={{ display: 'block', fontSize: '12px', fontWeight: '500', marginBottom: '6px', color: '#374151' }}>
              Loại hành động
            </label>
            <select
                value={componentData.events?.onClick?.type || 'none'}
                onChange={(e) => handleComponentDataChange('events', {
                  ...componentData.events,
                  onClick: { ...componentData.events?.onClick, type: e.target.value }
                })}
                style={{
                  width: '100%',
                  padding: '8px 12px',
                  fontSize: '13px',
                  border: '1px solid #e5e7eb',
                  borderRadius: '6px',
                  background: '#fff'
                }}
            >
              <option value="none">Không có</option>
              <option value="navigate">Chuyển hướng URL</option>
              <option value="triggerApi">Gọi API</option>
            </select>
          </div>
          {componentData.events?.onClick?.type === 'navigate' && (
              <div>
                <label style={{ display: 'block', fontSize: '12px', fontWeight: '500', marginBottom: '6px', color: '#374151' }}>
                  URL
                </label>
                <input
                    type="text"
                    value={componentData.events?.onClick?.url || ''}
                    onChange={(e) => {
                      const value = e.target.value;
                      handleComponentDataChange('events', {
                        ...componentData.events,
                        onClick: { ...componentData.events?.onClick, url: value }
                      });
                    }}
                    placeholder="/page or https://..."
                    style={{
                      width: '100%',
                      padding: '8px 12px',
                      fontSize: '13px',
                      border: '1px solid #e5e7eb',
                      borderRadius: '6px',
                      boxSizing: 'border-box',
                      outline: 'none'
                    }}
                    onFocus={(e) => e.target.style.borderColor = '#2563eb'}
                    onBlur={(e) => e.target.style.borderColor = '#e5e7eb'}
                />
              </div>
          )}
          {componentData.events?.onClick?.type === 'triggerApi' && (
              <div>
                <label style={{ display: 'block', fontSize: '12px', fontWeight: '500', marginBottom: '6px', color: '#374151' }}>
                  API URL
                </label>
                <input
                    type="text"
                    value={componentData.events?.onClick?.apiUrl || ''}
                    onChange={(e) => {
                      const value = e.target.value;
                      handleComponentDataChange('events', {
                        ...componentData.events,
                        onClick: { ...componentData.events?.onClick, apiUrl: value }
                      });
                    }}
                    placeholder="/api/endpoint"
                    style={{
                      width: '100%',
                      padding: '8px 12px',
                      fontSize: '13px',
                      border: '1px solid #e5e7eb',
                      borderRadius: '6px',
                      boxSizing: 'border-box',
                      outline: 'none'
                    }}
                    onFocus={(e) => e.target.style.borderColor = '#2563eb'}
                    onBlur={(e) => e.target.style.borderColor = '#e5e7eb'}
                />
              </div>
          )}
        </div>

        {/* Animation */}
        <div style={{ marginBottom: '24px' }}>
          <h4 style={{ margin: '0 0 12px', fontSize: '13px', fontWeight: '600', display: 'flex', alignItems: 'center', gap: '6px' }}>
            <Sparkles size={14} />
            Hoạt hình
          </h4>
          <div style={{ marginBottom: '12px' }}>
            <label style={{ display: 'block', fontSize: '12px', fontWeight: '500', marginBottom: '6px', color: '#374151' }}>
              Loại hoạt hình
            </label>
            <select
                value={componentData.animation?.type || 'none'}
                onChange={(e) => handleComponentDataChange('animation', {
                  ...componentData.animation,
                  type: e.target.value
                })}
                style={{
                  width: '100%',
                  padding: '8px 12px',
                  fontSize: '13px',
                  border: '1px solid #e5e7eb',
                  borderRadius: '6px',
                  background: '#fff'
                }}
            >
              {ANIMATION_PRESETS.map((anim) => (
                  <option key={anim.value} value={anim.value}>
                    {anim.name}
                  </option>
              ))}
            </select>
          </div>
          {componentData.animation?.type && componentData.animation.type !== 'none' && (
              <>
                <div style={{ marginBottom: '12px' }}>
                  <label style={{ display: 'block', fontSize: '12px', fontWeight: '500', marginBottom: '6px', color: '#374151' }}>
                    Thời gian (ms)
                  </label>
                  <div style={{ display: 'flex', alignItems: 'center', gap: '12px' }}>
                    <input
                        type="range"
                        min="100"
                        max="3000"
                        step="100"
                        value={componentData.animation?.duration || 1000}
                        onChange={(e) => handleComponentDataChange('animation', {
                          ...componentData.animation,
                          duration: parseInt(e.target.value)
                        })}
                        style={{ flex: 1 }}
                    />
                    <span style={{ fontSize: '12px', color: '#6b7280', minWidth: '48px' }}>
                  {componentData.animation?.duration || 1000}ms
                </span>
                  </div>
                </div>
                <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
                  <input
                      type="checkbox"
                      id="repeat"
                      checked={componentData.animation?.repeat || false}
                      onChange={(e) => handleComponentDataChange('animation', {
                        ...componentData.animation,
                        repeat: e.target.checked
                      })}
                      style={{ cursor: 'pointer' }}
                  />
                  <label htmlFor="repeat" style={{ fontSize: '12px', color: '#374151', cursor: 'pointer' }}>
                    Lặp lại hoạt hình
                  </label>
                </div>
              </>
          )}
        </div>
      </div>
  );

  // Upload Modal
  const renderUploadModal = () => (
      <div style={{
        position: 'fixed',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        background: 'rgba(0, 0, 0, 0.5)',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        zIndex: 10000
      }}>
        <div style={{
          background: '#ffffff',
          borderRadius: '12px',
          width: '90%',
          maxWidth: '700px',
          maxHeight: '80vh',
          display: 'flex',
          flexDirection: 'column',
          boxShadow: '0 20px 25px -5px rgba(0, 0, 0, 0.1)'
        }}>
          <div style={{
            padding: '20px 24px',
            borderBottom: '1px solid #e5e7eb',
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center'
          }}>
            <h3 style={{ margin: 0, fontSize: '18px', fontWeight: '600' }}>Thư viện hình ảnh</h3>
            <button
                onClick={() => setShowUploadModal(false)}
                style={{
                  background: 'transparent',
                  border: 'none',
                  cursor: 'pointer',
                  padding: '4px',
                  borderRadius: '4px',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center'
                }}
                onMouseEnter={(e) => e.currentTarget.style.background = '#f3f4f6'}
                onMouseLeave={(e) => e.currentTarget.style.background = 'transparent'}
            >
              <X size={20} />
            </button>
          </div>

          <div style={{ padding: '20px 24px', borderBottom: '1px solid #e5e7eb' }}>
            <input
                ref={fileInputRef}
                type="file"
                accept="image/*"
                multiple
                onChange={handleFileSelect}
                style={{ display: 'none' }}
            />
            <button
                onClick={() => fileInputRef.current?.click()}
                disabled={uploading}
                style={{
                  width: '100%',
                  padding: '12px',
                  background: uploading ? '#9ca3af' : '#3b82f6',
                  color: '#ffffff',
                  border: 'none',
                  borderRadius: '8px',
                  cursor: uploading ? 'not-allowed' : 'pointer',
                  fontSize: '14px',
                  fontWeight: '600',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  gap: '8px',
                  transition: 'background 0.2s'
                }}
                onMouseEnter={(e) => !uploading && (e.currentTarget.style.background = '#2563eb')}
                onMouseLeave={(e) => !uploading && (e.currentTarget.style.background = '#3b82f6')}
            >
              {uploading ? <RefreshCw size={18} className="animate-spin" /> : <Upload size={18} />}
              {uploading ? `Đang tải lên... ${progress}%` : 'Tải ảnh lên S3'}
            </button>
            <p style={{
              margin: '8px 0 0 0',
              fontSize: '12px',
              color: '#6b7280',
              textAlign: 'center'
            }}>
              Hỗ trợ JPG, PNG, GIF, WebP - Tối đa 10MB
            </p>
            {uploading && (
                <div style={{
                  marginTop: '12px',
                  width: '100%',
                  height: '6px',
                  background: '#e5e7eb',
                  borderRadius: '3px',
                  overflow: 'hidden'
                }}>
                  <div style={{
                    width: `${progress}%`,
                    height: '100%',
                    background: '#3b82f6',
                    transition: 'width 0.3s ease'
                  }} />
                </div>
            )}
          </div>

          <div style={{
            flex: 1,
            overflowY: 'auto',
            padding: '20px 24px'
          }}>
            {loading ? (
                <div style={{
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  height: '200px'
                }}>
                  <RefreshCw size={24} className="animate-spin" color="#3b82f6" />
                </div>
            ) : images.length === 0 ? (
                <div style={{
                  display: 'flex',
                  flexDirection: 'column',
                  alignItems: 'center',
                  justifyContent: 'center',
                  height: '200px',
                  color: '#9ca3af'
                }}>
                  <ImageIcon size={48} strokeWidth={1.5} />
                  <p style={{ marginTop: '12px', fontSize: '14px' }}>Chưa có hình ảnh nào</p>
                </div>
            ) : (
                <div style={{
                  display: 'grid',
                  gridTemplateColumns: 'repeat(auto-fill, minmax(150px, 1fr))',
                  gap: '16px'
                }}>
                  {images.map((image) => (
                      <div
                          key={image.key || image.id}
                          style={{
                            position: 'relative',
                            aspectRatio: '1',
                            borderRadius: '8px',
                            overflow: 'hidden',
                            border: componentData.imageUrl === image.url ? '3px solid #3b82f6' : '2px solid #e5e7eb',
                            cursor: 'pointer',
                            transition: 'all 0.2s'
                          }}
                          onClick={() => {
                            handleComponentDataChange('imageUrl', image.url);
                            setShowUploadModal(false);
                          }}
                      >
                        <img
                            src={image.url}
                            alt={image.name}
                            style={{
                              width: '100%',
                              height: '100%',
                              objectFit: 'cover'
                            }}
                        />
                        <div
                            style={{
                              position: 'absolute',
                              top: 0,
                              left: 0,
                              right: 0,
                              bottom: 0,
                              background: 'rgba(0, 0, 0, 0)',
                              display: 'flex',
                              alignItems: 'center',
                              justifyContent: 'center',
                              opacity: 0,
                              transition: 'all 0.2s'
                            }}
                            onMouseEnter={(e) => {
                              e.currentTarget.style.opacity = '1';
                              e.currentTarget.style.background = 'rgba(0, 0, 0, 0.5)';
                            }}
                            onMouseLeave={(e) => {
                              e.currentTarget.style.opacity = '0';
                              e.currentTarget.style.background = 'rgba(0, 0, 0, 0)';
                            }}
                        >
                          <button
                              onClick={(e) => {
                                e.stopPropagation();
                                handleDeleteImage(image.key || image.id);
                              }}
                              style={{
                                background: '#ef4444',
                                color: '#ffffff',
                                border: 'none',
                                borderRadius: '6px',
                                padding: '8px',
                                cursor: 'pointer',
                                display: 'flex',
                                alignItems: 'center',
                                justifyContent: 'center'
                              }}
                          >
                            <Trash2 size={16} />
                          </button>
                        </div>
                        <div style={{
                          position: 'absolute',
                          bottom: 0,
                          left: 0,
                          right: 0,
                          background: 'linear-gradient(to top, rgba(0,0,0,0.7), transparent)',
                          padding: '8px',
                          fontSize: '11px',
                          color: '#ffffff',
                          overflow: 'hidden',
                          textOverflow: 'ellipsis',
                          whiteSpace: 'nowrap'
                        }}>
                          {image.name}
                        </div>
                      </div>
                  ))}
                </div>
            )}
          </div>

          <div style={{
            padding: '16px 24px',
            borderTop: '1px solid #e5e7eb',
            display: 'flex',
            justifyContent: 'space-between',
            gap: '12px'
          }}>
            <button
                onClick={loadImages}
                style={{
                  padding: '8px 16px',
                  background: '#10b981',
                  color: '#ffffff',
                  border: 'none',
                  borderRadius: '6px',
                  cursor: 'pointer',
                  fontSize: '13px',
                  fontWeight: '600',
                  display: 'flex',
                  alignItems: 'center',
                  gap: '8px'
                }}
                onMouseEnter={(e) => e.currentTarget.style.background = '#059669'}
                onMouseLeave={(e) => e.currentTarget.style.background = '#10b981'}
            >
              <RefreshCw size={16} />
              Làm mới
            </button>
            <button
                onClick={() => setShowUploadModal(false)}
                style={{
                  padding: '8px 16px',
                  background: '#f3f4f6',
                  color: '#374151',
                  border: 'none',
                  borderRadius: '6px',
                  cursor: 'pointer',
                  fontSize: '13px',
                  fontWeight: '600'
                }}
                onMouseEnter={(e) => e.currentTarget.style.background = '#e5e7eb'}
                onMouseLeave={(e) => e.currentTarget.style.background = '#f3f4f6'}
            >
              Đóng
            </button>
          </div>
        </div>
      </div>
  );

  return (
      <div style={{
        width: '320px',
        height: '100%',
        background: '#ffffff',
        borderLeft: '1px solid #e5e7eb',
        display: 'flex',
        flexDirection: 'column',
        fontFamily: 'system-ui, -apple-system, sans-serif'
      }}>
        <div style={{
          padding: '16px',
          borderBottom: '1px solid #e5e7eb',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'space-between'
        }}>
          <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
            <Star size={16} color="#2563eb" />
            <h3 style={{ margin: 0, fontSize: '14px', fontWeight: '600' }}>Thuộc tính Icon</h3>
          </div>
          <button
              onClick={onToggle}
              style={{
                border: 'none',
                background: 'transparent',
                cursor: 'pointer',
                padding: '4px',
                display: 'flex',
                alignItems: 'center'
              }}
          >
            <ChevronRight size={18} />
          </button>
        </div>

        <div style={{
          display: 'flex',
          borderBottom: '1px solid #e5e7eb'
        }}>
          <button
              onClick={() => setActiveTab('design')}
              style={{
                flex: 1,
                padding: '12px',
                fontSize: '13px',
                fontWeight: activeTab === 'design' ? '600' : '400',
                borderBottom: activeTab === 'design' ? '2px solid #2563eb' : 'none',
                background: 'transparent',
                cursor: 'pointer',
                color: activeTab === 'design' ? '#2563eb' : '#374151',
                transition: 'all 0.2s'
              }}
              onMouseEnter={(e) => {
                if (activeTab !== 'design') e.currentTarget.style.background = '#f3f4f6';
              }}
              onMouseLeave={(e) => {
                if (activeTab !== 'design') e.currentTarget.style.background = 'transparent';
              }}
          >
            Thiết kế
          </button>
          <button
              onClick={() => setActiveTab('interaction')}
              style={{
                flex: 1,
                padding: '12px',
                fontSize: '13px',
                fontWeight: activeTab === 'interaction' ? '600' : '400',
                borderBottom: activeTab === 'interaction' ? '2px solid #2563eb' : 'none',
                background: 'transparent',
                cursor: 'pointer',
                color: activeTab === 'interaction' ? '#2563eb' : '#374151',
                transition: 'all 0.2s'
              }}
              onMouseEnter={(e) => {
                if (activeTab !== 'interaction') e.currentTarget.style.background = '#f3f4f6';
              }}
              onMouseLeave={(e) => {
                if (activeTab !== 'interaction') e.currentTarget.style.background = 'transparent';
              }}
          >
            Tương tác
          </button>
        </div>

        {activeTab === 'design' ? renderDesignTab() : renderInteractionTab()}
        {showUploadModal && renderUploadModal()}
      </div>
  );
};

export default IconPropertiesPanel;
