import React, { useRef, useMemo, useCallback, useState } from 'react';import { useDrop, useDrag } from 'react-dnd';
import { useHotkeys } from 'react-hotkeys-hook';
import { toast } from 'react-toastify';
import QuickEditToolkit from './QuickEditToolkit';
import './Canvas.css';

const ItemTypes = {
  COMPONENT: 'component',
  EXISTING_ELEMENT: 'existingElement',
};

// Memoized component for rendering individual elements
const Element = React.memo(
  ({
    component,
    index,
    isSelected,
    hasQuickEdit,
    onSelectElement,
    handleElementHover,
    onDuplicateElement,
    onDeleteElement,
    onMoveElement,
    updatePosition,
    canvasRef,
  }) => {
    const [{ isDragging, initialOffset }, drag, preview] = useDrag(() => ({
      type: ItemTypes.EXISTING_ELEMENT,
      item: { id: component.id, index },
      collect: (monitor) => ({
        isDragging: !!monitor.isDragging(),
        initialOffset: monitor.getInitialClientOffset(),
      }),
    }));

    const [{ isOver }, drop] = useDrop(() => ({
      accept: ItemTypes.EXISTING_ELEMENT,
      hover: (item) => {
        if (item.id !== component.id) {
          onMoveElement(item, index);
        }
      },
    }));

    const content = useMemo(() => {
      if (!component.json) {
        console.warn('Invalid component structure:', component);
        return <div className="canvas-error-message">Component lỗi: Thiếu json</div>;
      }

      const styles = {
        position: 'absolute',
        left: component.json.styles?.position?.x || 0,
        top: component.json.styles?.position?.y || 0,
        width: component.json.styles?.width || 'auto',
        height: component.json.styles?.height || 'auto',
        transition: isDragging ? 'none' : 'all 0.3s ease',
        ...component.json.styles,
      };

      switch (component.json.type) {
        case 'heading':
          const HeadingTag = component.json.level === 2 ? 'h2' : 'h1';
          return <HeadingTag style={styles}>{component.json.content || 'Tiêu đề'}</HeadingTag>;
        case 'paragraph':
          return <p style={styles}>{component.json.content || 'Đoạn văn'}</p>;
        case 'button':
          return <button style={styles}>{component.json.content || 'Nút'}</button>;
        case 'button-group':
          return (
            <div style={styles}>
              {component.json.content?.map((btn, idx) => (
                <button key={idx} style={{ marginRight: '10px', ...btn.styles }}>
                  {btn.text || 'Nút'}
                </button>
              )) || 'Nhóm nút'}
            </div>
          );
        case 'form':
          return (
            <div style={styles}>
              {component.json.content?.fields?.map((field, idx) => (
                <input key={idx} placeholder={field} style={{ marginBottom: '10px', ...component.json.styles }} />
              )) || 'Form'}
            </div>
          );
        case 'image':
          return <img src={component.json.content || 'https://via.placeholder.com/300x200'} style={styles} alt="Image" />;
        case 'gallery':
          return (
            <div style={{ ...styles, display: 'flex', gap: '10px' }}>
              {component.json.content?.map((src, idx) => (
                <img key={idx} src={src || 'https://via.placeholder.com/150x150'} style={{ width: '150px' }} alt="Gallery" />
              )) || 'Gallery'}
            </div>
          );
        case 'product':
          return (
            <div style={styles}>
              <h3>{component.json.content?.title || 'Sản phẩm'}</h3>
              <p>{component.json.content?.price || '$0.00'}</p>
              <button>{component.json.content?.button || 'Mua ngay'}</button>
            </div>
          );
        case 'carousel':
          return (
            <div style={{ ...styles, overflow: 'hidden', width: '400px', height: '200px' }}>
              <div style={{ display: 'flex', transition: 'transform 0.5s' }}>
                {component.json.content?.map((src, idx) => (
                  <img key={idx} src={src || 'https://via.placeholder.com/400x200'} style={{ width: '400px' }} alt="Carousel" />
                ))}
              </div>
            </div>
          );
        case 'section':
          return (
            <section style={styles}>
              {component.json.layout === 'hero' && (
                <div className="container mx-auto">
                  <h1 className="text-4xl font-bold mb-4 text-white">
                    {component.json.content?.title || 'Welcome'}
                  </h1>
                  <p className="text-xl mb-8 text-white">{component.json.content?.subtitle || 'Subtitle'}</p>
                  <div className="flex gap-4 justify-center">
                    {component.json.content?.buttons?.map((btn, idx) => (
                      <button
                        key={idx}
                        className={`px-6 py-3 rounded-lg font-semibold ${
    btn.type === 'primary'
        ? 'bg-white text-blue-600'
        : 'border-2 border-white text-white bg-transparent'
}`}
                      >
                        {btn.text || 'Nút'}
                      </button>
                    )) || null}
                  </div>
                </div>
              )}
              {isSelected && <div className="canvas-section-label">Hero Section</div>}
            </section>
          );
        default:
          return <div className="canvas-unsupported-type">Loại không hỗ trợ</div>;
      }
    }, [component, isDragging, isSelected]);

    if (component.json?.type === 'section') {
      return content;
    }

    return (
      <div
        ref={(node) => drag(drop(node))}
        className={`canvas-element ${isSelected ? 'selected' : ''} ${hasQuickEdit ? 'quick-edit' : ''} ${
    isOver ? 'hover-over' : ''
}`}
        onClick={() => onSelectElement(component)}
        onMouseEnter={(e) => handleElementHover(e, component)}
        style={{
          zIndex: isDragging ? 1000 : 'auto',
          width: isDragging ? component.json.styles?.width : 'auto',
          height: isDragging ? component.json.styles?.height : 'auto',
        }}
      >
        {content}
        {isSelected && (
          <div className="canvas-element-label">
            {component.json?.type.charAt(0).toUpperCase() + component.json?.type.slice(1) || 'Không xác định'}
          </div>
        )}
      </div>
    );
  }
);

const Canvas = React.memo(
  ({
    pageStructure,
    selectedElement,
    onSelectElement,
    quickEditElement,
    quickEditPosition,
    onQuickEdit,
    onCloseQuickEdit,
    onDuplicateElement,
    onDeleteElement,
    onMoveElement,
    viewMode,
    addComponent,
    moveElement,
    zoomLevel,
    setPageStructure,
  }) => {
    const canvasRef = useRef(null);
    const [dropIndex, setDropIndex] = useState(null);
    const [history, setHistory] = useState([]);
    const [historyIndex, setHistoryIndex] = useState(-1);

    const updatePosition = useCallback((id, newPosition) => {
      setPageStructure((prev) => {
        const newStructure = prev.map((el) =>
          el.id === id
            ? {
                ...el,
                json: {
                  ...el.json,
                  styles: {
                    ...el.json.styles,
                    position: {
                      x: newPosition.x,
                      y: newPosition.y,
                    },
                  },
                },
              }
            : el
        );
        return newStructure;
      });
    }, [setPageStructure]);

    // Make the entire canvas a drop target
    const [{ isOver }, drop] = useDrop(() => ({
      accept: [ItemTypes.COMPONENT, ItemTypes.EXISTING_ELEMENT],
      hover: (item, monitor) => {
        const clientOffset = monitor.getClientOffset();
        if (clientOffset && canvasRef.current) {
          const rect = canvasRef.current.getBoundingClientRect();
          const y = clientOffset.y - rect.top;
          const index = calculateDropIndex(y);
          setDropIndex(index);

          if (item.componentData) {
            item.componentData.json.styles = {
              ...item.componentData.json.styles,
              position: { x: clientOffset.x - rect.left, y },
            };
          }
        }
      },
      drop: (item, monitor) => {
        const clientOffset = monitor.getClientOffset();
        if (!clientOffset || !canvasRef.current) {
          toast.error('Không thể xác định vị trí thả. Vui lòng thử lại!');
          return;
        }

        const rect = canvasRef.current.getBoundingClientRect();
        const position = {
          x: clientOffset.x - rect.left,
          y: clientOffset.y - rect.top,
        };

        setHistory((prev) => {
          const newHistory = prev.slice(0, historyIndex + 1);
          newHistory.push([...pageStructure]);
          return newHistory;
        });
        setHistoryIndex((prev) => prev + 1);

        if (item.componentData) {
          const index = calculateDropIndex(position.y);
          addComponent(
            {
              ...item.componentData,
              json: {
                ...item.componentData.json,
                styles: { ...item.componentData.json.styles, position },
              },
            },
            index
          );
        } else {
          const newIndex = calculateDropIndex(position.y);
          moveElement(item.id, newIndex);
          const updatedStructure = [...pageStructure];
          const targetIndex = updatedStructure.findIndex((el) => el.id === item.id);
          if (targetIndex !== -1) {
            updatedStructure[targetIndex] = {
              ...updatedStructure[targetIndex],
              json: {
                ...updatedStructure[targetIndex].json,
                styles: { ...updatedStructure[targetIndex].json.styles, position },
              },
            };
            setPageStructure(updatedStructure);
          }
        }
        setDropIndex(null);
      },
      collect: (monitor) => ({
        isOver: !!monitor.isOver(),
      }),
    }));

    // Calculate drop index based on y-position
    const calculateDropIndex = useCallback(
      (y) => {
        if (!canvasRef.current) return pageStructure.length;
        const elements = canvasRef.current.querySelectorAll('.canvas-element');
        let index = pageStructure.length;

        elements.forEach((el, i) => {
          const rect = el.getBoundingClientRect();
          const canvasRect = canvasRef.current.getBoundingClientRect();
          const elTop = rect.top - canvasRect.top;
          const elHeight = rect.height;

          if (y < elTop + elHeight / 2) {
            index = i;
            return false;
          }
        });

        return index;
      },
      [pageStructure]
    );

    // Handle element hover for quick edit
    const handleElementHover = useCallback(
      (e, component) => {
        if (quickEditElement?.id === component.id) return;

        const rect = e.currentTarget.getBoundingClientRect();
        const canvasRect = canvasRef.current?.getBoundingClientRect() || { left: 0, top: 0 };
        onQuickEdit(component, {
          x: rect.left - canvasRect.left,
          y: rect.top - canvasRect.top + 20,
        });
      },
      [quickEditElement, onQuickEdit]
    );

    // Keyboard shortcuts
    useHotkeys(
      'delete',
      () => {
        if (selectedElement) {
          setHistory((prev) => {
            const newHistory = prev.slice(0, historyIndex + 1);
            newHistory.push([...pageStructure]);
            return newHistory;
          });
          setHistoryIndex((prev) => prev + 1);
          onDeleteElement(selectedElement);
        }
      },
      [selectedElement, onDeleteElement]
    );
    useHotkeys(
      'up',
      () => {
        if (selectedElement) {
          onMoveElement(selectedElement, 'up');
        }
      },
      [selectedElement, onMoveElement]
    );
    useHotkeys(
      'down',
      () => {
        if (selectedElement) {
          onMoveElement(selectedElement, 'down');
        }
      },
      [selectedElement, onMoveElement]
    );
    useHotkeys(
      'ctrl+z',
      () => {
        if (historyIndex > 0) {
          setHistoryIndex((prev) => prev - 1);
          setPageStructure([...history[historyIndex - 1]]);
          toast.info('Đã hoàn tác!');
        }
      },
      [historyIndex, history, setPageStructure]
    );
    useHotkeys(
      'ctrl+y',
      () => {
        if (historyIndex < history.length - 1) {
          setHistoryIndex((prev) => prev + 1);
          setPageStructure([...history[historyIndex + 1]]);
          toast.info('Đã khôi phục!');
        }
      },
      [historyIndex, history, setPageStructure]
    );

    return (
      <div className="canvas-wrapper">
        <div
          ref={(node) => {
            canvasRef.current = node;
            drop(node);
          }}
          className={`canvas ${viewMode === 'mobile' ? 'mobile' : viewMode === 'tablet' ? 'tablet' : ''}`}
          onMouseLeave={onCloseQuickEdit}
          style={{ zoom: `${zoomLevel / 100}`, position: 'relative' }}
        >
          <div className="canvas-content">
            {pageStructure.length === 0 ? (
              <div className="canvas-empty-state">
                <i className="far fa-mouse-pointer"></i>
                <p>Kéo thả component để bắt đầu</p>
              </div>
            ) : (
              <>
                {pageStructure.map((component, index) => (
                  <React.Fragment key={component.id || index}>
                    {dropIndex === index && <div className="canvas-drop-zone" />}
                    <div className="canvas-element-wrapper">
                      <Element
                        component={component}
                        index={index}
                        isSelected={selectedElement?.id === component.id}
                        hasQuickEdit={quickEditElement?.id === component.id}
                        onSelectElement={onSelectElement}
                        handleElementHover={handleElementHover}
                        onDuplicateElement={onDuplicateElement}
                        onDeleteElement={onDeleteElement}
                        onMoveElement={onMoveElement}
                        updatePosition={updatePosition}
                        canvasRef={canvasRef}
                      />
                    </div>
                  </React.Fragment>
                ))}
                {dropIndex === pageStructure.length && <div className="canvas-drop-zone" />}
              </>
            )}
          </div>

          <QuickEditToolkit
            element={quickEditElement}
            position={quickEditPosition}
            onEdit={() => onSelectElement(quickEditElement)}
            onDuplicate={() => onDuplicateElement(quickEditElement)}
            onDelete={() => onDeleteElement(quickEditElement)}
            onMoveUp={() => onMoveElement(quickEditElement, 'up')}
            onMoveDown={() => onMoveElement(quickEditElement, 'down')}
            onClose={onCloseQuickEdit}
          />
        </div>
      </div>
    );
  }
);

export default Canvas;
