import React, { useState, useMemo, useCallback } from 'react';
import { useDrag } from 'react-dnd';
import { toast } from 'react-toastify';
import { EXTENDED_LIBRARY } from '../../constants/ExtendedLibrary';
import '../../styles/CreateLanding.css';

const ItemTypes = {
    ELEMENT: 'element',
};

const ComponentLibrary = ({ isCollapsed, onToggle }) => {
    const [activeTab, setActiveTab] = useState('sections');
    const [searchTerm, setSearchTerm] = useState('');
    const [activeCategory, setActiveCategory] = useState(null);
    const [activeSubcategory, setActiveSubcategory] = useState(null);

    const categories = useMemo(() => {
        return {
            sections: EXTENDED_LIBRARY.sections.subCategories || [],
            elements: EXTENDED_LIBRARY.elements.subCategories || [],
            popups: EXTENDED_LIBRARY.popups.subCategories || [],
            templates: EXTENDED_LIBRARY.templates.subCategories || [],
            assets: EXTENDED_LIBRARY.assets.subCategories || [],
            dynamicContent: EXTENDED_LIBRARY.dynamicContent.subCategories || [],
            contentManagement: EXTENDED_LIBRARY.contentManagement.subCategories || [],
            mediaManagement: EXTENDED_LIBRARY.mediaManagement.subCategories || [],
            documentManagement: EXTENDED_LIBRARY.documentManagement.subCategories || [],
            fontManagement: EXTENDED_LIBRARY.fontManagement.subCategories || [],
        };
    }, []);

    const filteredItems = useMemo(() => {
        const currentCategory = categories[activeTab] || [];
        let items = [];
        if (activeSubcategory) {
            const subcategory = currentCategory.find((cat) => cat.id === activeSubcategory);
            items = subcategory ? subcategory.templates : [];
        } else if (activeCategory) {
            const category = currentCategory.find((cat) => cat.id === activeCategory);
            items = category ? category.templates : [];
        } else {
            items = currentCategory.flatMap((cat) => cat.templates);
        }
        if (!searchTerm) return items;
        return items.filter((item) =>
            item.name.toLowerCase().includes(searchTerm.toLowerCase())
        );
    }, [activeTab, activeCategory, activeSubcategory, searchTerm, categories]);

    const ComponentItem = ({ item }) => {
        const [{ isDragging }, drag] = useDrag(() => ({
            type: ItemTypes.ELEMENT,
            item: {
                id: item.id,
                isNew: true,
                json: {
                    type: item.json.type,
                    componentData: item.json.componentData,
                    size: item.json.size,
                    styles: item.json.styles,
                    children: item.json.children || [],
                },
            },
            collect: (monitor) => ({
                isDragging: monitor.isDragging(),
            }),
        }));

        return (
            <div
                ref={drag}
                className={`lpb-template-item ${isDragging ? 'lpb-template-item-dragging' : ''}`}
            >
                <i className={`fas fa-${item.icon || 'cube'}`}></i>
                {!isCollapsed && <span>{item.name}</span>}
                {!isCollapsed && <div className="lpb-template-tooltip">{item.description}</div>}
            </div>
        );
    };

    const handleTabClick = useCallback((tab) => {
        setActiveTab(tab);
        setActiveCategory(null);
        setActiveSubcategory(null);
        setSearchTerm('');
    }, []);

    const handleCategoryClick = useCallback((categoryId) => {
        setActiveCategory(activeCategory === categoryId ? null : categoryId);
        setActiveSubcategory(null);
    }, [activeCategory]);

    const handleSubcategoryClick = useCallback((subcategoryId) => {
        setActiveSubcategory(activeSubcategory === subcategoryId ? null : subcategoryId);
    }, [activeSubcategory]);

    // Thêm cột giữa để hiển thị preview khi chọn danh mục
    const renderCategoryPreview = () => {
        if (!activeCategory || isCollapsed) return null;
        const currentCategory = categories[activeTab].find((cat) => cat.id === activeCategory);
        return (
            <div className="lpb-category-preview">
                {currentCategory.templates.map((item) => (
                    <div key={item.id} className="lpb-preview-item" style={item.json.styles}>
                        {item.json.type === 'heading' && <h2>{item.json.componentData.content}</h2>}
                        {item.json.type === 'paragraph' && <p>{item.json.componentData.content}</p>}
                        {item.json.type !== 'heading' && item.json.type !== 'paragraph' && (
                            <span>{item.name}</span>
                        )}
                    </div>
                ))}
            </div>
        );
    };

    return (
        <div className="lpb-main-layout">
            <div className={`lpb-sidebar-left ${isCollapsed ? 'lpb-sidebar-collapsed' : ''}`}>
                <div className="lpb-component-library">
                    <div className="lpb-component-library-header">
                        {!isCollapsed && <h2 className="lpb-component-library-title">Thư viện</h2>}
                        <button onClick={onToggle} className="lpb-component-library-toggle">
                            <i className={`fas fa-chevron-${isCollapsed ? 'right' : 'left'}`}></i>
                        </button>
                    </div>
                    {!isCollapsed && (
                        <>
                            <div className="lpb-component-library-search">
                                <input
                                    type="text"
                                    placeholder="Tìm kiếm..."
                                    value={searchTerm}
                                    onChange={(e) => setSearchTerm(e.target.value)}
                                />
                            </div>
                            <div className="lpb-component-library-tabs">
                                {Object.keys(categories).map((tab) => (
                                    <button
                                        key={tab}
                                        className={`lpb-component-library-tab ${activeTab === tab ? 'active' : ''}`}
                                        onClick={() => handleTabClick(tab)}
                                    >
                                        <i className={`fas fa-${EXTENDED_LIBRARY[tab]?.icon || 'cube'}`}></i>
                                        {!isCollapsed && EXTENDED_LIBRARY[tab]?.name}
                                    </button>
                                ))}
                            </div>
                            <div className="lpb-component-library-wrapper">
                                {categories[activeTab]?.map((category) => (
                                    <div key={category.id} className="lpb-component-library-category-nav">
                                        <button
                                            className={`lpb-component-library-category-btn ${activeCategory === category.id ? 'lpb-component-library-category-btn-active' : ''}`}
                                            onClick={() => handleCategoryClick(category.id)}
                                        >
                                            <i className="fas fa-folder"></i>
                                            {!isCollapsed && category.name}
                                        </button>
                                        {activeCategory === category.id && (
                                            <div className="lpb-component-library-subcategory-nav">
                                                {category.subCategories?.map((subcategory) => (
                                                    <button
                                                        key={subcategory.id}
                                                        className={`lpb-component-library-subcategory-btn ${activeSubcategory === subcategory.id ? 'lpb-component-library-subcategory-btn-active' : ''}`}
                                                        onClick={() => handleSubcategoryClick(subcategory.id)}
                                                    >
                                                        {!isCollapsed && subcategory.name}
                                                    </button>
                                                ))}
                                            </div>
                                        )}
                                    </div>
                                ))}
                                {!isCollapsed && (
                                    <div className="lpb-component-library-template-column">
                                        <div className="lpb-component-library-template-list">
                                            {filteredItems.map((item) => (
                                                <ComponentItem key={item.id} item={item} />
                                            ))}
                                        </div>
                                    </div>
                                )}
                            </div>
                        </>
                    )}
                    {isCollapsed && (
                        <div className="lpb-collapsed-tabs">
                            {Object.keys(categories).map((tab) => (
                                <button
                                    key={tab}
                                    className={`lpb-collapsed-tab ${activeTab === tab ? 'active' : ''}`}
                                    onClick={() => handleTabClick(tab)}
                                >
                                    <i className={`fas fa-${EXTENDED_LIBRARY[tab]?.icon || 'cube'}`}></i>
                                </button>
                            ))}
                        </div>
                    )}
                </div>
            </div>

            {/* Cột giữa (Category Preview) */}
            {!isCollapsed && (
                <div className="lpb-category-column">
                    {renderCategoryPreview()}
                </div>
            )}

            {/* Canvas trung tâm */}
            <div className="lpb-canvas">
                <div className="lpb-guideline"></div>
                <button className="lpb-add-section">Thêm mới Section</button>
            </div>

            {/* Thanh công cụ phụ (Floating Bar) - Hiển thị khi chọn Section */}
            <div className="lpb-floating-bar">
                <i className="fas fa-cog"></i>
                <i className="fas fa-save"></i>
                <i className="fas fa-eye"></i>
                <i className="fas fa-trash"></i>
                <i className="fas fa-arrows-alt-v"></i>
            </div>

            {/* Sidebar phải (Properties Panel) */}
            <div className="lpb-properties-panel">
                <div className="lpb-panel-tabs">
                    <button className="lpb-panel-tab active">Thiết kế</button>
                    <button className="lpb-panel-tab">Sự kiện</button>
                    <button className="lpb-panel-tab">Hiệu ứng</button>
                    <button className="lpb-panel-tab">Nâng cao</button>
                </div>
                <div className="lpb-panel-content">
                    <div className="lpb-setting-block">
                        <label>Kiểu căn chỉnh</label>
                        <select>
                            <option>Tự động</option>
                            <option>Thủ công</option>
                        </select>
                    </div>
                    <div className="lpb-setting-block">
                        <label>Thiết lập Section</label>
                        <input type="text" placeholder="Tên Section" />
                    </div>
                    <div className="lpb-setting-block">
                        <label>Kích thước</label>
                        <input type="number" placeholder="Chiều rộng" /> x <input type="number" placeholder="Chiều cao" />
                    </div>
                    <div className="lpb-setting-block">
                        <label>Màu nền</label>
                        <input type="color" />
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ComponentLibrary;
