import React, { useState, useRef, useCallback } from 'react';
import { DndProvider } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
import { TouchBackend } from 'react-dnd-touch-backend';
import { MultiBackend, TouchTransition } from 'react-dnd-multi-backend';
import { useDrag, useDrop } from 'react-dnd';
import update from 'immutability-helper';

import Toolbar from "./create-page/Toolbar";
import ComponentLibrary from "./create-page/ComponentLibrary";
import Canvas from "./create-page/Canvas";
import PropertiesPanel from "./create-page/PropertiesPanel";

// Multi-backend configuration for desktop and touch devices
const HTML5toTouch = {
    backends: [
        {
            id: 'html5',
            backend: HTML5Backend,
            transition: TouchTransition,
        },
        {
            id: 'touch',
            backend: TouchBackend,
            options: { enableMouseEvents: true },
            preview: true,
            transition: TouchTransition,
        },
    ],
};

// Item types for DnD
const ItemTypes = {
    LIBRARY_COMPONENT: 'library_component',
    PAGE_ELEMENT: 'page_element',
};

// Draggable Component from Library
const DraggableLibraryItem = ({ component, children }) => {
    const [{ isDragging }, drag, dragPreview] = useDrag(() => ({
        type: ItemTypes.LIBRARY_COMPONENT,
        item: { component },
        collect: (monitor) => ({
            isDragging: monitor.isDragging(),
        }),
    }));

    return (
        <>
            <div ref={dragPreview} style={{ display: 'none' }}>
                <div className="bg-white shadow-lg border border-blue-300 rounded-lg p-3 max-w-xs">
                    <div className="text-sm font-medium text-gray-700">
                        {component.json?.type || component.type || 'Component'}
                    </div>
                    <div className="text-xs text-gray-500 mt-1">
                        Adding to page...
                    </div>
                </div>
            </div>
            <div
                ref={drag}
                className={`cursor-grab active:cursor-grabbing ${
                    isDragging ? 'opacity-50' : 'opacity-100'
                }`}
            >
                {children}
            </div>
        </>
    );
};

// Sortable Page Element
const SortablePageElement = ({
                                 element,
                                 index,
                                 moveElement,
                                 children,
                                 isSelected,
                                 onSelect
                             }) => {
    const ref = useRef(null);

    const [{ handlerId }, drop] = useDrop({
        accept: ItemTypes.PAGE_ELEMENT,
        collect(monitor) {
            return {
                handlerId: monitor.getHandlerId(),
            };
        },
        hover(item, monitor) {
            if (!ref.current) {
                return;
            }
            const dragIndex = item.index;
            const hoverIndex = index;

            // Don't replace items with themselves
            if (dragIndex === hoverIndex) {
                return;
            }

            // Determine rectangle on screen
            const hoverBoundingRect = ref.current?.getBoundingClientRect();

            // Get vertical middle
            const hoverMiddleY = (hoverBoundingRect.bottom - hoverBoundingRect.top) / 2;

            // Determine mouse position
            const clientOffset = monitor.getClientOffset();

            // Get pixels to the top
            const hoverClientY = clientOffset.y - hoverBoundingRect.top;

            // Only perform the move when the mouse has crossed half of the items height
            // When dragging downwards, only move when the cursor is below 50%
            // When dragging upwards, only move when the cursor is above 50%

            // Dragging downwards
            if (dragIndex < hoverIndex && hoverClientY < hoverMiddleY) {
                return;
            }

            // Dragging upwards
            if (dragIndex > hoverIndex && hoverClientY > hoverMiddleY) {
                return;
            }

            // Time to actually perform the action
            moveElement(dragIndex, hoverIndex);

            // Note: we're mutating the monitor item here!
            // Generally it's better to avoid mutations,
            // but it's good here for the sake of performance
            // to avoid expensive index searches.
            item.index = hoverIndex;
        },
    });

    const [{ isDragging }, drag, dragPreview] = useDrag({
        type: ItemTypes.PAGE_ELEMENT,
        item: () => {
            return { id: element.id, index };
        },
        collect: (monitor) => ({
            isDragging: monitor.isDragging(),
        }),
    });

    drag(drop(ref));

    return (
        <>
            <div ref={dragPreview} style={{ display: 'none' }}>
                <div className="bg-white shadow-xl border-2 border-blue-500 rounded-lg p-4 transform rotate-2 max-w-xs">
                    <div className="text-sm font-medium text-gray-700">
                        {element.json?.type || 'Element'}
                    </div>
                    <div className="text-xs text-gray-500 mt-1">
                        Reordering...
                    </div>
                </div>
            </div>
            <div
                ref={ref}
                data-handler-id={handlerId}
                className={`relative group transition-all duration-200 ${
                    isSelected ? 'ring-2 ring-blue-500 ring-opacity-50' : ''
                } ${isDragging ? 'opacity-30' : 'opacity-100'}`}
                onClick={(e) => {
                    e.stopPropagation();
                    onSelect(element);
                }}
            >
                {/* Drag Handle */}
                <div className="absolute -left-10 top-2 opacity-0 group-hover:opacity-100 transition-all duration-200 z-20">
                    <div className="bg-gray-600 hover:bg-gray-700 text-white p-2 rounded-md cursor-grab active:cursor-grabbing shadow-lg">
                        <svg className="w-4 h-4" fill="currentColor" viewBox="0 0 20 20">
                            <path d="M7 2a2 2 0 1 1 0 4 2 2 0 0 1 0-4zM7 8a2 2 0 1 1 0 4 2 2 0 0 1 0-4zM7 14a2 2 0 1 1 0 4 2 2 0 0 1 0-4zM13 2a2 2 0 1 1 0 4 2 2 0 0 1 0-4zM13 8a2 2 0 1 1 0 4 2 2 0 0 1 0-4zM13 14a2 2 0 1 1 0 4 2 2 0 0 1 0-4z"/>
                        </svg>
                    </div>
                </div>

                {/* Selection Indicator */}
                {isSelected && (
                    <div className="absolute inset-0 ring-2 ring-blue-500 ring-opacity-50 pointer-events-none rounded-md bg-blue-50 bg-opacity-10" />
                )}

                {/* Hover Effect */}
                <div className="group-hover:bg-blue-50 group-hover:bg-opacity-30 transition-all duration-200 rounded-md">
                    {children}
                </div>
            </div>
        </>
    );
};

// Canvas Drop Zone
const CanvasDropZone = ({
                            children,
                            onDropFromLibrary,
                            canDrop,
                            isOver
                        }) => {
    const [{ canDropHere, isOverCurrent }, drop] = useDrop(() => ({
        accept: ItemTypes.LIBRARY_COMPONENT,
        drop: (item, monitor) => {
            if (monitor.didDrop()) {
                return;
            }
            onDropFromLibrary(item.component);
        },
        collect: (monitor) => ({
            isOverCurrent: monitor.isOver({ shallow: true }),
            canDropHere: monitor.canDrop(),
        }),
    }));

    return (
        <div
            ref={drop}
            className={`flex-1 relative transition-all duration-200 ${
                isOverCurrent && canDropHere
                    ? 'bg-blue-50 border-2 border-dashed border-blue-400'
                    : canDropHere
                        ? 'bg-gray-50 border border-dashed border-gray-300'
                        : ''
            }`}
        >
            {children}

            {/* Drop Indicator */}
            {isOverCurrent && canDropHere && (
                <div className="absolute inset-0 flex items-center justify-center pointer-events-none z-10">
                    <div className="bg-blue-500 text-white px-6 py-3 rounded-lg shadow-lg font-medium">
                        <i className="fas fa-plus-circle mr-2"></i>
                        Drop component here
                    </div>
                </div>
            )}
        </div>
    );
};

// Insert Drop Zone between elements
const InsertDropZone = ({ index, onInsert }) => {
    const [{ isOver, canDrop }, drop] = useDrop(() => ({
        accept: ItemTypes.LIBRARY_COMPONENT,
        drop: (item) => {
            onInsert(item.component, index);
        },
        collect: (monitor) => ({
            isOver: monitor.isOver(),
            canDrop: monitor.canDrop(),
        }),
    }));

    if (!canDrop && !isOver) return null;

    return (
        <div
            ref={drop}
            className={`h-2 mx-4 rounded transition-all duration-200 ${
                isOver
                    ? 'bg-blue-500 shadow-lg h-3'
                    : canDrop
                        ? 'bg-blue-200 border-2 border-dashed border-blue-300'
                        : 'bg-gray-200'
            }`}
        />
    );
};

const CreateLandingPage = () => {
    const [pageStructure, setPageStructure] = useState([]);
    const [selectedElement, setSelectedElement] = useState(null);
    const [leftSidebarCollapsed, setLeftSidebarCollapsed] = useState(false);
    const [rightSidebarCollapsed, setRightSidebarCollapsed] = useState(false);
    const [quickEditElement, setQuickEditElement] = useState(null);
    const [quickEditPosition, setQuickEditPosition] = useState(null);
    const [viewMode, setViewMode] = useState('desktop');
    const [zoomLevel, setZoomLevel] = useState(100);

    // Handle dropping from component library
    const handleDropFromLibrary = useCallback((component) => {
        const newComponent = {
            ...component,
            id: `${component.id}-${Date.now()}`
        };
        setPageStructure(prev => [...prev, newComponent]);
        setQuickEditElement(null);
        setQuickEditPosition(null);
    }, []);

    // Handle inserting component at specific position
    const handleInsertComponent = useCallback((component, index) => {
        const newComponent = {
            ...component,
            id: `${component.id}-${Date.now()}`
        };
        setPageStructure(prev => {
            const newStructure = [...prev];
            newStructure.splice(index, 0, newComponent);
            return newStructure;
        });
    }, []);

    // Handle moving elements within the page
    const moveElement = useCallback((dragIndex, hoverIndex) => {
        setPageStructure(prev =>
            update(prev, {
                $splice: [
                    [dragIndex, 1],
                    [hoverIndex, 0, prev[dragIndex]],
                ],
            })
        );
    }, []);

    const handleSelectElement = (element) => {
        setSelectedElement(element);
        setQuickEditElement(null);
        setQuickEditPosition(null);
    };

    const handleUpdateElement = (updatedElement) => {
        setPageStructure(prev =>
            prev.map(el => el.id === updatedElement.id ? updatedElement : el)
        );
        setSelectedElement(updatedElement);
    };

    const handleQuickEdit = (element, position) => {
        setQuickEditElement(element);
        setQuickEditPosition(position);
    };

    const handleCloseQuickEdit = () => {
        setQuickEditElement(null);
        setQuickEditPosition(null);
    };

    const handleDuplicateElement = (element) => {
        if (!element) return;
        const duplicated = {
            ...element,
            id: `${element.id}-copy-${Date.now()}`
        };
        const elementIndex = pageStructure.findIndex(el => el.id === element.id);
        const newStructure = [...pageStructure];
        newStructure.splice(elementIndex + 1, 0, duplicated);
        setPageStructure(newStructure);
        setQuickEditElement(null);
        setQuickEditPosition(null);
    };

    const handleDeleteElement = (element) => {
        if (!element) return;
        setPageStructure(prev => prev.filter(el => el.id !== element.id));
        if (selectedElement?.id === element.id) {
            setSelectedElement(null);
        }
        setQuickEditElement(null);
        setQuickEditPosition(null);
    };

    const handleZoom = (direction) => {
        const newZoom = direction === 'in'
            ? Math.min(200, zoomLevel + 10)
            : Math.max(50, zoomLevel - 10);
        setZoomLevel(newZoom);
    };

    const handleGenerateCode = async () => {
        const pageConfig = {
            components: pageStructure.map(comp => comp.json),
            meta: {
                title: "Generated Landing Page",
                description: "Page generated from JSON"
            },
            viewMode,
            zoomLevel
        };

        console.log('Generated page config:', pageConfig);
        alert('Code generated! Check console for details.');
    };

    const handlePreview = () => {
        const previewData = encodeURIComponent(JSON.stringify({
            pageStructure,
            viewMode
        }));
        window.open(`/preview?data=${previewData}`, '_blank');
    };

    const handleSave = () => {
        const saveData = {
            pageStructure,
            viewMode,
            zoomLevel,
            savedAt: new Date().toISOString()
        };
        localStorage.setItem('landing-page-builder', JSON.stringify(saveData));
        alert('Đã lưu trang thành công!');
    };

    // Auto-save functionality
    React.useEffect(() => {
        const autoSave = setTimeout(() => {
            if (pageStructure.length > 0) {
                const autoSaveData = {
                    pageStructure,
                    viewMode,
                    zoomLevel,
                    autoSavedAt: new Date().toISOString()
                };
                localStorage.setItem('landing-page-builder-autosave', JSON.stringify(autoSaveData));
            }
        }, 30000);

        return () => clearTimeout(autoSave);
    }, [pageStructure, viewMode, zoomLevel]);

    return (
        <DndProvider backend={MultiBackend} options={HTML5toTouch}>
            <div className="h-screen flex flex-col bg-gray-50">
                <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.0.0/css/all.min.css" />

                <Toolbar
                    onSave={handleSave}
                    onPreview={handlePreview}
                    onGenerateCode={handleGenerateCode}
                    viewMode={viewMode}
                    onViewModeChange={setViewMode}
                />

                <div className="flex-1 flex overflow-hidden">
                    <ComponentLibrary
                        onDragStart={() => {}} // react-dnd handles this
                        isCollapsed={leftSidebarCollapsed}
                        onToggle={() => setLeftSidebarCollapsed(!leftSidebarCollapsed)}
                        DraggableComponent={DraggableLibraryItem}
                    />

                    <CanvasDropZone
                        onDropFromLibrary={handleDropFromLibrary}
                    >
                        <div className="flex-1 flex flex-col overflow-hidden">
                            <Canvas
                                pageStructure={pageStructure}
                                onDrop={() => {}} // react-dnd handles this
                                selectedElement={selectedElement}
                                onSelectElement={handleSelectElement}
                                quickEditElement={quickEditElement}
                                quickEditPosition={quickEditPosition}
                                onQuickEdit={handleQuickEdit}
                                onCloseQuickEdit={handleCloseQuickEdit}
                                onDuplicateElement={handleDuplicateElement}
                                onDeleteElement={handleDeleteElement}
                                onMoveElement={() => {}} // react-dnd handles this
                                viewMode={viewMode}
                                SortablePageElement={SortablePageElement}
                                InsertDropZone={InsertDropZone}
                                moveElement={moveElement}
                                onInsertComponent={handleInsertComponent}
                            />
                        </div>
                    </CanvasDropZone>

                    <PropertiesPanel
                        selectedElement={selectedElement}
                        onUpdateElement={handleUpdateElement}
                        isCollapsed={rightSidebarCollapsed}
                        onToggle={() => setRightSidebarCollapsed(!rightSidebarCollapsed)}
                    />
                </div>

                {/* Bottom Status Bar */}
                <div className="h-10 bg-white border-t border-gray-200 flex items-center justify-between px-4">
                    <div className="flex items-center gap-4 text-sm text-gray-600">
                        <span>Elements: {pageStructure.length}</span>
                        <span>|</span>
                        <span>Selected: {selectedElement ? selectedElement.json?.type || 'Unknown' : 'None'}</span>
                    </div>

                    <div className="flex items-center gap-2">
                        <span className="text-sm text-gray-600 mr-2">View:</span>
                        <div className="flex border border-gray-300 rounded-md overflow-hidden">
                            <button
                                onClick={() => setViewMode('desktop')}
                                className={`px-3 py-1 text-xs font-medium transition-colors ${
                                    viewMode === 'desktop'
                                        ? 'bg-blue-600 text-white'
                                        : 'bg-white text-gray-600 hover:bg-gray-50'
                                }`}
                            >
                                Responsive
                            </button>
                            <button
                                onClick={() => setViewMode('mobile')}
                                className={`px-3 py-1 text-xs font-medium transition-colors ${
                                    viewMode === 'mobile'
                                        ? 'bg-blue-600 text-white'
                                        : 'bg-white text-gray-600 hover:bg-gray-50'
                                }`}
                            >
                                Mobile Only
                            </button>
                            <button
                                onClick={() => setViewMode('adaptive')}
                                className={`px-3 py-1 text-xs font-medium transition-colors ${
                                    viewMode === 'adaptive'
                                        ? 'bg-blue-600 text-white'
                                        : 'bg-white text-gray-600 hover:bg-gray-50'
                                }`}
                            >
                                Adaptive
                            </button>
                        </div>

                        <div className="ml-4 flex items-center gap-1">
                            <button
                                className="p-1 hover:bg-gray-100 rounded text-gray-500 transition-colors"
                                title="Zoom Out"
                                onClick={() => handleZoom('out')}
                            >
                                <i className="fas fa-search-minus text-xs"></i>
                            </button>
                            <span className="text-xs text-gray-500 px-2">{zoomLevel}%</span>
                            <button
                                className="p-1 hover:bg-gray-100 rounded text-gray-500 transition-colors"
                                title="Zoom In"
                                onClick={() => handleZoom('in')}
                            >
                                <i className="fas fa-search-plus text-xs"></i>
                            </button>
                        </div>
                    </div>
                </div>
            </div>
        </DndProvider>
    );
};

export default CreateLandingPage;
