import React, { useState, useEffect, useContext } from 'react';
import { UserContext } from '../context/UserContext';
import Header from '../components/Header';
import Sidebar from '../components/Sidebar';
import PayoutRequest from '../components/PayoutRequest';
import { jwtDecode } from 'jwt-decode';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { toast } from 'react-toastify';
import AOS from 'aos';
import 'aos/dist/aos.css';
import '../styles/Payments.css';
import DogLoader from '../components/Loader';
import {
    DollarSign, CreditCard, Clock, CheckCircle, XCircle,
    Download, Filter, Calendar, TrendingUp, Users, Eye
} from 'lucide-react';

const Payments = () => {
    const { user } = useContext(UserContext);
    const [loading, setLoading] = useState(true);
    const [userRole, setUserRole] = useState(null);
    const [transactions, setTransactions] = useState([]);
    const [stats, setStats] = useState(null);
    const [selectedStatus, setSelectedStatus] = useState('all');
    const [selectedMethod, setSelectedMethod] = useState('all');
    const [dateRange, setDateRange] = useState({ start: '', end: '' });
    const [currentPage, setCurrentPage] = useState(1);
    const [totalPages, setTotalPages] = useState(1);
    const [selectedTransaction, setSelectedTransaction] = useState(null);
    const [showDetailModal, setShowDetailModal] = useState(false);
    const [showPayoutModal, setShowPayoutModal] = useState(false);
    const navigate = useNavigate();
    const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

    const statusOptions = [
        { value: 'all', label: 'Táº¥t cáº£ tráº¡ng thÃ¡i' },
        { value: 'PENDING', label: 'Chá» thanh toÃ¡n', color: '#f59e0b' },
        { value: 'PROCESSING', label: 'Äang xá»­ lÃ½', color: '#3b82f6' },
        { value: 'COMPLETED', label: 'HoÃ n thÃ nh', color: '#10b981' },
        { value: 'FAILED', label: 'Tháº¥t báº¡i', color: '#ef4444' },
        { value: 'CANCELLED', label: 'ÄÃ£ há»§y', color: '#6b7280' },
        { value: 'REFUNDED', label: 'ÄÃ£ hoÃ n tiá»n', color: '#8b5cf6' }
    ];

    const paymentMethods = [
        { value: 'all', label: 'Táº¥t cáº£ phÆ°Æ¡ng thá»©c' },
        { value: 'MOMO', label: 'MOMO', icon: 'ðŸ“±' },
        { value: 'VNPAY', label: 'VNPay', icon: 'ðŸ¦' },
        { value: 'SANDBOX', label: 'Sandbox', icon: 'ðŸ’³' },
        { value: 'BANK_TRANSFER', label: 'Chuyá»ƒn khoáº£n', icon: 'ðŸ§' }
