import React, { useState, useCallback } from 'react';
import { Settings, HelpCircle, Save, ArrowUp, ArrowDown, Eye, EyeOff, Copy, Trash2 } from 'lucide-react';
import { toast } from 'react-toastify';
import '../../styles/CreateLanding.css';

const SectionToolkit = ({
                            element,
                            position,
                            onEdit,
                            onSaveTemplate,
                            onMoveUp,
                            onMoveDown,
                            onToggleVisibility,
                            onDuplicate,
                            onDelete,
                        }) => {
    const { id, type, visible = true } = element;
    const [showSettingsPopup, setShowSettingsPopup] = useState(false);
    const [showTooltip, setShowTooltip] = useState(false);

    const handleSettingsClick = useCallback(() => {
        setShowSettingsPopup(true);
    }, []);

    const handleCloseSettings = useCallback(() => {
        setShowSettingsPopup(false);
    }, []);

    const handleHelpClick = useCallback(() => {
        setShowTooltip(!showTooltip);
    }, [showTooltip]);

    const handleSaveTemplate = useCallback(() => {
        onSaveTemplate(id);
        toast.success('Đã lưu section thành template!');
    }, [id, onSaveTemplate]);

    const handleDelete = useCallback(() => {
        if (window.confirm('Bạn có chắc muốn xóa section này?')) {
            onDelete(id);
            toast.success('Đã xóa section!');
        }
    }, [id, onDelete]);

    return (
        <>
            <div
                className="lpb-section-toolkit"
                style={{
                    position: 'absolute',
                    top: position.y - 40, // Position above the section
                    left: position.x,
                    zIndex: 1002,
                    background: '#fff',
                    border: '1px solid #d1d5db',
                    borderRadius: '8px',
                    boxShadow: '0 4px 12px rgba(0,0,0,0.15)',
                    display: 'flex',
                    gap: '6px',
                    padding: '6px',
                }}
            >
                <button
                    onClick={handleSettingsClick}
                    className="p-2 hover:bg-gray-100 rounded"
                    title="Cấu hình Section"
                >
                    <Settings size={18} className="text-gray-600" />
                </button>
                <button
                    onClick={handleHelpClick}
                    className="p-2 hover:bg-gray-100 rounded"
                    title="Thông tin Section"
                >
                    <HelpCircle size={18} className="text-gray-600" />
                </button>
                <button
                    onClick={handleSaveTemplate}
                    className="p-2 hover:bg-gray-100 rounded"
                    title="Lưu Section Template"
                >
                    <Save size={18} className="text-gray-600" />
                </button>
                <button
                    onClick={() => onMoveUp(id)}
                    className="p-2 hover:bg-gray-100 rounded"
                    title="Di chuyển lên"
                >
                    <ArrowUp size={18} className="text-gray-600" />
                </button>
                <button
                    onClick={() => onMoveDown(id)}
                    className="p-2 hover:bg-gray-100 rounded"
                    title="Di chuyển xuống"
                >
                    <HelpCircle size={18} className="text-gray-600" />
                </button>
                <button
                    onClick={() => onToggleVisibility(id)}
                    className="p-2 hover:bg-gray-100 rounded"
                    title={visible ? 'Ẩn Section' : 'Hiện Section'}
                >
                    {visible ? (
                        <Eye size={18} className="text-gray-600" />
                    ) : (
                        <EyeOff size={18} className="text-gray-600" />
                    )}
                </button>
                <button
                    onClick={() => onDuplicate(id)}
                    className="p-2 hover:bg-gray-100 rounded"
                    title="Nhân bản Section"
                >
                    <Copy size={18} className="text-gray-600" />
                </button>
                <button
                    onClick={handleDelete}
                    className="p-2 hover:bg-red-100 rounded"
                    title="Xóa Section"
                >
                    <Trash2 size={18} className="text-red-500" />
                </button>
            </div>

            {showTooltip && (
                <div
                    className="lpb-tooltip"
                    style={{
                        position: 'absolute',
                        top: position.y - 80,
                        left: position.x,
                        zIndex: 1003,
                        background: '#1f2937',
                        color: '#fff',
                        padding: '8px 12px',
                        borderRadius: '6px',
                        fontSize: '12px',
                        maxWidth: '300px',
                    }}
                >
                    <p>Section ID: {id}</p>
                    <p>Loại Layout: {element.componentData?.structure || 'ladi-standard'}</p>
                    <p>Chiều cao: {element.size?.height || 400}px</p>
                    <p>Trạng thái: {visible ? 'Hiện' : 'Ẩn'}</p>
                </div>
            )}

            {showSettingsPopup && (
                <>
                    <div
                        className="lpb-settings-popup-backdrop"
                        style={{
                            position: 'absolute',
                            top: 0,
                            left: 0,
                            right: 0,
                            bottom: 0,
                            background: 'rgba(0, 0, 0, 0.4)',
                            zIndex: 1003,
                            pointerEvents: 'auto',
                        }}
                        onClick={handleCloseSettings}
                    />
                    <div
                        className="lpb-settings-popup"
                        style={{
                            position: 'absolute',
                            top: '50%',
                            left: '50%',
                            transform: 'translate(-50%, -50%)',
                            width: '90%',
                            maxWidth: '400px',
                            background: '#fff',
                            borderRadius: '12px',
                            boxShadow: '0 8px 32px rgba(0, 0, 0, 0.25)',
                            zIndex: 1004,
                            padding: '20px',
                            display: 'flex',
                            flexDirection: 'column',
                            gap: '12px',
                        }}
                    >
                        <div
                            style={{
                                display: 'flex',
                                justifyContent: 'space-between',
                                alignItems: 'center',
                                borderBottom: '1px solid #e5e7eb',
                                paddingBottom: '8px',
                            }}
                        >
                            <h3 style={{ margin: 0, fontSize: '16px', fontWeight: '600' }}>
                                Cấu hình Section
                            </h3>
                            <button
                                onClick={handleCloseSettings}
                                style={{
                                    background: 'transparent',
                                    border: 'none',
                                    cursor: 'pointer',
                                    padding: '4px',
                                }}
                            >
                                <i className="fas fa-times" style={{ fontSize: '14px', color: '#6b7280' }} />
                            </button>
                        </div>
                        <div>
                            <label
                                style={{ display: 'block', fontSize: '14px', marginBottom: '4px' }}
                            >
                                Chiều cao Section (px)
                            </label>
                            <input
                                type="number"
                                defaultValue={element.size?.height || 400}
                                onChange={(e) =>
                                    onEdit(id, { size: { height: parseInt(e.target.value) || 400 } })
                                }
                                style={{
                                    width: '100%',
                                    padding: '8px',
                                    border: '1px solid #d1d5db',
                                    borderRadius: '6px',
                                }}
                            />
                        </div>
                        <div>
                            <label
                                style={{ display: 'block', fontSize: '14px', marginBottom: '4px' }}
                            >
                                Màu nền
                            </label>
                            <input
                                type="color"
                                defaultValue={element.componentData?.backgroundColor || '#ffffff'}
                                onChange={(e) =>
                                    onEdit(id, { componentData: { backgroundColor: e.target.value } })
                                }
                                style={{ width: '100%', padding: '4px', borderRadius: '6px' }}
                            />
                        </div>
                        <button
                            onClick={handleCloseSettings}
                            style={{
                                padding: '8px 16px',
                                background: '#2563eb',
                                color: '#fff',
                                border: 'none',
                                borderRadius: '6px',
                                cursor: 'pointer',
                                alignSelf: 'flex-end',
                            }}
                        >
                            Lưu
                        </button>
                    </div>
                </>
            )}
            <style>{`
        .lpb-section-toolkit button:hover {
          background: #f3f4f6 !important;
        }
        .lpb-settings-popup {
          animation: popupSlideIn 0.3s ease-out;
        }
        @keyframes popupSlideIn {
          from {
            opacity: 0;
            transform: translate(-50%, -60%) scale(0.95);
          }
          to {
            opacity: 1;
            transform: translate(-50%, -50%) scale(1);
          }
        }
      `}</style>
        </>
    );
};

export default SectionToolkit;
