import React, { useState, useRef, useCallback } from 'react';
import { DndProvider } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
import { TouchBackend } from 'react-dnd-touch-backend';
import { MultiBackend, TouchTransition } from 'react-dnd-multi-backend';
import { useDrag, useDrop } from 'react-dnd';
import update from 'immutability-helper';

// Multi-backend configuration
const HTML5toTouch = {
    backends: [
        {
            id: 'html5',
            backend: HTML5Backend,
            transition: TouchTransition,
        },
        {
            id: 'touch',
            backend: TouchBackend,
            options: { enableMouseEvents: true },
            preview: true,
            transition: TouchTransition,
        },
    ],
};

// Item types for DnD
const ItemTypes = {
    LIBRARY_COMPONENT: 'library_component',
    PAGE_ELEMENT: 'page_element',
};

// Sample components library
const componentLibrary = [
    {
        id: 'hero',
        name: 'Hero Section',
        category: 'headers',
        json: {
            type: 'hero',
            props: {
                title: 'Welcome to Our Platform',
                subtitle: 'Transform your business with our solutions',
                ctaText: 'Get Started'
            }
        }
    },
    {
        id: 'features',
        name: 'Feature Grid',
        category: 'content',
        json: {
            type: 'features',
            props: {
                title: 'Our Features',
                features: [
                    { icon: 'rocket', title: 'Fast', description: 'Lightning fast' },
                    { icon: 'shield', title: 'Secure', description: 'Bank-level security' },
                    { icon: 'users', title: 'Collaborative', description: 'Team work' }
                ]
            }
        }
    },
    {
        id: 'contact',
        name: 'Contact Form',
        category: 'forms',
        json: {
            type: 'form',
            props: {
                title: 'Contact Us',
                fields: ['name', 'email', 'message'],
                submitText: 'Send Message'
            }
        }
    }
];

// Draggable Library Item
const DraggableLibraryItem = ({ component }) => {
    const [{ isDragging }, drag, dragPreview] = useDrag(() => ({
        type: ItemTypes.LIBRARY_COMPONENT,
        item: { component },
        collect: (monitor) => ({
            isDragging: monitor.isDragging(),
        }),
    }));

    return (
        <>
            <div ref={dragPreview} style={{ display: 'none' }}>
                <div className="bg-white shadow-lg border border-blue-300 rounded-lg p-3 max-w-xs">
                    <div className="text-sm font-medium text-gray-700">
                        {component.name}
                    </div>
                    <div className="text-xs text-gray-500 mt-1">
                        Adding to page...
                    </div>
                </div>
            </div>
            <div
                ref={drag}
                className={`bg-white p-4 rounded-lg border-2 border-dashed border-gray-200 hover:border-blue-400 cursor-grab active:cursor-grabbing transition-all ${
                    isDragging ? 'opacity-50' : 'opacity-100'
                }`}
            >
                <div className="text-center">
                    <div className="text-2xl mb-2">ðŸ“¦</div>
                    <div className="font-medium text-sm">{component.name}</div>
                    <div className="text-xs text-gray-500">{component.category}</div>
                </div>
            </div>
        </>
    );
};

// Component Renderer
const ComponentRenderer = ({ element, isSelected, onSelect }) => {
    const { type, props } = element.json;

    const handleClick = (e) => {
        e.stopPropagation();
        onSelect(element);
    };

    switch (type) {
        case 'hero':
            return (
                <section
                    onClick={handleClick}
                    className={`bg-gradient-to-r from-blue-600 to-purple-600 text-white py-20 px-8 text-center cursor-pointer ${
                        isSelected ? 'ring-4 ring-blue-400 ring-opacity-50' : ''
                    }`}
                >
                    <h1 className="text-4xl font-bold mb-4">{props.title}</h1>
                    <p className="text-xl mb-8">{props.subtitle}</p>
                    <button className="bg-white text-blue-600 px-8 py-3 rounded-lg font-semibold hover:bg-gray-100">
                        {props.ctaText}
                    </button>
                </section>
            );

        case 'features':
            return (
                <section
                    onClick={handleClick}
                    className={`py-16 px-8 bg-gray-50 cursor-pointer ${
                        isSelected ? 'ring-4 ring-blue-400 ring-opacity-50' : ''
                    }`}
                >
                    <h2 className="text-3xl font-bold text-center mb-12">{props.title}</h2>
                    <div className="grid md:grid-cols-3 gap-8 max-w-4xl mx-auto">
                        {props.features.map((feature, index) => (
                            <div key={index} className="text-center p-6 bg-white rounded-lg shadow-md">
                                <div className="text-blue-600 text-3xl mb-4">
                                    <i className={`fas fa-${feature.icon}`}></i>
                                </div>
                                <h3 className="text-xl font-semibold mb-2">{feature.title}</h3>
                                <p className="text-gray-600">{feature.description}</p>
                            </div>
                        ))}
                    </div>
                </section>
            );

        case 'form':
            return (
                <section
                    onClick={handleClick}
                    className={`py-16 px-8 bg-white cursor-pointer ${
                        isSelected ? 'ring-4 ring-blue-400 ring-opacity-50' : ''
                    }`}
                >
                    <div className="max-w-md mx-auto">
                        <h2 className="text-2xl font-bold text-center mb-8">{props.title}</h2>
                        <div className="space-y-4">
                            {props.fields.map((field, index) => (
                                <div key={index}>
                                    <label className="block text-sm font-medium text-gray-700 mb-1 capitalize">
                                        {field}
                                    </label>
                                    {field === 'message' ? (
                                        <textarea
                                            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                                            rows={4}
                                            placeholder={`Enter your ${field}`}
                                        />
                                    ) : (
                                        <input
                                            type={field === 'email' ? 'email' : 'text'}
                                            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                                            placeholder={`Enter your ${field}`}
                                        />
                                    )}
                                </div>
                            ))}
                            <button className="w-full bg-blue-600 hover:bg-blue-700 text-white py-3 px-6 rounded-md font-semibold">
                                {props.submitText}
                            </button>
                        </div>
                    </div>
                </section>
            );

        default:
            return (
                <div
                    onClick={handleClick}
                    className={`p-4 bg-gray-100 border border-gray-300 rounded cursor-pointer ${
                        isSelected ? 'ring-4 ring-blue-400 ring-opacity-50' : ''
                    }`}
                >
                    <p>Unknown component type: {type}</p>
                </div>
            );
    }
};

// Sortable Page Element
const SortablePageElement = ({
                                 element,
                                 index,
                                 moveElement,
                                 isSelected,
                                 onSelect,
                                 onDelete
                             }) => {
    const ref = useRef(null);

    const [{ handlerId }, drop] = useDrop({
        accept: ItemTypes.PAGE_ELEMENT,
        collect(monitor) {
            return {
                handlerId: monitor.getHandlerId(),
            };
        },
        hover(item, monitor) {
            if (!ref.current) {
                return;
            }
            const dragIndex = item.index;
            const hoverIndex = index;

            if (dragIndex === hoverIndex) {
                return;
            }

            const hoverBoundingRect = ref.current?.getBoundingClientRect();
            const hoverMiddleY = (hoverBoundingRect.bottom - hoverBoundingRect.top) / 2;
            const clientOffset = monitor.getClientOffset();
            const hoverClientY = clientOffset.y - hoverBoundingRect.top;

            if (dragIndex < hoverIndex && hoverClientY < hoverMiddleY) {
                return;
            }

            if (dragIndex > hoverIndex && hoverClientY > hoverMiddleY) {
                return;
            }

            moveElement(dragIndex, hoverIndex);
            item.index = hoverIndex;
        },
    });

    const [{ isDragging }, drag] = useDrag({
        type: ItemTypes.PAGE_ELEMENT,
        item: () => {
            return { id: element.id, index };
        },
        collect: (monitor) => ({
            isDragging: monitor.isDragging(),
        }),
    });

    drag(drop(ref));

    return (
        <div
            ref={ref}
            data-handler-id={handlerId}
            className={`relative group transition-all duration-200 ${
                isDragging ? 'opacity-30' : 'opacity-100'
            }`}
        >
            {/* Control Buttons */}
            <div className="absolute -right-12 top-4 opacity-0 group-hover:opacity-100 transition-all duration-200 z-20 flex flex-col gap-1">
                {/* Drag Handle */}
                <div className="bg-gray-600 hover:bg-gray-700 text-white p-1 rounded cursor-grab active:cursor-grabbing shadow-lg">
                    <svg className="w-4 h-4" fill="currentColor" viewBox="0 0 20 20">
                        <path d="M7 2a2 2 0 1 1 0 4 2 2 0 0 1 0-4zM7 8a2 2 0 1 1 0 4 2 2 0 0 1 0-4zM7 14a2 2 0 1 1 0 4 2 2 0 0 1 0-4zM13 2a2 2 0 1 1 0 4 2 2 0 0 1 0-4zM13 8a2 2 0 1 1 0 4 2 2 0 0 1 0-4zM13 14a2 2 0 1 1 0 4 2 2 0 0 1 0-4z"/>
                    </svg>
                </div>

                {/* Delete Button */}
                <button
                    onClick={(e) => {
                        e.stopPropagation();
                        onDelete(element);
                    }}
                    className="bg-red-600 hover:bg-red-700 text-white p-1 rounded shadow-lg"
                    title="Delete"
                >
                    <svg className="w-4 h-4" fill="currentColor" viewBox="0 0 20 20">
                        <path fillRule="evenodd" d="M9 2a1 1 0 000 2h2a1 1 0 100-2H9zM4 5a2 2 0 012-2v1a1 1 0 001 1h6a1 1 0 001-1V3a2 2 0 012 2v6a2 2 0 01-2 2H6a2 2 0 01-2-2V5zM8 11a1 1 0 112 0v3a1 1 0 11-2 0v-3zM10 8a1 1 0 011 1v3a1 1 0 11-2 0V9a1 1 0 011-1z" clipRule="evenodd"/>
                    </svg>
                </button>
            </div>

            <ComponentRenderer
                element={element}
                isSelected={isSelected}
                onSelect={onSelect}
            />
        </div>
    );
};

// Canvas Drop Zone
const CanvasDropZone = ({
                            children,
                            onDropFromLibrary
                        }) => {
    const [{ isOver, canDrop }, drop] = useDrop(() => ({
        accept: ItemTypes.LIBRARY_COMPONENT,
        drop: (item, monitor) => {
            if (monitor.didDrop()) {
                return;
            }
            onDropFromLibrary(item.component);
        },
        collect: (monitor) => ({
            isOver: monitor.isOver({ shallow: true }),
            canDrop: monitor.canDrop(),
        }),
    }));

    return (
        <div
            ref={drop}
            className={`flex-1 min-h-full transition-all duration-200 ${
                isOver && canDrop
                    ? 'bg-blue-50 border-4 border-dashed border-blue-400'
                    : canDrop
                        ? 'bg-gray-50 border-2 border-dashed border-gray-300'
                        : ''
            }`}
        >
            {children}

            {/* Drop Indicator */}
            {isOver && canDrop && (
                <div className="absolute inset-0 flex items-center justify-center pointer-events-none z-10">
                    <div className="bg-blue-500 text-white px-6 py-3 rounded-lg shadow-lg font-medium">
                        <i className="fas fa-plus-circle mr-2"></i>
                        Drop component here
                    </div>
                </div>
            )}
        </div>
    );
};

// Main Component
const CreateLandingPage = () => {
    const [pageStructure, setPageStructure] = useState([]);
    const [selectedElement, setSelectedElement] = useState(null);

    // Handle dropping from component library
    const handleDropFromLibrary = useCallback((component) => {
        const newComponent = {
            ...component,
            id: `${component.id}-${Date.now()}`
        };
        setPageStructure(prev => [...prev, newComponent]);
    }, []);

    // Handle moving elements within the page
    const moveElement = useCallback((dragIndex, hoverIndex) => {
        setPageStructure(prev =>
            update(prev, {
                $splice: [
                    [dragIndex, 1],
                    [hoverIndex, 0, prev[dragIndex]],
                ],
            })
        );
    }, []);

    const handleSelectElement = (element) => {
        setSelectedElement(element);
    };

    const handleDeleteElement = (element) => {
        setPageStructure(prev => prev.filter(el => el.id !== element.id));
        if (selectedElement?.id === element.id) {
            setSelectedElement(null);
        }
    };

    const clearAll = () => {
        setPageStructure([]);
        setSelectedElement(null);
    };

    return (
        <DndProvider backend={MultiBackend} options={HTML5toTouch}>
            <div className="h-screen flex bg-gray-100">
                {/* Component Library Sidebar */}
                <div className="w-64 bg-white border-r border-gray-200 p-4 overflow-y-auto">
                    <div className="mb-4">
                        <h2 className="text-lg font-semibold text-gray-800 mb-2">Components</h2>
                        <p className="text-sm text-gray-600">Drag components to canvas</p>
                    </div>

                    <div className="space-y-3">
                        {componentLibrary.map((component) => (
                            <DraggableLibraryItem
                                key={component.id}
                                component={component}
                            />
                        ))}
                    </div>
                </div>

                {/* Main Canvas Area */}
                <div className="flex-1 flex flex-col">
                    {/* Toolbar */}
                    <div className="bg-white border-b border-gray-200 p-4 flex justify-between items-center">
                        <div className="flex items-center gap-4">
                            <h1 className="text-xl font-bold">Landing Page Builder</h1>
                            <span className="text-sm text-gray-500">
                                Elements: {pageStructure.length}
                            </span>
                        </div>
                        <div className="flex gap-2">
                            <button
                                onClick={clearAll}
                                className="px-4 py-2 text-red-600 border border-red-200 rounded hover:bg-red-50"
                            >
                                Clear All
                            </button>
                            <button className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700">
                                Preview
                            </button>
                        </div>
                    </div>

                    {/* Canvas */}
                    <CanvasDropZone onDropFromLibrary={handleDropFromLibrary}>
                        <div className="flex-1 overflow-y-auto">
                            {pageStructure.length === 0 ? (
                                <div className="h-full flex items-center justify-center">
                                    <div className="text-center text-gray-500">
                                        <div className="text-6xl mb-4">ðŸ“„</div>
                                        <p className="text-xl font-medium mb-2">Empty Canvas</p>
                                        <p>Drag components from the left sidebar to get started</p>
                                    </div>
                                </div>
                            ) : (
                                <div className="relative">
                                    {pageStructure.map((element, index) => (
                                        <SortablePageElement
                                            key={element.id}
                                            index={index}
                                            element={element}
                                            moveElement={moveElement}
                                            isSelected={selectedElement?.id === element.id}
                                            onSelect={handleSelectElement}
                                            onDelete={handleDeleteElement}
                                        />
                                    ))}
                                </div>
                            )}
                        </div>
                    </CanvasDropZone>
                </div>

                {/* Properties Panel */}
                <div className="w-64 bg-white border-l border-gray-200 p-4">
                    <h3 className="text-lg font-semibold text-gray-800 mb-4">Properties</h3>
                    {selectedElement ? (
                        <div>
                            <div className="mb-4">
                                <label className="block text-sm font-medium text-gray-700 mb-1">
                                    Component Type
                                </label>
                                <div className="text-sm text-gray-600 capitalize">
                                    {selectedElement.json.type}
                                </div>
                            </div>

                            <div className="mb-4">
                                <label className="block text-sm font-medium text-gray-700 mb-1">
                                    Element ID
                                </label>
                                <div className="text-xs text-gray-500 font-mono">
                                    {selectedElement.id}
                                </div>
                            </div>

                            <div className="mb-4">
                                <label className="block text-sm font-medium text-gray-700 mb-1">
                                    JSON Configuration
                                </label>
                                <pre className="text-xs bg-gray-100 p-2 rounded overflow-auto max-h-40">
                                    {JSON.stringify(selectedElement.json, null, 2)}
                                </pre>
                            </div>
                        </div>
                    ) : (
                        <p className="text-gray-500 text-sm">
                            Select an element to view its properties
                        </p>
                    )}
                </div>
            </div>
        </DndProvider>
    );
};

export default CreateLandingPage;
