                .arc-action-btn.arc-delete:hover {
                    background: #ef4444;
                    color: #ffffff;
                }

                .arc-action-btn[data-tooltip]:hover:after {
                    content: attr(data-tooltip);
                    position: absolute;
                    top: 100%;
                    left: 50%;
                    transform: translateX(-50%) translateY(4px);
                    background: #1f2937;
