        }
      } else if (item.json) {
        setDragPreview({
          id: 'preview',
          x: snapped.x,
          y: snapped.y,
          size: item.json.size || {
            width: 200,
            height: 50
          },
          type: item.json.type,
          componentData: item.json.componentData,
          styles: item.json.styles
        });
      }
      const newGuidelines = [];
      pageData.elements.forEach(el => {
        if (el.id === item.id) return;
        const bounds = getElementBounds(el);
        if (Math.abs(snapped.x - bounds.left) < 15) newGuidelines.push({
          vertical: true,
