import React, { useState, useEffect, useContext } from 'react';
import { UserContext } from '../context/UserContext';
import Header from '../components/Header';
import Sidebar from '../components/Sidebar';
import DogLoader from '../components/Loader';
import { useNavigate } from 'react-router-dom';
import AOS from 'aos';
import 'aos/dist/aos.css';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { userApi } from '../utils/userApi';
import '../styles/AdminUser.css';

const AdminUser = () => {
    const { user } = useContext(UserContext);
    const [loading, setLoading] = useState(true);
    const [userRole, setUserRole] = useState(null);
    const [users, setUsers] = useState([]);
    const [form, setForm] = useState({
        name: '',
        email: '',
        password: '',
        role: 'user',
        subscription: 'free',
    });
    const [editingId, setEditingId] = useState(null);
    const navigate = useNavigate();

    // -------------------- DEBUG LOG --------------------
    useEffect(() => {
        console.log('[AdminUser] mount, user:', user);
    }, [user]);

    // -------------------- AUTH CHECK --------------------
    useEffect(() => {
        if (!user) return; // ch∆∞a load xong
        console.log('[AdminUser] user.role:', user.role);
        if (user.role !== 'admin') {
            toast.error('B·∫°n kh√¥ng c√≥ quy·ªÅn truy c·∫≠p trang n√†y');
            navigate('/auth');
        } else {
            setUserRole(user.role);
        }
    }, [user, navigate]);

    // -------------------- INIT --------------------
    useEffect(() => {
        AOS.init({ duration: 600, once: true });
    }, []);

    // -------------------- LOAD USERS --------------------
    useEffect(() => {
        if (userRole === 'admin') {
            loadUsers();
        }
    }, [userRole]);

    const loadUsers = async () => {
        console.log('[AdminUser] Loading users...');
        try {
            setLoading(true);
            const res = await userApi.getAll();
            console.log('[AdminUser] users:', res.data);
            setUsers(res.data);
        } catch (err) {
            console.error('[AdminUser] loadUsers error:', err);
            toast.error('Kh√¥ng th·ªÉ t·∫£i danh s√°ch ng∆∞·ªùi d√πng');
        } finally {
            setLoading(false);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            if (editingId) {
                await userApi.update(editingId, form);
                toast.success('C·∫≠p nh·∫≠t ng∆∞·ªùi d√πng th√†nh c√¥ng');
            } else {
                await userApi.create(form);
                toast.success('T·∫°o ng∆∞·ªùi d√πng m·ªõi th√†nh c√¥ng');
            }
            setForm({ name: '', email: '', password: '', role: 'user', subscription: 'free' });
            setEditingId(null);
            loadUsers();
        } catch (err) {
            console.error('[AdminUser] handleSubmit error:', err);
            toast.error(err.response?.data?.message || 'L·ªói h·ªá th·ªëng');
        }
    };

    const handleEdit = (u) => {
        setEditingId(u._id);
        setForm({ ...u, password: '' });
    };

    const handleDelete = async (id) => {
        if (!window.confirm('B·∫°n c√≥ ch·∫Øc ch·∫Øn mu·ªën x√≥a ng∆∞·ªùi d√πng n√†y?')) return;
        try {
            await userApi.remove(id);
            toast.info('ƒê√£ x√≥a ng∆∞·ªùi d√πng');
            loadUsers();
        } catch (err) {
            console.error('[AdminUser] handleDelete error:', err);
            toast.error('Kh√¥ng th·ªÉ x√≥a ng∆∞·ªùi d√πng');
        }
    };

    if (loading && users.length === 0) return <DogLoader />;

    return (
        <div className="admin-user-container">
            <Sidebar role={userRole} />
            <div className="admin-user-main">
                <Header />
                <div className="admin-user-content" data-aos="fade-up">
                    <h1>üë• Qu·∫£n l√Ω t√†i kho·∫£n</h1>

                    {/* Form th√™m/s·ª≠a user */}
                    <form onSubmit={handleSubmit} className="admin-user-form">
                        <input type="text" placeholder="T√™n" value={form.name} onChange={e => setForm({...form, name: e.target.value})} />
                        <input type="email" placeholder="Email" value={form.email} onChange={e => setForm({...form, email: e.target.value})} />
                        {!editingId && <input type="password" placeholder="M·∫≠t kh·∫©u" value={form.password} onChange={e => setForm({...form, password: e.target.value})} />}
                        <select value={form.role} onChange={e => setForm({...form, role: e.target.value})}>
                            <option value="user">User</option>
                            <option value="admin">Admin</option>
                        </select>
                        <select value={form.subscription} onChange={e => setForm({...form, subscription: e.target.value})}>
                            <option value="free">Free</option>
                            <option value="premium">Premium</option>
                        </select>
                        <button type="submit" className="btn-primary">{editingId ? 'C·∫≠p nh·∫≠t' : 'Th√™m m·ªõi'}</button>
                    </form>

                    {/* B·∫£ng user */}
                    <div className="user-table-container">
                        {loading ? <DogLoader /> : (
                            <table className="user-table">
                                <thead>
                                <tr>
                                    <th>T√™n</th>
                                    <th>Email</th>
                                    <th>Role</th>
                                    <th>G√≥i</th>
                                    <th>Ng√†y t·∫°o</th>
                                    <th>H√†nh ƒë·ªông</th>
                                </tr>
                                </thead>
                                <tbody>
                                {users.map(u => (
                                    <tr key={u._id}>
                                        <td>{u.name}</td>
                                        <td>{u.email}</td>
                                        <td>{u.role}</td>
                                        <td>{u.subscription}</td>
                                        <td>{new Date(u.createdAt).toLocaleDateString('vi-VN')}</td>
                                        <td>
                                            <button onClick={() => handleEdit(u)}>S·ª≠a</button>
                                            <button onClick={() => handleDelete(u._id)}>X√≥a</button>
                                        </td>
                                    </tr>
                                ))}
                                </tbody>
                            </table>
                        )}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default AdminUser;
