    const canvasWidth = pageData.canvas?.width || '1200px';
    const canvasBackground = pageData.canvas?.background || '#ffffff';

    let html = `
<!DOCTYPE html>
<html lang="vi">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
