        }
        .ladi-video {
            max-width: 100%;
        }
        .ladi-icon {
            display: inline-block;
        }
        .ladi-gallery {
            display: grid;
            gap: 10px;
        }
        .ladi-carousel {
            overflow: hidden;
            position: relative;
        }
        .ladi-tabs {
            display: flex;
            flex-direction: column;
        }
        .ladi-tabs-nav {
            display: flex;
            gap: 10px;
            border-bottom: 1px solid var(--border-color);
        }
        .ladi-tab {
            padding: 8px 16px;
            cursor: pointer;
        }
        .ladi-tab-content {
            padding: 16px;
        }
        .ladi-accordion-item {
            border-bottom: 1px solid var(--border-color);
        }
        .ladi-accordion-title {
            padding: 8px;
            cursor: pointer;
        }
        .ladi-table {
            width: 100%;
            border-collapse: collapse;
        }
        .ladi-table th, .ladi-table td {
            border: 1px solid var(--border-color);
            padding: 8px;
            text-align: left;
        }
        .ladi-survey {
            display: flex;
            flex-direction: column;
            gap: 10px;
        }
        .ladi-html {
            overflow: auto;
        }
        .ladi-collection {
            display: flex;
            flex-direction: column;
            gap: 20px;
        }
        .upload-input {
            margin: 10px 0;
        }
    </style>
    <script>
        function handleDragStart(event) {
            event.dataTransfer.setData('text/plain', event.target.id);
            event.target.style.opacity = '0.5';
        }
        function handleDragOver(event) {
            event.preventDefault();
        }
        function handleDrop(event) {
            event.preventDefault();
            const id = event.dataTransfer.getData('text/plain');
            const element = document.getElementById(id);
            const rect = event.currentTarget.getBoundingClientRect();
            const x = event.clientX - rect.left;
            const y = event.clientY - rect.top;
            element.style.left = x + 'px';
            element.style.top = y + 'px';
            element.style.opacity = '1';
            console.log('Dropped element:', id, 'at position:', { x, y });
        }
        function handleFileUpload(event, elementId) {
            const file = event.target.files[0];
            if (file) {
                console.log('Uploading file:', file.name, 'for element:', elementId);
                // Simulate S3 upload (replace with actual API call)
                const formData = new FormData();
                formData.append('file', file);
                fetch('/api/upload', {
                    method: 'POST',
                    body: formData
                })
                .then(response => response.json())
                .then(data => {
                    console.log('Uploaded to S3:', data.url);
                    const img = document.getElementById(elementId + '-img');
                    if (img) img.src = data.url;
                })
                .catch(error => console.error('Upload failed:', error));
            }
        }
    </script>
</head>
<body>
    <div class="page-container" ondragover="handleDragOver(event)" ondrop="handleDrop(event)">
    `;

    const renderElement = (element, isChild = false, parentType = '') => {
        const { id, type, componentData = {}, position = {}, size = {}, styles = {}, children = [] } = element;

        // Normalize styles and position
        const positionStyles = position.desktop ? `left: ${position.desktop.x || 0}px; top: ${position.desktop.y || 0}px;` : '';
        const sizeStyles = size ? `width: ${size.width || 200}px; height: ${size.height || 50}px;` : '';
        const cssStyles = styles ? Object.entries(styles).filter(([key]) => key !== 'position').map(([key, value]) => `${key}: ${value};`).join(' ') : '';
        const elementClass = isChild ? `lpb-child-element ladi-${type}` : `lpb-canvas-element ladi-${type}`;
        const elementPosition = isChild ? `position: absolute; ${positionStyles}` : (position.desktop ? `position: absolute; transform: translate(${position.desktop.x || 0}px, ${position.desktop.y || 0}px);` : '');

        let elementHtml = `<div id="${id}" class="${elementClass}" style="${elementPosition} ${sizeStyles} ${cssStyles}" data-type="${type}">`;

        switch (type) {
            case 'section':
                if (componentData.structure === 'ladi-standard') {
                    elementHtml += `
                        <div class="ladi-section" style="width: 100%; height: 100%; position: relative;">
                            <div class="ladi-section-background" style="background-color: ${componentData.backgroundColor || 'transparent'}; background-image: ${componentData.backgroundImage ? `url(${componentData.backgroundImage})` : 'none'}; background-size: cover; background-position: center;"></div>
                            <div class="ladi-overlay" style="background-color: ${componentData.overlayColor || 'transparent'}; opacity: ${componentData.overlayOpacity || 0};"></div>
                            <div class="ladi-container" style="padding: ${componentData.padding || '20px'}; width: 100%; height: 100%; position: relative;" ondragover="handleDragOver(event)" ondrop="handleDrop(event)">
                    `;
                    children.forEach((child) => {
                        elementHtml += renderElement(child, true, type);
                    });
                    elementHtml += `</div></div>`;
                } else {
                    elementHtml += `<div style="display: ${componentData.layoutType || 'flex'}; flex-direction: ${componentData.direction || 'column'}; gap: ${componentData.gap || '10px'}; padding: ${componentData.padding || '0'}; background: ${componentData.background || 'transparent'}; ${cssStyles}" ondragover="handleDragOver(event)" ondrop="handleDrop(event)">`;
                    if (componentData.title && !children.some(child => child?.type === 'heading')) {
                        elementHtml += `<h2 style="font-size: ${componentData.titleFontSize || '1.8rem'}; margin: ${componentData.titleMargin || '0'}; color: ${componentData.titleColor || '#1f2937'}; font-weight: ${componentData.titleFontWeight || '700'};">${componentData.title}</h2>`;
                    }
                    children.forEach((child) => {
                        elementHtml += renderElement(child, true, type);
                    });
                    elementHtml += `</div>`;
                }
                break;
            case 'container':
            case 'box':
                elementHtml += `<div style="display: ${componentData.layoutType || 'flex'}; flex-direction: ${componentData.direction || 'column'}; gap: ${componentData.gap || '10px'}; padding: ${componentData.padding || '20px'}; background: ${componentData.background || 'transparent'}; ${cssStyles}" ondragover="handleDragOver(event)" ondrop="handleDrop(event)">`;
                if (componentData.title && !children.some(child => child?.type === 'heading')) {
                    elementHtml += `<h2 style="font-size: ${componentData.titleFontSize || '1.8rem'}; margin: ${componentData.titleMargin || '0'}; color: ${componentData.titleColor || '#1f2937'}; font-weight: ${componentData.titleFontWeight || '700'};">${componentData.title}</h2>`;
                }
                children.forEach((child) => {
                    elementHtml += renderElement(child, true, type);
                });
                elementHtml += `</div>`;
                break;
            case 'heading':
                const headingLevel = componentData.level && ['h1', 'h2', 'h3', 'h4', 'h5', 'h6'].includes(componentData.level)
                    ? componentData.level
                    : 'h2';
                elementHtml += `<${headingLevel} style="font-size: ${componentData.fontSize || '1.5rem'}; color: ${componentData.color || '#1f2937'}; margin: ${componentData.margin || '0'}; padding: ${componentData.padding || '0'}; font-weight: ${componentData.fontWeight || '700'}; text-align: ${componentData.textAlign || 'left'}; ${cssStyles}">${componentData.content || 'Tiêu đề'}</${headingLevel}>`;
                break;
            case 'paragraph':
                elementHtml += `<p style="font-size: ${componentData.fontSize || '1rem'}; color: ${componentData.color || '#1f2937'}; margin: ${componentData.margin || '0'}; padding: ${componentData.padding || '0'}; font-weight: ${componentData.fontWeight || '400'}; line-height: ${componentData.lineHeight || '1.6'}; text-align: ${componentData.textAlign || 'left'}; ${cssStyles}">${componentData.content || 'Đoạn văn'}</p>`;
                break;
            case 'button':
                elementHtml += `<button style="padding: ${componentData.padding || '8px 16px'}; border-radius: ${componentData.borderRadius || '8px'}; background: ${componentData.background || '#2563eb'}; color: ${componentData.color || '#fff'}; border: ${componentData.border || 'none'}; cursor: pointer; font-size: ${componentData.fontSize || '1rem'}; font-weight: ${componentData.fontWeight || '600'}; text-align: ${componentData.textAlign || 'center'}; ${cssStyles}">${componentData.content || 'Nút'}</button>`;
                break;
            case 'image':
                elementHtml += `
                    <div>
                        <input type="file" class="upload-input" accept="image/*" onchange="handleFileUpload(event, '${id}')">
                        <img id="${id}-img" src="${componentData.src || 'https://via.placeholder.com/150?text=Image'}" alt="${componentData.alt || 'Hình ảnh'}" style="max-width: ${componentData.maxWidth || '100%'}; height: ${componentData.height || 'auto'}; border-radius: ${componentData.borderRadius || '0'}; ${cssStyles}" />
                    </div>`;
                break;
            case 'form':
                elementHtml += `<form style="display: flex; flex-direction: ${componentData.direction || 'column'}; gap: ${componentData.gap || '10px'}; ${cssStyles}" ondragover="handleDragOver(event)" ondrop="handleDrop(event)">`;
                if (componentData.title && !children.some(child => child?.type === 'heading')) {
                    elementHtml += `<h3 style="margin: ${componentData.titleMargin || '0'}; font-size: ${componentData.titleFontSize || '1.2rem'}; color: ${componentData.titleColor || '#1f2937'}; font-weight: ${componentData.titleFontWeight || '600'};">${componentData.title}</h3>`;
                }
                if (Array.isArray(componentData.fields) && componentData.fields.length > 0) {
                    componentData.fields.forEach((field, index) => {
                        elementHtml += `<input type="${field.type || 'text'}" placeholder="${field.placeholder || field.label || 'Nhập...'}" style="padding: ${field.padding || '8px'}; border-radius: ${field.borderRadius || '8px'}; border: ${field.border || '1px solid #ccc'}; font-size: ${field.fontSize || '1rem'};">`;
                    });
                } else {
                    elementHtml += `<input type="${componentData.inputType || 'text'}" placeholder="${componentData.placeholder || 'Nhập...'}" style="padding: ${componentData.inputPadding || '8px'}; border-radius: ${componentData.inputBorderRadius || '8px'}; border: ${componentData.inputBorder || '1px solid #ccc'}; font-size: ${componentData.inputFontSize || '1rem'};">`;
                }
                if (componentData.buttonText && !children.some(child => child?.type === 'button')) {
                    elementHtml += `<button type="submit" style="background: ${componentData.buttonBackground || '#2563eb'}; color: ${componentData.buttonColor || '#fff'}; padding: ${componentData.buttonPadding || '8px 16px'}; border-radius: ${componentData.buttonBorderRadius || '8px'}; border: ${componentData.buttonBorder || 'none'}; cursor: pointer; font-size: ${componentData.buttonFontSize || '1rem'}; font-weight: ${componentData.buttonFontWeight || '600'};">${componentData.buttonText || 'Gửi'}</button>`;
                }
                children.forEach((child) => {
                    elementHtml += renderElement(child, true, type);
                });
                elementHtml += `</form>`;
                break;
            case 'card':
                elementHtml += `<div class="ladi-card" style="padding: ${componentData.padding || '16px'}; background: ${componentData.background || '#ffffff'}; border-radius: ${componentData.borderRadius || '8px'}; box-shadow: ${componentData.boxShadow || '0 2px 8px rgba(0,0,0,0.1)'}; ${cssStyles}">`;
                if (componentData.title) {
                    elementHtml += `<h3 style="font-size: ${componentData.titleFontSize || '1.2rem'}; margin: ${componentData.titleMargin || '0 0 8px'}; color: ${componentData.titleColor || '#1f2937'}; font-weight: ${componentData.titleFontWeight || '600'};">${componentData.title}</h3>`;
                }
                if (componentData.content) {
                    elementHtml += `<p style="font-size: ${componentData.contentFontSize || '1rem'}; margin: ${componentData.contentMargin || '0'}; color: ${componentData.contentColor || '#4b5563'};">${componentData.content}</p>`;
                }
                children.forEach((child) => {
                    elementHtml += renderElement(child, true, type);
                });
                elementHtml += `</div>`;
                break;
            case 'testimonial':
                elementHtml += `<div class="ladi-testimonial" style="padding: ${componentData.padding || '20px'}; background: ${componentData.background || '#f9fafb'}; border-radius: ${componentData.borderRadius || '8px'}; ${cssStyles}">`;
                if (componentData.quote) {
                    elementHtml += `<p style="font-size: ${componentData.quoteFontSize || '1rem'}; margin: ${componentData.quoteMargin || '0 0 8px'}; color: ${componentData.quoteColor || '#1f2937'}; font-style: italic;">"${componentData.quote}"</p>`;
                }
                if (componentData.author) {
                    elementHtml += `<p style="font-size: ${componentData.authorFontSize || '0.9rem'}; margin: ${componentData.authorMargin || '0'}; color: ${componentData.authorColor || '#4b5563'}; font-weight: ${componentData.authorFontWeight || '600'};">- ${componentData.author}</p>`;
                }
                children.forEach((child) => {
                    elementHtml += renderElement(child, true, type);
                });
                elementHtml += `</div>`;
                break;
            case 'menu':
                elementHtml += `<ul class="ladi-menu" style="${cssStyles}">`;
                if (Array.isArray(componentData.items)) {
                    componentData.items.forEach((item) => {
                        elementHtml += `<li><a href="${item.url || '#'}" style="font-size: ${componentData.itemFontSize || '1rem'}; color: ${componentData.itemColor || '#1f2937'}; ${cssStyles}">${item.label || 'Menu Item'}</a></li>`;
                    });
                }
                children.forEach((child) => {
                    elementHtml += renderElement(child, true, type);
                });
                elementHtml += `</ul>`;
                break;
            case 'grid':
                elementHtml += `<div class="ladi-grid" style="grid-template-columns: repeat(${componentData.columns || 2}, 1fr); gap: ${componentData.gap || '20px'}; ${cssStyles}" ondragover="handleDragOver(event)" ondrop="handleDrop(event)">`;
                if (Array.isArray(componentData.items)) {
                    componentData.items.forEach((item, index) => {
                        elementHtml += `<div style="padding: ${componentData.itemPadding || '16px'}; background: ${componentData.itemBackground || '#ffffff'}; border-radius: ${componentData.itemBorderRadius || '8px'}; box-shadow: ${componentData.itemBoxShadow || '0 2px 8px rgba(0,0,0,0.1)'}; ${cssStyles}" draggable="true" ondragstart="handleDragStart(event)">`;
                        if (item.image) {
                            elementHtml += `<input type="file" class="upload-input" accept="image/*" onchange="handleFileUpload(event, '${id}-item-${index}')">`;
                            elementHtml += `<img id="${id}-item-${index}-img" src="${item.image}" alt="${item.title || 'Item'}" style="max-width: 100%; height: auto; border-radius: ${componentData.imageBorderRadius || '8px'};" />`;
                        }
                        if (item.title) {
                            elementHtml += `<h4 style="font-size: ${componentData.titleFontSize || '1.1rem'}; margin: ${componentData.titleMargin || '8px 0 0'}; color: ${componentData.titleColor || '#1f2937'};">${item.title}</h4>`;
                        }
                        if (item.price) {
                            elementHtml += `<p style="font-size: ${componentData.priceFontSize || '1rem'}; margin: ${componentData.priceMargin || '4px 0 0'}; color: ${componentData.priceColor || '#4b5563'};">${item.price}</p>`;
                        }
                        elementHtml += `</div>`;
                    });
                }
                children.forEach((child) => {
                    elementHtml += renderElement(child, true, type);
                });
                elementHtml += `</div>`;
                break;
            case 'list':
                elementHtml += `<ul class="ladi-list" style="${cssStyles}">`;
                if (Array.isArray(componentData.items)) {
                    componentData.items.forEach((item) => {
                        elementHtml += `<li style="font-size: ${componentData.itemFontSize || '1rem'}; color: ${componentData.itemColor || '#1f2937'}; margin: ${componentData.itemMargin || '4px 0'}; ${cssStyles}">${item.title || item}</li>`;
                    });
                }
                children.forEach((child) => {
                    elementHtml += renderElement(child, true, type);
                });
                elementHtml += `</ul>`;
                break;
            case 'video':
                elementHtml += `<video class="ladi-video" controls style="${cssStyles}"><source src="${componentData.src || 'https://via.placeholder.com/150?text=Video'}" type="video/mp4"></video>`;
                children.forEach((child) => {
                    elementHtml += renderElement(child, true, type);
                });
                break;
            case 'icon':
                elementHtml += `<i class="${componentData.icon || 'fas fa-star'} ladi-icon" style="${cssStyles}"></i>`;
                children.forEach((child) => {
                    elementHtml += renderElement(child, true, type);
                });
                break;
            case 'product':
                elementHtml += `<div class="ladi-product" style="padding: ${componentData.padding || '16px'}; background: ${componentData.background || '#ffffff'}; border-radius: ${componentData.borderRadius || '8px'}; box-shadow: ${componentData.boxShadow || '0 2px 8px rgba(0,0,0,0.1)'}; ${cssStyles}">`;
                if (componentData.image) {
                    elementHtml += `<input type="file" class="upload-input" accept="image/*" onchange="handleFileUpload(event, '${id}')">`;
                    elementHtml += `<img id="${id}-img" src="${componentData.image}" alt="${componentData.title || 'Product'}" style="max-width: 100%; height: auto; border-radius: ${componentData.imageBorderRadius || '8px'};" />`;
                }
                if (componentData.title) {
                    elementHtml += `<h4 style="font-size: ${componentData.titleFontSize || '1.1rem'}; margin: ${componentData.titleMargin || '8px 0 0'}; color: ${componentData.titleColor || '#1f2937'};">${componentData.title}</h4>`;
                }
                if (componentData.price) {
                    elementHtml += `<p style="font-size: ${componentData.priceFontSize || '1rem'}; margin: ${componentData.priceMargin || '4px 0 0'}; color: ${componentData.priceColor || '#4b5563'};">${componentData.price}</p>`;
                }
                children.forEach((child) => {
                    elementHtml += renderElement(child, true, type);
                });
                elementHtml += `</div>`;
                break;
            case 'gallery':
                elementHtml += `<div class="ladi-gallery" style="grid-template-columns: ${componentData.gridTemplateColumns || 'repeat(auto-fill, minmax(150px, 1fr))'}; gap: ${componentData.gap || '10px'}; ${cssStyles}" ondragover="handleDragOver(event)" ondrop="handleDrop(event)">`;
                if (Array.isArray(componentData.images)) {
                    componentData.images.forEach((image, index) => {
                        elementHtml += `<div>`;
                        elementHtml += `<input type="file" class="upload-input" accept="image/*" onchange="handleFileUpload(event, '${id}-img-${index}')">`;
                        elementHtml += `<img id="${id}-img-${index}" src="${image}" alt="Gallery Image" style="max-width: 100%; height: auto; border-radius: ${componentData.imageBorderRadius || '8px'};" />`;
                        elementHtml += `</div>`;
                    });
                }
                children.forEach((child) => {
                    elementHtml += renderElement(child, true, type);
                });
                elementHtml += `</div>`;
                break;
            case 'carousel':
                elementHtml += `<div class="ladi-carousel" style="${cssStyles}" ondragover="handleDragOver(event)" ondrop="handleDrop(event)">`;
                if (Array.isArray(componentData.items)) {
                    componentData.items.forEach((item, index) => {
                        elementHtml += `<div style="padding: ${componentData.itemPadding || '16px'}; background: ${componentData.itemBackground || '#ffffff'}; ${cssStyles}" draggable="true" ondragstart="handleDragStart(event)">`;
                        if (item.image) {
                            elementHtml += `<input type="file" class="upload-input" accept="image/*" onchange="handleFileUpload(event, '${id}-item-${index}')">`;
                            elementHtml += `<img id="${id}-item-${index}-img" src="${item.image}" alt="${item.title || 'Slide'}" style="max-width: 100%; height: auto; border-radius: ${componentData.imageBorderRadius || '8px'};" />`;
                        }
                        if (item.title) {
                            elementHtml += `<h4 style="font-size: ${componentData.titleFontSize || '1.1rem'}; margin: ${componentData.titleMargin || '8px 0 0'}; color: ${componentData.titleColor || '#1f2937'};">${item.title}</h4>`;
                        }
                        elementHtml += `</div>`;
                    });
                }
                children.forEach((child) => {
                    elementHtml += renderElement(child, true, type);
                });
                elementHtml += `</div>`;
                break;
            case 'tabs':
                elementHtml += `<div class="ladi-tabs" style="${cssStyles}" ondragover="handleDragOver(event)" ondrop="handleDrop(event)">`;
                if (Array.isArray(componentData.tabs)) {
                    elementHtml += `<div class="ladi-tabs-nav">`;
                    componentData.tabs.forEach((tab, index) => {
                        elementHtml += `<div class="ladi-tab" style="padding: ${componentData.tabPadding || '8px 16px'}; ${cssStyles}">${tab.title}</div>`;
                    });
                    elementHtml += `</div>`;
                    elementHtml += `<div class="ladi-tab-content" style="padding: ${componentData.contentPadding || '16px'}; background: ${componentData.contentBackground || '#ffffff'}; ${cssStyles}">`;
                    componentData.tabs.forEach((tab) => {
                        elementHtml += `<div style="display: none;">${tab.content}</div>`;
                    });
                    elementHtml += `</div>`;
                }
                children.forEach((child) => {
                    elementHtml += renderElement(child, true, type);
                });
                elementHtml += `</div>`;
                break;
            case 'accordion':
                elementHtml += `<div class="ladi-accordion" style="${cssStyles}" ondragover="handleDragOver(event)" ondrop="handleDrop(event)">`;
                if (Array.isArray(componentData.items)) {
                    componentData.items.forEach((item) => {
                        elementHtml += `<div class="ladi-accordion-item">`;
                        elementHtml += `<div class="ladi-accordion-title" style="padding: ${componentData.titlePadding || '8px'}; background: ${componentData.titleBackground || '#f9fafb'}; ${cssStyles}">${item.title}</div>`;
                        elementHtml += `<div class="ladi-accordion-content" style="padding: ${componentData.contentPadding || '8px'}; display: none; ${cssStyles}">${item.content}</div>`;
                        elementHtml += `</div>`;
                    });
                }
                children.forEach((child) => {
                    elementHtml += renderElement(child, true, type);
                });
                elementHtml += `</div>`;
                break;
            case 'table':
                elementHtml += `<table class="ladi-table" style="${cssStyles}" ondragover="handleDragOver(event)" ondrop="handleDrop(event)">`;
                if (Array.isArray(componentData.headers)) {
                    elementHtml += `<thead><tr>`;
                    componentData.headers.forEach((header) => {
                        elementHtml += `<th style="padding: ${componentData.headerPadding || '8px'}; background: ${componentData.headerBackground || '#f9fafb'}; ${cssStyles}">${header}</th>`;
                    });
                    elementHtml += `</tr></thead>`;
                }
                if (Array.isArray(componentData.rows)) {
                    elementHtml += `<tbody>`;
                    componentData.rows.forEach((row) => {
                        elementHtml += `<tr>`;
                        row.forEach((cell) => {
                            elementHtml += `<td style="padding: ${componentData.cellPadding || '8px'}; ${cssStyles}">${cell}</td>`;
                        });
                        elementHtml += `</tr>`;
                    });
                    elementHtml += `</tbody>`;
                }
                children.forEach((child) => {
                    elementHtml += renderElement(child, true, type);
                });
                elementHtml += `</table>`;
                break;
            case 'survey':
                elementHtml += `<form class="ladi-survey" style="${cssStyles}" ondragover="handleDragOver(event)" ondrop="handleDrop(event)">`;
                if (componentData.title) {
                    elementHtml += `<h3 style="font-size: ${componentData.titleFontSize || '1.2rem'}; margin: ${componentData.titleMargin || '0'}; color: ${componentData.titleColor || '#1f2937'}; font-weight: ${componentData.titleFontWeight || '600'};">${componentData.title}</h3>`;
                }
                if (Array.isArray(componentData.questions)) {
                    componentData.questions.forEach((question, index) => {
                        elementHtml += `<div style="margin: ${componentData.questionMargin || '8px 0'}; ${cssStyles}">`;
                        elementHtml += `<label style="font-size: ${componentData.labelFontSize || '1rem'}; color: ${componentData.labelColor || '#1f2937'};">${question.label}</label>`;
                        if (question.type === 'text') {
                            elementHtml += `<input type="text" placeholder="${question.placeholder || 'Nhập...'}" style="padding: ${question.padding || '8px'}; border-radius: ${question.borderRadius || '8px'}; border: ${question.border || '1px solid #ccc'}; width: 100%; box-sizing: border-box; ${cssStyles}" />`;
                        } else if (question.type === 'radio') {
                            if (Array.isArray(question.options)) {
                                question.options.forEach((option) => {
                                    elementHtml += `<div><input type="radio" name="${question.label}-${index}" value="${option}" style="${cssStyles}" /> ${option}</div>`;
                                });
                            }
                        }
                        elementHtml += `</div>`;
                    });
                }
                if (componentData.buttonText) {
                    elementHtml += `<button type="submit" style="background: ${componentData.buttonBackground || '#2563eb'}; color: ${componentData.buttonColor || '#fff'}; padding: ${componentData.buttonPadding || '8px 16px'}; border-radius: ${componentData.buttonBorderRadius || '8px'}; border: ${componentData.buttonBorder || 'none'}; cursor: pointer; font-size: ${componentData.buttonFontSize || '1rem'}; font-weight: ${componentData.buttonFontWeight || '600'};">${componentData.buttonText}</button>`;
                }
                children.forEach((child) => {
                    elementHtml += renderElement(child, true, type);
                });
                elementHtml += `</form>`;
                break;
            case 'html':
                elementHtml += `<div class="ladi-html" style="${cssStyles}">${componentData.content || '<p>Custom HTML content</p>'}</div>`;
                children.forEach((child) => {
                    elementHtml += renderElement(child, true, type);
                });
                break;
            case 'collection':
                elementHtml += `<div class="ladi-collection" style="${cssStyles}" ondragover="handleDragOver(event)" ondrop="handleDrop(event)">`;
                if (Array.isArray(componentData.items)) {
                    componentData.items.forEach((item) => {
                        elementHtml += `<div style="padding: ${componentData.itemPadding || '16px'}; background: ${componentData.itemBackground || '#ffffff'}; border-radius: ${componentData.itemBorderRadius || '8px'}; box-shadow: ${componentData.itemBoxShadow || '0 2px 8px rgba(0,0,0,0.1)'}; ${cssStyles}" draggable="true" ondragstart="handleDragStart(event)">`;
                        if (item.title) {
                            elementHtml += `<h4 style="font-size: ${componentData.titleFontSize || '1.1rem'}; margin: ${componentData.titleMargin || '0 0 8px'}; color: ${componentData.titleColor || '#1f2937'};">${item.title}</h4>`;
                        }
                        if (item.content) {
                            elementHtml += `<p style="font-size: ${componentData.contentFontSize || '1rem'}; margin: ${componentData.contentMargin || '0'}; color: ${componentData.contentColor || '#4b5563'};">${item.content}</p>`;
                        }
                        elementHtml += `</div>`;
                    });
                }
                children.forEach((child) => {
                    elementHtml += renderElement(child, true, type);
                });
                elementHtml += `</div>`;
                break;
            case 'divider':
                elementHtml += `<hr style="border: none; background: ${componentData.color || '#e5e7eb'}; height: ${componentData.thickness || '2px'}; width: ${componentData.width || '100%'}; margin: ${componentData.margin || '0'}; ${cssStyles}" />`;
                break;
            case 'spacer':
                elementHtml += `<div style="background: ${componentData.background || 'transparent'}; height: ${componentData.height || '40px'}; width: ${componentData.width || '100%'}; ${cssStyles}"></div>`;
                break;
            case 'popup':
                elementHtml += `<div style="display: ${componentData.display || 'flex'}; flex-direction: ${componentData.direction || 'column'}; gap: ${componentData.gap || '10px'}; text-align: ${componentData.textAlign || 'center'}; padding: ${componentData.padding || '20px'}; background: ${componentData.background || 'rgba(255, 255, 255, 0.9)'}; border-radius: ${componentData.borderRadius || '12px'}; ${cssStyles}" ondragover="handleDragOver(event)" ondrop="handleDrop(event)">`;
                if (componentData.title && !children.some(child => child?.type === 'heading')) {
                    elementHtml += `<h3 style="font-size: ${componentData.titleFontSize || '1.5rem'}; margin: ${componentData.titleMargin || '0'}; color: ${componentData.titleColor || '#1f2937'}; font-weight: ${componentData.titleFontWeight || '600'};">${componentData.title}</h3>`;
                }
                if (componentData.content && !children.some(child => child?.type === 'paragraph')) {
                    elementHtml += `<p style="font-size: ${componentData.contentFontSize || '1rem'}; margin: ${componentData.contentMargin || '0'}; color: ${componentData.contentColor || '#4b5563'};">${componentData.content}</p>`;
                }
                if (componentData.buttonText && !children.some(child => child?.type === 'button')) {
                    elementHtml += `<button style="padding: ${componentData.buttonPadding || '10px 20px'}; border-radius: ${componentData.buttonBorderRadius || '8px'}; background: ${componentData.buttonBackground || '#2563eb'}; color: ${componentData.buttonColor || '#fff'}; border: ${componentData.buttonBorder || 'none'}; cursor: pointer; font-size: ${componentData.buttonFontSize || '1rem'}; font-weight: ${componentData.buttonFontWeight || '600'};">${componentData.buttonText}</button>`;
                }
                children.forEach((child) => {
                    elementHtml += renderElement(child, true, type);
                });
                elementHtml += `</div>`;
                break;
            case 'modal':
                elementHtml += `<div style="display: ${componentData.display || 'flex'}; flex-direction: ${componentData.direction || 'column'}; gap: ${componentData.gap || '10px'}; padding: ${componentData.padding || '20px'}; background: ${componentData.background || 'rgba(255, 255, 255, 0.9)'}; border-radius: ${componentData.borderRadius || '12px'}; ${cssStyles}" ondragover="handleDragOver(event)" ondrop="handleDrop(event)">`;
                if (componentData.title && !children.some(child => child?.type === 'heading')) {
                    elementHtml += `<h3 style="font-size: ${componentData.titleFontSize || '1.5rem'}; margin: ${componentData.titleMargin || '0'}; color: ${componentData.titleColor || '#1f2937'}; font-weight: ${componentData.titleFontWeight || '600'};">${componentData.title}</h3>`;
                }
                if (componentData.content && !children.some(child => child?.type === 'paragraph')) {
                    elementHtml += `<p style="font-size: ${componentData.contentFontSize || '1rem'}; margin: ${componentData.contentMargin || '0'}; color: ${componentData.contentColor || '#4b5563'};">${componentData.content}</p>`;
                }
                if (componentData.closeButton && !children.some(child => child?.type === 'button')) {
                    elementHtml += `<button style="padding: ${componentData.buttonPadding || '8px 16px'}; border-radius: ${componentData.buttonBorderRadius || '8px'}; background: ${componentData.buttonBackground || '#6b7280'}; color: ${componentData.buttonColor || '#fff'}; border: ${componentData.buttonBorder || 'none'}; cursor: pointer; align-self: flex-end; font-size: ${componentData.buttonFontSize || '1rem'}; font-weight: ${componentData.buttonFontWeight || '600'};">${componentData.buttonText || 'Đóng'}</button>`;
                }
                children.forEach((child) => {
                    elementHtml += renderElement(child, true, type);
                });
                elementHtml += `</div>`;
                break;
            default:
                // Skip unknown components silently
                console.warn(`Skipping unknown component type: ${type}`, { element });
                return '';
        }

        elementHtml += `</div>`;
        return elementHtml;
    };

    // Render all top-level elements
    if (Array.isArray(pageData.elements)) {
        pageData.elements.forEach((element) => {
            html += renderElement(element);
        });
    } else {
        console.warn('pageData.elements is not an array:', pageData.elements);
        html += `<div style="color: #ff0000; padding: 10px; border: 1px dashed #ff0000;">Error: No valid elements found in pageData</div>`;
    }

    html += `
    </div>
</body>
</html>
    `;

    console.log('Generated HTML:', html);
    return html.trim();
};

// ErrorBoundary để bắt lỗi render
class ErrorBoundary extends React.Component {
    state = { error: null };
    static getDerivedStateFromError(error) {
        return { error };
    }
    render() {
        if (this.state.error) {
            console.error('Error in component:', this.state.error);
            return <div>Lỗi: {this.state.error.message}</div>;
        }
        return this.props.children;
    }
}

const CreateLanding = () => {
    const [guideLine, setGuideLine] = useState({ show: false, y: 0 });
    const [searchParams] = useSearchParams();
    const navigate = useNavigate();
    const pageId = searchParams.get('id');

    const [pageData, setPageData] = useState({
        canvas: { width: parseInt(CSS_VARIABLES['--canvas-desktop']), background: CSS_VARIABLES['--white'] },
        elements: [],
    });
    const [selectedIds, setSelectedIds] = useState([]);
    const [selectedChildId, setSelectedChildId] = useState(null);
    const [viewMode, setViewMode] = useState('desktop');
    const [zoomLevel, setZoomLevel] = useState(100);
    const [gridSize, setGridSize] = useState(10);
    const [showGrid, setShowGrid] = useState(true);
    const [history, setHistory] = useState([]);
    const [historyIndex, setHistoryIndex] = useState(-1);
    const [isLoading, setIsLoading] = useState(true);
    const [isLibraryCollapsed, setIsLibraryCollapsed] = useState(false);
    const [isPropertiesCollapsed, setIsPropertiesCollapsed] = useState(false);

    const handleToggleLibrary = useCallback(() => {
        setIsLibraryCollapsed((prev) => {
            console.log('isLibraryCollapsed:', !prev);
            return !prev;
        });
    }, []);
    const handleViewModeChange = useCallback((mode) => {
        console.log('Changing view mode to:', mode);
        setViewMode(mode);
        setPageData((prev) => {
            const canvasWidth = mode === 'desktop' ? 1200 : 375;
            const newElements = prev.elements.map((element) => ({
                ...element,
                size: {
                    ...element.size,
                    width: mode === 'mobile' ? '100%' : element.size.width || 1200,
                },
                position: {
                    ...element.position,
                    [mode]: {
                        ...element.position[mode],
                        x: mode === 'mobile' ? 0 : element.position[mode]?.x || 0,
                    },
                },
            }));
            const newPageData = {
                ...prev,
                canvas: {
                    ...prev.canvas,
                    width: canvasWidth,
                },
                elements: newElements,
            };
            setHistory([...history.slice(0, historyIndex + 1), newPageData]);
            setHistoryIndex(historyIndex + 1);
            return newPageData;
        });
        toast.info(`Đã chuyển sang chế độ ${mode === 'desktop' ? 'Desktop' : 'Mobile'}`);
    }, [history, historyIndex]);
    const handleShowAddSectionGuide = useCallback((show) => {
        console.log('Toggling add section guide:', show);
        if (show) {
            const lastSectionY = pageData.elements
                .filter((el) => el.type === 'section')
                .reduce((maxY, el) => {
                    const y = el.position?.[viewMode]?.y || 0;
                    const height = el.size?.height || 400;
                    return Math.max(maxY, y + height);
                }, 0);
            setGuideLine({ show: true, y: lastSectionY + 10 });
        } else {
            setGuideLine({ show: false, y: 0 });
        }
    }, [pageData.elements, viewMode]);
    useEffect(() => {
        const updateCanvasHeight = () => {
            const totalHeight = pageData.elements
                .filter((el) => el.type === 'section')
                .reduce((maxY, el) => {
                    const y = el.position?.[viewMode]?.y || 0;
                    const height = el.size?.height || 400;
                    return Math.max(maxY, y + height);
                }, 0);
            setPageData((prev) => ({
                ...prev,
                canvas: {
                    ...prev.canvas,
                    height: totalHeight + 100,
                },
            }));
        };
        updateCanvasHeight();
    }, [pageData.elements, viewMode]);

    useEffect(() => {
        const initializeAuth = async () => {
            const token = localStorage.getItem('token');
            if (!token) {
                console.error('No token found in localStorage');
                toast.error('Vui lòng đăng nhập để tiếp tục');
                navigate('/auth');
                return;
            }

            let decoded;
            try {
                decoded = jwtDecode(token);
                const currentTime = Math.floor(Date.now() / 1000);
                if (decoded.exp && decoded.exp < currentTime) {
                    console.error('Token has expired');
                    localStorage.removeItem('token');
                    toast.error('Phiên đăng nhập đã hết hạn. Vui lòng đăng nhập lại.');
                    navigate('/auth');
                    return;
                }
                api.defaults.headers.common['Authorization'] = `Bearer ${token}`;
            } catch (err) {
                console.error('Error decoding token:', err);
                toast.error('Phiên đăng nhập không hợp lệ');
                navigate('/auth');
                return;
            }
        };
        initializeAuth();
    }, [navigate]);

    useEffect(() => {
        if (!pageId) {
            toast.error('Không tìm thấy ID trang. Vui lòng tạo trang mới từ dashboard.');
            navigate('/pages');
            return;
        }

        if (!pageId.match(/^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i)) {
            toast.error('ID trang không hợp lệ. Vui lòng kiểm tra lại.');
            navigate('/pages');
            return;
        }

        const fetchPageContent = async () => {
            setIsLoading(true);
            try {
                console.log('Fetching content for pageId:', pageId);
                const response = await api.get(`/api/pages/${pageId}/content`);
                console.log('Page content response:', response.data);
                if (response.data.html) {
                    const parsedPageData = parseHTMLToPageData(response.data.html);
                    setPageData(parsedPageData);
                    setHistory([parsedPageData]);
                    setHistoryIndex(0);
                } else {
                    console.warn('No HTML content found in response');
                    toast.warn('Không tìm thấy nội dung trang. Khởi tạo trang trống.');
                    setPageData({ canvas: { width: parseInt(CSS_VARIABLES['--canvas-desktop']), background: CSS_VARIABLES['--white'] }, elements: [] });
                }
            } catch (error) {
                console.error('Lỗi khi lấy nội dung trang:', error);
                const errorMessage = error.response?.data?.error || error.message;
                toast.error('Lỗi khi lấy nội dung trang: ' + errorMessage);
                if (error.response?.status === 401) {
                    localStorage.removeItem('token');
                    navigate('/auth');
                } else if (error.response?.status === 400 && errorMessage.includes('Invalid page ID')) {
                    toast.error('ID trang không hợp lệ. Vui lòng kiểm tra lại.');
                    navigate('/pages');
                } else {
                    navigate('/pages');
                }
            } finally {
                setIsLoading(false);
            }
        };
        fetchPageContent();
    }, [pageId, navigate]);

    const handleSelectElement = useCallback((ids, append = false) => {
        if (Array.isArray(ids)) {
            setSelectedIds(append ? [...new Set([...selectedIds, ...ids])] : ids);
        } else {
            setSelectedIds(append ? [...new Set([...selectedIds, ids])] : [ids]);
        }
        setSelectedChildId(null);
    }, [selectedIds]);

    const handleSelectChild = useCallback((parentId, childId) => {
        if (!parentId) {
            setSelectedIds([]);
            setSelectedChildId(null);
            return;
        }
        if (!pageData.elements.find((el) => el.id === parentId)) {
            console.warn(`Parent element with id ${parentId} not found`);
            setSelectedIds([]);
            setSelectedChildId(null);
            return;
        }
        setSelectedIds([parentId]);
        setSelectedChildId(childId);
    }, [pageData.elements]);

    const handleAddElement = useCallback((element) => {
        console.log('Adding new element:', element);
        setPageData((prev) => {
            const newElement = {
                ...element,
                id: `${element.id}-${Date.now()}`,
                position: {
                    desktop: element.position?.desktop || { x: 0, y: 0 },
                    tablet: element.position?.tablet || { x: 0, y: 0 },
                    mobile: element.position?.mobile || { x: 0, y: 0 },
                },
                styles: {
                    ...element.styles,
                    borderRadius: element.styles?.borderRadius || CSS_VARIABLES['--border-radius'],
                    backgroundColor: element.styles?.backgroundColor || CSS_VARIABLES['--white'],
                    boxShadow: element.styles?.boxShadow || CSS_VARIABLES['--shadow'],
                    zIndex: element.type === 'section' ? 1 : 10,
                },
                children: element.children || [],
            };
            const newPageData = {
                ...prev,
                elements: [...prev.elements, newElement],
            };
            setHistory([...history.slice(0, historyIndex + 1), newPageData]);
            setHistoryIndex(historyIndex + 1);
            return newPageData;
        });
        toast.success('Đã thêm phần tử!');
    }, [historyIndex]);

    const handleAddChild = useCallback((parentId, newChild) => {
        console.log('Adding child to parent:', { parentId, newChild });
        setPageData((prev) => {
            const newPageData = {
                ...prev,
                elements: prev.elements.map((el) => {
                    if (el.id === parentId) {
                        return {
                            ...el,
                            children: [...(el.children || []), {
                                ...newChild,
                                position: {
                                    desktop: newChild.position?.desktop || { x: 0, y: 0 },
                                    tablet: newChild.position?.tablet || { x: 0, y: 0 },
                                    mobile: newChild.position?.mobile || { x: 0, y: 0 },
                                },
                                styles: {
                                    ...newChild.styles,
                                    zIndex: 2,
                                },
                            }],
                        };
                    }
                    return el;
                }),
            };
            setHistory([...history.slice(0, historyIndex + 1), newPageData]);
            setHistoryIndex(historyIndex + 1);
            return newPageData;
        });
        toast.success('Đã thêm thành phần con!');
    }, [historyIndex]);

    const handleUpdatePosition = useCallback((id, position, mode) => {
        setPageData((prev) => {
            const newPageData = {
                ...prev,
                elements: prev.elements.map((el) =>
                    el.id === id
                        ? {
                            ...el,
                            position: mode === 'replace' ? position : { ...el.position, ...position },
                        }
                        : el
                ),
            };
            setHistory([...history.slice(0, historyIndex + 1), newPageData]);
            setHistoryIndex(historyIndex + 1);
            return newPageData;
        });
    }, [historyIndex]);

    const handleUpdateSize = useCallback((id, size) => {
        setPageData((prev) => {
            const newPageData = {
                ...prev,
                elements: prev.elements.map((el) =>
                    el.id === id ? { ...el, size } : el
                ),
            };
            setHistory([...history.slice(0, historyIndex + 1), newPageData]);
            setHistoryIndex(historyIndex + 1);
            return newPageData;
        });
    }, [historyIndex]);

    const handleUpdateCanvas = useCallback((canvasUpdates) => {
        setPageData((prev) => {
            const newPageData = {
                ...prev,
                canvas: {
                    ...prev.canvas,
                    ...canvasUpdates,
                },
            };
            setHistory([...history.slice(0, historyIndex + 1), newPageData]);
            setHistoryIndex(historyIndex + 1);
            return newPageData;
        });
        toast.success('Đã cập nhật cài đặt canvas!');
    }, [historyIndex]);

    const handleDeleteElement = useCallback((id) => {
        setPageData((prev) => {
            const newPageData = {
                ...prev,
                elements: prev.elements.filter((el) => el.id !== id),
            };
            setHistory([...history.slice(0, historyIndex + 1), newPageData]);
            setHistoryIndex(historyIndex + 1);
            return newPageData;
        });
        setSelectedIds((prev) => prev.filter((selId) => selId !== id));
        setSelectedChildId(null);
        toast.success('Đã xóa phần tử!');
    }, [historyIndex]);

    const handleEditElement = useCallback((updatedElement) => {
        console.log('Updating element:', updatedElement);
        setPageData((prev) => {
            const newPageData = {
                ...prev,
                elements: prev.elements.map((el) =>
                    el.id === selectedIds[0]
                        ? updatedElement.isChild
                            ? {
                                ...el,
                                children: el.children.map((child) =>
                                    child.id === selectedChildId
                                        ? { ...child, ...updatedElement.json }
                                        : child
                                ),
                            }
                            : { ...el, ...updatedElement.json }
                        : el
                ),
            };
            setHistory([...history.slice(0, historyIndex + 1), newPageData]);
            setHistoryIndex(historyIndex + 1);
            return newPageData;
        });
        toast.success('Đã cập nhật phần tử!');
    }, [historyIndex, selectedIds, selectedChildId]);

    const handleDuplicateElement = useCallback((id) => {
        const element = pageData.elements.find((el) => el.id === id);
        if (element) {
            const newElement = {
                ...element,
                id: `${element.id}-${Date.now()}`,
                position: {
                    desktop: {
                        x: (element.position.desktop?.x || 0) + 10,
                        y: (element.position.desktop?.y || 0) + 10,
                    },
                    tablet: {
                        x: (element.position.tablet?.x || 0) + 10,
                        y: (element.position.tablet?.y || 0) + 10,
                    },
                    mobile: {
                        x: (element.position.mobile?.x || 0) + 10,
                        y: (element.position.mobile?.y || 0) + 10,
                    },
                },
            };
            setPageData((prev) => {
                const newPageData = {
                    ...prev,
                    elements: [...prev.elements, newElement],
                };
                setHistory([...history.slice(0, historyIndex + 1), newPageData]);
                setHistoryIndex(historyIndex + 1);
                return newPageData;
            });
            toast.success('Đã sao chép phần tử!');
        }
    }, [historyIndex, pageData.elements]);

    const handleMoveElementUp = useCallback((id) => {
        setPageData((prev) => {
            const index = prev.elements.findIndex((el) => el.id === id);
            if (index <= 0) return prev;
            const newElements = [...prev.elements];
            [newElements[index - 1], newElements[index]] = [newElements[index], newElements[index - 1]];
            const newPageData = { ...prev, elements: newElements };
            setHistory([...history.slice(0, historyIndex + 1), newPageData]);
            setHistoryIndex(historyIndex + 1);
            return newPageData;
        });
        toast.success('Đã di chuyển phần tử lên!');
    }, [historyIndex]);

    const handleMoveElementDown = useCallback((id) => {
        setPageData((prev) => {
            const index = prev.elements.findIndex((el) => el.id === id);
            if (index >= prev.elements.length - 1) return prev;
            const newElements = [...prev.elements];
            [newElements[index], newElements[index + 1]] = [newElements[index + 1], newElements[index]];
            const newPageData = { ...prev, elements: newElements };
            setHistory([...history.slice(0, historyIndex + 1), newPageData]);
            setHistoryIndex(historyIndex + 1);
            return newPageData;
        });
        toast.success('Đã di chuyển phần tử xuống!');
    }, [historyIndex]);

    const handleGroupElements = useCallback((ids) => {
        const groupId = `group-${Date.now()}`;
        setPageData((prev) => {
            const selectedElements = prev.elements.filter((el) => ids.includes(el.id));
            if (selectedElements.length === 0) return prev;
            const newPageData = {
                ...prev,
                elements: [
                    ...prev.elements.filter((el) => !ids.includes(el.id)),
                    {
                        id: groupId,
                        type: 'group',
                        position: {
                            desktop: { x: 0, y: 0 },
                            tablet: { x: 0, y: 0 },
                            mobile: { x: 0, y: 0 },
                        },
                        size: { width: 300, height: 200 },
                        styles: {
                            borderRadius: CSS_VARIABLES['--border-radius'],
                            backgroundColor: CSS_VARIABLES['--white'],
                            boxShadow: CSS_VARIABLES['--shadow'],
                            zIndex: 10,
                        },
                        children: selectedElements.map((el) => ({
                            ...el,
                            position: {
                                desktop: { x: 0, y: 0 },
                                tablet: { x: 0, y: 0 },
                                mobile: { x: 0, y: 0 },
                            },
                        })),
                    },
                ],
            };
            setHistory([...history.slice(0, historyIndex + 1), newPageData]);
            setHistoryIndex(historyIndex + 1);
            return newPageData;
        });
        setSelectedIds([groupId]);
        toast.success('Đã nhóm các phần tử!');
    }, [historyIndex]);

    const handleUpdateChildPosition = useCallback((parentId, childId, newPosition) => {
        setPageData((prev) => {
            const newPageData = {
                ...prev,
                elements: prev.elements.map((el) => {
                    if (el.id === parentId) {
                        return {
                            ...el,
                            children: el.children.map((child) =>
                                child.id === childId
                                    ? { ...child, position: { ...child.position, [viewMode]: newPosition } }
                                    : child
                            ),
                        };
                    }
                    return el;
                }),
            };
            setHistory([...history.slice(0, historyIndex + 1), newPageData]);
            setHistoryIndex(historyIndex + 1);
            return newPageData;
        });
        toast.success('Đã cập nhật vị trí thành phần con!');
    }, [historyIndex, viewMode]);

    const handleMoveChild = useCallback((sourceParentId, childId, targetParentId, newElementOrPosition) => {
        setPageData((prev) => {
            let newElements = [...prev.elements];
            let childElement;

            if (sourceParentId) {
                newElements = newElements.map((el) => {
                    if (el.id === sourceParentId) {
                        const childIndex = el.children.findIndex((child) => child.id === childId);
                        if (childIndex !== -1) {
                            childElement = {
                                ...el.children[childIndex],
                                position: {
                                    ...el.children[childIndex].position,
                                    [viewMode]:
                                        typeof newElementOrPosition === 'object' && newElementOrPosition.position
                                            ? newElementOrPosition.position[viewMode]
                                            : newElementOrPosition,
                                },
                            };
                            return { ...el, children: el.children.filter((child) => child.id !== childId) };
                        }
                    }
                    return el;
                });
            } else {
                const childIndex = newElements.findIndex((el) => el.id === childId);
                if (childIndex !== -1) {
                    childElement = {
                        ...newElements[childIndex],
                        position: {
                            ...newElements[childIndex].position,
                            [viewMode]:
                                typeof newElementOrPosition === 'object' && newElementOrPosition.position
                                    ? newElementOrPosition.position[viewMode]
                                    : newElementOrPosition,
                        },
                    };
                    newElements.splice(childIndex, 1);
                }
            }

            if (targetParentId) {
                newElements = newElements.map((el) => {
                    if (el.id === targetParentId) {
                        return { ...el, children: [...(el.children || []), childElement] };
                    }
                    return el;
                });
            } else {
                newElements.push(
                    typeof newElementOrPosition === 'object' && newElementOrPosition.id
                        ? newElementOrPosition
                        : childElement
                );
            }

            const newPageData = { ...prev, elements: newElements };
            setHistory([...history.slice(0, historyIndex + 1), newPageData]);
            setHistoryIndex(historyIndex + 1);
            return newPageData;
        });
        toast.success(targetParentId ? 'Đã di chuyển thành phần con!' : 'Đã chuyển thành phần con thành phần tử độc lập!');
    }, [historyIndex, viewMode]);

    const handleUpdateChildren = useCallback((parentId, dragIndex, dropIndex) => {
        setPageData((prev) => {
            const newPageData = {
                ...prev,
                elements: prev.elements.map((el) => {
                    if (el.id === parentId) {
                        const newChildren = [...(el.children || [])];
                        if (dragIndex >= 0 && dragIndex < newChildren.length && dropIndex >= 0 && dropIndex < newChildren.length) {
                            const [movedChild] = newChildren.splice(dragIndex, 1);
                            newChildren.splice(dropIndex, 0, movedChild);
                            return { ...el, children: newChildren };
                        }
                    }
                    return el;
                }),
            };
            setHistory([...history.slice(0, historyIndex + 1), newPageData]);
            setHistoryIndex(historyIndex + 1);
            return newPageData;
        });
        toast.success('Đã sắp xếp lại thành phần con!');
    }, [historyIndex]);

    const handleUndo = useCallback(() => {
        if (historyIndex >= 0) {
            setPageData(history[historyIndex]);
            setHistoryIndex(historyIndex - 1);
            toast.info('Đã hoàn tác!');
        }
    }, [historyIndex]);

    const handleRedo = useCallback(() => {
        if (historyIndex < history.length - 1) {
            setPageData(history[historyIndex + 1]);
            setHistoryIndex(historyIndex + 1);
            toast.info('Đã khôi phục!');
        }
    }, [historyIndex, history.length]);

    const handleSave = useCallback(async () => {
        if (!pageId) {
            toast.error('Không tìm thấy ID trang. Vui lòng tạo trang mới từ dashboard.');
            return;
        }

        try {
            // Kiểm tra tính hợp lệ của pageData
            if (!pageData.elements.length) {
                toast.warn('Trang hiện tại trống. Vui lòng thêm nội dung trước khi lưu.');
                return;
            }

            const htmlContent = renderStaticHTML(pageData);
            console.log('Saving page with pageId:', pageId, 'Elements count:', pageData.elements.length, 'Canvas settings:', pageData.canvas);

            // Gửi sự kiện phân tích (nếu có, ví dụ Google Analytics)
            if (window.gtag) {
                window.gtag('event', 'page_save', {
                    event_category: 'Page Editor',
                    event_label: `Page ${pageId}`,
                    value: pageData.elements.length,
                });
            }

            const response = await api.put(`/api/pages/${pageId}`, { html: htmlContent });
            if (response.data.success) {
                toast.success(`Lưu trang thành công! (${pageData.elements.length} phần tử)`);
                alert('✅ Lưu trang thành công!');
            } else {
                toast.error('Lưu trang thất bại: ' + (response.data.error || 'Lỗi không xác định'));
            }
        } catch (error) {
            console.error('Lỗi khi lưu trang:', error);
            const errorMessage = error.response?.data?.error || error.message;
            toast.error('Lỗi khi lưu trang: ' + errorMessage);
            if (error.response?.status === 401) {
                localStorage.removeItem('token');
                navigate('/auth');
            }
        }
    }, [pageId, navigate, pageData]);

    const handlePreview = useCallback(() => {
        const htmlContent = renderStaticHTML(pageData);
        const newWindow = window.open('', '_blank');
        newWindow.document.write(htmlContent);
        newWindow.document.close();
    }, [pageData]);

    const handleGenerateCode = useCallback(async () => {
        if (!pageId) {
            toast.error('Không tìm thấy ID trang để triển khai.');
            return;
        }

        try {
            const htmlContent = renderStaticHTML(pageData);
            console.log('Saving before publish, sending PUT request:', { html: htmlContent });
            await api.put(`/api/pages/${pageId}`, { html: htmlContent });
            console.log('Sending POST request to /api/pages/:id/publish');
            const response = await api.post(`/api/pages/${pageId}/publish`);
            console.log('Publish response:', response.data);
            if (response.data.success && response.data.page?.url) {
                toast.success(`Triển khai thành công! Truy cập tại: ${response.data.page.url}`);
            } else {
                toast.error('Triển khai thất bại: ' + (response.data.error || 'Lỗi không xác định'));
            }
        } catch (error) {
            console.error('Lỗi khi triển khai:', error);
            const errorMessage = error.response?.data?.error || error.message;
            toast.error('Lỗi khi triển khai trang: ' + errorMessage);
            if (error.response?.status === 401) {
                localStorage.removeItem('token');
                navigate('/auth');
            }
        }
    }, [pageId, navigate, pageData]);

    const selectedElement = (() => {
        if (selectedChildId) {
            const parent = pageData.elements.find((el) => el.id === selectedIds[0]);
            if (!parent || !parent.children) return null;
            const child = parent.children.find((child) => child.id === selectedChildId);
            return child ? { json: child, isChild: true } : null;
        }
        const element = pageData.elements.find((el) => el.id === selectedIds[0]);
        return element ? { json: element, isChild: false } : null;
    })();

    if (isLoading) {
        return (
            <div className="lpb-loading">
                <div className="lpb-loading-spinner"></div>
                <p>Đang tải trang...</p>
            </div>
        );
    }

    return (
        <DndProvider backend={HTML5Backend}>
            <div className="lpb-container flex h-screen">
                <ErrorBoundary>
                    <link
                        rel="stylesheet"
                        href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.5.1/css/all.min.css"
                        crossOrigin="anonymous"
                        referrerPolicy="no-referrer"
                    />
                    <ComponentLibrary
                        isCollapsed={isLibraryCollapsed}
                        onToggle={handleToggleLibrary}
                        onAddElement={handleAddElement}
                        onAddChild={handleAddChild}
                        pageData={pageData}
                    />
                </ErrorBoundary>
                <div className="lpb-main-content flex-1 flex flex-col">
                    <ErrorBoundary>
                        <Toolbar
                            onSave={handleSave}
                            onPreview={handlePreview}
                            onGenerateCode={handleGenerateCode}
                            viewMode={viewMode}
                            onViewModeChange={setViewMode}
                            onUndo={handleUndo}
                            onRedo={handleRedo}
                            canUndo={historyIndex >= 0}
                            canRedo={historyIndex < history.length - 1}
                            zoomLevel={zoomLevel}
                            onZoom={(direction) => setZoomLevel(direction === 'in' ? Math.min(200, zoomLevel + 10) : Math.max(50, zoomLevel - 10))}
                            onToggleGrid={() => setShowGrid(!showGrid)}
                            showGrid={showGrid}
                            gridSize={gridSize}
                            pageData={pageData} // Prop này
                            onSetGridSize={setGridSize}
                            selectedElement={selectedElement}
                            onShowAddSectionGuide={handleShowAddSectionGuide}
                        />
                    </ErrorBoundary>
                    <ErrorBoundary>
                        <Canvas
                            pageData={pageData}
                            selectedIds={selectedIds}
                            onSelectElement={handleSelectElement}
                            onUpdatePosition={handleUpdatePosition}
                            onUpdateSize={handleUpdateSize}
                            onDeleteElement={handleDeleteElement}
                            onAddElement={handleAddElement}
                            onGroupElements={handleGroupElements}
                            viewMode={viewMode}
                            zoomLevel={zoomLevel}
                            gridSize={gridSize}
                            showGrid={showGrid}
                            onEditElement={handleEditElement}
                            onDuplicateElement={handleDuplicateElement}
                            onMoveElementUp={handleMoveElementUp}
                            onMoveElementDown={handleMoveElementDown}
                            onSelectChild={handleSelectChild}
                            selectedChildId={selectedChildId}
                            onUpdateChildren={handleUpdateChildren}
                            onAddChild={handleAddChild}
                            onUpdateChildPosition={handleUpdateChildPosition}
                            onMoveChild={handleMoveChild}
                            guideLine={guideLine}
                        />
                    </ErrorBoundary>
                </div>
                <ErrorBoundary>
                    <div className="lpb-sidebar-right">
                        <PropertiesPanel
                            selectedElement={selectedElement}
                            onUpdateElement={handleEditElement}
                            onUpdateCanvas={handleUpdateCanvas}
                            isCollapsed={isPropertiesCollapsed}
                            onToggle={() => setIsPropertiesCollapsed(!isPropertiesCollapsed)}
                            pageData={pageData}
                        />
                    </div>
                </ErrorBoundary>
            </div>
        </DndProvider>
    );
};

export default CreateLanding;
