        if (componentData.title && !children.some(child => (child === null || child === void 0 ? void 0 : child.type) === 'heading')) {
          elementHtml += `<h3 style="font-size: ${componentData.titleFontSize || '1.5rem'}; margin: ${componentData.titleMargin || '0'}; color: ${componentData.titleColor || '#1f2937'}; font-weight: ${componentData.titleFontWeight || '600'};">${componentData.title}</h3>`;
