import { parse } from 'node-html-parser';

export const CSS_VARIABLES = {
    '--primary': '#6366f1',
    '--primary-dark': '#4f46e5',
    '--primary-light': '#a5b4fc',
    '--secondary': '#f1f5f9',
    '--accent': '#06b6d4',
    '--success': '#10b981',
    '--warning': '#f59e0b',
    '--danger': '#ef4444',
    '--dark': '#1e293b',
    '--light': '#f8fafc',
    '--white': '#ffffff',
    '--text-primary': '#334155',
    '--text-secondary': '#64748b',
    '--text-light': '#94a3b8',
    '--text-inverse': '#ffffff',
    '--border-color': '#e2e8f0',
    '--border-radius': '8px',
    '--border-radius-lg': '12px',
    '--border-radius-xl': '16px',
    '--shadow-sm': '0 1px 2px 0 rgba(0, 0, 0, 0.05)',
    '--shadow': '0 4px 6px -1px rgba(0, 0, 0, 0.1)',
    '--shadow-lg': '0 10px 15px -3px rgba(0, 0, 0, 0.1)',
    '--shadow-xl': '0 20px 25px -5px rgba(0, 0, 0, 0.1)',
    '--canvas-desktop': '1200px',
};

export const parseStyles = (styleString) => {
    if (!styleString) return {};
    return styleString.split(';').reduce((acc, style) => {
        const [key, value] = style.split(':').map(s => s.trim());
        if (key && value) {
            const camelKey = key.replace(/-([a-z])/g, (g) => g[1].toUpperCase());
            acc[camelKey] = value;
        }
        return acc;
    }, {});
};

export const parseHTMLToPageData = (html) => {
    try {
        console.log('Parsing HTML from S3:', html);

        // Phân tích HTML sử dụng html-react-parser
        const root = parse(html, { blockTextElements: { style: true } });
        const pageContainer = root.querySelector ? root.querySelector('.page-container') : null;

        // Kiểm tra .page-container
        if (!pageContainer) {
            console.warn('No .page-container found in HTML');
            toast.warn('HTML không chứa .page-container. Khởi tạo trang trống.');
            return {
                canvas: {
                    width: parseInt(CSS_VARIABLES['--canvas-desktop']) || 1200,
                    background: CSS_VARIABLES['--white'] || '#ffffff',
                },
                elements: [],
            };
        }

        // Lấy dữ liệu canvas
        const canvasData = {
            width: parseFloat(pageContainer.style?.width) || parseInt(CSS_VARIABLES['--canvas-desktop']) || 1200,
            background: pageContainer.style?.background || CSS_VARIABLES['--white'] || '#ffffff',
        };

        const elements = [];
        // Duyệt qua các section và canvas elements
        pageContainer.querySelectorAll('.ladi-section, .lpb-canvas-element').forEach((elementNode, index) => {
            const type = elementNode.getAttribute('data-type') || 'section';
            const id = elementNode.getAttribute('id') || `element-${index}-${Date.now()}`;
            const styles = parseStyles(elementNode.getAttribute('style') || '');
            const componentData = parseDataAttribute(elementNode, 'data-component');

            // Lấy vị trí cho từng viewMode
            const position = {
                desktop: parseDataAttribute(elementNode, 'data-position-desktop') || {
                    x: parseFloat(styles.left) || 0,
                    y: parseFloat(styles.top) || 0,
                },
                tablet: parseDataAttribute(elementNode, 'data-position-tablet') || {
                    x: parseFloat(styles.left) || 0,
                    y: parseFloat(styles.top) || 0,
                },
                mobile: parseDataAttribute(elementNode, 'data-position-mobile') || {
                    x: parseFloat(styles.left) || 0,
                    y: parseFloat(styles.top) || 0,
                },
            };

            // Lấy kích thước
            const size = {
                width: parseFloat(styles.width) || (type === 'section' ? 420 : 200),
                height: parseFloat(styles.height) || (type === 'section' ? 400 : 50),
            };

            const children = [];
            // Xử lý children trong section hoặc container
            if (['section', 'container', 'box', 'form', 'grid', 'carousel', 'tabs', 'accordion', 'table', 'survey', 'collection', 'popup'].includes(type)) {
                const containerNode = elementNode.querySelector('.ladi-container') || elementNode;
                if (containerNode) {
                    containerNode.querySelectorAll('.lpb-child-element').forEach((child, childIndex) => {
                        const childType = child.getAttribute('data-type') || child.tagName.toLowerCase();
                        const childId = `child-${index}-${childIndex}-${Date.now()}`;
                        const childStyles = parseStyles(child.getAttribute('style') || '');
                        const childComponentData = parseDataAttribute(child, 'data-component');
                        const childPosition = {
                            desktop: parseDataAttribute(child, 'data-position-desktop') || {
                                x: parseFloat(childStyles.left) || 0,
                                y: parseFloat(childStyles.top) || 0,
                            },
                            tablet: parseDataAttribute(child, 'data-position-tablet') || {
                                x: parseFloat(childStyles.left) || 0,
                                y: parseFloat(childStyles.top) || 0,
                            },
                            mobile: parseDataAttribute(child, 'data-position-mobile') || {
                                x: parseFloat(childStyles.left) || 0,
                                y: parseFloat(childStyles.top) || 0,
                            },
                        };
                        const childSize = {
                            width: parseFloat(childStyles.width) || 200,
                            height: parseFloat(childStyles.height) || 50,
                        };

                        switch (childType) {
                            case 'heading':
                            case 'h1':
                            case 'h2':
                            case 'h3':
                            case 'h4':
                            case 'h5':
                            case 'h6':
                                children.push({
                                    id: childId,
                                    type: 'heading',
                                    componentData: {
                                        content: child.textContent.trim() || 'Tiêu đề',
                                        level: childType,
                                        ...childComponentData,
                                    },
                                    styles: {
                                        fontSize: childStyles.fontSize || '1.5rem',
                                        color: childStyles.color || CSS_VARIABLES['--text-primary'] || '#1f2937',
                                        ...childStyles,
                                    },
                                    position: childPosition,
                                    size: childSize,
                                });
                                break;
                            case 'paragraph':
                            case 'p':
                                children.push({
                                    id: childId,
                                    type: 'paragraph',
                                    componentData: {
                                        content: child.textContent.trim() || 'Đoạn văn',
                                        ...childComponentData,
                                    },
                                    styles: {
                                        fontSize: childStyles.fontSize || '1rem',
                                        color: childStyles.color || CSS_VARIABLES['--text-secondary'] || '#4b5563',
                                        ...childStyles,
                                    },
                                    position: childPosition,
                                    size: childSize,
                                });
                                break;
                            case 'button':
                                children.push({
                                    id: childId,
                                    type: 'button',
                                    componentData: {
                                        content: child.textContent.trim() || 'Nút',
                                        ...childComponentData,
                                    },
                                    styles: {
                                        background: childStyles.background || CSS_VARIABLES['--primary'] || '#2563eb',
                                        color: childStyles.color || CSS_VARIABLES['--text-inverse'] || '#ffffff',
                                        borderRadius: childStyles.borderRadius || CSS_VARIABLES['--border-radius'] || '8px',
                                        padding: childStyles.padding || '8px 16px',
                                        ...childStyles,
                                    },
                                    position: childPosition,
                                    size: childSize,
                                });
                                break;
                            case 'image':
                            case 'img':
                                children.push({
                                    id: childId,
                                    type: 'image',
                                    componentData: {
                                        src: child.getAttribute('src') || '',
                                        alt: child.getAttribute('alt') || 'Hình ảnh',
                                        ...childComponentData,
                                    },
                                    styles: {
                                        maxWidth: childStyles.maxWidth || '100%',
                                        borderRadius: childStyles.borderRadius || '0',
                                        ...childStyles,
                                    },
                                    position: childPosition,
                                    size: childSize,
                                });
                                break;
                            case 'video':
                                const source = child.querySelector('source');
                                children.push({
                                    id: childId,
                                    type: 'video',
                                    componentData: {
                                        src: source?.getAttribute('src') || '',
                                        ...childComponentData,
                                    },
                                    styles: {
                                        maxWidth: childStyles.maxWidth || '100%',
                                        ...childStyles,
                                    },
                                    position: childPosition,
                                    size: childSize,
                                });
                                break;
                            case 'icon':
                                children.push({
                                    id: childId,
                                    type: 'icon',
                                    componentData: {
                                        icon: child.className.match(/fa[sr] fa-[^\s]+/)?.[0] || 'fas fa-star',
                                        ...childComponentData,
                                    },
                                    styles: {
                                        fontSize: childStyles.fontSize || '2rem',
                                        color: childStyles.color || '#1f2937',
                                        ...childStyles,
                                    },
                                    position: childPosition,
                                    size: childSize,
                                });
                                break;
                            case 'divider':
                                children.push({
                                    id: childId,
                                    type: 'divider',
                                    componentData: {
                                        ...childComponentData,
                                    },
                                    styles: {
                                        background: childStyles.background || '#e5e7eb',
                                        height: childStyles.height || '2px',
                                        width: childStyles.width || '100%',
                                        ...childStyles,
                                    },
                                    position: childPosition,
                                    size: childSize,
                                });
                                break;
                            case 'spacer':
                                children.push({
                                    id: childId,
                                    type: 'spacer',
                                    componentData: {
                                        ...childComponentData,
                                    },
                                    styles: {
                                        background: childStyles.background || 'transparent',
                                        height: childStyles.height || '40px',
                                        width: childStyles.width || '100%',
                                        ...childStyles,
                                    },
                                    position: childPosition,
                                    size: childSize,
                                });
                                break;
                            case 'form':
                            case 'survey':
                                const fields = [];
                                child.querySelectorAll('input').forEach((input) => {
                                    const inputStyles = parseStyles(input.getAttribute('style') || '');
                                    fields.push({
                                        type: input.getAttribute('type') || 'text',
                                        placeholder: input.getAttribute('placeholder') || 'Nhập...',
                                        label: input.getAttribute('label') || '',
                                        padding: inputStyles.padding || '8px',
                                        borderRadius: inputStyles.borderRadius || '8px',
                                        border: inputStyles.border || '1px solid #ccc',
                                        fontSize: inputStyles.fontSize || '1rem',
                                    });
                                });
                                const button = child.querySelector('button');
                                const buttonStyles = button ? parseStyles(button.getAttribute('style') || '') : {};
                                children.push({
                                    id: childId,
                                    type: childType,
                                    componentData: {
                                        title: child.querySelector('h3')?.textContent.trim() || '',
                                        fields,
                                        buttonText: button?.textContent.trim() || 'Gửi',
                                        buttonBackground: buttonStyles.background || '#2563eb',
                                        buttonColor: buttonStyles.color || '#fff',
                                        buttonPadding: buttonStyles.padding || '8px 16px',
                                        buttonBorderRadius: buttonStyles.borderRadius || '8px',
                                        buttonBorder: buttonStyles.border || 'none',
                                        ...childComponentData,
                                    },
                                    styles: childStyles,
                                    position: childPosition,
                                    size: childSize,
                                });
                                break;
                            case 'card':
                                children.push({
                                    id: childId,
                                    type: 'card',
                                    componentData: {
                                        title: child.querySelector('h3, h4')?.textContent.trim() || '',
                                        content: child.querySelector('p')?.textContent.trim() || '',
                                        ...childComponentData,
                                    },
                                    styles: {
                                        padding: childStyles.padding || '16px',
                                        background: childStyles.background || '#ffffff',
                                        borderRadius: childStyles.borderRadius || '8px',
                                        boxShadow: childStyles.boxShadow || '0 2px 8px rgba(0,0,0,0.1)',
                                        ...childStyles,
                                    },
                                    position: childPosition,
                                    size: childSize,
                                });
                                break;
                            case 'testimonial':
                                children.push({
                                    id: childId,
                                    type: 'testimonial',
                                    componentData: {
                                        quote: child.querySelector('p:not(.author)')?.textContent.trim() || '',
                                        author: child.querySelector('p.author')?.textContent.trim()?.replace(/^- /, '') || '',
                                        ...childComponentData,
                                    },
                                    styles: {
                                        padding: childStyles.padding || '20px',
                                        background: childStyles.background || '#f9fafb',
                                        borderRadius: childStyles.borderRadius || '8px',
                                        ...childStyles,
                                    },
                                    position: childPosition,
                                    size: childSize,
                                });
                                break;
                            case 'menu':
                                const menuItems = [];
                                child.querySelectorAll('li a').forEach((link) => {
                                    menuItems.push({
                                        label: link.textContent.trim() || 'Menu Item',
                                        url: link.getAttribute('href') || '#',
                                    });
                                });
                                children.push({
                                    id: childId,
                                    type: 'menu',
                                    componentData: {
                                        items: menuItems,
                                        ...childComponentData,
                                    },
                                    styles: {
                                        display: childStyles.display || 'flex',
                                        gap: childStyles.gap || '20px',
                                        ...childStyles,
                                    },
                                    position: childPosition,
                                    size: childSize,
                                });
                                break;
                            case 'grid':
                                const gridItems = [];
                                child.querySelectorAll('.grid-item').forEach((item) => {
                                    const itemStyles = parseStyles(item.getAttribute('style') || '');
                                    gridItems.push({
                                        title: item.querySelector('h4')?.textContent.trim() || '',
                                        image: item.querySelector('img')?.getAttribute('src') || '',
                                        price: item.querySelector('p')?.textContent.trim() || '',
                                        styles: itemStyles,
                                    });
                                });
                                children.push({
                                    id: childId,
                                    type: 'grid',
                                    componentData: {
                                        items: gridItems,
                                        columns: parseInt(childStyles.gridTemplateColumns?.match(/repeat\((\d+)/)?.[1]) || 2,
                                        gap: childStyles.gap || '20px',
                                        ...childComponentData,
                                    },
                                    styles: {
                                        display: 'grid',
                                        gridTemplateColumns: childStyles.gridTemplateColumns || `repeat(${childComponentData.columns || 2}, 1fr)`,
                                        gap: childStyles.gap || '20px',
                                        ...childStyles,
                                    },
                                    position: childPosition,
                                    size: childSize,
                                });
                                break;
                            case 'list':
                                const listItems = [];
                                child.querySelectorAll('li').forEach((item) => {
                                    listItems.push({
                                        title: item.textContent.trim() || '',
                                    });
                                });
                                children.push({
                                    id: childId,
                                    type: 'list',
                                    componentData: {
                                        items: listItems,
                                        ...childComponentData,
                                    },
                                    styles: {
                                        listStyleType: childStyles.listStyleType || 'none',
                                        padding: childStyles.padding || '0',
                                        ...childStyles,
                                    },
                                    position: childPosition,
                                    size: childSize,
                                });
                                break;
                            case 'product':
                                children.push({
                                    id: childId,
                                    type: 'product',
                                    componentData: {
                                        title: child.querySelector('h4')?.textContent.trim() || '',
                                        image: child.querySelector('img')?.getAttribute('src') || '',
                                        price: child.querySelector('p')?.textContent.trim() || '',
                                        ...childComponentData,
                                    },
                                    styles: {
                                        padding: childStyles.padding || '16px',
                                        background: childStyles.background || '#ffffff',
                                        borderRadius: childStyles.borderRadius || '8px',
                                        boxShadow: childStyles.boxShadow || '0 2px 8px rgba(0,0,0,0.1)',
                                        ...childStyles,
                                    },
                                    position: childPosition,
                                    size: childSize,
                                });
                                break;
                            case 'gallery':
                                const galleryImages = [];
                                child.querySelectorAll('img').forEach((img) => {
                                    galleryImages.push(img.getAttribute('src') || '');
                                });
                                children.push({
                                    id: childId,
                                    type: 'gallery',
                                    componentData: {
                                        images: galleryImages,
                                        ...childComponentData,
                                    },
                                    styles: {
                                        display: 'grid',
                                        gridTemplateColumns: childStyles.gridTemplateColumns || 'repeat(auto-fill, minmax(150px, 1fr))',
                                        gap: childStyles.gap || '10px',
                                        ...childStyles,
                                    },
                                    position: childPosition,
                                    size: childSize,
                                });
                                break;
                            case 'carousel':
                                const carouselItems = [];
                                child.querySelectorAll('.carousel-item').forEach((item) => {
                                    const itemStyles = parseStyles(item.getAttribute('style') || '');
                                    carouselItems.push({
                                        title: item.querySelector('h4')?.textContent.trim() || '',
                                        image: item.querySelector('img')?.getAttribute('src') || '',
                                        styles: itemStyles,
                                    });
                                });
                                children.push({
                                    id: childId,
                                    type: 'carousel',
                                    componentData: {
                                        items: carouselItems,
                                        ...childComponentData,
                                    },
                                    styles: {
                                        overflow: childStyles.overflow || 'hidden',
                                        ...childStyles,
                                    },
                                    position: childPosition,
                                    size: childSize,
                                });
                                break;
                            case 'tabs':
                                const tabs = [];
                                child.querySelectorAll('.ladi-tab').forEach((tab, tabIndex) => {
                                    const content = child.querySelectorAll('.ladi-tab-content > div')[tabIndex]?.innerHTML || '';
                                    tabs.push({
                                        title: tab.textContent.trim() || `Tab ${tabIndex + 1}`,
                                        content,
                                    });
                                });
                                children.push({
                                    id: childId,
                                    type: 'tabs',
                                    componentData: {
                                        tabs,
                                        ...childComponentData,
                                    },
                                    styles: {
                                        display: childStyles.display || 'flex',
                                        flexDirection: childStyles.flexDirection || 'column',
                                        ...childStyles,
                                    },
                                    position: childPosition,
                                    size: childSize,
                                });
                                break;
                            case 'accordion':
                                const accordionItems = [];
                                child.querySelectorAll('.ladi-accordion-item').forEach((item) => {
                                    accordionItems.push({
                                        title: item.querySelector('.ladi-accordion-title')?.textContent.trim() || '',
                                        content: item.querySelector('.ladi-accordion-content')?.innerHTML || '',
                                    });
                                });
                                children.push({
                                    id: childId,
                                    type: 'accordion',
                                    componentData: {
                                        items: accordionItems,
                                        ...childComponentData,
                                    },
                                    styles: childStyles,
                                    position: childPosition,
                                    size: childSize,
                                });
                                break;
                            case 'table':
                                const headers = [];
                                const rows = [];
                                child.querySelectorAll('thead tr th').forEach((th) => {
                                    headers.push(th.textContent.trim());
                                });
                                child.querySelectorAll('tbody tr').forEach((tr) => {
                                    const row = [];
                                    tr.querySelectorAll('td').forEach((td) => {
                                        row.push(td.textContent.trim());
                                    });
                                    rows.push(row);
                                });
                                children.push({
                                    id: childId,
                                    type: 'table',
                                    componentData: {
                                        headers,
                                        rows,
                                        ...childComponentData,
                                    },
                                    styles: {
                                        border: childStyles.border || '1px solid #e5e7eb',
                                        borderCollapse: childStyles.borderCollapse || 'collapse',
                                        ...childStyles,
                                    },
                                    position: childPosition,
                                    size: childSize,
                                });
                                break;
                            case 'collection':
                                const collectionItems = [];
                                child.querySelectorAll('.collection-item').forEach((item) => {
                                    const itemStyles = parseStyles(item.getAttribute('style') || '');
                                    collectionItems.push({
                                        title: item.querySelector('h4')?.textContent.trim() || '',
                                        content: item.querySelector('p')?.textContent.trim() || '',
                                        styles: itemStyles,
                                    });
                                });
                                children.push({
                                    id: childId,
                                    type: 'collection',
                                    componentData: {
                                        items: collectionItems,
                                        ...childComponentData,
                                    },
                                    styles: {
                                        display: childStyles.display || 'flex',
                                        flexDirection: childStyles.flexDirection || 'column',
                                        gap: childStyles.gap || '20px',
                                        ...childStyles,
                                    },
                                    position: childPosition,
                                    size: childSize,
                                });
                                break;
                            case 'box':
                                children.push({
                                    id: childId,
                                    type: 'box',
                                    componentData: {
                                        title: child.querySelector('h3, h2')?.textContent.trim() || '',
                                        ...childComponentData,
                                    },
                                    styles: {
                                        padding: childStyles.padding || '20px',
                                        background: childStyles.background || '#ffffff',
                                        border: childStyles.border || '1px solid #e5e7eb',
                                        borderRadius: childStyles.borderRadius || '8px',
                                        ...childStyles,
                                    },
                                    position: childPosition,
                                    size: childSize,
                                });
                                break;
                            case 'popup':
                            case 'modal':
                                const popupChildren = [];
                                child.querySelectorAll('.lpb-child-element').forEach((popupChild, popupChildIndex) => {
                                    const popupChildType = popupChild.getAttribute('data-type') || popupChild.tagName.toLowerCase();
                                    const popupChildId = `popup-child-${index}-${childIndex}-${popupChildIndex}-${Date.now()}`;
                                    const popupChildStyles = parseStyles(popupChild.getAttribute('style') || '');
                                    const popupChildComponentData = parseDataAttribute(popupChild, 'data-component');
                                    const popupChildPosition = {
                                        desktop: parseDataAttribute(popupChild, 'data-position-desktop') || {
                                            x: parseFloat(popupChildStyles.left) || 0,
                                            y: parseFloat(popupChildStyles.top) || 0,
                                        },
                                        tablet: parseDataAttribute(popupChild, 'data-position-tablet') || {
                                            x: parseFloat(popupChildStyles.left) || 0,
                                            y: parseFloat(popupChildStyles.top) || 0,
                                        },
                                        mobile: parseDataAttribute(popupChild, 'data-position-mobile') || {
                                            x: parseFloat(popupChildStyles.left) || 0,
                                            y: parseFloat(popupChildStyles.top) || 0,
                                        },
                                    };
                                    const popupChildSize = {
                                        width: parseFloat(popupChildStyles.width) || 200,
                                        height: parseFloat(popupChildStyles.height) || 50,
                                    };
                                    popupChildren.push({
                                        id: popupChildId,
                                        type: popupChildType,
                                        componentData: {
                                            content: popupChild.textContent.trim() || '',
                                            ...popupChildComponentData,
                                        },
                                        styles: popupChildStyles,
                                        position: popupChildPosition,
                                        size: popupChildSize,
                                    });
                                });
                                children.push({
                                    id: childId,
                                    type: childType,
                                    componentData: {
                                        title: child.querySelector('h3')?.textContent.trim() || '',
                                        content: child.querySelector('p')?.textContent.trim() || '',
                                        buttonText: child.querySelector('button')?.textContent.trim() || '',
                                        ...childComponentData,
                                    },
                                    styles: {
                                        display: childStyles.display || 'none',
                                        background: childStyles.background || '#ffffff',
                                        borderRadius: childStyles.borderRadius || '12px',
                                        ...childStyles,
                                    },
                                    position: childPosition,
                                    size: childSize,
                                    children: popupChildren,
                                });
                                break;
                            case 'html':
                                children.push({
                                    id: childId,
                                    type: 'html',
                                    componentData: {
                                        content: child.innerHTML || '<p>Custom HTML content</p>',
                                        ...childComponentData,
                                    },
                                    styles: childStyles,
                                    position: childPosition,
                                    size: childSize,
                                });
                                break;
                            default:
                                console.warn(`Unknown child type: ${childType}`, child);
                                break;
                        }
                    });
                }
            }

            // Tạo element chính
            elements.push({
                id,
                type,
                componentData: {
                    ...componentData,
                    backgroundColor: styles.backgroundColor || componentData.backgroundColor || '#ffffff',
                },
                position,
                size,
                styles: {
                    ...styles,
                    borderRadius: styles.borderRadius || CSS_VARIABLES['--border-radius'] || '8px',
                    boxShadow: styles.boxShadow || CSS_VARIABLES['--shadow'] || 'none',
                    zIndex: styles.zIndex || (type === 'section' ? 1 : 10),
                },
                children,
            });
        });

        const pageData = { canvas: canvasData, elements };
        console.log('Parsed pageData:', JSON.stringify(pageData, null, 2));
        return pageData;
    } catch (error) {
        console.error('Error parsing HTML:', error);
        toast.error('Lỗi khi phân tích nội dung trang: ' + error.message);
        return {
            canvas: {
                width: parseInt(CSS_VARIABLES['--canvas-desktop']) || 1200,
                background: CSS_VARIABLES['--white'] || '#ffffff',
            },
            elements: [],
        };
    }
};

export const renderStaticHTML = (pageData) => {
    console.log('Rendering static HTML with pageData:', JSON.stringify(pageData, null, 2));

    const canvasWidth = pageData.canvas?.width || '1200px';
    const canvasBackground = pageData.canvas?.background || '#ffffff';

    let html = `
<!DOCTYPE html>
<html lang="vi">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Landing Page</title>
    <style>
        :root {
            --primary: ${CSS_VARIABLES['--primary']};
            --primary-dark: ${CSS_VARIABLES['--primary-dark']};
            --primary-light: ${CSS_VARIABLES['--primary-light']};
            --secondary: ${CSS_VARIABLES['--secondary']};
            --accent: ${CSS_VARIABLES['--accent']};
            --success: ${CSS_VARIABLES['--success']};
            --warning: ${CSS_VARIABLES['--warning']};
            --danger: ${CSS_VARIABLES['--danger']};
            --dark: ${CSS_VARIABLES['--dark']};
            --light: ${CSS_VARIABLES['--light']};
            --white: ${CSS_VARIABLES['--white']};
            --text-primary: ${CSS_VARIABLES['--text-primary']};
            --text-secondary: ${CSS_VARIABLES['--text-secondary']};
            --text-light: ${CSS_VARIABLES['--text-light']};
            --text-inverse: ${CSS_VARIABLES['--text-inverse']};
            --border-color: ${CSS_VARIABLES['--border-color']};
            --border-radius: ${CSS_VARIABLES['--border-radius']};
            --shadow: ${CSS_VARIABLES['--shadow']};
        }
        body {
            font-family: 'Inter', Arial, sans-serif;
            margin: 0;
            padding: 0;
            background: ${canvasBackground};
        }
        .page-container {
            position: relative;
            width: ${canvasWidth};
            min-height: 100vh;
            margin: 0 auto;
            background: ${canvasBackground};
        }
        .ladi-section {
            width: 100%;
            max-width: 1200px;
            margin: 0 auto;
            position: relative;
            overflow: hidden;
        }
        .ladi-section-background {
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            z-index: 0;
        }
        .ladi-overlay {
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            z-index: 1;
            pointer-events: none;
        }
        .ladi-container {
            position: relative;
            z-index: 2;
            padding: 20px;
            min-height: 100%;
            box-sizing: border-box;
        }
        .lpb-canvas-element, .lpb-child-element {
            position: absolute;
            cursor: move;
        }
        .lpb-canvas-element:hover, .lpb-child-element:hover {
            outline: 2px dashed var(--primary);
        }
        @media (max-width: 768px) {
            .page-container {
                width: 100%;
            }
            .ladi-section {
                width: 100%;
                min-width: auto;
            }
            .lpb-child-element {
                left: 0 !important;
                width: 100% !important;
            }
        }
        @media (max-width: 480px) {
            .lpb-child-element {
                left: 0 !important;
                width: 100% !important;
            }
        }
        .ladi-card, .ladi-product, .ladi-testimonial, .ladi-box {
            padding: 16px;
            box-sizing: border-box;
        }
        .ladi-menu {
            display: flex;
            gap: 20px;
            list-style: none;
            padding: 0;
        }
        .ladi-menu a {
            color: inherit;
            text-decoration: none;
        }
        .ladi-grid {
            display: grid;
        }
        .ladi-list {
            list-style: none;
            padding: 0;
        }
        .ladi-video {
            max-width: 100%;
        }
        .ladi-icon {
            display: inline-block;
        }
        .ladi-gallery {
            display: grid;
            gap: 10px;
        }
        .ladi-carousel {
            overflow: hidden;
            position: relative;
        }
        .ladi-tabs {
            display: flex;
            flex-direction: column;
        }
        .ladi-tabs-nav {
            display: flex;
            gap: 10px;
            border-bottom: 1px solid var(--border-color);
        }
        .ladi-tab {
            padding: 8px 16px;
            cursor: pointer;
        }
        .ladi-tab-content {
            padding: 16px;
        }
        .ladi-accordion-item {
            border-bottom: 1px solid var(--border-color);
        }
        .ladi-accordion-title {
            padding: 8px;
            cursor: pointer;
        }
        .ladi-table {
            width: 100%;
            border-collapse: collapse;
        }
        .ladi-table th, .ladi-table td {
            border: 1px solid var(--border-color);
            padding: 8px;
            text-align: left;
        }
        .ladi-survey {
            display: flex;
            flex-direction: column;
            gap: 10px;
        }
        .ladi-html {
            overflow: auto;
        }
        .ladi-collection {
            display: flex;
            flex-direction: column;
            gap: 20px;
        }
        .upload-input {
            margin: 10px 0;
        }
    </style>
    <script>
        function handleDragStart(event) {
            event.dataTransfer.setData('text/plain', event.target.id);
            event.target.style.opacity = '0.5';
        }
        function handleDragOver(event) {
            event.preventDefault();
        }
        function handleDrop(event) {
            event.preventDefault();
            const id = event.dataTransfer.getData('text/plain');
            const element = document.getElementById(id);
            const rect = event.currentTarget.getBoundingClientRect();
            const x = event.clientX - rect.left;
            const y = event.clientY - rect.top;
            element.style.left = x + 'px';
            element.style.top = y + 'px';
            element.style.opacity = '1';
            console.log('Dropped element:', id, 'at position:', { x, y });
        }
        function handleFileUpload(event, elementId) {
            const file = event.target.files[0];
            if (file) {
                console.log('Uploading file:', file.name, 'for element:', elementId);
                // Simulate S3 upload (replace with actual API call)
                const formData = new FormData();
                formData.append('file', file);
                fetch('/api/upload', {
                    method: 'POST',
                    body: formData
                })
                .then(response => response.json())
                .then(data => {
                    console.log('Uploaded to S3:', data.url);
                    const img = document.getElementById(elementId + '-img');
                    if (img) img.src = data.url;
                })
                .catch(error => console.error('Upload failed:', error));
            }
        }
    </script>
</head>
<body>
    <div class="page-container" ondragover="handleDragOver(event)" ondrop="handleDrop(event)">
    `;

    const renderElement = (element, isChild = false, parentType = '') => {
        const { id, type, componentData = {}, position = {}, size = {}, styles = {}, children = [] } = element;

        // Normalize styles and position
        const positionStyles = position.desktop ? `left: ${position.desktop.x || 0}px; top: ${position.desktop.y || 0}px;` : '';
        const sizeStyles = size ? `width: ${size.width || 200}px; height: ${size.height || 50}px;` : '';
        const cssStyles = styles ? Object.entries(styles).filter(([key]) => key !== 'position').map(([key, value]) => `${key}: ${value};`).join(' ') : '';
        const elementClass = isChild ? `lpb-child-element ladi-${type}` : `lpb-canvas-element ladi-${type}`;
        const elementPosition = isChild ? `position: absolute; ${positionStyles}` : (position.desktop ? `position: absolute; transform: translate(${position.desktop.x || 0}px, ${position.desktop.y || 0}px);` : '');

        let elementHtml = `<div id="${id}" class="${elementClass}" style="${elementPosition} ${sizeStyles} ${cssStyles}" data-type="${type}">`;

        switch (type) {
            case 'section':
                if (componentData.structure === 'ladi-standard') {
                    elementHtml += `
                        <div class="ladi-section" style="width: 100%; height: 100%; position: relative;">
                            <div class="ladi-section-background" style="background-color: ${componentData.backgroundColor || 'transparent'}; background-image: ${componentData.backgroundImage ? `url(${componentData.backgroundImage})` : 'none'}; background-size: cover; background-position: center;"></div>
                            <div class="ladi-overlay" style="background-color: ${componentData.overlayColor || 'transparent'}; opacity: ${componentData.overlayOpacity || 0};"></div>
                            <div class="ladi-container" style="padding: ${componentData.padding || '20px'}; width: 100%; height: 100%; position: relative;" ondragover="handleDragOver(event)" ondrop="handleDrop(event)">
                    `;
                    children.forEach((child) => {
                        elementHtml += renderElement(child, true, type);
                    });
                    elementHtml += `</div></div>`;
                } else {
                    elementHtml += `<div style="display: ${componentData.layoutType || 'flex'}; flex-direction: ${componentData.direction || 'column'}; gap: ${componentData.gap || '10px'}; padding: ${componentData.padding || '0'}; background: ${componentData.background || 'transparent'}; ${cssStyles}" ondragover="handleDragOver(event)" ondrop="handleDrop(event)">`;
                    if (componentData.title && !children.some(child => child?.type === 'heading')) {
                        elementHtml += `<h2 style="font-size: ${componentData.titleFontSize || '1.8rem'}; margin: ${componentData.titleMargin || '0'}; color: ${componentData.titleColor || '#1f2937'}; font-weight: ${componentData.titleFontWeight || '700'};">${componentData.title}</h2>`;
                    }
                    children.forEach((child) => {
                        elementHtml += renderElement(child, true, type);
                    });
                    elementHtml += `</div>`;
                }
                break;
            case 'container':
            case 'box':
                elementHtml += `<div style="display: ${componentData.layoutType || 'flex'}; flex-direction: ${componentData.direction || 'column'}; gap: ${componentData.gap || '10px'}; padding: ${componentData.padding || '20px'}; background: ${componentData.background || 'transparent'}; ${cssStyles}" ondragover="handleDragOver(event)" ondrop="handleDrop(event)">`;
                if (componentData.title && !children.some(child => child?.type === 'heading')) {
                    elementHtml += `<h2 style="font-size: ${componentData.titleFontSize || '1.8rem'}; margin: ${componentData.titleMargin || '0'}; color: ${componentData.titleColor || '#1f2937'}; font-weight: ${componentData.titleFontWeight || '700'};">${componentData.title}</h2>`;
                }
                children.forEach((child) => {
                    elementHtml += renderElement(child, true, type);
                });
                elementHtml += `</div>`;
                break;
            case 'heading':
                const headingLevel = componentData.level && ['h1', 'h2', 'h3', 'h4', 'h5', 'h6'].includes(componentData.level)
                    ? componentData.level
                    : 'h2';
                elementHtml += `<${headingLevel} style="font-size: ${componentData.fontSize || '1.5rem'}; color: ${componentData.color || '#1f2937'}; margin: ${componentData.margin || '0'}; padding: ${componentData.padding || '0'}; font-weight: ${componentData.fontWeight || '700'}; text-align: ${componentData.textAlign || 'left'}; ${cssStyles}">${componentData.content || 'Tiêu đề'}</${headingLevel}>`;
                break;
            case 'paragraph':
