import React, {useState, useCallback, useMemo, useEffect} from 'react';
import {useDrag} from 'react-dnd';
import {useHotkeys} from 'react-hotkeys-hook';
import {toast} from 'react-toastify';
import COMPONENT_LIBRARY from '../../constants/componentLibrary';

const ItemTypes = {
    COMPONENT: 'component',
    TEMPLATE: 'template',
};

const EXTENDED_LIBRARY = {
    // (Giữ nguyên nội dung EXTENDED_LIBRARY như trong mã ban đầu)
    elements: {
        name: 'Phần tử',
        icon: 'fas fa-cube',
        subCategories: [
            {
                id: 'text',
                name: 'Văn bản',
                templates: [
                    {
                        id: 'text-basic',
                        name: 'Cơ bản',
                        icon: 'far fa-font',
                        description: 'Văn bản đơn giản, dễ chỉnh sửa',
                        level: 'basic',
                        json: {
                            type: 'heading',
                            content: 'Tiêu đề mẫu',
                            styles: {color: '#333', fontSize: '24px'},
                        },
                    },
                    {
                        id: 'text-advanced',
                        name: 'Nâng cao',
                        icon: 'far fa-pen',
                        description: 'Văn bản với hiệu ứng động và tùy chỉnh',
                        level: 'advanced',
                        variants: ['bold', 'italic', 'animated'],
                        json: {
                            type: 'paragraph',
                            content: 'Văn bản nâng cao với hiệu ứng',
                            styles: {color: '#555', fontStyle: 'italic', animation: 'fadeIn 1s'},
                        },
                    },
                ],
            },
            {
                id: 'buttons',
                name: 'Nút bấm',
                templates: [
                    {
                        id: 'button-basic',
                        name: 'Nút cơ bản',
                        icon: 'far fa-square',
                        description: 'Nút hành động đơn giản, dễ sử dụng',
                        level: 'basic',
                        json: {
                            type: 'button',
                            content: 'Nhấn vào',
                            styles: {background: '#D4E4E4', color: '#333', padding: '8px 16px', borderRadius: '4px'},
                        },
                    },
                    {
                        id: 'button-group',
                        name: 'Nhóm nút',
                        icon: 'far fa-th',
                        description: 'Nhóm nút liên kết với bố cục linh hoạt',
                        level: 'advanced',
                        json: {
                            type: 'button-group',
                            content: ['Nút 1', 'Nút 2'],
                            styles: {gap: '10px'},
                        },
                    },
                    {
                        id: 'button-send',
                        name: 'Gửi',
                        icon: 'far fa-paper-plane',
                        description: 'Nút gửi thông tin',
                        level: 'basic',
                        json: {
                            type: 'button',
                            content: 'Gửi',
                            styles: {background: '#E6F3D6', color: '#333'},
                        },
                    },
                    {
                        id: 'button-contact',
                        name: 'Liên hệ',
                        icon: 'far fa-envelope',
                        description: 'Nút liên hệ nhanh',
                        level: 'basic',
                        json: {
                            type: 'button',
                            content: 'Liên hệ',
                            styles: {background: '#FAD2E1', color: '#333'},
                        },
                    },
                    {
                        id: 'button-readmore',
                        name: 'Đọc thêm',
                        icon: 'far fa-book-open',
                        description: 'Nút dẫn đến nội dung chi tiết',
                        level: 'basic',
                        json: {
                            type: 'button',
                            content: 'Đọc thêm',
                            styles: {background: '#D4E4E4', color: '#333'},
                        },
                    },
                ],
            },
            {
                id: 'forms',
                name: 'Form',
                templates: [
                    {
                        id: 'form-basic',
                        name: 'Form cơ bản',
                        icon: 'far fa-wpforms',
                        description: 'Form liên hệ đơn giản, dễ tùy chỉnh',
                        level: 'basic',
                        json: {
                            type: 'form',
                            content: {fields: ['Tên', 'Email']},
                            styles: {border: '1px solid #D4E4E4', padding: '16px'},
                        },
                    },
                    {
                        id: 'form-advanced',
                        name: 'Form nâng cao',
                        icon: 'far fa-clipboard',
                        description: 'Form đa bước với xác thực',
                        level: 'advanced',
                        json: {
                            type: 'form',
                            content: {fields: ['Tên', 'Email', 'Số điện thoại'], steps: 2},
                            styles: {background: '#E6F3D6', padding: '20px'},
                        },
                    },
                ],
            },
        ],
    },
    sections: {
        name: 'Section',
        icon: 'fas fa-layer-group',
        subCategories: [
            {
                id: 'images',
                name: 'Ảnh',
                templates: [
                    {
                        id: 'image-basic',
                        name: 'Ảnh đơn',
                        icon: 'far fa-image',
                        description: 'Hình ảnh cơ bản, dễ chèn',
                        level: 'basic',
                        json: {
                            type: 'image',
                            content: 'https://via.placeholder.com/300x200',
                            styles: {width: '300px', borderRadius: '8px'},
                        },
                    },
                    {
                        id: 'image-gallery',
                        name: 'Hình hộp',
                        icon: 'far fa-images',
                        description: 'Gallery hình ảnh với bố cục lưới',
                        level: 'advanced',
                        json: {
                            type: 'gallery',
                            content: ['https://via.placeholder.com/150x150', 'https://via.placeholder.com/150x150'],
                            styles: {gap: '10px'},
                        },
                    },
                ],
            },
            {
                id: 'icons',
                name: 'Biểu tượng',
                templates: [
                    {
                        id: 'icon-basic',
                        name: 'Biểu tượng đơn',
                        icon: 'far fa-star',
                        description: 'Icon cơ bản, dễ tùy chỉnh',
                        level: 'basic',
                        json: {
                            type: 'icon',
                            content: 'star',
                            styles: {color: '#FAD2E1', fontSize: '24px'},
                        },
                    },
                ],
            },
            {
                id: 'lines',
                name: 'Dòng kẻ',
                templates: [
                    {
                        id: 'line-basic',
                        name: 'Dòng kẻ',
                        icon: 'far fa-minus',
                        description: 'Dòng kẻ phân cách trang nhã',
                        level: 'basic',
                        json: {
                            type: 'line',
                            styles: {borderTop: '1px solid #D4E4E4', width: '100%'},
                        },
                    },
                ],
            },
            {
                id: 'media',
                name: 'Truyền thông',
                templates: [
                    {
                        id: 'product-basic',
                        name: 'Sản phẩm cơ bản',
                        icon: 'far fa-box',
                        description: 'Hiển thị sản phẩm đơn giản',
                        level: 'basic',
                        json: {
                            type: 'product',
                            content: {title: 'Sản phẩm 1'},
                            styles: {border: '1px solid #E6F3D6'},
                        },
                    },
                    {
                        id: 'product-advanced',
                        name: 'Sản phẩm nâng cao',
                        icon: 'far fa-shopping-cart',
                        description: 'Với giá và nút mua',
                        level: 'advanced',
                        json: {
                            type: 'product',
                            content: {title: 'Sản phẩm 2', price: '$19.99', button: 'Mua ngay'},
                            styles: {background: '#FAD2E1', padding: '10px'},
                        },
                    },
                ],
            },
            {
                id: 'video',
                name: 'Video',
                templates: [
                    {
                        id: 'video-basic',
                        name: 'Video nhúng',
                        icon: 'far fa-video',
                        description: 'Video cơ bản với điều khiển',
                        level: 'basic',
                        json: {
                            type: 'video',
                            content: 'https://www.youtube.com/embed/dQw4w9WgXcQ',
                            styles: {width: '100%', height: '200px'},
                        },
                    },
                ],
            },
            {
                id: 'lists',
                name: 'Danh sách',
                templates: [
                    {
                        id: 'list-basic',
                        name: 'Danh sách cơ bản',
                        icon: 'far fa-list',
                        description: 'Danh sách sản phẩm hoặc nội dung',
                        level: 'basic',
                        json: {
                            type: 'list',
                            content: ['Item 1', 'Item 2', 'Item 3'],
                            styles: {color: '#333'},
                        },
                    },
                ],
            },
            {
                id: 'carousel',
                name: 'Carousel',
                templates: [
                    {
                        id: 'carousel-basic',
                        name: 'Carousel ảnh',
                        icon: 'far fa-sliders-h',
                        description: 'Trình chiếu ảnh cơ bản',
                        level: 'basic',
                        json: {
                            type: 'carousel',
                            content: ['https://via.placeholder.com/400x200', 'https://via.placeholder.com/400x200'],
                            styles: {height: '200px'},
                        },
                    },
                    {
                        id: 'carousel-advanced',
                        name: 'Carousel nâng cao',
                        icon: 'far fa-images',
                        description: 'Với autoplay và điều khiển',
                        level: 'advanced',
                        json: {
                            type: 'carousel',
                            content: ['https://via.placeholder.com/400x200', 'https://via.placeholder.com/400x200'],
                            styles: {height: '200px', controls: true, autoplay: true},
                        },
                    },
                ],
            },
            {
                id: 'tabs',
                name: 'Tabs',
                templates: [
                    {
                        id: 'tabs-basic',
                        name: 'Tabs cơ bản',
                        icon: 'far fa-folder',
                        description: 'Tabs chuyển đổi nội dung',
                        level: 'basic',
                        json: {
                            type: 'tabs',
                            content: {tabs: ['Tab 1', 'Tab 2']},
                            styles: {border: '1px solid #D4E4E4'},
                        },
                    },
                ],
            },
            {
                id: 'accordion',
                name: 'Accordion',
                templates: [
                    {
                        id: 'accordion-basic',
                        name: 'Accordion FAQ',
                        icon: 'far fa-caret-down',
                        description: 'Mở rộng nội dung FAQ',
                        level: 'basic',
                        json: {
                            type: 'accordion',
                            content: {items: ['Câu 1', 'Câu 2']},
                            styles: {background: '#E6F3D6'},
                        },
                    },
                ],
            },
            {
                id: 'table',
                name: 'Bảng',
                templates: [
                    {
                        id: 'table-basic',
                        name: 'Bảng cơ bản',
                        icon: 'far fa-table',
                        description: 'Bảng dữ liệu đơn giản',
                        level: 'basic',
                        json: {
                            type: 'table',
                            content: {headers: ['Cột 1', 'Cột 2'], rows: [['Dữ liệu 1', 'Dữ liệu 2']]},
                            styles: {border: '1px solid #D4E4E4'},
                        },
                    },
                ],
            },
            {
                id: 'survey',
                name: 'Khảo sát',
                templates: [
                    {
                        id: 'survey-basic',
                        name: 'Khảo sát cơ bản',
                        icon: 'far fa-poll',
                        description: 'Form khảo sát đơn giản',
                        level: 'basic',
                        json: {
                            type: 'survey',
                            content: {questions: ['Câu hỏi 1']},
                            styles: {padding: '16px'},
                        },
                    },
                ],
            },
            {
                id: 'menu',
                name: 'Navigation Menu',
                templates: [
                    {
                        id: 'menu-basic',
                        name: 'Menu điều hướng',
                        icon: 'far fa-bars',
                        description: 'Menu điều hướng cơ bản',
                        level: 'basic',
                        json: {
                            type: 'menu',
                            content: ['Trang chủ', 'Giới thiệu'],
                            styles: {background: '#FAD2E1'},
                        },
                    },
                    {
                        id: 'menu-html',
                        name: 'Mã HTML',
                        icon: 'far fa-code',
                        description: 'Menu tùy chỉnh bằng HTML',
                        level: 'advanced',
                        json: {
                            type: 'menu',
                            content: '<nav>Menu HTML</nav>',
                            styles: {padding: '10px'},
                        },
                    },
                ],
            },
        ],
    },
    popups: {
        name: 'Popup',
        icon: 'fas fa-window-restore',
        subCategories: [
            {
                id: 'basic',
                name: 'Popup cơ bản',
                templates: [
                    {
                        id: 'popup-basic',
                        name: 'Popup chào mừng',
                        icon: 'far fa-window-maximize',
                        description: 'Popup chào mừng đơn giản',
                        level: 'basic',
                        json: {
                            type: 'popup',
                            content: 'Chào mừng bạn!',
                            styles: {background: '#E6F3D6', borderRadius: '8px'},
                        },
                    },
                    {
                        id: 'popup-advanced',
                        name: 'Popup khuyến mãi',
                        icon: 'far fa-gift',
                        description: 'Popup với timer và form',
                        level: 'advanced',
                        json: {
                            type: 'popup',
                            content: {message: 'Khuyến mãi!', form: ['Email']},
                            styles: {background: '#FAD2E1', padding: '20px'},
                        },
                    },
                ],
            },
            {
                id: 'banner',
                name: 'Banner Ads',
                templates: [
                    {
                        id: 'banner-basic',
                        name: 'Banner ngang',
                        icon: 'far fa-ad',
                        description: 'Banner quảng cáo ngang',
                        level: 'basic',
                        json: {
                            type: 'banner',
                            content: 'Quảng cáo mẫu',
                            styles: {background: '#D4E4E4', height: '60px'},
                        },
                    },
                ],
            },
        ],
    },
    products: {
        name: 'Sản phẩm',
        icon: 'fas fa-shopping-bag',
        subCategories: [
            {
                id: 'listings',
                name: 'Danh sách sản phẩm',
                templates: [
                    {
                        id: 'product-list-basic',
                        name: 'Lưới sản phẩm',
                        icon: 'far fa-th',
                        description: 'Hiển thị sản phẩm dạng lưới',
                        level: 'basic',
                        json: {
                            type: 'product-list',
                            content: [{title: 'Sản phẩm 1'}, {title: 'Sản phẩm 2'}],
                            styles: {gap: '10px'},
                        },
                    },
                    {
                        id: 'product-list-advanced',
                        name: 'Danh sách chi tiết',
                        icon: 'far fa-list-ul',
                        description: 'Danh sách với mô tả đầy đủ',
                        level: 'advanced',
                        json: {
                            type: 'product-list',
                            content: [
                                {title: 'Sản phẩm 1', description: 'Mô tả chi tiết'},
                                {title: 'Sản phẩm 2', description: 'Mô tả chi tiết'},
                            ],
                            styles: {padding: '10px'},
                        },
                    },
                ],
            },
            {
                id: 'orders',
                name: 'Form đơn hàng',
                templates: [
                    {
                        id: 'order-form-basic',
                        name: 'Form mua hàng',
                        icon: 'far fa-cart-plus',
                        description: 'Form mua hàng cơ bản',
                        level: 'basic',
                        json: {
                            type: 'order-form',
                            content: {fields: ['Sản phẩm', 'Số lượng']},
                            styles: {border: '1px solid #D4E4E4'},
                        },
                    },
                ],
            },
        ],
    },
    templates: {
        name: 'Templates',
        icon: 'fas fa-file-alt',
        subCategories: [
            {
                id: 'landing',
                name: 'Landing Page',
                templates: [
                    {
                        id: 'lp-education',
                        name: 'Giáo dục',
                        icon: 'far fa-graduation-cap',
                        description: 'Template cho khóa học trực tuyến',
                        level: 'basic',
                        structure: [
                            {
                                id: 'header-edu',
                                json: {
                                    type: 'section',
                                    content: {title: 'Khóa học trực tuyến', subtitle: 'Học mọi lúc, mọi nơi'},
                                    styles: {background: '#E6F3D6'},
                                },
                            },
                            {
                                id: 'form-edu',
                                json: {type: 'form', content: {fields: ['Tên', 'Email']}, styles: {}},
                            },
                        ],
                    },
                    {
                        id: 'lp-retail',
                        name: 'Bán lẻ',
                        icon: 'far fa-store',
                        description: 'Template cho cửa hàng bán lẻ',
                        level: 'advanced',
                        structure: [
                            {
                                id: 'header-retail',
                                json: {
                                    type: 'section',
                                    content: {title: 'Chào mừng đến cửa hàng', subtitle: 'Khám phá sản phẩm'},
                                    styles: {background: '#FAD2E1'},
                                },
                            },
                            {
                                id: 'product-list',
                                json: {
                                    type: 'product-list',
                                    content: [{title: 'Sản phẩm 1'}, {title: 'Sản phẩm 2'}],
                                    styles: {},
                                },
                            },
                        ],
                    },
                ],
            },
        ],
    },
};
const DraggableComponent = React.memo(({ item, isTemplate = false }) => {
    const [{ isDragging }, drag] = useDrag(() => ({
        type: isTemplate ? ItemTypes.TEMPLATE : ItemTypes.COMPONENT,
        item: { componentData: isTemplate ? item.structure : item },
        collect: (monitor) => ({
            isDragging: !!monitor.isDragging(),
        }),
    }));

    return (
        <div
            ref={drag}
            className={`component-library-template-item ${isDragging ? 'dragging' : ''}`}
            style={{ opacity: isDragging ? 0.5 : 1 }}
            onMouseDown={() => toast.info(`Bắt đầu kéo ${item.name}`)}
            data-tooltip-id="component-tooltip"
            data-tooltip-content={item.description || `Kéo để thêm ${item.name}`}
        >
            <i className={`${item.icon} text-gray-500 text-base`}></i>
            <span className="font-medium text-gray-700 truncate w-20">{item.name}</span>
            {item.level && <span className="component-library-level">{item.level.toUpperCase()}</span>}
        </div>
    );
});

const ComponentLibrary = React.memo(({ isCollapsed: initialCollapsed, onToggle }) => {
    const [isCollapsed, setIsCollapsed] = useState(initialCollapsed);
    const [activeCategory, setActiveCategory] = useState('elements');
    const [searchQuery, setSearchQuery] = useState('');
    const [expandedSubs, setExpandedSubs] = useState({});
    const [isDragging, setIsDragging] = useState(false);

    const categories = Object.keys(EXTENDED_LIBRARY).map((key) => ({
        id: key,
        ...EXTENDED_LIBRARY[key],
    }));

    const handleToggle = useCallback(() => {
        setIsCollapsed((prev) => !prev);
        onToggle();
    }, [onToggle]);

    const toggleSub = useCallback((catId, subId) => {
        setExpandedSubs((prev) => ({
            ...prev,
            [catId]: { ...prev[catId], [subId]: !prev[catId]?.[subId] },
        }));
    }, []);

    const filteredItems = useMemo(() => {
        const categoryData = EXTENDED_LIBRARY[activeCategory];
        if (!categoryData) return [];

        let items = [];
        categoryData.subCategories.forEach((sub) => {
            if (expandedSubs[activeCategory]?.[sub.id]) {
                items = [...items, ...sub.templates];
            }
        });

        if (!searchQuery) return items;
        return items.filter((item) =>
            item.name.toLowerCase().includes(searchQuery.toLowerCase())
        );
    }, [activeCategory, searchQuery, expandedSubs]);

    const handleCategoryChange = useCallback((categoryId) => {
        setActiveCategory(categoryId);
        setSearchQuery('');
        toast.info(`Đã chuyển sang danh mục ${EXTENDED_LIBRARY[categoryId]?.name}`);
    }, []);

    categories.forEach((cat, index) => {
        useHotkeys(`ctrl+${index + 1}`, () => handleCategoryChange(cat.id), [handleCategoryChange]);
    });

    useEffect(() => {
        if (!isDragging) {
            setActiveCategory('elements');
        }
    }, [isDragging]);

    if (isCollapsed) {
        return (
            <div className="component-library-container component-library-collapsed">
                
                {categories.map((cat) => (
                    <button
                        key={cat.id}
                        className="component-library-icon-collapsed"
                        data-tooltip-id="component-tooltip"
                        data-tooltip-content={cat.name}
                        onClick={() => {
                            handleCategoryChange(cat.id);
                            setIsCollapsed(false);
                            onToggle();
                        }}
                    >
                        <i className={cat.icon}></i>
                    </button>
                ))}
            </div>
        );
    }

    const renderSubCategories = (categoryData) => {
        return categoryData.subCategories.map((sub) => (
            <div key={sub.id} className="component-library-subcategory">
                <button
                    onClick={() => toggleSub(activeCategory, sub.id)}
                    className="component-library-subcategory-toggle"
                >
                    <span>{sub.name}</span>
                    <i
                        className={`fas fa-chevron-${expandedSubs[activeCategory]?.[sub.id] ? 'down' : 'right'}`}
                    ></i>
                </button>
                {expandedSubs[activeCategory]?.[sub.id] && (
                    <div className="component-library-template-column">
                        {Array.from({ length: 2 }, (_, rowIndex) => (
                            <div key={rowIndex} className="component-library-template-list">
                                {sub.templates.slice(rowIndex * 2, rowIndex * 2 + 2).map((template) => (
                                    <DraggableComponent
                                        key={template.id}
                                        item={template}
                                        isTemplate={activeCategory === 'templates'}
                                        onDrag={() => setIsDragging(true)}
                                        onDragEnd={() => setIsDragging(false)}
                                    />
                                ))}
                            </div>
                        ))}
                    </div>
                )}
            </div>
        ));
    };

    return (
        <div className="component-library-container">
            <div className="component-library-wrapper">
                <div className="component-library-header">
                    <h2 className="component-library-title">Thư viện</h2>
                    <button
                        onClick={handleToggle}
                        className="component-library-toggle"
                        data-tooltip-id="component-tooltip"
                        data-tooltip-content={isCollapsed ? 'Mở thư viện' : 'Thu gọn thư viện'}
                    >
                        <i className={`fas fa-chevron-${isCollapsed ? 'right' : 'left'}`}></i>
                    </button>
                </div>
                <div className="component-library-category-nav">
                    {categories.map((cat) => (
                        <button
                            key={cat.id}
                            onClick={() => handleCategoryChange(cat.id)}
                            className={`component-library-category-btn ${activeCategory === cat.id ? 'active' : ''}`}
                            data-tooltip-id="component-tooltip"
                            data-tooltip-content={`Chuyển sang ${cat.name}`}
                        >
                            {cat.name}
                        </button>
                    ))}
                </div>
                <div className="component-library-content">
                    <div className="component-library-search">
                        <input
                            type="text"
                            placeholder="Tìm kiếm thành phần..."
                            value={searchQuery}
                            onChange={(e) => setSearchQuery(e.target.value)}
                            className="component-library-search-input"
                        />
                    </div>
                    {renderSubCategories(EXTENDED_LIBRARY[activeCategory])}
                    {filteredItems.length === 0 && (
                        <div className="component-library-no-results">Không tìm thấy thành phần</div>
                    )}
                </div>
                <div className="component-library-template-column">
                    {Array.from({ length: 2 }, (_, rowIndex) => (
                        <div key={rowIndex} className="component-library-template-list">
                            {filteredItems.slice(rowIndex * 2, rowIndex * 2 + 2).map((item) => (
                                <DraggableComponent
                                    key={item.id}
                                    item={item}
                                    isTemplate={activeCategory === 'templates'}
                                    onDrag={() => setIsDragging(true)}
                                    onDragEnd={() => setIsDragging(false)}
                                />
                            ))}
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
});

export default ComponentLibrary;
