import React from 'react';

export const ItemTypes = {
    ELEMENT: 'element',
    CHILD_ELEMENT: 'child_element',
};

export const getCanvasPosition = (clientX, clientY, canvasRef, zoomLevel) => {
    if (!canvasRef.current) return { x: 0, y: 0 };
    const rect = canvasRef.current.getBoundingClientRect();
    return {
        x: (clientX - rect.left) / (zoomLevel / 100),
        y: (clientY - rect.top) / (zoomLevel / 100),
    };
};

export const snapToGrid = (x, y, gridSize, snapPoints = []) => {
    let snappedX = Math.round(x / gridSize) * gridSize;
    let snappedY = Math.round(y / gridSize) * gridSize;

    snapPoints.forEach((point) => {
        if (Math.abs(x - point.x) < 15) snappedX = point.x;
        if (Math.abs(y - point.y) < 15) snappedY = point.y;
    });

    return { x: Math.max(0, snappedX), y: Math.max(0, snappedY) };
};

export const getElementBounds = (element) => {
    const pos = element.position?.desktop || { x: 0, y: 0 };
    const size = element.size || { width: 200, height: 50 };
    return {
        left: pos.x,
        top: pos.y,
        right: pos.x + size.width,
        bottom: pos.y + size.height,
        centerX: pos.x + size.width / 2,
        centerY: pos.y + size.height / 2,
    };
};

export const renderComponentContent = (type, componentData = {}, styles = {}) => {
    switch (type) {
        case 'section':
            return (
                <div style={{ padding: '20px', background: '#f9fafb', ...styles }}>
                    {componentData.title || 'Section'}
                </div>
            );
        case 'popup':
            return (
                <div style={{ padding: '20px', textAlign: 'center', background: '#fff', boxShadow: '0 4px 16px rgba(0, 0, 0, 0.2)', ...styles }}>
                    {componentData.content || componentData.title || 'Popup'}
                    {componentData.fields && (
                        <form style={{ display: 'flex', flexDirection: 'column', gap: '10px', marginTop: '10px' }}>
                            {componentData.fields.map((field, index) => (
                                <input
                                    key={index}
                                    type={field.type}
                                    placeholder={field.placeholder}
                                    style={{ padding: '8px', borderRadius: '4px', border: '1px solid #e5e7eb' }}
                                />
                            ))}
                            {componentData.buttonText && (
                                <button
                                    style={{
                                        padding: '8px 16px',
                                        background: '#2563eb',
                                        color: '#fff',
                                        border: 'none',
                                        borderRadius: '4px',
                                        cursor: 'pointer',
                                    }}
                                >
                                    {componentData.buttonText}
                                </button>
                            )}
                        </form>
                    )}
                </div>
            );
        case 'heading':
            return (
                <h2 style={{ fontSize: styles.fontSize || '2rem', ...styles }}>
                    {componentData.content || componentData.title || 'Tiêu đề'}
                </h2>
            );
        case 'paragraph':
            return (
                <p style={{ fontSize: styles.fontSize || '1rem', lineHeight: '1.5', ...styles }}>
                    {componentData.content || componentData.title || 'Đoạn văn'}
                </p>
            );
        case 'button':
            return (
                <button
                    style={{
                        padding: '8px 16px',
                        background: styles.background || '#2563eb',
                        color: styles.color || '#fff',
                        border: 'none',
                        borderRadius: '4px',
                        cursor: 'pointer',
                        ...styles,
                    }}
                >
                    {componentData.content || componentData.title || 'Nút bấm'}
                </button>
            );
        case 'form':
            return (
                <form style={{ display: 'flex', flexDirection: 'column', gap: '10px', ...styles }}>
                    {componentData.fields?.map((field, index) => (
                        <div key={index}>
                            {field.label && <label>{field.label}</label>}
                            <input
                                type={field.type}
                                placeholder={field.placeholder}
                                style={{ padding: '8px', borderRadius: '4px', border: '1px solid #e5e7eb', width: '100%' }}
                            />
                        </div>
                    ))}
                    {componentData.buttonText && (
                        <button
                            style={{
                                padding: '8px 16px',
                                background: '#2563eb',
                                color: '#fff',
                                border: 'none',
                                borderRadius: '4px',
                                cursor: 'pointer',
                            }}
                        >
                            {componentData.buttonText}
                        </button>
                    )}
                </form>
            );
        case 'image':
            return (
                <img
                    src={componentData.src || 'https://via.placeholder.com/200'}
                    alt={componentData.alt || 'Hình ảnh'}
                    style={{ maxWidth: '100%', ...styles }}
                />
            );
        case 'video':
            return (
                <video
                    src={componentData.src || 'https://www.w3schools.com/html/mov_bbb.mp4'}
                    controls
                    style={{ maxWidth: '100%', ...styles }}
                />
            );
        case 'icon':
            return (
                <i
                    className={componentData.icon || 'fas fa-star'}
                    style={{ fontSize: styles.fontSize || '2rem', color: styles.color || '#000', ...styles }}
                />
            );
        case 'divider':
            return <hr style={{ border: 'none', height: '2px', background: '#e5e7eb', ...styles }} />;
        case 'product':
            return (
                <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', ...styles }}>
                    <img
                        src={componentData.image || 'https://via.placeholder.com/150'}
                        alt={componentData.title || 'Sản phẩm'}
                        style={{ maxWidth: '100%', borderRadius: '8px' }}
                    />
                    <h3>{componentData.title || 'Sản phẩm'}</h3>
                    <p>{componentData.price || '$0.00'}</p>
                </div>
            );
        case 'gallery':
            return (
                <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(100px, 1fr))', gap: '10px', ...styles }}>
                    {componentData.images?.map((src, index) => (
                        <img key={index} src={src} alt={`Hình ${index + 1}`} style={{ maxWidth: '100%', borderRadius: '4px' }} />
                    ))}
                </div>
            );
        case 'carousel':
            return (
                <div style={{ overflow: 'hidden', ...styles }}>
                    {componentData.items?.map((item, index) => (
                        <div key={index} style={{ display: 'inline-block', width: '100%' }}>
                            <img src={item.image} alt={item.title} style={{ maxWidth: '100%' }} />
                            <p>{item.title}</p>
                        </div>
                    ))}
                </div>
            );
        case 'tabs':
            return (
                <div style={{ ...styles }}>
                    {componentData.tabs?.map((tab, index) => (
                        <div key={index}>
                            <button style={{ padding: '8px', marginRight: '5px' }}>{tab.title}</button>
                            {index === 0 && <p>{tab.content}</p>}
                        </div>
                    ))}
                </div>
            );
        case 'accordion':
            return (
                <div style={{ ...styles }}>
                    {componentData.items?.map((item, index) => (
                        <div key={index}>
                            <button style={{ padding: '8px', width: '100%', textAlign: 'left' }}>{item.title}</button>
                            {index === 0 && <p>{item.content}</p>}
                        </div>
                    ))}
                </div>
            );
        case 'table':
            return (
                <table style={{ borderCollapse: 'collapse', width: '100%', ...styles }}>
                    <thead>
                    <tr>
                        {componentData.headers?.map((header, index) => (
                            <th key={index} style={{ border: '1px solid #e5e7eb', padding: '8px' }}>{header}</th>
                        ))}
                    </tr>
                    </thead>
                    <tbody>
                    {componentData.rows?.map((row, rowIndex) => (
                        <tr key={rowIndex}>
                            {row.map((cell, cellIndex) => (
                                <td key={cellIndex} style={{ border: '1px solid #e5e7eb', padding: '8px' }}>{cell}</td>
                            ))}
                        </tr>
                    ))}
                    </tbody>
                </table>
            );
        case 'survey':
            return (
                <form style={{ display: 'flex', flexDirection: 'column', gap: '10px', ...styles }}>
                    {componentData.questions?.map((question, index) => (
                        <div key={index}>
                            <label>{question.label}</label>
                            {question.type === 'text' && (
                                <input type="text" style={{ padding: '8px', borderRadius: '4px', border: '1px solid #e5e7eb' }} />
                            )}
                            {question.type === 'radio' && (
                                <div>
                                    {question.options?.map((option, optIndex) => (
                                        <label key={optIndex}>
                                            <input type="radio" name={`question-${index}`} value={option} /> {option}
                                        </label>
                                    ))}
                                </div>
                            )}
                        </div>
                    ))}
                    {componentData.buttonText && (
                        <button
                            style={{
                                padding: '8px 16px',
                                background: '#2563eb',
                                color: '#fff',
                                border: 'none',
                                borderRadius: '4px',
                                cursor: 'pointer',
                            }}
                        >
                            {componentData.buttonText}
                        </button>
                    )}
                </form>
            );
        case 'menu':
            return (
                <nav style={{ display: 'flex', gap: '20px', ...styles }}>
                    {componentData.items?.map((item, index) => (
                        <a key={index} href={item.url} style={{ textDecoration: 'none', color: styles.color || '#000' }}>
                            {item.label}
                        </a>
                    ))}
                </nav>
            );
        case 'html':
            return <div dangerouslySetInnerHTML={{ __html: componentData.content || '<p>Nội dung HTML</p>' }} />;
        case 'collection':
            return (
                <div style={{ display: 'flex', flexDirection: 'column', gap: '10px', ...styles }}>
                    {componentData.items?.map((item, index) => (
                        <div key={index}>
                            <h4>{item.title}</h4>
                            <p>{item.content}</p>
                        </div>
                    ))}
                </div>
            );
        case 'box':
            return <div style={{ border: '1px solid #e5e7eb', padding: '10px', ...styles }}>{componentData.title || 'Hộp chứa'}</div>;
        default:
            return <div style={styles}>{componentData.title || 'Thành phần không xác định'}</div>;
    }
};
