import React, { useState, useEffect, useContext, useCallback, useMemo } from 'react';
import { UserContext } from '../context/UserContext';
import Header from '../components/Header';
import Sidebar from '../components/Sidebar';
import * as jwtDecode from 'jwt-decode';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { toast } from 'react-toastify';
import AOS from 'aos';
import 'aos/dist/aos.css';
import '../styles/AdminMarketplace.css';
import DogLoader from '../components/Loader';
import {
    Check, X, Eye, Star, AlertTriangle, Trash2, Download, RefreshCw, Filter, Pause
} from 'lucide-react';

const AdminMarketplace = () => {
    const { user } = useContext(UserContext);
    const [loading, setLoading] = useState(true);
    const [actionLoading, setActionLoading] = useState(false);
    const [userRole, setUserRole] = useState(null);
    const [pages, setPages] = useState([]);
    const [stats, setStats] = useState(null);
    const [transactions, setTransactions] = useState([]);
    const [refundRequests, setRefundRequests] = useState([]);
    const [selectedStatus, setSelectedStatus] = useState('PENDING');
    const [selectedPage, setSelectedPage] = useState(null);
    const [showPreviewModal, setShowPreviewModal] = useState(false);
    const [selectedPages, setSelectedPages] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [rejectReason, setRejectReason] = useState('');
    const [suspendReason, setSuspendReason] = useState('');
    const [showRejectModal, setShowRejectModal] = useState(false);
    const [showSuspendModal, setShowSuspendModal] = useState(false);
    const [pageToReject, setPageToReject] = useState(null);
    const [pageToSuspend, setPageToSuspend] = useState(null);
    const [currentTab, setCurrentTab] = useState('pages');
    const [page, setPage] = useState(1);
    const [limit] = useState(20);
    const [totalPages, setTotalPages] = useState(1);
    const navigate = useNavigate();
    const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

    const statusOptions = useMemo(() => [
        { value: 'all', label: 'T·∫•t c·∫£' },
        { value: 'PENDING', label: 'Ch·ªù duy·ªát' },
        { value: 'ACTIVE', label: 'ƒê√£ duy·ªát' },
        { value: 'REJECTED', label: 'B·ªã t·ª´ ch·ªëi' },
        { value: 'SUSPENDED', label: 'T·∫°m ng∆∞ng' }
    ], []);

    const transactionStatusOptions = useMemo(() => [
        { value: 'all', label: 'T·∫•t c·∫£' },
        { value: 'PENDING', label: 'Ch·ªù x·ª≠ l√Ω' },
        { value: 'COMPLETED', label: 'Ho√†n th√†nh' },
        { value: 'REFUND_PENDING', label: 'Ch·ªù ho√†n ti·ªÅn' }
    ], []);

    // Authentication & Role Check
    useEffect(() => {
        const initializeAuth = async () => {
            const token = localStorage.getItem('token');
            if (!token) {
                toast.error('Vui l√≤ng ƒëƒÉng nh·∫≠p');
                navigate('/auth');
                setLoading(false);
                return;
            }
            try {
                const decodedToken = jwtDecode.default(token);
                if (decodedToken.role !== 'admin') {
                    toast.error('B·∫°n kh√¥ng c√≥ quy·ªÅn truy c·∫≠p');
                    navigate('/dashboard');
                    return;
                }
                setUserRole(decodedToken.role);
            } catch (err) {
                toast.error('Phi√™n ƒëƒÉng nh·∫≠p kh√¥ng h·ª£p l·ªá');
                navigate('/auth');
            } finally {
                setLoading(false);
            }
        };

        if (user?.role === 'admin') {
            setUserRole(user.role);
            setLoading(false);
        } else {
            initializeAuth();
        }
    }, [user, navigate]);

    useEffect(() => { AOS.init({ duration: 600, once: true }); }, []);

    // Load Functions
    const loadPages = useCallback(async () => {
        setLoading(true);
        try {
            const token = localStorage.getItem('token');
            const url = selectedStatus === 'PENDING'
                ? `${API_BASE_URL}/api/admin/marketplace/pending?page=${page}&limit=${limit}`
                : `${API_BASE_URL}/api/admin/marketplace/pages?page=${page}&limit=${limit}${selectedStatus !== 'all' ? `&status=${selectedStatus}` : ''}${searchTerm ? `&q=${encodeURIComponent(searchTerm)}` : ''}`;

            const response = await axios.get(url, { headers: { Authorization: `Bearer ${token}` } });
            setPages(response.data.data || []);
            setTotalPages(response.data.pagination?.totalPages || 1);
        } catch (err) {
            toast.error(err.response?.data?.message || 'Kh√¥ng th·ªÉ t·∫£i danh s√°ch pages');
        } finally { setLoading(false); }
    }, [selectedStatus, page, limit, searchTerm]);

    const loadStats = useCallback(async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await axios.get(`${API_BASE_URL}/api/admin/marketplace/stats`, { headers: { Authorization: `Bearer ${token}` } });
            setStats(response.data.data);
        } catch (err) {
            toast.error('Kh√¥ng th·ªÉ t·∫£i th·ªëng k√™');
        }
    }, []);

    const loadTransactions = useCallback(async () => {
        setLoading(true);
        try {
            const token = localStorage.getItem('token');
            const url = `${API_BASE_URL}/api/admin/marketplace/transactions?page=${page}&limit=${limit}${selectedStatus !== 'all' ? `&status=${selectedStatus}` : ''}${searchTerm ? `&q=${encodeURIComponent(searchTerm)}` : ''}`;
            const response = await axios.get(url, { headers: { Authorization: `Bearer ${token}` } });
            setTransactions(response.data.data || []);
            setTotalPages(response.data.pagination?.totalPages || 1);
        } catch (err) {
            toast.error('Kh√¥ng th·ªÉ t·∫£i giao d·ªãch');
        } finally { setLoading(false); }
    }, [selectedStatus, page, limit, searchTerm]);

    const loadRefundRequests = useCallback(async () => {
        setLoading(true);
        try {
            const token = localStorage.getItem('token');
            const response = await axios.get(`${API_BASE_URL}/api/admin/marketplace/refunds`, { headers: { Authorization: `Bearer ${token}` } });
            setRefundRequests(response.data.data || []);
        } catch (err) {
            toast.error('Kh√¥ng th·ªÉ t·∫£i y√™u c·∫ßu ho√†n ti·ªÅn');
        } finally { setLoading(false); }
    }, []);

    useEffect(() => {
        if (userRole === 'admin') {
            if (currentTab === 'pages') { loadPages(); loadStats(); }
            else if (currentTab === 'transactions') loadTransactions();
            else if (currentTab === 'refunds') loadRefundRequests();
        }
    }, [userRole, currentTab, loadPages, loadStats, loadTransactions, loadRefundRequests]);

    // Status Badge
    const getStatusBadge = useCallback((status) => {
        const badges = {
            DRAFT: { color: '#9CA3AF', label: 'B·∫£n nh√°p' },
            PENDING: { color: '#FBBF24', label: 'Ch·ªù duy·ªát' },
            ACTIVE: { color: '#10B981', label: 'ƒê√£ duy·ªát' },
            REJECTED: { color: '#EF4444', label: 'T·ª´ ch·ªëi' },
            SUSPENDED: { color: '#F97316', label: 'T·∫°m ng∆∞ng' },
            COMPLETED: { color: '#10B981', label: 'Ho√†n th√†nh' },
            REFUND_PENDING: { color: '#FBBF24', label: 'Ch·ªù ho√†n ti·ªÅn' }
        };
        const badge = badges[status] || { color: '#9CA3AF', label: status };
        return (
            <span className="status-badge" style={{ backgroundColor: badge.color }}>
                {badge.label}
            </span>
        );
    }, []);

    const formatPrice = useCallback((price) => {
        return new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(price);
    }, []);

    const formatDate = useCallback((date) => new Date(date).toLocaleString('vi-VN'), []);

    const handleSelectPage = useCallback((pageId) => {
        setSelectedPages(prev => prev.includes(pageId) ? prev.filter(id => id !== pageId) : [...prev, pageId]);
    }, []);

    const handleSelectAll = useCallback(() => {
        setSelectedPages(selectedPages.length === pages.length ? [] : pages.map(p => p._id));
    }, [selectedPages, pages]);

    // Bulk Actions
    const handleBulkApprove = async () => {
        if (!selectedPages.length) return toast.warning('Ch·ªçn √≠t nh·∫•t 1 trang');
        if (!window.confirm(`Duy·ªát ${selectedPages.length} trang?`)) return;
        setActionLoading(true);
        try {
            const token = localStorage.getItem('token');
            await Promise.all(selectedPages.map(id =>
                axios.post(`${API_BASE_URL}/api/admin/marketplace/pages/${id}/approve`, {}, { headers: { Authorization: `Bearer ${token}` } })
            ));
