import React, { useState, useCallback } from 'react';
import { useDrag } from 'react-dnd';

const ItemTypes = {
    SECTION: 'section',
    ELEMENT: 'element',
};

const DraggableComponent = React.memo(({ item, isSection = false, onDrag, onDragEnd, onSelect, viewMode = 'desktop' }) => {
    const dragType = isSection ? ItemTypes.SECTION : ItemTypes.ELEMENT;
    const [isSelected, setIsSelected] = useState(false);

    const [{ isDragging }, drag] = useDrag(() => ({
        type: dragType,
        item: () => {
            const newItem = {
                id: `${item.id}-${Date.now()}`,
                type: item.json.type,
                componentData: item.json.componentData,
                styles: item.json.styles,
                children: item.json.children || [],
            };
            onDrag?.();
            setIsSelected(true);
            onSelect?.(newItem.id);
            return newItem;
        },
        end: (item, monitor) => {
            onDragEnd?.();
            if (item && !monitor.didDrop()) {
                setIsSelected(false);
            }
        },
        collect: (monitor) => ({
            isDragging: !!monitor.isDragging(),
        }),
    }));

    const handleClick = useCallback(
        (e) => {
            e.stopPropagation();
            setIsSelected(true);
            onSelect?.(item.id);
        },
        [item.id, onSelect]
    );

    const renderPreview = () => {
        const { type = 'unknown', componentData = {}, styles = { desktop: {}, mobile: {} }, children = [] } = item.json || {};
        const styleProps = {
            ...styles[viewMode], // Chọn style theo chế độ desktop/mobile
            opacity: isDragging ? 0.5 : 1,
            margin: '5px',
            maxWidth: '100%',
            boxSizing: 'border-box',
            padding: type === 'section' || children.length > 0 ? '10px' : '0',
            border: '1px solid #ddd',
            borderRadius: '5px',
            position: type === 'section' ? 'relative' : 'absolute',
            outline: isSelected ? '2px solid #4a90e2' : 'none',
        };

        const getTemplateClass = () => {
            switch (type) {
                case 'form':
                case 'contact-form':
                    return 'lpb-template-form';
                case 'gallery':
                case 'carousel':
                    return 'lpb-template-gallery';
                case 'pricing':
                    return 'lpb-template-pricing';
                case 'testimonials':
                    return 'lpb-template-testimonials';
                case 'faq':
                    return 'lpb-template-faq';
                default:
                    return '';
            }
        };

        const renderChildren = () => {
            return children.map((child, index) => (
                <DraggableComponent
                    key={child.id}
                    item={{ id: child.id, json: child }}
                    isSection={child.type === 'section'}
                    onSelect={onSelect}
                    viewMode={viewMode}
                />
            ));
        };

        if (children.length > 0) {
            switch (type) {
                case 'form':
                    return (
                        <div
                            ref={drag}
                            data-id={item.id}
                            style={styleProps}
                            className={`lpb-template-item ${getTemplateClass()}`}
                            onClick={handleClick}
                        >
                            <form style={{ width: '100%' }}>
                                {(componentData.fields || []).map((field, index) => (
                                    <input
                                        key={index}
                                        type="text"
                                        placeholder={field || `Trường ${index + 1}`}
                                        style={{ marginBottom: '8px', width: '100%', ...styles[viewMode] }}
                                    />
                                ))}
                                <button type="submit" style={{ ...styles[viewMode], marginTop: '8px' }}>
                                    Submit
                                </button>
                                {renderChildren()}
                            </form>
                        </div>
                    );
                case 'button-group':
                    return (
                        <div
                            ref={drag}
                            data-id={item.id}
                            style={styleProps}
                            className={`lpb-template-item ${getTemplateClass()}`}
                            onClick={handleClick}
                        >
                            <div style={{ display: 'flex', gap: '10px', width: '100%' }}>
                                {renderChildren()}
                            </div>
                        </div>
                    );
                case 'gallery':
                case 'carousel':
                    return (
                        <div
                            ref={drag}
                            data-id={item.id}
                            style={styleProps}
                            className={`lpb-template-item ${getTemplateClass()}`}
                            onClick={handleClick}
                        >
                            <div style={{ display: type === 'carousel' ? 'flex' : 'flex', flexWrap: type === 'gallery' ? 'wrap' : 'nowrap', gap: '10px', width: '100%' }}>
                                {renderChildren()}
                            </div>
                        </div>
                    );
                case 'section':
                case 'page':
                    return (
                        <div
                            ref={drag}
                            data-id={item.id}
                            style={styleProps}
                            className={`lpb-template-item ${getTemplateClass()}`}
                            onClick={handleClick}
                        >
                            <h3>{componentData.title || 'Section'}</h3>
                            {componentData.subtitle && <p>{componentData.subtitle}</p>}
                            {renderChildren()}
                        </div>
                    );
                default:
                    return (
                        <div
                            ref={drag}
                            data-id={item.id}
                            style={styleProps}
                            className={`lpb-template-item ${getTemplateClass()}`}
                            onClick={handleClick}
                        >
                            {item.name || 'Unknown Component'}
                            {renderChildren()}
                        </div>
                    );
            }
        }

        switch (type) {
            case 'section':
                return (
                    <div
                        ref={drag}
                        data-id={item.id}
                        style={styleProps}
                        className={`lpb-template-item ${getTemplateClass()}`}
                        onClick={handleClick}
                    >
                        <h3>{componentData.title || 'Section'}</h3>
                        {componentData.subtitle && <p>{componentData.subtitle}</p>}
                    </div>
                );
            case 'heading':
                return (
                    <div
                        ref={drag}
                        data-id={item.id}
                        style={styleProps}
                        className={`lpb-template-item ${getTemplateClass()}`}
                        onClick={handleClick}
                    >
                        <h2>{componentData.content || 'Tiêu đề'}</h2>
                    </div>
                );
            case 'paragraph':
                return (
                    <div
                        ref={drag}
                        data-id={item.id}
                        style={styleProps}
                        className={`lpb-template-item ${getTemplateClass()}`}
                        onClick={handleClick}
                    >
                        <p>{componentData.content || 'Đoạn văn'}</p>
                    </div>
                );
            case 'button':
                return (
                    <div
                        ref={drag}
                        data-id={item.id}
                        style={styleProps}
                        className={`lpb-template-item ${getTemplateClass()}`}
                        onClick={handleClick}
                    >
                        <button style={styles[viewMode]}>{componentData.content || 'Nút'}</button>
                    </div>
                );
            case 'image':
                return (
                    <div
                        ref={drag}
                        data-id={item.id}
                        style={styleProps}
                        className={`lpb-template-item ${getTemplateClass()}`}
                        onClick={handleClick}
                    >
                        <img
                            src={componentData.src || 'https://via.placeholder.com/150'}
                            alt={item.name || 'Image'}
                            style={{ ...styles[viewMode], maxWidth: '100%', height: 'auto' }}
                        />
                    </div>
                );
            case 'input':
                return (
                    <div
                        ref={drag}
                        data-id={item.id}
                        style={styleProps}
                        className={`lpb-template-item ${getTemplateClass()}`}
                        onClick={handleClick}
                    >
                        <input
                            type={componentData.type || 'text'}
                            placeholder={componentData.label || 'Input'}
                            style={{ ...styles[viewMode], width: '100%' }}
                        />
                    </div>
                );
            case 'textarea':
                return (
                    <div
                        ref={drag}
                        data-id={item.id}
                        style={styleProps}
                        className={`lpb-template-item ${getTemplateClass()}`}
                        onClick={handleClick}
                    >
                        <textarea
                            placeholder={componentData.label || 'Textarea'}
                            style={{ ...styles[viewMode], width: '100%' }}
                        />
                    </div>
                );
            default:
                return (
                    <div
                        ref={drag}
                        data-id={item.id}
                        style={styleProps}
                        className={`lpb-template-item ${getTemplateClass()}`}
                        onClick={handleClick}
                    >
                        {item.name || 'Unknown Component'}
                    </div>
                );
        }
    };

    return renderPreview();
});

export default DraggableComponent;
