// F:\landinghub-iconic\apps\web\src\components\Header.js
import React, { useContext, useState } from 'react';
import { UserContext } from '../context/UserContext';
import { useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';
import UserProfile from './UserProfile';
import './UserProfile.css';

const Header = () => {
    const { user, setUser } = useContext(UserContext);
    const navigate = useNavigate();
    const token = localStorage.getItem('token');
    const decodedToken = token ? jwtDecode(token) : null;

    console.log('User from context:', user);
    console.log('Decoded Token:', decodedToken);

    const handleLogout = () => {
        localStorage.removeItem('token');
        setUser(null);
        window.location.href = '/auth';
    };

    const displayName = user?.name || (decodedToken?.userId ? `User ${decodedToken.userId}` : 'Khách');

    const [showProfile, setShowProfile] = useState(false);

    return (
        <header className="dashboard-header">
            <div className="logo">LandingHub</div>
            <div className="user-info">
                {token || user ? (
                    <>
                        <span>Chào {displayName}</span>
                        <button onClick={() => setShowProfile(!showProfile)}>Thông tin</button>
                        <button onClick={handleLogout}>Đăng xuất</button>
                        {showProfile && (
                            <div className="profile-popup">
                                <UserProfile />
                                <button onClick={() => setShowProfile(false)}>Đóng</button>
                            </div>
                        )}
                    </>
                ) : (
                    <button onClick={() => navigate('/auth')}>Đăng nhập</button>
                )}
            </div>
        </header>
    );
};

export default React.memo(Header);
