import React, { useState } from 'react';
import { DndProvider } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
import Canvas from './Canvas';
import ComponentLibrary from './ComponentLibrary';
import PropertiesPanel from './PropertiesPanel';
import QuickEditToolkit from './QuickEditToolkit';
import './CreateLanding.css';

const CreateLanding = () => {
    const [pageData, setPageData] = useState({
        canvas: { width: 1200, background: '#ffffff' },
        elements: [],
    });
    const [selectedIds, setSelectedIds] = useState([]);
    const [selectedChildId, setSelectedChildId] = useState(null);
    const [viewMode, setViewMode] = useState('desktop');
    const [zoomLevel, setZoomLevel] = useState(100);
    const [gridSize, setGridSize] = useState(10);
    const [showGrid, setShowGrid] = useState(true);

    const handleSelectElement = (ids, append = false) => {
        if (Array.isArray(ids)) {
            setSelectedIds(append ? [...selectedIds, ...ids] : ids);
        } else {
            setSelectedIds(append ? [...selectedIds, ids] : [ids]);
        }
        setSelectedChildId(null); // Clear child selection when selecting parent
    };

    const handleSelectChild = (parentId, childId) => {
        if (!parentId) {
            setSelectedIds([]);
            setSelectedChildId(null);
            return;
        }
        if (!pageData.elements.find((el) => el.id === parentId)) {
            console.warn(`Parent element with id ${parentId} not found`);
            setSelectedIds([]);
            setSelectedChildId(null);
            return;
        }
        setSelectedIds([parentId]); // Select parent
        setSelectedChildId(childId); // Select child
    };

    const handleAddElement = (element) => {
        setPageData((prev) => ({
            ...prev,
            elements: [...prev.elements, element],
        }));
    };

    const handleUpdatePosition = (id, position, mode) => {
        setPageData((prev) => ({
            ...prev,
            elements: prev.elements.map((el) =>
                el.id === id
                    ? {
                        ...el,
                        position: mode === 'replace' ? position : { ...el.position, ...position },
                    }
                    : el
            ),
        }));
    };

    const handleUpdateSize = (id, size) => {
        setPageData((prev) => ({
            ...prev,
            elements: prev.elements.map((el) =>
                el.id === id ? { ...el, size } : el
            ),
        }));
    };

    const handleDeleteElement = (id) => {
        setPageData((prev) => ({
            ...prev,
            elements: prev.elements.filter((el) => el.id !== id),
        }));
        setSelectedIds((prev) => prev.filter((selId) => selId !== id));
        setSelectedChildId(null);
    };

    const handleEditElement = (id) => {
        console.log('Edit element:', id);
        // Implement edit logic
    };

    const handleDuplicateElement = (id) => {
        const element = pageData.elements.find((el) => el.id === id);
        if (element) {
            const newElement = {
                ...element,
                id: `${element.id}-${Date.now()}`,
                position: {
                    ...element.position,
                    desktop: {
                        x: (element.position.desktop?.x || 0) + 10,
                        y: (element.position.desktop?.y || 0) + 10,
                    },
                },
            };
            setPageData((prev) => ({
                ...prev,
                elements: [...prev.elements, newElement],
            }));
        }
    };

    const handleMoveElementUp = (id) => {
        setPageData((prev) => {
            const index = prev.elements.findIndex((el) => el.id === id);
            if (index <= 0) return prev;
            const newElements = [...prev.elements];
            [newElements[index - 1], newElements[index]] = [newElements[index], newElements[index - 1]];
            return { ...prev, elements: newElements };
        });
    };

    const handleMoveElementDown = (id) => {
        setPageData((prev) => {
            const index = prev.elements.findIndex((el) => el.id === id);
            if (index >= prev.elements.length - 1) return prev;
            const newElements = [...prev.elements];
            [newElements[index], newElements[index + 1]] = [newElements[index + 1], newElements[index]];
            return { ...prev, elements: newElements };
        });
    };

    const handleGroupElements = (ids) => {
        console.log('Group elements:', ids);
        // Implement grouping logic
    };

    const handleUpdateChildren = (parentId, dragIndex, dropIndex) => {
        setPageData((prev) => ({
            ...prev,
            elements: prev.elements.map((el) =>
                el.id === parentId
                    ? {
                        ...el,
                        children: reorderArray(el.children || [], dragIndex, dropIndex),
                    }
                    : el
            ),
        }));
        if (selectedChildId) {
            const parent = pageData.elements.find((el) => el.id === parentId);
            const newChildren = reorderArray(parent?.children || [], dragIndex, dropIndex);
            const draggedChild = parent?.children[dragIndex];
            if (draggedChild?.id === selectedChildId) {
                setSelectedChildId(newChildren[dropIndex].id);
            }
        }
    };

    const reorderArray = (array, fromIndex, toIndex) => {
        const newArray = [...array];
        const [movedItem] = newArray.splice(fromIndex, 1);
        newArray.splice(toIndex, 0, movedItem);
        return newArray;
    };

    const selectedElement = (() => {
        if (selectedChildId) {
            const parent = pageData.elements.find((el) => el.id === selectedIds[0]);
            if (!parent || !parent.children) return null;
            const child = parent.children.find((child) => child.id === selectedChildId);
            return child ? { json: child, isChild: true } : null;
        }
        const element = pageData.elements.find((el) => el.id === selectedIds[0]);
        return element ? { json: element, isChild: false } : null;
    })();

    return (
        <DndProvider backend={HTML5Backend}>
            <div className="lpb-container">
                <ComponentLibrary isCollapsed={false} onToggle={() => {}} />
                <Canvas
                    pageData={pageData}
                    selectedIds={selectedIds}
                    onSelectElement={handleSelectElement}
                    onUpdatePosition={handleUpdatePosition}
                    onUpdateSize={handleUpdateSize}
                    onDeleteElement={handleDeleteElement}
                    onAddElement={handleAddElement}
                    onGroupElements={handleGroupElements}
                    viewMode={viewMode}
                    zoomLevel={zoomLevel}
                    gridSize={gridSize}
                    showGrid={showGrid}
                    onEditElement={handleEditElement}
                    onDuplicateElement={handleDuplicateElement}
                    onMoveElementUp={handleMoveElementUp}
                    onMoveElementDown={handleMoveElementDown}
                    onSelectChild={handleSelectChild}
                    selectedChildId={selectedChildId}
                    onUpdateChildren={handleUpdateChildren}
                />
                <PropertiesPanel
                    selectedElement={selectedElement}
                    onUpdateElement={(updatedElement) => {
                        setPageData((prev) => ({
                            ...prev,
                            elements: prev.elements.map((el) =>
                                el.id === selectedIds[0]
                                    ? updatedElement.isChild
                                        ? {
                                            ...el,
                                            children: el.children.map((child) =>
                                                child.id === selectedChildId
                                                    ? { ...child, ...updatedElement.json }
                                                    : child
                                            ),
                                        }
                                        : { ...el, ...updatedElement.json }
                                    : el
                            ),
                        }));
                    }}
                    isCollapsed={false} // Sửa từ true thành false để hiển thị
                    onToggle={() => {}}
                />
            </div>
        </DndProvider>
    );
};

export default CreateLanding;
