import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';
import api from '@landinghub/api';
import SidebarComponent from '../components/pages-content/SidebarComponent';
import HeaderComponent from '../components/pages-content/HeaderComponent';
import ControlsComponent from '../components/pages-content/ControlsComponent';
import LandingList from '../components/pages-content/LandingList';
import CreatePagePopup from '../components/pages-content/CreatePagePopup'; // Import the popup component
import DogLoader from '../components/DogLoader';
import { toast } from 'react-toastify';
import '../styles/PageContent.css';
const PageContent = () => {
    const [userData, setUserData] = useState(null);
    const [userId, setUserId] = useState(null);
    const [landingPages, setLandingPages] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [statusFilter, setStatusFilter] = useState('Trạng thái');
    const [menuOpen, setMenuOpen] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [isPopupOpen, setIsPopupOpen] = useState(false);
    const navigate = useNavigate();

    useEffect(() => {
        const initializeAuth = async () => {
            const token = localStorage.getItem('token');
            if (!token) {
                console.error('No token found in localStorage');
                setError('Vui lòng đăng nhập để tiếp tục');
                setLoading(false);
                navigate('/auth');
                return;
            }

            let decoded;
            try {
                decoded = jwtDecode(token);
                if (!decoded.userId) {
                    throw new Error('Invalid token: userId not found');
                }
            } catch (err) {
                console.error('Error decoding token:', err);
                setError('Phiên đăng nhập không hợp lệ');
                setLoading(false);
                navigate('/auth');
                return;
            }

            setUserId(decoded.userId);
            api.defaults.headers.common['Authorization'] = `Bearer ${token}`;

            try {
                const res = await api.get('/api/user/info');
                setUserData(res);
            } catch (err) {
                console.error('Error fetching user data:', err);
                setError('Không thể tải dữ liệu người dùng: ' + (err.response?.data?.error || err.message));
            }
        };
        initializeAuth();
    }, [navigate]);

    useEffect(() => {
        if (!userId) {
            return;
        }

        const fetchLandingPages = async () => {
            try {
                const response = await api.get('/api/pages');
                setLandingPages(response);
            } catch (err) {
                console.error('Fetch error:', err);
                setError('Không thể tải danh sách landing page: ' + (err.response?.data?.error || err.message));
            } finally {
                setLoading(false);
            }
        };
        fetchLandingPages();
    }, [userId]);

    const filteredPages = landingPages.filter((page) => {
        const matchesSearch = page.name.toLowerCase().includes(searchTerm.toLowerCase());
        const matchesStatus = statusFilter === 'Trạng thái' || page.status === statusFilter;
        return matchesSearch && matchesStatus;
    });

    const handleCreatePage = () => {
        setIsPopupOpen(true);
    };

    const handleCreateSuccess = (newPage) => {
        setLandingPages([...landingPages, newPage]);
        navigate(`/pages/create`, { state: { page: newPage } });
    };

    const handleEditPage = (page) => {
        navigate(`/pages/edit/${page._id}`, { state: { page } });
    };

    const handlePreviewPage = (page) => {
        if (page.url) {
            window.open(page.url, '_blank');
        } else {
            toast.error('Trang chưa được triển khai');
        }
    };

    const handlePublishPage = async (pageId) => {
        try {
            setLoading(true);
            const response = await api.post(`/api/pages/${pageId}/publish`);
            if (response.success && response.page) {
                setLandingPages(landingPages.map((p) => (p._id === pageId ? response.page : p)));
                toast.success('Triển khai landing page thành công!');
            } else {
                toast.error('Triển khai landing page thất bại');
            }
        } catch (err) {
            console.error('Publish error:', err);
            toast.error('Lỗi khi triển khai landing page: ' + (err.response?.data?.error || err.message));
        } finally {
            setLoading(false);
        }
    };

    const handleDeletePage = async (pageId) => {
        if (!window.confirm('Bạn có chắc muốn xóa landing page này?')) return;
        try {
            setLoading(true);
            await api.delete(`/api/pages/${pageId}`);
            setLandingPages(landingPages.filter((p) => p._id !== pageId));
            toast.success('Xóa landing page thành công!');
        } catch (err) {
            console.error('Delete error:', err);
            toast.error('Lỗi khi xóa landing page: ' + (err.response?.data?.error || err.message));
        } finally {
            setLoading(false);
        }
    };

    if (loading) {
        return <DogLoader />;
    }

    return (
        <div className="page-content1">
            <SidebarComponent />
            <div className="main-content">
                <HeaderComponent onCreateClick={handleCreatePage} />
                <ControlsComponent
                    searchTerm={searchTerm}
                    setSearchTerm={setSearchTerm}
                    statusFilter={statusFilter}
                    setStatusFilter={setStatusFilter}
                />
                <LandingList
                    landingPages={landingPages}
                    filteredPages={filteredPages}
                    menuOpen={menuOpen}
                    setMenuOpen={setMenuOpen}
                    handlePreviewPage={handlePreviewPage}
                    handleEditPage={handleEditPage}
                    handlePublishPage={handlePublishPage}
                    handleDeletePage={handleDeletePage}
                />
                <CreatePagePopup
                    isOpen={isPopupOpen}
                    onClose={() => setIsPopupOpen(false)}
                    onCreateSuccess={handleCreateSuccess}
                />
            </div>
        </div>
    );
};

export default PageContent;
