import React, { useRef, useCallback, useState, useEffect } from 'react';
import { useDrop, useDrag } from 'react-dnd';
import { getEmptyImage } from 'react-dnd-html5-backend';
import { useHotkeys } from 'react-hotkeys-hook';
import { toast } from 'react-toastify';
import Guidelines from './Guidelines';
import SelectionOverlay from './SelectionOverlay';
import QuickEditToolkit from './QuickEditToolkit';
import '../../styles/CreateLanding.css';

const ItemTypes = {
    ELEMENT: 'element',
    EXISTING_ELEMENT: 'existingElement',
    CHILD_ELEMENT: 'childElement',
};

// Tính toán tọa độ chính xác trong canvas
const getCanvasPosition = (mouseX, mouseY, canvasElement, zoomLevel) => {
    if (!canvasElement) {
        console.error('Canvas element is null in getCanvasPosition');
        return { x: 0, y: 0 };
    }

    const canvasRect = canvasElement.getBoundingClientRect();
    const scrollX = canvasElement.scrollLeft || 0;
    const scrollY = canvasElement.scrollTop || 0;

    const rawX = (mouseX - canvasRect.left + scrollX) / (zoomLevel / 100);
    const rawY = (mouseY - canvasRect.top + scrollY) / (zoomLevel / 100);

    const x = Math.max(0, rawX);
    const y = Math.max(0, rawY);

    return { x, y };
};

// Snap to grid với snap points
const snapToGrid = (x, y, gridSize, snapPoints = []) => {
    let snappedX = Math.round(x / gridSize) * gridSize;
    let snappedY = Math.round(y / gridSize) * gridSize;

    snapPoints.forEach((point) => {
        if (Math.abs(x - point.x) < 15) snappedX = point.x;
        if (Math.abs(y - point.y) < 15) snappedY = point.y;
    });

    return { x: snappedX, y: snappedY };
};

// Tính bounds của element
const getElementBounds = (element) => ({
    left: element.position.desktop?.x || 0,
    top: element.position.desktop?.y || 0,
    right: (element.position.desktop?.x || 0) + (element.size?.width || 200),
    bottom: (element.position.desktop?.y || 0) + (element.size?.height || 50),
    centerX: (element.position.desktop?.x || 0) + (element.size?.width || 200) / 2,
    centerY: (element.position.desktop?.y || 0) + (element.size?.height || 50) / 2,
});

// Render nội dung component
const renderComponentContent = (type, componentData = {}, styles = {}, children = []) => {
    console.log('renderComponentContent:', { type, componentData, children });

    // Kiểm tra đầu vào
    if (!type || typeof type !== 'string') {
        return (
            <div style={{ color: '#ef4444', padding: '12px', border: '1px dashed #ef4444', borderRadius: '4px', ...styles }}>
                Lỗi: Loại component không hợp lệ
            </div>
        );
    }
    if (!componentData || typeof componentData !== 'object') {
        return (
            <div style={{ color: '#ef4444', padding: '12px', border: '1px dashed #ef4444', borderRadius: '4px', ...styles }}>
                Lỗi: Dữ liệu component không hợp lệ
            </div>
        );
    }

    // Xử lý đặc biệt cho section với cấu trúc ladi-standard
    if (type === 'section' && componentData.structure === 'ladi-standard') {
        return (
            <div className="ladi-section" style={{ ...styles }}>
                <div className="ladi-section-background" style={{
                    backgroundColor: componentData.backgroundColor || 'transparent',
                    backgroundImage: componentData.backgroundImage ? `url(${componentData.backgroundImage})` : 'none',
                    backgroundSize: 'cover',
                    backgroundPosition: 'center',
                    position: 'absolute',
                    top: 0,
                    left: 0,
                    right: 0,
                    bottom: 0,
                    zIndex: 0
                }}></div>
                <div className="ladi-overlay" style={{
                    backgroundColor: componentData.overlayColor || 'transparent',
                    opacity: componentData.overlayOpacity || 0,
                    position: 'absolute',
                    top: 0,
                    left: 0,
                    right: 0,
                    bottom: 0,
                    zIndex: 0
                }}></div>
                <div className="ladi-container" style={{ position: 'relative', zIndex: 1, padding: componentData.padding || '20px' }}>
                    {/* Render title */}
                    {componentData.title && !Array.isArray(children) || !children.some(child => child?.type === 'heading') && (
                        <h2 style={{
                            fontSize: componentData.titleFontSize || '1.8rem',
                            margin: componentData.titleMargin || '0 0 20px 0',
                            textAlign: componentData.textAlign || 'center',
                            fontWeight: componentData.titleFontWeight || '700',
                            color: componentData.titleColor || '#1f2937'
                        }}>
                            {componentData.title}
                        </h2>
                    )}

                    {/* Render subtitle */}
                    {componentData.subtitle && !Array.isArray(children) || !children.some(child => child?.type === 'paragraph') && (
                        <h3 style={{
                            fontSize: componentData.subtitleFontSize || '1.2rem',
                            margin: componentData.subtitleMargin || '0 0 15px 0',
                            textAlign: componentData.textAlign || 'center',
                            opacity: componentData.subtitleOpacity || 0.9,
                            fontWeight: componentData.subtitleFontWeight || '500',
                            color: componentData.subtitleColor || '#1f2937'
                        }}>
                            {componentData.subtitle}
                        </h3>
                    )}

                    {/* Render description */}
                    {componentData.description && !Array.isArray(children) || !children.some(child => child?.type === 'paragraph') && (
                        <p style={{
                            fontSize: componentData.descriptionFontSize || '1rem',
                            margin: componentData.descriptionMargin || '0 0 20px 0',
                            textAlign: componentData.textAlign || 'center',
                            lineHeight: componentData.descriptionLineHeight || '1.6',
                            opacity: componentData.descriptionOpacity || 0.9,
                            color: componentData.descriptionColor || '#4b5563'
                        }}>
                            {componentData.description}
                        </p>
                    )}

                    {/* Render features list */}
                    {Array.isArray(componentData.features) && componentData.features.length > 0 && !Array.isArray(children) || !children.some(child => child?.type === 'list') && (
                        <ul style={{
                            listStyle: 'none',
                            padding: componentData.featuresPadding || 0,
                            margin: componentData.featuresMargin || '0 0 20px 0',
                            display: 'flex',
                            flexDirection: componentData.featuresDirection || 'column',
                            gap: componentData.featuresGap || '8px',
                            textAlign: componentData.textAlign || 'center'
                        }}>
                            {componentData.features.map((feature, index) => (
                                <li key={index} style={{
                                    fontSize: componentData.featureFontSize || '1rem',
                                    display: 'flex',
                                    alignItems: 'center',
                                    justifyContent: componentData.textAlign === 'center' ? 'center' : 'flex-start',
                                    color: componentData.featureColor || '#1f2937'
                                }}>
                                    <span style={{
                                        marginRight: componentData.featureIconMargin || '8px',
                                        color: componentData.featureIconColor || '#10b981'
                                    }}>✓</span>
                                    {feature}
                                </li>
                            ))}
                        </ul>
                    )}

                    {/* Render pricing plans */}
                    {Array.isArray(componentData.pricingPlans) && componentData.pricingPlans.length > 0 && (
                        <div style={{
                            display: 'grid',
                            gridTemplateColumns: componentData.pricingColumns || 'repeat(auto-fit, minmax(200px, 1fr))',
                            gap: componentData.pricingGap || '20px',
                            marginTop: componentData.pricingMarginTop || '30px'
                        }}>
                            {componentData.pricingPlans.map((plan, index) => (
                                <div key={index} style={{
                                    background: plan.isPopular ? (plan.popularBackground || '#2563eb') : (plan.background || '#ffffff'),
                                    color: plan.isPopular ? (plan.popularColor || '#ffffff') : (plan.color || '#1f2937'),
                                    padding: plan.padding || '20px',
                                    borderRadius: plan.borderRadius || '12px',
                                    border: plan.isPopular ? 'none' : (plan.border || '1px solid #e5e7eb'),
                                    textAlign: 'center',
                                    position: 'relative'
                                }}>
                                    {plan.isPopular && (
                                        <div style={{
                                            position: 'absolute',
                                            top: plan.popularBadgeTop || '-10px',
                                            left: '50%',
                                            transform: 'translateX(-50%)',
                                            background: plan.popularBadgeBackground || '#ef4444',
                                            color: plan.popularBadgeColor || '#ffffff',
                                            padding: plan.popularBadgePadding || '4px 16px',
                                            borderRadius: plan.popularBadgeBorderRadius || '20px',
                                            fontSize: plan.popularBadgeFontSize || '0.875rem',
                                            fontWeight: plan.popularBadgeFontWeight || '600'
                                        }}>
                                            {plan.popularBadgeText || 'Phổ biến nhất'}
                                        </div>
                                    )}
                                    <h4 style={{
                                        margin: plan.nameMargin || '0 0 10px 0',
                                        fontSize: plan.nameFontSize || '1.25rem',
                                        fontWeight: plan.nameFontWeight || '600',
                                        color: plan.nameColor || 'inherit'
                                    }}>
                                        {plan.name || 'Gói dịch vụ'}
                                    </h4>
                                    <div style={{ margin: plan.priceMargin || '0 0 10px 0' }}>
                                        <span style={{
                                            fontSize: plan.priceFontSize || '2rem',
                                            fontWeight: plan.priceFontWeight || '700',
                                            color: plan.priceColor || 'inherit'
                                        }}>
                                            {plan.price || 'Liên hệ'}
                                        </span>
                                        {plan.period && (
                                            <span style={{
                                                fontSize: plan.periodFontSize || '1rem',
                                                opacity: plan.periodOpacity || 0.8,
                                                color: plan.periodColor || 'inherit'
                                            }}>
                                                {plan.period}
                                            </span>
                                        )}
                                    </div>
                                    <p style={{
                                        margin: plan.descriptionMargin || '0 0 15px 0',
                                        fontSize: plan.descriptionFontSize || '0.875rem',
                                        opacity: plan.descriptionOpacity || 0.8,
                                        color: plan.descriptionColor || 'inherit'
                                    }}>
                                        {plan.description || 'Mô tả gói dịch vụ'}
                                    </p>
                                    <ul style={{
                                        listStyle: 'none',
                                        padding: plan.featuresPadding || 0,
                                        margin: plan.featuresMargin || '0 0 20px 0'
                                    }}>
                                        {Array.isArray(plan.features) && plan.features.map((feature, fIndex) => (
                                            <li key={fIndex} style={{
                                                fontSize: plan.featureFontSize || '0.875rem',
                                                margin: plan.featureMargin || '8px 0',
                                                display: 'flex',
                                                alignItems: 'center',
                                                justifyContent: 'center',
                                                color: plan.featureColor || 'inherit'
                                            }}>
                                                <span style={{
                                                    marginRight: plan.featureIconMargin || '8px',
                                                    color: plan.isPopular ? (plan.featureIconPopularColor || '#ffffff') : (plan.featureIconColor || '#10b981')
                                                }}>
                                                    ✓
                                                </span>
                                                {feature}
                                            </li>
                                        ))}
                                    </ul>
                                    <button style={{
                                        background: plan.isPopular ? (plan.buttonPopularBackground || '#ffffff') : (plan.buttonBackground || '#2563eb'),
                                        color: plan.isPopular ? (plan.buttonPopularColor || '#2563eb') : (plan.buttonColor || '#ffffff'),
                                        padding: plan.buttonPadding || '10px 20px',
                                        borderRadius: plan.buttonBorderRadius || '8px',
                                        border: plan.buttonBorder || 'none',
                                        fontSize: plan.buttonFontSize || '1rem',
                                        fontWeight: plan.buttonFontWeight || '600',
                                        cursor: 'pointer',
                                        width: plan.buttonWidth || '100%'
                                    }}>
                                        {plan.buttonText || (plan.price === 'Liên hệ' ? 'Liên hệ' : 'Chọn gói')}
                                    </button>
                                </div>
                            ))}
                        </div>
                    )}

                    {/* Render testimonials */}
                    {Array.isArray(componentData.testimonials) && componentData.testimonials.length > 0 && (
                        <div style={{
                            display: 'grid',
                            gridTemplateColumns: componentData.testimonialColumns || 'repeat(auto-fit, minmax(300px, 1fr))',
                            gap: componentData.testimonialGap || '20px',
                            marginTop: componentData.testimonialMarginTop || '30px'
                        }}>
                            {componentData.testimonials.map((testimonial, index) => (
                                <div key={index} style={{
                                    background: testimonial.background || 'rgba(255, 255, 255, 0.1)',
                                    padding: testimonial.padding || '20px',
                                    borderRadius: testimonial.borderRadius || '12px',
                                    textAlign: testimonial.textAlign || 'left'
                                }}>
                                    <p style={{
                                        fontSize: testimonial.contentFontSize || '1rem',
                                        margin: testimonial.contentMargin || '0 0 15px 0',
                                        fontStyle: testimonial.contentFontStyle || 'italic',
                                        lineHeight: testimonial.contentLineHeight || '1.6',
                                        color: testimonial.contentColor || 'inherit'
                                    }}>
                                        "{testimonial.content || 'Chưa có nội dung'}"
                                    </p>
                                    <div style={{
                                        display: 'flex',
                                        alignItems: 'center',
                                        gap: testimonial.avatarGap || '12px'
                                    }}>
                                        <img
                                            src={testimonial.avatar || 'https://via.placeholder.com/40?text=Avatar'}
                                            alt={testimonial.author || 'Người dùng'}
                                            style={{
                                                width: testimonial.avatarWidth || '40px',
                                                height: testimonial.avatarHeight || '40px',
                                                borderRadius: testimonial.avatarBorderRadius || '50%',
                                                objectFit: 'cover'
                                            }}
                                        />
                                        <div>
                                            <div style={{
                                                fontWeight: testimonial.authorFontWeight || '600',
                                                fontSize: testimonial.authorFontSize || '0.875rem',
                                                color: testimonial.authorColor || 'inherit'
                                            }}>
                                                {testimonial.author || 'Ẩn danh'}
                                            </div>
                                            <div style={{
                                                fontSize: testimonial.positionFontSize || '0.75rem',
                                                opacity: testimonial.positionOpacity || 0.8,
                                                color: testimonial.positionColor || 'inherit'
                                            }}>
                                                {testimonial.position || 'Khách hàng'}
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            ))}
                        </div>
                    )}

                    {/* Render stats */}
                    {Array.isArray(componentData.stats) && componentData.stats.length > 0 && (
                        <div style={{
                            display: 'grid',
                            gridTemplateColumns: componentData.statsColumns || 'repeat(auto-fit, minmax(150px, 1fr))',
                            gap: componentData.statsGap || '30px',
                            marginTop: componentData.statsMarginTop || '30px'
                        }}>
                            {componentData.stats.map((stat, index) => (
                                <div key={index} style={{
                                    textAlign: stat.textAlign || 'center'
                                }}>
                                    <div style={{
                                        fontSize: stat.numberFontSize || '2.5rem',
                                        fontWeight: stat.numberFontWeight || '800',
                                        margin: stat.numberMargin || '0 0 8px 0',
                                        color: stat.numberColor || 'inherit'
                                    }}>
                                        {stat.number || '0'}
                                    </div>
                                    <div style={{
                                        fontSize: stat.labelFontSize || '1rem',
                                        opacity: stat.labelOpacity || 0.9,
                                        fontWeight: stat.labelFontWeight || '500',
                                        color: stat.labelColor || 'inherit'
                                    }}>
                                        {stat.label || 'Thống kê'}
                                    </div>
                                </div>
                            ))}
                        </div>
                    )}

                    {/* Render contact info */}
                    {componentData.contactInfo && (
                        <div style={{
                            margin: componentData.contactMargin || '20px 0',
                            textAlign: componentData.textAlign || 'center'
                        }}>
                            {componentData.contactInfo.email && (
                                <p style={{
                                    margin: componentData.contactEmailMargin || '8px 0',
                                    fontSize: componentData.contactEmailFontSize || '0.95rem',
                                    color: componentData.contactEmailColor || 'inherit'
                                }}>
                                    <strong>Email:</strong> {componentData.contactInfo.email}
                                </p>
                            )}
                            {componentData.contactInfo.phone && (
                                <p style={{
                                    margin: componentData.contactPhoneMargin || '8px 0',
                                    fontSize: componentData.contactPhoneFontSize || '0.95rem',
                                    color: componentData.contactPhoneColor || 'inherit'
                                }}>
                                    <strong>Điện thoại:</strong> {componentData.contactInfo.phone}
                                </p>
                            )}
                            {componentData.contactInfo.address && (
                                <p style={{
                                    margin: componentData.contactAddressMargin || '8px 0',
                                    fontSize: componentData.contactAddressFontSize || '0.95rem',
                                    color: componentData.contactAddressColor || 'inherit'
                                }}>
                                    <strong>Địa chỉ:</strong> {componentData.contactInfo.address}
                                </p>
                            )}
                        </div>
                    )}

                    {/* Render primary CTA button */}
                    {componentData.ctaText && !Array.isArray(children) || !children.some(child => child?.type === 'button') && (
                        <div style={{
                            textAlign: 'center',
                            marginTop: componentData.ctaMarginTop || '25px'
                        }}>
                            <button style={{
                                padding: componentData.ctaPadding || '14px 28px',
                                borderRadius: componentData.ctaBorderRadius || '8px',
                                background: componentData.ctaBackground || '#ef4444',
                                color: componentData.ctaColor || '#fff',
                                border: componentData.ctaBorder || 'none',
                                cursor: 'pointer',
                                fontSize: componentData.ctaFontSize || '1.1rem',
                                fontWeight: componentData.ctaFontWeight || '600',
                                boxShadow: componentData.ctaBoxShadow || '0 4px 14px rgba(239, 68, 68, 0.3)',
                                marginRight: componentData.secondaryCta ? (componentData.ctaMarginRight || '12px') : '0'
                            }}>
                                {componentData.ctaText}
                            </button>

                            {/* Secondary CTA button */}
                            {componentData.secondaryCta && (
                                <button style={{
                                    padding: componentData.secondaryCtaPadding || '14px 28px',
                                    borderRadius: componentData.secondaryCtaBorderRadius || '8px',
                                    background: componentData.secondaryCtaBackground || 'transparent',
                                    color: componentData.secondaryCtaColor || 'inherit',
                                    border: componentData.secondaryCtaBorder || '2px solid currentColor',
                                    cursor: 'pointer',
                                    fontSize: componentData.secondaryCtaFontSize || '1.1rem',
                                    fontWeight: componentData.secondaryCtaFontWeight || '600'
                                }}>
                                    {componentData.secondaryCta}
                                </button>
                            )}
                        </div>
                    )}

                    {/* Render copyright */}
                    {componentData.copyright && (
                        <div style={{
                            marginTop: componentData.copyrightMarginTop || '30px',
                            paddingTop: componentData.copyrightPaddingTop || '20px',
                            borderTop: componentData.copyrightBorderTop || '1px solid rgba(255,255,255,0.2)',
                            textAlign: componentData.copyrightTextAlign || 'center',
                            fontSize: componentData.copyrightFontSize || '0.875rem',
                            opacity: componentData.copyrightOpacity || 0.8,
                            color: componentData.copyrightColor || 'inherit'
                        }}>
                            {componentData.copyright}
                        </div>
                    )}

                    {/* Render children nếu có */}
                    {Array.isArray(children) && children.length > 0 && children.map((child, index) => (
                        <div key={child.id || index} style={{ marginBottom: child.marginBottom || '10px' }}>
                            {renderComponentContent(child.type, child.componentData || {}, child.styles || {})}
                        </div>
                    ))}
                </div>
            </div>
        );
    }

    switch (type) {
        case 'heading':
            const headingLevel = componentData.level && ['h1', 'h2', 'h3', 'h4', 'h5', 'h6'].includes(componentData.level)
                ? componentData.level
                : 'h2';
            const HeadingTag = headingLevel;
            return (
                <HeadingTag style={{
                    fontSize: componentData.fontSize || '1.5rem',
                    fontWeight: componentData.fontWeight || '700',
                    color: componentData.color || '#1f2937',
                    margin: componentData.margin || '0',
                    padding: componentData.padding || '0',
                    textAlign: componentData.textAlign || 'left',
                    ...styles
                }}>
                    {componentData.content || 'Tiêu đề'}
                </HeadingTag>
            );

        case 'paragraph':
            return (
                <p style={{
                    fontSize: componentData.fontSize || '1rem',
                    fontWeight: componentData.fontWeight || '400',
                    color: componentData.color || '#4b5563',
                    margin: componentData.margin || '0',
                    padding: componentData.padding || '0',
                    lineHeight: componentData.lineHeight || '1.6',
                    textAlign: componentData.textAlign || 'left',
                    ...styles
                }}>
                    {componentData.content || 'Đoạn văn'}
                </p>
            );

        case 'button':
            return (
                <button style={{
                    padding: componentData.padding || '12px 24px',
                    borderRadius: componentData.borderRadius || '8px',
                    background: componentData.background || '#2563eb',
                    color: componentData.color || '#fff',
                    border: componentData.border || (componentData.background === 'transparent' ? '2px solid currentColor' : 'none'),
                    cursor: 'pointer',
                    fontSize: componentData.fontSize || (componentData.size === 'large' ? '1.125rem' : '1rem'),
                    fontWeight: componentData.fontWeight || (componentData.size === 'large' ? '700' : '600'),
                    textAlign: componentData.textAlign || 'center',
                    ...styles
                }}>
                    {componentData.content || 'Nút bấm'}
                </button>
            );

        case 'image':
            return (
                <img
                    src={componentData.src || 'https://via.placeholder.com/300x200/4f46e5/ffffff?text=Image'}
                    alt={componentData.alt || 'Hình ảnh'}
                    style={{
                        maxWidth: componentData.maxWidth || '100%',
                        height: componentData.height || 'auto',
                        display: componentData.display || 'block',
                        borderRadius: componentData.borderRadius || '0',
                        ...styles
                    }}
                    draggable={componentData.draggable ?? false}
                />
            );

        case 'form':
            return (
                <div style={{
                    display: 'flex',
                    flexDirection: componentData.direction || 'column',
                    gap: componentData.gap || '16px',
                    ...styles
                }}>
                    {componentData.title && !Array.isArray(children) || !children.some(child => child?.type === 'heading') && (
                        <h3 style={{
                            margin: componentData.titleMargin || '0',
                            fontSize: componentData.titleFontSize || '1.25rem',
                            fontWeight: componentData.titleFontWeight || '600',
                            color: componentData.titleColor || '#1f2937'
                        }}>
                            {componentData.title}
                        </h3>
                    )}
                    {componentData.subtitle && !Array.isArray(children) || !children.some(child => child?.type === 'paragraph') && (
                        <p style={{
                            margin: componentData.subtitleMargin || '0',
                            fontSize: componentData.subtitleFontSize || '0.875rem',
                            color: componentData.subtitleColor || '#6b7280'
                        }}>
                            {componentData.subtitle}
                        </p>
                    )}

                    {Array.isArray(componentData.fields) && componentData.fields.length > 0 ? (
                        componentData.fields.map((field, index) => {
                            if (field.type === 'textarea') {
                                return (
                                    <textarea
                                        key={index}
                                        placeholder={field.placeholder || field.label || 'Nhập văn bản...'}
                                        required={field.required ?? false}
                                        style={{
                                            padding: field.padding || '12px',
                                            borderRadius: field.borderRadius || '8px',
                                            border: field.border || '1px solid #d1d5db',
                                            fontSize: field.fontSize || '1rem',
                                            minHeight: field.minHeight || '100px',
                                            resize: field.resize || 'vertical',
                                            fontFamily: 'inherit',
                                            ...styles
                                        }}
                                    />
                                );
                            }
                            return (
                                <input
                                    key={index}
                                    type={field.type || 'text'}
                                    placeholder={field.placeholder || field.label || 'Nhập...'}
                                    required={field.required ?? false}
                                    style={{
                                        padding: field.padding || '12px',
                                        borderRadius: field.borderRadius || '8px',
                                        border: field.border || '1px solid #d1d5db',
                                        fontSize: field.fontSize || '1rem',
                                        ...styles
                                    }}
                                />
                            );
                        })
                    ) : (
                        !Array.isArray(children) || !children.some(child => child?.type === 'input') && (
                            <input
                                type={componentData.inputType || 'email'}
                                placeholder={componentData.placeholder || 'Nhập email...'}
                                style={{
                                    padding: componentData.inputPadding || '12px',
                                    borderRadius: componentData.inputBorderRadius || '8px',
                                    border: componentData.inputBorder || '1px solid #d1d5db',
                                    fontSize: componentData.inputFontSize || '1rem',
                                    ...styles
                                }}
                            />
                        )
                    )}

                    {!Array.isArray(children) || !children.some(child => child?.type === 'button') && (
                        <button
                            type="submit"
                            style={{
                                background: componentData.submitBackground || '#2563eb',
                                color: componentData.submitColor || '#fff',
                                padding: componentData.submitPadding || '12px 24px',
                                borderRadius: componentData.submitBorderRadius || '8px',
                                border: componentData.submitBorder || 'none',
                                cursor: 'pointer',
                                fontSize: componentData.submitFontSize || '1rem',
                                fontWeight: componentData.submitFontWeight || '600',
                                ...styles
                            }}
                        >
                            {componentData.submitText || componentData.buttonText || 'Gửi'}
                        </button>
                    )}

                    {Array.isArray(children) && children.length > 0 && children.map((child, index) => (
                        <div key={child.id || index} style={{ marginBottom: child.marginBottom || '10px' }}>
                            {renderComponentContent(child.type, child.componentData || {}, child.styles || {})}
                        </div>
                    ))}
                </div>
            );

        case 'container':
            return (
                <div style={{
                    display: componentData.layoutType || 'flex',
                    flexDirection: componentData.direction || 'column',
                    alignItems: componentData.alignItems || 'flex-start',
                    gap: componentData.gap || '16px',
                    padding: componentData.padding || '0',
                    background: componentData.background || 'transparent',
                    ...styles
                }}>
                    {Array.isArray(children) && children.length > 0 ? (
                        children.map((child, index) => (
                            <div key={child.id || index} style={{ marginBottom: child.marginBottom || '10px' }}>
                                {renderComponentContent(child.type, child.componentData || {}, child.styles || {})}
                            </div>
                        ))
                    ) : (
                        <span style={{ color: componentData.placeholderColor || '#9ca3af', fontStyle: 'italic' }}>
                            {componentData.placeholderText || 'Container trống - thả các phần tử vào đây'}
                        </span>
                    )}
                </div>
            );

        case 'divider':
            return (
                <hr style={{
                    border: 'none',
                    background: componentData.color || '#e5e7eb',
                    height: componentData.thickness || '1px',
                    width: componentData.width || '100%',
                    margin: componentData.margin || '0',
                    ...styles
                }} />
            );

        case 'spacer':
            return (
                <div style={{
                    background: componentData.background || 'transparent',
                    height: componentData.height || '40px',
                    width: componentData.width || '100%',
                    ...styles
                }} />
            );

        case 'popup':
        case 'modal':
            return (
                <div style={{
                    display: 'flex',
                    flexDirection: componentData.direction || 'column',
                    gap: componentData.gap || '16px',
                    padding: componentData.padding || '20px',
                    background: componentData.background || 'rgba(255, 255, 255, 0.9)',
                    borderRadius: componentData.borderRadius || '12px',
                    boxShadow: componentData.boxShadow || '0 4px 14px rgba(0, 0, 0, 0.1)',
                    ...styles
                }}>
                    {componentData.title && !Array.isArray(children) || !children.some(child => child?.type === 'heading') && (
                        <h3 style={{
                            fontSize: componentData.titleFontSize || '1.5rem',
                            margin: componentData.titleMargin || '0',
                            fontWeight: componentData.titleFontWeight || '600',
                            color: componentData.titleColor || '#1f2937',
                            textAlign: componentData.textAlign || 'center'
                        }}>
                            {componentData.title}
                        </h3>
                    )}
                    {componentData.content && !Array.isArray(children) || !children.some(child => child?.type === 'paragraph') && (
                        <p style={{
                            fontSize: componentData.contentFontSize || '1rem',
                            margin: componentData.contentMargin || '0',
                            lineHeight: componentData.contentLineHeight || '1.6',
                            color: componentData.contentColor || '#4b5563',
                            textAlign: componentData.textAlign || 'center'
                        }}>
                            {componentData.content}
                        </p>
                    )}
                    {componentData.ctaText && !Array.isArray(children) || !children.some(child => child?.type === 'button') && (
                        <button style={{
                            padding: componentData.ctaPadding || '12px 24px',
                            borderRadius: componentData.ctaBorderRadius || '8px',
                            background: componentData.ctaBackground || '#2563eb',
                            color: componentData.ctaColor || '#fff',
                            border: componentData.ctaBorder || 'none',
                            cursor: 'pointer',
                            fontSize: componentData.ctaFontSize || '1rem',
                            fontWeight: componentData.ctaFontWeight || '600',
                            alignSelf: type === 'modal' ? 'flex-end' : 'center',
                            ...styles
                        }}>
                            {componentData.ctaText || (type === 'modal' ? 'Đóng' : 'Hành động')}
                        </button>
                    )}

                    {Array.isArray(children) && children.length > 0 && children.map((child, index) => (
                        <div key={child.id || index} style={{ marginBottom: child.marginBottom || '10px' }}>
                            {renderComponentContent(child.type, child.componentData || {}, child.styles || {})}
                        </div>
                    ))}
                </div>
            );

        default:
            return (
                <div style={{
                    color: componentData.errorColor || '#ef4444',
                    padding: '12px',
                    border: '1px dashed #ef4444',
                    borderRadius: '4px',
                    textAlign: 'center',
                    ...styles
                }}>
                    Không tìm thấy component: {type}
                </div>
            );
    }
};

// Component ChildElement
const ChildElement = React.memo(
    ({
         parentId,
         id,
         type,
         content,
         componentData = {},
         styles = {},
         index,
         isSelected,
         onSelectChild,
         onUpdateChildren,
         zoomLevel,
         gridSize,
         showGrid,
     }) => {
        const dragRef = useRef(null);
        const dropRef = useRef(null);

        const [{ isDragging }, drag] = useDrag(() => ({
            type: ItemTypes.CHILD_ELEMENT,
            item: () => {
                console.log('ChildElement drag started:', { childId: id, parentId, index }); // Debug drag
                return { childId: id, parentId, index, isExisting: true };
            },
            collect: (monitor) => ({
                isDragging: monitor.isDragging(),
            }),
        }));

        const [{ isOver, canDrop }, drop] = useDrop(() => ({
            accept: ItemTypes.CHILD_ELEMENT,
            canDrop: (item) => item.parentId === parentId, // Chỉ cho phép thả trong cùng parent
            drop: (item, monitor) => {
                if (!monitor.canDrop()) {
                    console.warn('Drop not allowed: Different parent or same position');
                    toast.error('Không thể di chuyển thành phần con sang section khác!');
                    return { moved: false };
                }
                console.log('ChildElement drop:', {
                    childId: item.childId,
                    parentId,
                    dragIndex: item.index,
                    dropIndex: index,
                }); // Debug drop
                if (item.index !== index) {
                    console.log('Calling onUpdateChildren:', { parentId, dragIndex: item.index, dropIndex: index });
                    try {
                        onUpdateChildren(parentId, item.index, index);
                        toast.success('Đã di chuyển thành phần con!');
                        return { moved: true, parentId, dropIndex: index };
                    } catch (error) {
                        console.error('Error in onUpdateChildren:', error);
                        toast.error('Lỗi khi di chuyển thành phần con!');
                        return { moved: false };
                    }
                }
                return { moved: false };
            },
            collect: (monitor) => ({
                isOver: monitor.isOver(),
                canDrop: monitor.canDrop(),
            }),
        }));

        const handleClick = (e) => {
            e.stopPropagation();
            console.log('ChildElement clicked:', { parentId, id, isSelected }); // Debug click
            if (typeof onSelectChild === 'function') {
                onSelectChild(parentId, id);
            } else {
                console.warn('onSelectChild is not a function');
            }
        };

        useEffect(() => {
            drag(dragRef);
            drop(dropRef);
        }, [drag, drop]);

        return (
            <div
                ref={dropRef}
                className={`lpb-child-element ${isSelected ? 'lpb-child-element-selected' : ''} ${isDragging ? 'lpb-child-element-dragging' : ''} ${isOver && canDrop ? 'lpb-child-element-hover' : ''}`}
                style={{
                    position: 'relative',
                    margin: '5px 0',
                    padding: '5px',
                    cursor: isDragging ? 'grabbing' : 'grab',
                    opacity: isDragging ? 0.5 : 1,
                    borderBottom: isOver && canDrop ? '2px solid #2563eb' : 'none',
                    ...styles,
                }}
                onClick={handleClick}
            >
                <div ref={dragRef}>
                    {renderComponentContent(type, { content, ...componentData }, styles)}
                </div>
                {isSelected && (
                    <QuickEditToolkit
                        element={{ id, type, content, styles }}
                        position={{ x: 0, y: -40 }}
                        onEdit={() => console.log('Edit child:', id)}
                        onDuplicate={() => console.log('Duplicate child:', id)}
                        onDelete={() => console.log('Delete child:', id)}
                        onMoveUp={() => console.log('Move up child:', id)}
                        onMoveDown={() => console.log('Move down child:', id)}
                        onClose={() => onSelectChild(parentId, null)}
                    />
                )}
            </div>
        );
    }
);

// Component Element
const Element = React.memo(
    ({
         element,
         isSelected,
         onSelectElement,
         onUpdatePosition,
         viewMode,
         zoomLevel,
         gridSize,
         onContextMenu,
         canvasBounds,
         canvasRef,
         onSelectChild,
         selectedChildId,
         onUpdateChildren,
         showGrid,
     }) => {
        const { id, type, componentData = {}, position = {}, size = { width: 200, height: 50 }, styles = {}, children = [] } = element;
        const elementRef = useRef(null);
        const [isDragStarted, setIsDragStarted] = useState(false);

        const [{ isDragging }, drag, preview] = useDrag(() => ({
            type: ItemTypes.EXISTING_ELEMENT,
            item: () => {
                setIsDragStarted(true);
                console.log('Element drag started:', { id, position: position[viewMode] }); // Debug drag
                return {
                    id,
                    elementSize: size,
                    elementPosition: position[viewMode] || { x: 0, y: 0 },
                    isExisting: true,
                };
            },
            end: (item, monitor) => {
                setIsDragStarted(false);
                const dropResult = monitor.getDropResult();
                if (!dropResult || !dropResult.moved) {
                    console.log('Drag cancelled or invalid drop for element:', id);
                    return;
                }
                console.log('Drag ended successfully for element:', id, 'New position:', dropResult.newPosition);
            },
            collect: (monitor) => ({
                isDragging: monitor.isDragging(),
            }),
        }), [id, size, position, viewMode]);

        useEffect(() => {
            preview(getEmptyImage(), { captureDraggingState: true });
        }, [preview]);

        const handleMouseDown = useCallback(
            (e) => {
                e.stopPropagation();
                onSelectElement(id, e.ctrlKey);
                onSelectChild(id, null); // Clear child selection when selecting parent
            },
            [id, onSelectElement, onSelectChild]
        );

        const handleContextMenu = useCallback(
            (e) => {
                e.preventDefault();
                e.stopPropagation();
                onContextMenu(id, { x: e.clientX, y: e.clientY });
            },
            [id, onContextMenu]
        );

        return (
            <div
                ref={(node) => {
                    elementRef.current = node;
                    drag(node);
                }}
                className={`lpb-canvas-element ${isSelected ? 'lpb-canvas-element-selected' : ''} ${isDragging ? 'lpb-canvas-element-dragging' : ''}`}
                style={{
                    position: 'absolute',
                    transform: `translate(${position[viewMode]?.x || 0}px, ${position[viewMode]?.y || 0}px)`,
                    width: size.width,
                    height: size.height,
                    zIndex: position.z || 1,
                    opacity: isDragging ? 0.5 : 1,
                    cursor: isDragging ? 'grabbing' : 'grab',
                    userSelect: 'none',
                    ...styles,
                }}
                onMouseDown={handleMouseDown}
                onContextMenu={handleContextMenu}
            >
                {renderComponentContent(type, componentData, styles, children)}
                {children.map((child, index) => (
                    <ChildElement
                        key={child.id}
                        parentId={id}
                        id={child.id}
                        type={child.type}
                        content={child.content}
                        componentData={child.componentData || {}}
                        styles={child.styles || {}}
                        index={index}
                        isSelected={selectedChildId === child.id}
                        onSelectChild={onSelectChild}
                        onUpdateChildren={onUpdateChildren}
                        zoomLevel={zoomLevel}
                        gridSize={gridSize}
                        showGrid={showGrid}
                    />
                ))}
            </div>
        );
    }
);

// Component Canvas
const Canvas = React.memo(
    ({
         pageData,
         selectedIds,
         onSelectElement,
         onUpdatePosition,
         onUpdateSize,
         onDeleteElement,
         onAddElement,
         onGroupElements,
         viewMode,
         zoomLevel,
         gridSize,
         showGrid,
         onEditElement,
         onDuplicateElement,
         onMoveElementUp,
         onMoveElementDown,
         onSelectChild,
         selectedChildId,
         onUpdateChildren,
     }) => {
        const canvasRef = useRef(null);
        const [guidelines, setGuidelines] = useState([]);
        const [contextMenu, setContextMenu] = useState(null);
        const [dragPreview, setDragPreview] = useState(null);

        const canvasBounds = canvasRef.current
            ? {
                width: canvasRef.current.offsetWidth / (zoomLevel / 100),
                height: canvasRef.current.offsetHeight / (zoomLevel / 100),
            }
            : { width: 1200, height: 1000 };

        const getSnapPoints = useCallback(() => {
            const points = [
                { x: 0, y: 0 },
                { x: canvasBounds.width, y: canvasBounds.height },
                { x: canvasBounds.width / 2, y: canvasBounds.height / 2 },
            ];

            pageData.elements.forEach((el) => {
                const bounds = getElementBounds(el);
                points.push(
                    { x: bounds.left, y: bounds.top },
                    { x: bounds.right, y: bounds.bottom },
                    { x: bounds.centerX, y: bounds.centerY },
                    { x: bounds.left, y: bounds.centerY },
                    { x: bounds.right, y: bounds.centerY },
                    { x: bounds.centerX, y: bounds.top },
                    { x: bounds.centerX, y: bounds.bottom }
                );
            });

            return points;
        }, [pageData.elements, canvasBounds]);

        const [{ isOver, dragItem }, drop] = useDrop(() => ({
            accept: [ItemTypes.ELEMENT, ItemTypes.EXISTING_ELEMENT],
            hover: (item, monitor) => {
                if (monitor.getItemType() === ItemTypes.CHILD_ELEMENT) {
                    console.log('Ignoring CHILD_ELEMENT hover in Canvas'); // Debug
                    return undefined; // Ngăn Canvas xử lý CHILD_ELEMENT
                }
                const clientOffset = monitor.getClientOffset();
                if (!clientOffset || !canvasRef.current) return;

                const pos = getCanvasPosition(clientOffset.x, clientOffset.y, canvasRef.current, zoomLevel);
                const snapPoints = getSnapPoints();
                const snapped = snapToGrid(pos.x, pos.y, showGrid ? gridSize : Infinity, snapPoints);

                if (monitor.getItemType() === ItemTypes.EXISTING_ELEMENT && item.isExisting) {
                    const draggingElement = pageData.elements.find((el) => el.id === item.id);
                    if (draggingElement) {
                        setDragPreview({
                            id: item.id,
                            x: snapped.x,
                            y: snapped.y,
                            size: draggingElement.size || { width: 200, height: 50 },
                            type: draggingElement.type,
                            componentData: draggingElement.componentData,
                            styles: draggingElement.styles,
                        });
                    }
                } else if (item.json) {
                    setDragPreview({
                        id: 'preview',
                        x: snapped.x,
                        y: snapped.y,
                        size: item.json.size || { width: 200, height: 50 },
                        type: item.json.type,
                        componentData: item.json.componentData,
                        styles: item.json.styles,
                    });
                }

                const newGuidelines = [];
                pageData.elements.forEach((el) => {
                    if (el.id === item.id) return;
                    const bounds = getElementBounds(el);

                    if (Math.abs(snapped.x - bounds.left) < 15) newGuidelines.push({ vertical: true, position: bounds.left });
                    if (Math.abs(snapped.x - bounds.right) < 15) newGuidelines.push({ vertical: true, position: bounds.right });
                    if (Math.abs(snapped.x - bounds.centerX) < 15) newGuidelines.push({ vertical: true, position: bounds.centerX });
                    if (Math.abs(snapped.y - bounds.top) < 15) newGuidelines.push({ vertical: false, position: bounds.top });
                    if (Math.abs(snapped.y - bounds.bottom) < 15) newGuidelines.push({ vertical: false, position: bounds.bottom });
                    if (Math.abs(snapped.y - bounds.centerY) < 15) newGuidelines.push({ vertical: false, position: bounds.centerY });
                });

                if (Math.abs(snapped.x) < 15) newGuidelines.push({ vertical: true, position: 0 });
                if (Math.abs(snapped.x - canvasBounds.width) < 15) newGuidelines.push({ vertical: true, position: canvasBounds.width });
                if (Math.abs(snapped.x - canvasBounds.width / 2) < 15) newGuidelines.push({ vertical: true, position: canvasBounds.width / 2 });
                if (Math.abs(snapped.y) < 15) newGuidelines.push({ vertical: false, position: 0 });
                if (Math.abs(snapped.y - canvasBounds.height) < 15) newGuidelines.push({ vertical: false, position: canvasBounds.height });
                if (Math.abs(snapped.y - canvasBounds.height / 2) < 15) newGuidelines.push({ vertical: false, position: canvasBounds.height / 2 });

                setGuidelines(newGuidelines);
            },
            drop: (item, monitor) => {
                if (monitor.getItemType() === ItemTypes.CHILD_ELEMENT) {
                    console.log('Ignoring CHILD_ELEMENT drop in Canvas'); // Debug
                    return undefined; // Ngăn Canvas xử lý CHILD_ELEMENT
                }
                const clientOffset = monitor.getClientOffset();
                if (!clientOffset || !canvasRef.current) {
                    toast.error('Không thể xác định vị trí thả!');
                    return { moved: false };
                }

                const pos = getCanvasPosition(clientOffset.x, clientOffset.y, canvasRef.current, zoomLevel);
                const snapPoints = getSnapPoints();
                const snapped = snapToGrid(pos.x, pos.y, showGrid ? gridSize : Infinity, snapPoints);

                console.group('Canvas Drop Debug');
                console.log('Drop position:', snapped);
                console.log('Item:', item);
                console.log('Item type:', monitor.getItemType());
                console.groupEnd();

                if (item.json && !item.isExisting) {
                    const newElement = {
                        id: `${item.id}-${Date.now()}`,
                        type: item.json.type,
                        componentData: item.json.componentData,
                        position: {
                            [viewMode]: snapped,
                            desktop: snapped,
                            tablet: snapped,
                            mobile: snapped,
                        },
                        size: item.json.size || { width: 200, height: 50 },
                        styles: item.json.styles || {},
                        children: item.json.children || [],
                    };
                    onAddElement(newElement);
                    setDragPreview(null);
                    setGuidelines([]);
                    return { moved: true, newPosition: snapped };
                } else if (item.id && item.isExisting) {
                    const elementToUpdate = pageData.elements.find((el) => el.id === item.id);
                    if (elementToUpdate) {
                        const updatedElement = {
                            ...elementToUpdate,
                            position: {
                                ...elementToUpdate.position,
                                [viewMode]: snapped,
                            },
                        };
                        onUpdatePosition(item.id, updatedElement.position, 'replace');
                        toast.success('Đã di chuyển phần tử!');
                        console.log('onUpdatePosition called with REPLACE mode:', item.id, updatedElement.position);
                    } else {
                        console.error('Element not found:', item.id);
                    }
                    setDragPreview(null);
                    setGuidelines([]);
                    return { moved: true, newPosition: snapped };
                }

                setDragPreview(null);
                setGuidelines([]);
                return { moved: false };
            },
            collect: (monitor) => ({
                isOver: monitor.isOver(),
                dragItem: monitor.getItem(),
            }),
        }), [pageData.elements, viewMode, zoomLevel, gridSize, showGrid, canvasBounds, onAddElement, onUpdatePosition]);

        const handleContextMenu = useCallback((id, position) => {
            setContextMenu({ id, position });
        }, []);

        const handleCanvasClick = useCallback(
            (e) => {
                if (e.target === canvasRef.current) {
                    onSelectElement([]);
                    onSelectChild(null, null);
                    setContextMenu(null);
                }
            },
            [onSelectElement, onSelectChild]
        );

        const handleKeyDown = useCallback(
            (e) => {
                const ids = Array.isArray(selectedIds) ? selectedIds : [];
                if (!ids.length) return;

                e.preventDefault();
                e.stopPropagation();

                const step = e.shiftKey ? 10 : 1;

                ids.forEach((id) => {
                    const element = pageData.elements.find((el) => el.id === id);
                    if (element) {
                        const newY = e.key === 'ArrowUp' ? Math.max(0, (element.position[viewMode]?.y || 0) - step) : (element.position[viewMode]?.y || 0) + step;
                        const newX = e.key === 'ArrowLeft' ? Math.max(0, (element.position[viewMode]?.x || 0) - step) : (element.position[viewMode]?.x || 0) + step;
                        switch (e.key) {
                            case 'ArrowUp':
                            case 'ArrowDown':
                                onUpdatePosition(id, { [viewMode]: { x: element.position[viewMode]?.x || 0, y: newY } }, 'absolute');
                                break;
                            case 'ArrowLeft':
                            case 'ArrowRight':
                                onUpdatePosition(id, { [viewMode]: { x: newX, y: element.position[viewMode]?.y || 0 } }, 'absolute');
                                break;
                            case 'Delete':
                            case 'Backspace':
                                onDeleteElement(id);
                                break;
                            case 'Escape':
                                onSelectElement([]);
                                onSelectChild(null, null);
                                setContextMenu(null);
                                break;
                            default:
                                break;
                        }
                    }
                });
            },
            [selectedIds, onUpdatePosition, onDeleteElement, onSelectElement, onSelectChild, viewMode, pageData.elements]
        );

        useHotkeys('ctrl+a', (e) => {
            e.preventDefault();
            onSelectElement(pageData.elements.map((el) => el.id));
        }, [pageData.elements, onSelectElement]);

        useHotkeys('ctrl+g', (e) => {
            e.preventDefault();
            const ids = Array.isArray(selectedIds) ? selectedIds : [];
            if (ids.length > 1) {
                onGroupElements(ids);
            }
        }, [selectedIds, onGroupElements]);

        useEffect(() => {
            const handleClickOutside = () => setContextMenu(null);
            if (contextMenu) {
                document.addEventListener('click', handleClickOutside);
                return () => document.removeEventListener('click', handleClickOutside);
            }
        }, [contextMenu]);

        const renderDragPreview = () => {
            if (!dragPreview) return null;

            const { x, y, size, type, componentData, styles } = dragPreview;
            const previewStyle = {
                position: 'absolute',
                transform: `translate(${x}px, ${y}px)`,
                width: size.width,
                height: size.height,
                border: '2px dashed #3b82f6',
                background: 'rgba(59, 130, 246, 0.1)',
                pointerEvents: 'none',
                zIndex: 1000,
                opacity: 0.5,
                ...styles,
            };

            return (
                <div className="lpb-drag-preview" style={previewStyle}>
                    {renderComponentContent(type, componentData || {}, {})}
                </div>
            );
        };

        return (
            <div className="lpb-canvas-wrapper" onKeyDown={handleKeyDown} tabIndex={0} style={{ outline: 'none' }}>
                <div
                    ref={(node) => {
                        canvasRef.current = node;
                        drop(node);
                    }}
                    className={`lpb-canvas ${viewMode === 'mobile' ? 'lpb-canvas-mobile' : viewMode === 'tablet' ? 'lpb-canvas-tablet' : ''}`}
                    onClick={handleCanvasClick}
                >
                    <Guidelines guidelines={guidelines} zoomLevel={zoomLevel} />
                    {pageData.elements.map((element) => (
                        <Element
                            key={element.id}
                            element={element}
                            isSelected={selectedIds.includes(element.id)}
                            onSelectElement={onSelectElement}
                            onUpdatePosition={onUpdatePosition}
                            viewMode={viewMode}
                            zoomLevel={zoomLevel}
                            gridSize={showGrid ? gridSize : Infinity}
                            onContextMenu={handleContextMenu}
                            canvasBounds={canvasBounds}
                            canvasRef={canvasRef}
                            onSelectChild={onSelectChild}
                            selectedChildId={selectedChildId}
                            onUpdateChildren={onUpdateChildren}
                            showGrid={showGrid}
                        />
                    ))}
                    {renderDragPreview()}
                    <SelectionOverlay
                        selectedElements={pageData.elements.filter((el) => Array.isArray(selectedIds) && selectedIds.includes(el.id))}
                        onResize={onUpdateSize}
                        zoomLevel={zoomLevel}
                    />
                    {contextMenu && (
                        <div
                            className="lpb-context-menu"
                            style={{
                                position: 'fixed',
                                left: contextMenu.position.x,
                                top: contextMenu.position.y,
                                zIndex: 10000,
                                background: '#fff',
                                border: '1px solid #ccc',
                                borderRadius: '4px',
                                boxShadow: '0 2px 8px rgba(0,0,0,0.15)',
                                minWidth: '120px',
                            }}
                        >
                            <div
                                className="lpb-context-menu-item"
                                style={{ padding: '8px 12px', cursor: 'pointer' }}
                                onClick={() => {
                                    onDeleteElement(contextMenu.id);
                                    setContextMenu(null);
                                }}
                            >
                                Delete
                            </div>
                            <div
                                className="lpb-context-menu-item"
                                style={{ padding: '8px 12px', cursor: 'pointer' }}
                                onClick={() => {
                                    onGroupElements([contextMenu.id]);
                                    setContextMenu(null);
                                }}
                            >
                                Group
                            </div>
                        </div>
                    )}
                </div>
            </div>
        );
    }
);

export default Canvas;
