import React, { useState, useEffect, useContext } from 'react';
import { UserContext } from '../context/UserContext';
import Header from '../components/Header';
import Sidebar from '../components/Sidebar';
import { jwtDecode } from 'jwt-decode';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import AOS from 'aos';
import 'aos/dist/aos.css';
import '../styles/MarketplaceDetail.css'; // File CSS duy nhất được sử dụng
import DogLoader from '../components/Loader';
import { toast } from 'react-toastify';
import {
    FiShoppingCart, FiStar, FiUser, FiHeart, FiExternalLink, FiDownload,
    FiCheckCircle, FiShield, FiZap, FiSmartphone, FiSliders, FiFileText, FiPackage,
    FiTag, FiLayers, FiChevronRight
} from 'react-icons/fi';

const MarketplaceDetail = () => {
    const { user } = useContext(UserContext);
    const { id } = useParams();
    const [loading, setLoading] = useState(true);
    const [userRole, setUserRole] = useState(null);
    const [page, setPage] = useState(null);
    const [error, setError] = useState('');
    const [purchasing, setPurchasing] = useState(false);
    const [selectedPaymentMethod, setSelectedPaymentMethod] = useState('SANDBOX');
    const [isLiked, setIsLiked] = useState(false);
    const [currentImageIndex, setCurrentImageIndex] = useState(0);
    const [hasPurchased, setHasPurchased] = useState(false);
    const [isSeller, setIsSeller] = useState(false);
    const [downloading, setDownloading] = useState(false);

    const navigate = useNavigate();
    const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

    const paymentMethods = [
        { value: 'SANDBOX', label: 'Sandbox (Thử nghiệm)', icon: 'https://cdn-icons-png.flaticon.com/512/8890/8890115.png' },
        { value: 'MOMO', label: 'Ví Momo', icon: 'https://upload.wikimedia.org/wikipedia/vi/f/fe/MoMo_Logo.png' },
        { value: 'VNPAY', label: 'Cổng VNPay', icon: 'https://vnpay.vn/s1/statics.vnpay.vn/logo-vnpay-qr-1.png' }
    ];

    const formatPrice = (price) => {
        if (price === 0) return 'Miễn phí';
        return new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(price);
    };

    const discount = page && page.original_price && page.price < page.original_price
        ? Math.round(((page.original_price - page.price) / page.original_price) * 100)
        : 0;

    // --- LOGIC & DATA FETCHING (Giữ nguyên) ---
    useEffect(() => {
        const initializeAuth = async () => {
            const token = localStorage.getItem('token');
            if (!token) {
                navigate('/auth'); return;
            }
            try {
                const decodedToken = jwtDecode(token);
                setUserRole(decodedToken.role);
            } catch (err) {
                console.error('Lỗi giải mã token:', err);
                navigate('/auth');
            }
        };

        if (user?.role) setUserRole(user.role);
        else initializeAuth();
    }, [user, navigate]);

    useEffect(() => {
        AOS.init({ duration: 600, once: true, easing: 'ease-out-cubic' });
        if (id) {
            loadPageDetail();
        }
    }, [id]);

    const loadPageDetail = async () => {
        try {
            setLoading(true);
            const token = localStorage.getItem('token');
            const response = await axios.get(`${API_BASE_URL}/api/marketplace/${id}`);
            setPage(response.data.data);

            if (token) {
                const decoded = jwtDecode(token);
                setIsLiked(response.data.data.liked_by?.includes(decoded.userId));
                setIsSeller(response.data.data.seller_id?._id === decoded.userId);
                await checkPurchaseStatus(token);
            }
        } catch (err) {
            setError('Không thể tải chi tiết: ' + (err.response?.data?.message || err.message));
        } finally {
            setLoading(false);
        }
    };

    const checkPurchaseStatus = async (token) => {
        if (!token) return;
        try {
            const response = await axios.get(`${API_BASE_URL}/api/payment/check-purchase/${id}`, { headers: { Authorization: `Bearer ${token}` } });
            setHasPurchased(response.data.hasPurchased || false);
        } catch (err) {
            setHasPurchased(false);
        }
    };

    const handlePurchase = async () => {
        const token = localStorage.getItem('token');
        if (!token) {
            toast.error('Vui lòng đăng nhập để mua hàng');
            navigate('/auth');
            return;
        }
        if (!selectedPaymentMethod && page.price > 0) {
            toast.warn('Vui lòng chọn phương thức thanh toán');
            return;
        }
        setPurchasing(true);
        try {
            const response = await axios.post(`${API_BASE_URL}/api/payment/create-transaction`, { marketplace_page_id: id, payment_method: page.price > 0 ? selectedPaymentMethod : 'FREE' }, { headers: { Authorization: `Bearer ${token}` } });
            if (response.data.success) {
                if(response.data.data.payment_url) {
                    window.location.href = response.data.data.payment_url;
                } else {
                    toast.success('Nhận sản phẩm miễn phí thành công!');
                    await loadPageDetail();
                }
            } else {
                toast.error(response.data.message);
            }
        } catch (err) {
            toast.error(err.response?.data?.message || 'Không thể tạo giao dịch');
        } finally {
            setPurchasing(false);
        }
    };

    const handleLike = async () => {
        const token = localStorage.getItem('token');
        if (!token) {
            toast.error('Vui lòng đăng nhập');
            navigate('/auth');
            return;
        }
        try {
            const response = await axios.post(`${API_BASE_URL}/api/marketplace/${id}/like`, {}, { headers: { Authorization: `Bearer ${token}` } });
            if (response.data.success) {
                setIsLiked(response.data.data.liked);
                setPage(p => ({ ...p, likes: response.data.data.likes }));
                toast.success(response.data.data.liked ? 'Đã thêm vào yêu thích' : 'Đã bỏ yêu thích');
            }
        } catch (err) {
            toast.error('Thao tác thất bại');
        }
    };

    const handleDownload = async (format) => {
        setDownloading(true);
        const token = localStorage.getItem('token');
        try {
            const endpoint = format === 'html' ? `${API_BASE_URL}/api/marketplace/${id}/download/html` : `${API_BASE_URL}/api/marketplace/${id}/download/iuhpage`;
            const response = await axios.get(endpoint, { headers: { Authorization: `Bearer ${token}` }, responseType: 'blob' });
            const url = window.URL.createObjectURL(new Blob([response.data]));
            const link = document.createElement('a');
            link.href = url;
            link.setAttribute('download', format === 'html' ? `${page.title}.zip` : `${page.title}.iuhpage`);
            document.body.appendChild(link);
            link.click();
            link.remove();
            window.URL.revokeObjectURL(url);
        } catch (err) {
            toast.error('Tải xuống thất bại');
        } finally {
            setDownloading(false);
        }
    };


    if (loading) return <DogLoader />;
    if (error) return (
        <div style={{ textAlign: 'center', marginTop: '5rem' }}>
            <h2>Có lỗi xảy ra</h2>
            <p>{error}</p>
        </div>
    );
    if (!page) return null;

    // --- RENDER ---
    return (
        <div className="marketplace-detail-container">
            <Sidebar userRole={userRole} />
            <div className="marketplace-detail-main">
                <Header />
                <div className="marketplace-detail-content">
                    {/* Breadcrumb */}
                    <div className="breadcrumb" data-aos="fade-down">
                        <span onClick={() => navigate('/marketplace')}>Marketplace</span>
                        <FiChevronRight className="separator" />
                        <span className="current">{page.title}</span>
                    </div>

                    {/* Layout 2 cột */}
                    <div className="detail-grid-layout">

                        {/* === CỘT TRÁI: THÔNG TIN SẢN PHẨM === */}
                        <div className="product-info-column">
                            {/* Gallery */}
                            <div className="gallery-container" data-aos="fade-up">
                                <div className="main-image-wrapper">
                                    <img src={(page.screenshots && page.screenshots[currentImageIndex]) || page.main_screenshot || '/placeholder.png'} alt={page.title} className="main-image"/>
                                </div>
                                {page.screenshots && page.screenshots.length > 1 && (
                                    <div className="thumbnail-gallery">
                                        {page.screenshots.map((ss, index) => (
                                            <div key={index} className={`thumbnail ${index === currentImageIndex ? 'active' : ''}`} onClick={() => setCurrentImageIndex(index)}>
                                                <img src={ss} alt={`Thumbnail ${index + 1}`} />
                                            </div>
                                        ))}
                                    </div>
                                )}
                            </div>

                            {/* Download Section (nếu đã mua/là seller) */}
                            {(hasPurchased || isSeller) && (
                                <div className="content-section download-section" data-aos="fade-up">
                                    <h2 className="section-title"><FiDownload /> Tải xuống sản phẩm</h2>
                                    <p className="section-subtitle">{isSeller ? 'Bạn là người bán của sản phẩm này.' : 'Cảm ơn bạn đã mua sản phẩm! Chọn định dạng để tải về.'}</p>
                                    <div className="download-options">
                                        <button className="download-btn" onClick={() => handleDownload('html')} disabled={downloading}><FiPackage /> Tải .ZIP</button>
                                        <button className="download-btn" onClick={() => handleDownload('iuhpage')} disabled={downloading}><FiFileText /> Tải .iuhpage</button>
                                    </div>
                                </div>
                            )}

                            {/* Mô tả */}
                            <div className="content-section" data-aos="fade-up">
                                <h2 className="section-title"><FiFileText /> Mô tả chi tiết</h2>
                                <div className="prose-content" dangerouslySetInnerHTML={{ __html: page.description.replace(/\n/g, '<br />') }}></div>
                            </div>

                            {/* Tính năng */}
                            <div className="content-section" data-aos="fade-up">
                                <h2 className="section-title"><FiLayers /> Tính năng nổi bật</h2>
                                <div className="features-grid">
                                    {page.responsive && <div className="feature-item"><FiSmartphone /><span>Thiết kế Responsive</span></div>}
                                    {page.customizable && <div className="feature-item"><FiSliders /><span>Dễ dàng tùy chỉnh</span></div>}
                                    <div className="feature-item"><FiZap /><span>Tối ưu tốc độ</span></div>
                                    <div className="feature-item"><FiShield /><span>An toàn & Bảo mật</span></div>
                                </div>
                            </div>

                            {/* Tags */}
                            {page.tags && page.tags.length > 0 && (
                                <div className="content-section" data-aos="fade-up">
                                    <h2 className="section-title"><FiTag /> Thẻ Tags</h2>
                                    <div className="tags-container">
                                        {page.tags.map((tag, index) => <span key={index} className="tag-item">{tag}</span>)}
                                    </div>
                                </div>
                            )}
                        </div>

                        {/* === CỘT PHẢI: PANEL MUA HÀNG === */}
                        <div className="purchase-panel-column" data-aos="fade-left">
                            <div className="purchase-panel-sticky-wrapper">
                                <h1 className="product-title">{page.title}</h1>
                                <div className="product-meta-info">
                                    <span><FiUser /> <strong>{page.seller_id?.name || 'Anonymous'}</strong></span>
                                    <span><FiStar /> {page.rating.toFixed(1)} ({page.review_count} đánh giá)</span>
                                    <span><FiShoppingCart /> {page.sold_count} đã bán</span>
                                </div>

                                <div className="price-section">
                                    <div className="price-display">
                                        <span className="current-price">{formatPrice(page.price)}</span>
                                        {page.original_price > page.price && (
                                            <span className="original-price">{formatPrice(page.original_price)}</span>
                                        )}
                                    </div>
                                    {discount > 0 && <div className="discount-badge">GIẢM {discount}%</div>}
                                </div>

                                {!(hasPurchased || isSeller) && (
                                    <>
                                        {page.price > 0 && (
                                            <div className="payment-options-container">
                                                <h3 className="payment-title">Chọn phương thức thanh toán</h3>
                                                {paymentMethods.map(method => (
                                                    <div key={method.value} className={`payment-option ${selectedPaymentMethod === method.value ? 'selected' : ''}`} onClick={() => setSelectedPaymentMethod(method.value)}>
                                                        <img src={method.icon} alt={method.label} className="payment-icon"/>
                                                        <span className="payment-label">{method.label}</span>
                                                        {selectedPaymentMethod === method.value && <div className="radio-select"><FiCheckCircle /></div>}
                                                    </div>
                                                ))}
                                            </div>
                                        )}
                                        <button className="action-btn purchase-btn" onClick={handlePurchase} disabled={purchasing}>
                                            <FiShoppingCart />
                                            {purchasing ? 'Đang xử lý...' : (page.price > 0 ? 'Mua ngay' : 'Nhận miễn phí')}
                                        </button>
                                    </>
                                )}

                                {(hasPurchased || isSeller) && (
                                    <div className="purchased-notice">
                                        <FiCheckCircle/>
                                        <span>{isSeller ? "Đây là sản phẩm của bạn" : "Bạn đã sở hữu sản phẩm này"}</span>
                                    </div>
                                )}

                                <div className="secondary-actions">
                                    {page.demo_url && (
                                        <a href={page.demo_url} target="_blank" rel="noopener noreferrer" className="action-btn demo-btn">
                                            <FiExternalLink /> Xem Demo
                                        </a>
                                    )}
                                    <button className={`action-btn like-btn ${isLiked ? 'liked' : ''}`} onClick={handleLike}>
                                        <FiHeart /> {isLiked ? 'Đã thích' : 'Yêu thích'}
                                    </button>
                                </div>
                            </div>
                        </div>

                    </div>
                </div>
            </div>
        </div>
    );
};

export default MarketplaceDetail;
