import React, { useState, useCallback, useRef } from 'react';
import Canvas from './Canvas';
import ComponentLibrary from './ComponentLibrary';
import Toolbar from './Toolbar';
import { DndProvider } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
import '../styles/CreateLanding.css';

const CreateLanding = () => {
    const [pageData, setPageData] = useState({
        canvas: { width: 1200, background: '#ffffff' },
        elements: [],
    });
    const [selectedIds, setSelectedIds] = useState([]);
    const [selectedChildId, setSelectedChildId] = useState(null);
    const [viewMode, setViewMode] = useState('desktop');
    const [zoomLevel, setZoomLevel] = useState(100);
    const [gridSize, setGridSize] = useState(10);
    const [showGrid, setShowGrid] = useState(true);
    const [history, setHistory] = useState([pageData]);
    const [historyIndex, setHistoryIndex] = useState(0);
    const [isSidebarCollapsed, setIsSidebarCollapsed] = useState(false);

    const handleSelectElement = useCallback((ids, addToSelection = false) => {
        setSelectedIds((prev) => (addToSelection ? [...new Set([...prev, ...ids])] : ids));
    }, []);

    const handleSelectChild = useCallback((parentId, childId) => {
        setSelectedChildId(childId);
    }, []);

    const handleAddElement = useCallback((newElement) => {
        setPageData((prev) => {
            const newPageData = {
                ...prev,
                elements: [...prev.elements, newElement],
            };
            setHistory((prevHistory) => [...prevHistory.slice(0, historyIndex + 1), newPageData]);
            setHistoryIndex((prevIndex) => prevIndex + 1);
            return newPageData;
        });
    }, [historyIndex]);

    const handleAddChild = useCallback((parentId, newChild) => {
        setPageData((prev) => {
            const newPageData = {
                ...prev,
                elements: prev.elements.map((el) => {
                    if (el.id === parentId) {
                        return {
                            ...el,
                            children: [...(el.children || []), newChild],
                        };
                    }
                    return el;
                }),
            };
            setHistory((prevHistory) => [...prevHistory.slice(0, historyIndex + 1), newPageData]);
            setHistoryIndex((prevIndex) => prevIndex + 1);
            return newPageData;
        });
    }, [historyIndex]);

    const handleUpdatePosition = useCallback((id, newPosition, mode) => {
        setPageData((prev) => {
            const newPageData = {
                ...prev,
                elements: prev.elements.map((el) => {
                    if (el.id === id) {
                        return {
                            ...el,
                            position: mode === 'replace' ? newPosition : {
                                ...el.position,
                                [viewMode]: {
                                    ...el.position[viewMode],
                                    ...newPosition[viewMode],
                                },
                            },
                        };
                    }
                    return el;
                }),
            };
            setHistory((prevHistory) => [...prevHistory.slice(0, historyIndex + 1), newPageData]);
            setHistoryIndex((prevIndex) => prevIndex + 1);
            return newPageData;
        });
    }, [viewMode, historyIndex]);

    const handleUpdateChildren = useCallback((parentId, dragIndex, dropIndex) => {
        setPageData((prev) => {
            const newPageData = {
                ...prev,
                elements: prev.elements.map((el) => {
                    if (el.id === parentId) {
                        const newChildren = [...(el.children || [])];
                        if (dragIndex >= 0 && dragIndex < newChildren.length && dropIndex >= 0 && dropIndex < newChildren.length) {
                            const [movedChild] = newChildren.splice(dragIndex, 1);
                            newChildren.splice(dropIndex, 0, movedChild);
                            return { ...el, children: newChildren };
                        }
                    }
                    return el;
                }),
            };
            setHistory((prevHistory) => [...prevHistory.slice(0, historyIndex + 1), newPageData]);
            setHistoryIndex((prevIndex) => prevIndex + 1);
            return newPageData;
        });
    }, [historyIndex]);

    const handleDeleteElement = useCallback((id) => {
        setPageData((prev) => {
            const newPageData = {
                ...prev,
                elements: prev.elements.filter((el) => el.id !== id),
            };
            setHistory((prevHistory) => [...prevHistory.slice(0, historyIndex + 1), newPageData]);
            setHistoryIndex((prevIndex) => prevIndex + 1);
            return newPageData;
        });
        setSelectedIds((prev) => prev.filter((selectedId) => selectedId !== id));
    }, [historyIndex]);

    const handleUpdateSize = useCallback((id, newSize) => {
        setPageData((prev) => {
            const newPageData = {
                ...prev,
                elements: prev.elements.map((el) => {
                    if (el.id === id) {
                        return { ...el, size: newSize };
                    }
                    return el;
                }),
            };
            setHistory((prevHistory) => [...prevHistory.slice(0, historyIndex + 1), newPageData]);
            setHistoryIndex((prevIndex) => prevIndex + 1);
            return newPageData;
        });
    }, [historyIndex]);

    const handleGroupElements = useCallback((ids) => {
        // Implement grouping logic if needed
    }, []);

    const handleEditElement = useCallback((id) => {
        // Implement edit logic
    }, []);

    const handleDuplicateElement = useCallback((id) => {
        // Implement duplicate logic
    }, []);

    const handleMoveElementUp = useCallback((id) => {
        // Implement move up logic
    }, []);

    const handleMoveElementDown = useCallback((id) => {
        // Implement move down logic
    }, []);

    return (
        <DndProvider backend={HTML5Backend}>
            <div className="lpb-create-landing">
                <ComponentLibrary
                    isCollapsed={isSidebarCollapsed}
                    onToggle={() => setIsSidebarCollapsed(!isSidebarCollapsed)}
                    onAddElement={handleAddElement}
                    onAddChild={handleAddChild}
                    pageData={pageData}
                />
                <div className="lpb-main-content">
                    <Toolbar
                        viewMode={viewMode}
                        setViewMode={setViewMode}
                        zoomLevel={zoomLevel}
                        setZoomLevel={setZoomLevel}
                        gridSize={gridSize}
                        setGridSize={setGridSize}
                        showGrid={showGrid}
                        setShowGrid={setShowGrid}
                    />
                    <Canvas
                        pageData={pageData}
                        selectedIds={selectedIds}
                        onSelectElement={handleSelectElement}
                        onUpdatePosition={handleUpdatePosition}
                        onUpdateSize={handleUpdateSize}
                        onDeleteElement={handleDeleteElement}
                        onAddElement={handleAddElement}
                        onGroupElements={handleGroupElements}
                        viewMode={viewMode}
                        zoomLevel={zoomLevel}
                        gridSize={gridSize}
                        showGrid={showGrid}
                        onEditElement={handleEditElement}
                        onDuplicateElement={handleDuplicateElement}
                        onMoveElementUp={handleMoveElementUp}
                        onMoveElementDown={handleMoveElementDown}
                        onSelectChild={handleSelectChild}
                        selectedChildId={selectedChildId}
                        onUpdateChildren={handleUpdateChildren}
                        onAddChild={handleAddChild}
                    />
                </div>
            </div>
        </DndProvider>
    );
};

export default CreateLanding;
