const sectionComponents = [
    {
        id: 'hero-section',
        name: 'Hero Section',
        icon: 'fas fa-banner',
        category: 'sections',
        json: {
            type: 'section',
            layout: 'hero',
            background: {
                type: 'gradient',
                value: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)'
            },
            content: {
                title: 'Tiêu đề chính của bạn',
                subtitle: 'Mô tả ngắn gọn về sản phẩm dịch vụ',
                buttons: [
                    { text: 'Bắt đầu ngay', type: 'primary' },
                    { text: 'Tìm hiểu thêm', type: 'secondary' }
                ]
            },
            styles: {
                minHeight: '100vh',
                padding: '80px 20px',
                textAlign: 'center',
                color: 'white'
            }
        }
    },
    {
        id: 'feature-grid',
        name: 'Lưới tính năng',
        icon: 'fas fa-th',
        category: 'sections',
        json: {
            type: 'section',
            layout: 'feature-grid',
            content: {
                title: 'Tính năng nổi bật',
                subtitle: 'Những điều tuyệt vời chúng tôi mang lại',
                features: [
                    {
                        icon: 'fas fa-rocket',
                        title: 'Tốc độ cao',
                        description: 'Hiệu suất tối ưu cho trải nghiệm tốt nhất'
                    },
                    {
                        icon: 'fas fa-shield-alt',
                        title: 'Bảo mật',
                        description: 'An toàn tuyệt đối cho dữ liệu của bạn'
                    },
                    {
                        icon: 'fas fa-cogs',
                        title: 'Tùy chỉnh',
                        description: 'Linh hoạt theo nhu cầu riêng'
                    }
                ]
            },
            styles: {
                padding: '80px 20px',
                backgroundColor: '#f8fafc'
            }
        }
    }
];

export default sectionComponents;
