import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';
import api from '@landinghub/api';

const CreateLanding = () => {
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (!token) {
            console.error('No token found, redirecting to /auth');
            navigate('/auth');
            return;
        }

        try {
            const decoded = jwtDecode(token);
            console.log('CreateLanding Decoded JWT:', decoded); // Debug
            if (!decoded.userId) {
                throw new Error('Invalid token: userId not found');
            }
            api.defaults.headers.common['Authorization'] = `Bearer ${token}`;
        } catch (err) {
            console.error('Error decoding token:', err);
            navigate('/auth');
        }
    }, [navigate]);

    return (
        <div>
            <h1>Create Landing Page</h1>
            {error && (
                <div className="error">
                    {error}
                    <button onClick={() => setError(null)}>Đóng</button>
                </div>
            )}
            <p>This is the Create Landing page. Implement your editor or form here.</p>
            <button onClick={() => navigate('/pages')}>Back to Pages</button>
        </div>
    );
};

export default CreateLanding;
