import React, { useRef, useMemo, useCallback, useState } from 'react';
import { useDrop, useDrag } from 'react-dnd';
import { useHotkeys } from 'react-hotkeys-hook';
import { toast } from 'react-toastify';
import QuickEditToolkit from './QuickEditToolkit';

const ItemTypes = {
    COMPONENT: 'component',
    EXISTING_ELEMENT: 'existingElement',
};

// Memoized component for rendering individual elements with error handling
const Element = React.memo(
    ({
         component,
         index,
         isSelected,
         hasQuickEdit,
         onSelectElement,
         handleElementHover,
         onDuplicateElement,
         onDeleteElement,
         onMoveElement,
     }) => {
        const [{ isDragging }, drag] = useDrag(() => ({
            type: ItemTypes.EXISTING_ELEMENT,
            item: { id: component.id },
            collect: (monitor) => ({
                isDragging: !!monitor.isDragging(),
            }),
        }));

        const content = useMemo(() => {
            // Safeguard against undefined json
            if (!component.json) {
                console.warn('Invalid component structure:', component);
                return <div className="text-red-500 text-xs">Component lỗi: Thiếu json</div>;
            }

            switch (component.json.type) {
                case 'heading':
                    const HeadingTag = component.json.level === 2 ? 'h2' : 'h1';
                    return <HeadingTag>{component.json.content || 'Tiêu đề'}</HeadingTag>;
                case 'paragraph':
                    return <p>{component.json.content || 'Đoạn văn'}</p>;
                case 'button':
                    return <button style={component.json.styles || {}}>{component.json.content || 'Nút'}</button>;
                case 'section':
                    return (
                        <section
                            style={{
                                ...component.json.styles,
                                background: component.json.background?.value,
                            }}
                        >
                            {component.json.layout === 'hero' && (
                                <div className="container mx-auto">
                                    <h1 className="text-4xl font-bold mb-4 text-white">
                                        {component.json.content?.title || 'Welcome'}
                                    </h1>
                                    <p className="text-xl mb-8 text-white">{component.json.content?.subtitle || 'Subtitle'}</p>
                                    <div className="flex gap-4 justify-center">
                                        {component.json.content?.buttons?.map((btn, idx) => (
                                            <button
                                                key={idx}
                                                className={`px-6 py-3 rounded-lg font-semibold ${
                                                    btn.type === 'primary'
                                                        ? 'bg-white text-blue-600'
                                                        : 'border-2 border-white text-white bg-transparent'
                                                }`}
                                            >
                                                {btn.text || 'Nút'}
                                            </button>
                                        )) || null}
                                    </div>
                                </div>
                            )}
                            {isSelected && (
                                <div className="absolute top-2 left-2 bg-gradient-to-r from-blue-500 to-blue-600 text-white text-xs px-2 py-1 rounded-lg">
                                    Hero Section
                                </div>
                            )}
                        </section>
                    );
                default:
                    return <div className="text-gray-500 text-xs">Loại không hỗ trợ</div>;
            }
        }, [component, isSelected]);

        if (component.json?.type === 'section') {
            return content;
        }

        return (
            <div
                ref={drag}
                className={`relative transition-all duration-300 ${
                    isSelected ? 'ring-4 ring-blue-500 ring-opacity-50' : ''
                } ${hasQuickEdit ? 'shadow-lg hover:shadow-xl' : ''}`}
                onClick={() => onSelectElement(component)}
                onMouseEnter={(e) => handleElementHover(e, component)}
                style={{ ...component.json?.styles, opacity: isDragging ? 0.5 : 1 }}
            >
                {content}
                {isSelected && (
                    <div className="absolute -top-6 left-0 bg-gradient-to-r from-blue-500 to-blue-600 text-white text-xs px-2 py-1 rounded-lg">
                        {component.json?.type.charAt(0).toUpperCase() + component.json?.type.slice(1) || 'Không xác định'}
                    </div>
                )}
            </div>
        );
    }
);

const Canvas = React.memo(
    ({
         pageStructure,
         selectedElement,
         onSelectElement,
         quickEditElement,
         quickEditPosition,
         onQuickEdit,
         onCloseQuickEdit,
         onDuplicateElement,
         onDeleteElement,
         onMoveElement,
         viewMode,
         addComponent,
         moveElement,
         zoomLevel,
         setPageStructure, // Added to support history
     }) => {
        const canvasRef = useRef(null);
        const [dropIndex, setDropIndex] = useState(null);
        const [history, setHistory] = useState([]);
        const [historyIndex, setHistoryIndex] = useState(-1);

        // Make the entire canvas a drop target
        const [{ isOver }, drop] = useDrop(() => ({
            accept: [ItemTypes.COMPONENT, ItemTypes.EXISTING_ELEMENT],
            hover: (item, monitor) => {
                const clientOffset = monitor.getClientOffset();
                const rect = canvasRef.current.getBoundingClientRect();
                const y = clientOffset.y - rect.top;
                const index = calculateDropIndex(y);
                setDropIndex(index);
            },
            drop: (item, monitor) => {
                const clientOffset = monitor.getClientOffset();
                const rect = canvasRef.current.getBoundingClientRect();
                const position = {
                    x: clientOffset.x - rect.left,
                    y: clientOffset.y - rect.top,
                };

                setHistory((prev) => {
                    const newHistory = prev.slice(0, historyIndex + 1);
                    newHistory.push([...pageStructure]);
                    return newHistory;
                });
                setHistoryIndex((prev) => prev + 1);

                if (item.componentData) {
                    const index = calculateDropIndex(position.y);
                    addComponent(item.componentData, index);
                    toast.success('Đã thêm thành phần mới!');
                } else {
                    const newIndex = calculateDropIndex(position.y);
                    moveElement(item.id, newIndex);
                    toast.info('Đã di chuyển thành phần!');
                }
                setDropIndex(null);
            },
            collect: (monitor) => ({
                isOver: !!monitor.isOver(),
            }),
        }));

        // Calculate drop index based on y-position
        const calculateDropIndex = useCallback(
            (y) => {
                const elements = canvasRef.current.querySelectorAll('.canvas-element');
                let index = pageStructure.length;

                elements.forEach((el, i) => {
                    const rect = el.getBoundingClientRect();
                    const canvasRect = canvasRef.current.getBoundingClientRect();
                    const elTop = rect.top - canvasRect.top;
                    const elHeight = rect.height;

                    if (y < elTop + elHeight / 2) {
                        index = i;
                        return false;
                    }
                });

                return index;
            },
            [pageStructure]
        );

        // Handle element hover for quick edit
        const handleElementHover = useCallback(
            (e, component) => {
                if (quickEditElement?.id === component.id) return;

                const rect = e.currentTarget.getBoundingClientRect();
                const canvasRect = canvasRef.current.getBoundingClientRect();
                onQuickEdit(component, {
                    x: rect.left - canvasRect.left,
                    y: rect.top - canvasRect.top + 20,
                });
            },
            [quickEditElement, onQuickEdit]
        );

        // Keyboard shortcuts
        useHotkeys(
            'delete',
            () => {
                if (selectedElement) {
                    setHistory((prev) => {
                        const newHistory = prev.slice(0, historyIndex + 1);
                        newHistory.push([...pageStructure]);
                        return newHistory;
                    });
                    setHistoryIndex((prev) => prev + 1);
                    onDeleteElement(selectedElement);
                }
            },
            [selectedElement, onDeleteElement]
        );
        useHotkeys(
            'up',
            () => {
                if (selectedElement) {
                    onMoveElement(selectedElement, 'up');
                }
            },
            [selectedElement, onMoveElement]
        );
        useHotkeys(
            'down',
            () => {
                if (selectedElement) {
                    onMoveElement(selectedElement, 'down');
                }
            },
            [selectedElement, onMoveElement]
        );
        useHotkeys(
            'ctrl+z',
            () => {
                if (historyIndex > 0) {
                    setHistoryIndex((prev) => prev - 1);
                    setPageStructure([...history[historyIndex - 1]]);
                    toast.info('Đã hoàn tác!');
                }
            },
            [historyIndex, history, setPageStructure]
        );
        useHotkeys(
            'ctrl+y',
            () => {
                if (historyIndex < history.length - 1) {
                    setHistoryIndex((prev) => prev + 1);
                    setPageStructure([...history[historyIndex + 1]]);
                    toast.info('Đã khôi phục!');
                }
            },
            [historyIndex, history, setPageStructure]
        );

        const getCanvasClasses = useCallback(() => {
            const baseClasses = "bg-white overflow-auto transition-all duration-300 rounded-xl shadow-lg";
            switch (viewMode) {
                case 'mobile':
                    return `${baseClasses} max-w-sm mx-auto border-4 border-gray-800 rounded-2xl p-4`;
                case 'tablet':
                    return `${baseClasses} max-w-2xl mx-auto border-4 border-gray-800 rounded-xl p-4`;
                default:
                    return `${baseClasses} w-full`;
            }
        }, [viewMode]);

        return (
            <div className="flex-1 bg-gray-50 p-4">
                <div
                    ref={(node) => {
                        canvasRef.current = node;
                        drop(node);
                    }}
                    className={getCanvasClasses()}
                    onMouseLeave={onCloseQuickEdit}
                    style={{
                        transform: `scale(${zoomLevel / 100})`,
                        transformOrigin: 'top left',
                        backgroundColor: isOver ? 'rgba(230, 243, 255, 0.8)' : 'white',
                        boxShadow: isOver ? '0 4px 12px rgba(0, 112, 243, 0.2)' : 'none',
                    }}
                >
                    <div className="min-h-full relative">
                        {pageStructure.length === 0 ? (
                            <div className="h-full flex items-center justify-center text-gray-500 min-h-96" data-tooltip-id="canvas-tooltip" data-tooltip-content="Kéo thả component từ thư viện vào đây">
                                <div className="text-center">
                                    <i className="fas fa-mouse-pointer text-6xl mb-4"></i>
                                    <p className="text-xl">Kéo thả component để bắt đầu</p>
                                </div>
                            </div>
                        ) : (
                            <>
                                {pageStructure.map((component, index) => (
                                    <React.Fragment key={component.id || index}>
                                        {dropIndex === index && (
                                            <div className="h-2 bg-blue-200 border-dashed border-2 border-blue-500 my-1 rounded" />
                                        )}
                                        <div className="canvas-element">
                                            <Element
                                                component={component}
                                                index={index}
                                                isSelected={selectedElement?.id === component.id}
                                                hasQuickEdit={quickEditElement?.id === component.id}
                                                onSelectElement={onSelectElement}
                                                handleElementHover={handleElementHover}
                                                onDuplicateElement={onDuplicateElement}
                                                onDeleteElement={onDeleteElement}
                                                onMoveElement={onMoveElement}
                                            />
                                        </div>
                                    </React.Fragment>
                                ))}
                                {dropIndex === pageStructure.length && (
                                    <div className="h-2 bg-blue-200 border-dashed border-2 border-blue-500 my-1 rounded" />
                                )}
                            </>
                        )}
                    </div>

                    <QuickEditToolkit
                        element={quickEditElement}
                        position={quickEditPosition}
                        onEdit={() => onSelectElement(quickEditElement)}
                        onDuplicate={() => onDuplicateElement(quickEditElement)}
                        onDelete={() => onDeleteElement(quickEditElement)}
                        onMoveUp={() => onMoveElement(quickEditElement, 'up')}
                        onMoveDown={() => onMoveElement(quickEditElement, 'down')}
                        onClose={onCloseQuickEdit}
                        className="bg-white shadow-lg rounded-lg p-2 hover:shadow-xl transition-all duration-200"
                    />
                </div>
            </div>
        );
    }
);

export default Canvas;
