import React, { useState } from "react";
import { MessageCircle, X } from "lucide-react";

const ChatLD = () => {
    const [open, setOpen] = useState(false);

    return (
        <>
            {!open && (
                <button
                    onClick={() => setOpen(true)}
                    style={{
                        position: "fixed",
                        bottom: "24px",
                        right: "24px",
                        width: "56px",
                        height: "56px",
                        borderRadius: "50%",
                        backgroundColor: "#2563eb",
                        color: "#fff",
                        border: "none",
                        boxShadow: "0 4px 10px rgba(0,0,0,0.3)",
                        cursor: "pointer",
                        zIndex: 10000,
                    }}
                >
                    <MessageCircle size={28} />
                </button>
            )}

            {open && (
                <div
                    style={{
                        position: "fixed",
                        bottom: "24px",
                        right: "24px",
                        width: "400px",
                        height: "550px",
                        backgroundColor: "#fff",
                        borderRadius: "16px",
                        boxShadow: "0 8px 24px rgba(0,0,0,0.2)",
                        overflow: "hidden",
                        display: "flex",
                        flexDirection: "column",
                        zIndex: 10000,
                    }}
                >
                    <div
                        style={{
                            padding: "12px 16px",
                            backgroundColor: "#2563eb",
                            color: "white",
                            fontWeight: "bold",
                            display: "flex",
                            justifyContent: "space-between",
                            alignItems: "center",
                        }}
                    >
                        <span>LD Assistant ðŸ¤–</span>
                        <X size={22} onClick={() => setOpen(false)} style={{ cursor: "pointer" }} />
                    </div>

                    <iframe
                        src={`https://www.coze.com/chatbot/${process.env.REACT_APP_COZE_BOT_ID}`}
                        style={{ flexGrow: 1, border: "none" }}
                        title="LD Chat"
                    ></iframe>
                </div>
            )}
        </>
    );
};

export default ChatLD;
