import React, { useState, useMemo, useCallback } from 'react';
import { useDrag } from 'react-dnd';
import { useHotkeys } from 'react-hotkeys-hook';
import { toast } from 'react-toastify';
import COMPONENT_LIBRARY from '../../constants/componentLibrary';

const ItemTypes = {
    COMPONENT: 'component',
};

const DraggableComponent = React.memo(({ component }) => {
    const [{ isDragging }, drag] = useDrag(() => ({
        type: ItemTypes.COMPONENT,
        item: { componentData: component },
        collect: (monitor) => ({
            isDragging: !!monitor.isDragging(),
        }),
    }));

    return (
        <div
            ref={drag}
            className="p-3 mb-2 border border-gray-200 rounded-lg cursor-move hover:border-blue-300 hover:shadow-sm hover:bg-blue-50 transition-all duration-200"
            style={{ opacity: isDragging ? 0.5 : 1 }}
            onMouseDown={() => toast.info(`Bắt đầu kéo ${component.name}`)}
            data-tooltip-id="component-tooltip"
            data-tooltip-content={component.description || `Kéo để thêm ${component.name}`}
        >
            <div className="flex items-center gap-3">
                <i className={`${component.icon} text-gray-500`}></i>
                <div>
                    <div className="font-medium text-sm text-gray-800">{component.name}</div>
                    <div className="text-xs text-gray-500">Kéo để thêm</div>
                </div>
            </div>
        </div>
    );
});

const ComponentLibrary = React.memo(({ isCollapsed, onToggle }) => {
    const [activeCategory, setActiveCategory] = useState('text');
    const [searchQuery, setSearchQuery] = useState('');

    const categories = [
        { id: 'text', name: 'Văn bản', icon: 'fas fa-font' },
        { id: 'buttons', name: 'Nút bấm', icon: 'fas fa-mouse-pointer' },
        { id: 'sections', name: 'Section', icon: 'fas fa-layer-group' },
        { id: 'media', name: 'Media', icon: 'fas fa-image' },
        { id: 'forms', name: 'Form', icon: 'fas fa-wpforms' },
    ];

    // Filter components based on search query
    const filteredComponents = useMemo(() => {
        if (!searchQuery) return COMPONENT_LIBRARY[activeCategory] || [];
        return (COMPONENT_LIBRARY[activeCategory] || []).filter((component) =>
            component.name.toLowerCase().includes(searchQuery.toLowerCase())
        );
    }, [activeCategory, searchQuery]);

    // Handle category change
    const handleCategoryChange = useCallback((categoryId) => {
        setActiveCategory(categoryId);
        setSearchQuery(''); // Reset search when changing category
        toast.info(`Đã chuyển sang danh mục ${categories.find((cat) => cat.id === categoryId)?.name}`);
    }, []);

    // Keyboard shortcuts for categories
    categories.forEach((cat, index) => {
        useHotkeys(`ctrl+${index + 1}`, () => handleCategoryChange(cat.id), [handleCategoryChange]);
    });

    if (isCollapsed) {
        return (
            <div className="w-12 bg-white border-r border-gray-200 h-full flex flex-col items-center py-2 transition-all duration-300">
                <button
                    onClick={onToggle}
                    className="p-2 hover:bg-blue-50 rounded mb-4 text-gray-600 hover:text-blue-600"
                    data-tooltip-id="component-tooltip"
                    data-tooltip-content="Mở thư viện thành phần"
                >
                    <i className="fas fa-chevron-right"></i>
                </button>
                {categories.map((cat) => (
                    <button
                        key={cat.id}
                        className="p-2 hover:bg-blue-50 rounded mb-1 text-gray-600 hover:text-blue-600"
                        data-tooltip-id="component-tooltip"
                        data-tooltip-content={cat.name}
                        onClick={() => {
                            handleCategoryChange(cat.id);
                            onToggle(); // Auto-expand when selecting category
                        }}
                    >
                        <i className={cat.icon}></i>
                    </button>
                ))}
            </div>
        );
    }

    return (
        <div className="w-64 bg-white border-r border-gray-200 h-full overflow-y-auto transition-all duration-300">
            <div className="p-4 border-b flex items-center justify-between">
                <h2 className="font-semibold text-gray-800">Thư viện thành phần</h2>
                <button
                    onClick={onToggle}
                    className="p-1 hover:bg-blue-50 rounded text-gray-600 hover:text-blue-600"
                    data-tooltip-id="component-tooltip"
                    data-tooltip-content="Thu gọn thư viện"
                >
                    <i className="fas fa-chevron-left"></i>
                </button>
            </div>

            <div className="p-2 border-b">
                <input
                    type="text"
                    placeholder="Tìm kiếm thành phần..."
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    className="w-full p-2 border border-gray-200 rounded-lg text-sm focus:outline-none focus:border-blue-500"
                />
            </div>

            <div className="border-b">
                {categories.map((cat) => (
                    <button
                        key={cat.id}
                        onClick={() => handleCategoryChange(cat.id)}
                        className={`w-full p-3 text-left flex items-center gap-2 hover:bg-blue-50 transition-all duration-200 ${
                            activeCategory === cat.id
                                ? 'bg-blue-50 border-r-2 border-blue-500 text-blue-600'
                                : 'text-gray-600'
                        }`}
                        data-tooltip-id="component-tooltip"
                        data-tooltip-content={`Danh mục ${cat.name}`}
                    >
                        <i className={cat.icon}></i>
                        <span className="text-sm font-medium">{cat.name}</span>
                    </button>
                ))}
            </div>

            <div className="p-2">
                {filteredComponents.length === 0 ? (
                    <div className="text-center text-gray-500 text-sm py-4">
                        Không tìm thấy thành phần
                    </div>
                ) : (
                    filteredComponents.map((component) => (
                        <DraggableComponent key={component.id} component={component} />
                    ))
                )}
            </div>
        </div>
    );
});

export default ComponentLibrary;
