import React, { useState } from 'react';
import {
    ChevronRight, ChevronLeft, MousePointer,
    Palette, Type, Layout, Database, Zap,
    Image as ImageIcon, Upload, Settings
} from 'lucide-react';
import '../../styles/PropertiesPanel.css';

const COLOR_PRESETS = {
    '--primary': '#667eea',
    '--primary-dark': '#5568d3',
    '--primary-light': '#a5b4fc',
    '--secondary': '#f1f5f9',
    '--accent': '#764ba2',
    '--success': '#10b981',
    '--warning': '#f59e0b',
    '--danger': '#ef4444',
    '--dark': '#1e293b',
    '--light': '#f8fafc',
    '--white': '#ffffff',
    '--text-primary': '#334155',
    '--text-secondary': '#64748b',
    '--text-light': '#94a3b8',
    '--text-inverse': '#ffffff',
};

const PropertiesPanel = ({ selectedElement, onUpdateElement, onUpdateCanvas, isCollapsed, onToggle, pageData }) => {
    const [activeTab, setActiveTab] = useState('content');
    const [activeEventType, setActiveEventType] = useState('onClick');
    const [imagePreview, setImagePreview] = useState(null);

    // Collapsed state
    if (isCollapsed) {
        return (
            <div className="properties-panel-collapsed">
                <button onClick={onToggle} className="toggle-button" title="Mở thuộc tính">
                    <ChevronLeft size={18} />
                </button>
            </div>
        );
    }

    // Empty state - chưa chọn element
    if (!selectedElement || !selectedElement.json) {
        return (
            <div className="properties-panel">
                <div className="panel-header">
                    <h3 className="panel-title">Thuộc tính</h3>
                    <button onClick={onToggle} className="toggle-button" title="Đóng">
                        <ChevronRight size={18} />
                    </button>
                </div>
                <div className="panel-empty">
                    <MousePointer className="empty-icon" size={48} strokeWidth={1.5} />
                    <p className="empty-text">Chọn một element để chỉnh sửa</p>
                </div>
            </div>
        );
    }

    // Event handlers
    const handleStyleChange = (property, value) => {
        const updated = {
            ...selectedElement,
            json: {
                ...selectedElement.json,
                styles: {
                    ...selectedElement.json.styles,
                    [property]: value,
                },
            },
        };
        onUpdateElement(updated);
    };

    const handleComponentDataChange = (key, value) => {
        const updated = {
            ...selectedElement,
            json: {
                ...selectedElement.json,
                componentData: {
                    ...selectedElement.json.componentData,
                    [key]: value,
                },
            },
        };
        onUpdateElement(updated);
    };

    const handleImageUpload = (e) => {
        const file = e.target.files[0];
        if (file) {
            const reader = new FileReader();
            reader.onloadend = () => {
                setImagePreview(reader.result);
                handleComponentDataChange('src', reader.result);
            };
            reader.readAsDataURL(file);
        }
    };

    const handleEventChange = (eventType, eventProperty, value) => {
        const updated = {
            ...selectedElement,
            json: {
                ...selectedElement.json,
                componentData: {
                    ...selectedElement.json.componentData,
                    events: {
                        ...selectedElement.json.componentData?.events,
                        [eventType]: {
                            ...selectedElement.json.componentData?.events?.[eventType],
                            [eventProperty]: value,
                        },
                    },
                },
            },
        };
        onUpdateElement(updated);
    };

    const handleDataSourceChange = (property, value) => {
        const updated = {
            ...selectedElement,
            json: {
                ...selectedElement.json,
                componentData: {
                    ...selectedElement.json.componentData,
                    dataSource: {
                        ...selectedElement.json.componentData?.dataSource,
                        [property]: value,
                    },
                },
            },
        };
        onUpdateElement(updated);
    };

    // Render content tab
    const renderContentTab = () => (
        <>
            {/* Canvas Settings */}
            {!selectedElement.isChild && (
                <div className="panel-section">
                    <h4 className="section-title">
                        <Palette size={16} strokeWidth={1.5} />
                        Canvas
                    </h4>
                    <div className="input-group">
                        <div>
                            <label className="input-label">Màu nền</label>
                            <input
                                type="color"
                                value={pageData.canvas?.background || '#ffffff'}
                                onChange={(e) => onUpdateCanvas({ background: e.target.value })}
                                className="input-color"
                            />
                            <div className="color-preset-grid">
                                {Object.entries(COLOR_PRESETS).map(([key, value]) => (
                                    <div
                                        key={key}
                                        className={`color-preset-item ${pageData.canvas?.background === value ? 'active' : ''}`}
                                        style={{ backgroundColor: value }}
                                        onClick={() => onUpdateCanvas({ background: value })}
                                        title={key}
                                    />
                                ))}
                            </div>
                        </div>
                    </div>
                </div>
            )}

            {/* Text Content */}
            {(selectedElement.json.type === 'heading' ||
                selectedElement.json.type === 'paragraph' ||
                selectedElement.json.type === 'button') && (
                <div className="panel-section">
                    <h4 className="section-title">
                        <Type size={16} strokeWidth={1.5} />
                        Nội dung
                    </h4>
                    <div className="input-group">
                        <div>
                            <textarea
                                value={selectedElement.json.componentData?.content || ''}
                                onChange={(e) => handleComponentDataChange('content', e.target.value)}
                                className="input-textarea"
                                rows={3}
                                placeholder="Nhập nội dung..."
                            />
                        </div>
                    </div>
                </div>
            )}

            {/* Image Upload */}
            {selectedElement.json.type === 'image' && (
                <div className="panel-section">
                    <h4 className="section-title">
                        <ImageIcon size={16} strokeWidth={1.5} />
                        Hình ảnh
                    </h4>
                    <div className="input-group">
                        <div>
                            <label className="input-label">Tải ảnh lên</label>
                            <input
                                type="file"
                                accept="image/*"
                                onChange={handleImageUpload}
                                style={{ display: 'none' }}
                                id="image-upload"
                            />
                            <label htmlFor="image-upload" className="upload-button">
                                <Upload size={16} strokeWidth={1.5} />
                                Chọn ảnh
                            </label>
                            {(imagePreview || selectedElement.json.componentData?.src) && (
                                <div className="image-preview">
                                    <img
                                        src={imagePreview || selectedElement.json.componentData?.src}
                                        alt="Preview"
                                    />
                                </div>
                            )}
                        </div>
                        <div>
                            <label className="input-label">URL</label>
                            <input
                                type="text"
                                value={selectedElement.json.componentData?.src || ''}
                                onChange={(e) => handleComponentDataChange('src', e.target.value)}
                                className="input-field"
                                placeholder="https://example.com/image.jpg"
                            />
                        </div>
                        <div>
                            <label className="input-label">Alt Text</label>
                            <input
                                type="text"
                                value={selectedElement.json.componentData?.alt || ''}
                                onChange={(e) => handleComponentDataChange('alt', e.target.value)}
                                className="input-field"
                                placeholder="Mô tả hình ảnh"
                            />
                        </div>
                    </div>
                </div>
            )}
        </>
    );

    // Render style tab
    const renderStyleTab = () => (
        <>
            {/* Typography */}
            {(selectedElement.json.type === 'heading' ||
                selectedElement.json.type === 'paragraph' ||
                selectedElement.json.type === 'button') && (
                <div className="panel-section">
                    <h4 className="section-title">
                        <Type size={16} strokeWidth={1.5} />
                        Typography
                    </h4>
                    <div className="input-group">
                        <div>
                            <label className="input-label">Kích thước</label>
                            <input
                                type="text"
                                value={selectedElement.json.styles?.fontSize || '16px'}
                                onChange={(e) => handleStyleChange('fontSize', e.target.value)}
                                className="input-field"
                                placeholder="16px"
                            />
                        </div>
                        <div>
                            <label className="input-label">Màu chữ</label>
                            <input
                                type="color"
                                value={selectedElement.json.styles?.color || '#000000'}
                                onChange={(e) => handleStyleChange('color', e.target.value)}
                                className="input-color"
                            />
                            <div className="color-preset-grid">
                                {Object.entries(COLOR_PRESETS).map(([key, value]) => (
                                    <div
                                        key={key}
                                        className={`color-preset-item ${selectedElement.json.styles?.color === value ? 'active' : ''}`}
                                        style={{ backgroundColor: value }}
                                        onClick={() => handleStyleChange('color', value)}
                                        title={key}
                                    />
                                ))}
                            </div>
                        </div>
                    </div>
                </div>
            )}

            {/* Appearance */}
            <div className="panel-section">
                <h4 className="section-title">
                    <Palette size={16} strokeWidth={1.5} />
                    Giao diện
                </h4>
                <div className="input-group">
                    <div>
                        <label className="input-label">Màu nền</label>
                        <input
                            type="color"
                            value={selectedElement.json.styles?.backgroundColor || '#ffffff'}
                            onChange={(e) => handleStyleChange('backgroundColor', e.target.value)}
                            className="input-color"
                        />
                        <div className="color-preset-grid">
                            {Object.entries(COLOR_PRESETS).map(([key, value]) => (
                                <div
                                    key={key}
                                    className={`color-preset-item ${selectedElement.json.styles?.backgroundColor === value ? 'active' : ''}`}
                                    style={{ backgroundColor: value }}
                                    onClick={() => handleStyleChange('backgroundColor', value)}
                                    title={key}
                                />
                            ))}
                        </div>
                    </div>
                    <div>
                        <label className="input-label">Padding</label>
                        <input
                            type="text"
                            value={selectedElement.json.styles?.padding || '0'}
                            onChange={(e) => handleStyleChange('padding', e.target.value)}
                            className="input-field"
                            placeholder="10px"
                        />
                    </div>
                    <div>
                        <label className="input-label">Border Radius</label>
                        <input
                            type="text"
                            value={selectedElement.json.styles?.borderRadius || '0'}
                            onChange={(e) => handleStyleChange('borderRadius', e.target.value)}
                            className="input-field"
                            placeholder="8px"
                        />
                    </div>
                </div>
            </div>
        </>
    );

    // Render data tab
    const renderDataTab = () => (
        <div className="panel-section">
            <h4 className="section-title">
                <Database size={16} strokeWidth={1.5} />
                Nguồn dữ liệu
            </h4>
            <div className="input-group">
                <div>
                    <label className="input-label">Loại nguồn</label>
                    <select
                        value={selectedElement.json.componentData?.dataSource?.type || 'static'}
                        onChange={(e) => handleDataSourceChange('type', e.target.value)}
                        className="input-select"
                    >
                        <option value="static">Static</option>
                        <option value="api">API</option>
                        <option value="state">State</option>
                    </select>
                </div>
                {selectedElement.json.componentData?.dataSource?.type === 'api' && (
                    <>
                        <div>
                            <label className="input-label">API URL</label>
                            <input
                                type="text"
                                value={selectedElement.json.componentData?.dataSource?.url || ''}
                                onChange={(e) => handleDataSourceChange('url', e.target.value)}
                                className="input-field"
                                placeholder="https://api.example.com/data"
                            />
                        </div>
                        <div>
                            <label className="input-label">Data Key</label>
                            <input
                                type="text"
                                value={selectedElement.json.componentData?.dataSource?.key || ''}
                                onChange={(e) => handleDataSourceChange('key', e.target.value)}
                                className="input-field"
                                placeholder="data.items"
                            />
                        </div>
                    </>
                )}
                {selectedElement.json.componentData?.dataSource?.type === 'state' && (
                    <div>
                        <label className="input-label">State Key</label>
                        <input
                            type="text"
                            value={selectedElement.json.componentData?.dataSource?.stateKey || ''}
                            onChange={(e) => handleDataSourceChange('stateKey', e.target.value)}
                            className="input-field"
                            placeholder="userInfo"
                        />
                    </div>
                )}
            </div>
        </div>
    );

    // Render events tab
    const renderEventsTab = () => (
        <div className="panel-section">
            <h4 className="section-title">
                <Zap size={16} strokeWidth={1.5} />
                Sự kiện
            </h4>
            <div className="event-type-selector">
                {['onClick', 'onHover', 'onLoad'].map((type) => (
                    <button
                        key={type}
                        className={`event-type-btn ${activeEventType === type ? 'active' : ''}`}
                        onClick={() => setActiveEventType(type)}
                    >
                        {type}
                    </button>
                ))}
            </div>
            <div className="input-group">
                <div>
                    <label className="input-label">Hành động</label>
                    <select
                        value={selectedElement.json.componentData?.events?.[activeEventType]?.type || 'none'}
                        onChange={(e) => handleEventChange(activeEventType, 'type', e.target.value)}
                        className="input-select"
                    >
                        <option value="none">Không có</option>
                        <option value="navigate">Điều hướng</option>
                        <option value="api">Gọi API</option>
                        <option value="popup">Mở Popup</option>
                        <option value="custom">Tùy chỉnh</option>
                    </select>
                </div>
                {selectedElement.json.componentData?.events?.[activeEventType]?.type === 'navigate' && (
                    <div>
                        <label className="input-label">URL</label>
                        <input
                            type="text"
                            value={selectedElement.json.componentData?.events?.[activeEventType]?.url || ''}
                            onChange={(e) => handleEventChange(activeEventType, 'url', e.target.value)}
                            className="input-field"
                            placeholder="/page-url"
                        />
                    </div>
                )}
                {selectedElement.json.componentData?.events?.[activeEventType]?.type === 'api' && (
                    <div>
                        <label className="input-label">API Endpoint</label>
                        <input
                            type="text"
                            value={selectedElement.json.componentData?.events?.[activeEventType]?.apiUrl || ''}
                            onChange={(e) => handleEventChange(activeEventType, 'apiUrl', e.target.value)}
                            className="input-field"
                            placeholder="https://api.example.com/action"
                        />
                    </div>
                )}
                {selectedElement.json.componentData?.events?.[activeEventType]?.type === 'popup' && (
                    <div>
                        <label className="input-label">Popup ID</label>
                        <input
                            type="text"
                            value={selectedElement.json.componentData?.events?.[activeEventType]?.popupId || ''}
                            onChange={(e) => handleEventChange(activeEventType, 'popupId', e.target.value)}
                            className="input-field"
                            placeholder="popup-1"
                        />
                    </div>
                )}
            </div>
        </div>
    );

    return (
        <div className="properties-panel">
            <div className="panel-header">
                <h3 className="panel-title">{selectedElement.json.type}</h3>
                <button onClick={onToggle} className="toggle-button" title="Đóng">
                    <ChevronRight size={18} />
                </button>
            </div>

            {/* Tabs Navigation */}
            <div className="panel-tabs">
                <button
                    className={`tab-button ${activeTab === 'content' ? 'active' : ''}`}
                    onClick={() => setActiveTab('content')}
                >
                    Nội dung
                </button>
                <button
                    className={`tab-button ${activeTab === 'style' ? 'active' : ''}`}
                    onClick={() => setActiveTab('style')}
                >
                    Kiểu dáng
                </button>
                <button
                    className={`tab-button ${activeTab === 'data' ? 'active' : ''}`}
                    onClick={() => setActiveTab('data')}
                >
                    Dữ liệu
                </button>
                <button
                    className={`tab-button ${activeTab === 'events' ? 'active' : ''}`}
                    onClick={() => setActiveTab('events')}
                >
                    Sự kiện
                </button>
            </div>

            {/* Tab Content */}
            <div className="panel-content">
                {activeTab === 'content' && renderContentTab()}
                {activeTab === 'style' && renderStyleTab()}
                {activeTab === 'data' && renderDataTab()}
                {activeTab === 'events' && renderEventsTab()}
            </div>
        </div>
    );
};

export default PropertiesPanel;
