import React, { useState } from 'react';
import api from '@landinghub/api'; // giữ nguyên alias nếu bạn đã cấu hình
import { GoogleLogin } from '@react-oauth/google';
import { useNavigate } from 'react-router-dom';

const Login = () => {
    const navigate = useNavigate();
    const [form, setForm] = useState({ email: '', password: '' });
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

    // Email/password login
    const handleLogin = async (e) => {
        e.preventDefault();
        setError('');
        setLoading(true);
        try {
            const res = await api.post('/api/auth/login', form);
            localStorage.setItem('token', res.data.token);
            navigate('/dashboard');
        } catch (err) {
            setError(err.response?.data?.msg || 'Đăng nhập thất bại');
        } finally {
            setLoading(false);
        }
    };

    // Google login (dùng credential = id_token)
    const handleGoogleSuccess = async (credentialResponse) => {
        setError('');
        // credentialResponse.credential chính là id_token
        const idToken = credentialResponse?.credential;
        if (!idToken) {
            setError('Không lấy được Google ID token');
            return;
        }
        try {
            const res = await api.post('/api/auth/google/callback', { token: idToken });
            // backend trả về { token, user } theo code anh sửa trước
            localStorage.setItem('token', res.data.token);
            navigate('/dashboard');
        } catch (err) {
            setError(err.response?.data?.msg || err.message || 'Google login failed');
        }
    };

    const handleGoogleError = () => setError('Google login failed');

    return (
        <div style={{ padding: 24, maxWidth: 420, margin: 'auto' }}>
            <h2 style={{ textAlign: 'center' }}>Đăng Nhập</h2>

            {error && <div style={{ color: 'red', marginBottom: 12 }}>{error}</div>}

            <form onSubmit={handleLogin}>
                <input
                    name="email"
                    value={form.email}
                    onChange={handleChange}
                    placeholder="Email"
                    type="email"
                    required
                    style={{ width: '100%', padding: 10, marginBottom: 10 }}
                />
                <input
                    name="password"
                    value={form.password}
                    onChange={handleChange}
                    placeholder="Mật khẩu"
                    type="password"
                    required
                    style={{ width: '100%', padding: 10, marginBottom: 10 }}
                />
                <button type="submit" disabled={loading} style={{ width: '100%', padding: 12 }}>
                    {loading ? 'Đang đăng nhập...' : 'Đăng Nhập'}
                </button>
            </form>

            <hr style={{ margin: '20px 0' }} />

            <GoogleLogin onSuccess={handleGoogleSuccess} onError={handleGoogleError} />
        </div>
    );
};

export default Login;
