import React, { useState, useMemo, useCallback, useEffect } from 'react';
import { DndProvider, useDrag, useDrop } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
import { useHotkeys } from 'react-hotkeys-hook';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import Toolbar from './create-page/Toolbar';
import ComponentLibrary from './create-page/ComponentLibrary';
import Canvas from './create-page/Canvas';
import PropertiesPanel from './create-page/PropertiesPanel';

// Define item types for React DnD
const ItemTypes = {
  COMPONENT: 'component',
  EXISTING_ELEMENT: 'existingElement',
};

// Memoize components to prevent unnecessary re-renders
const MemoizedCanvas = React.memo(Canvas);
const MemoizedComponentLibrary = React.memo(ComponentLibrary);
const MemoizedPropertiesPanel = React.memo(PropertiesPanel);

// Import Google Fonts
const fontStyles = `
@import url('https://fonts.googleapis.com/css2?family=Inter:wght@400;600&family=Poppins:wght@300;500&display=swap');
body {
    font-family: 'Poppins', sans-serif;
}
.font-inter {
    font-family: 'Inter', sans-serif;
}
`;

const CreateLandingPage = () => {
  const [pageStructure, setPageStructure] = useState([]);
  const [selectedElement, setSelectedElement] = useState(null);
  const [leftSidebarCollapsed, setLeftSidebarCollapsed] = useState(false);
  const [rightSidebarCollapsed, setRightSidebarCollapsed] = useState(false);
  const [quickEditElement, setQuickEditElement] = useState(null);
  const [quickEditPosition, setQuickEditPosition] = useState(null);
  const [viewMode, setViewMode] = useState('desktop'); // desktop, tablet, mobile, adaptive
  const [zoomLevel, setZoomLevel] = useState(100);
  const [history, setHistory] = useState([[]]); // Undo/redo history
  const [historyIndex, setHistoryIndex] = useState(0);

  // Memoized page structure to prevent unnecessary re-renders
  const memoizedPageStructure = useMemo(() => pageStructure, [pageStructure]);

  // Function to add a new component to the page structure
  const addComponent = useCallback(
    (componentData, index) => {
      const newComponent = {
        ...componentData,
        id: `${componentData.id}-${Date.now()}`,
      };
      setPageStructure((prev) => {
        const newStructure = [...prev];
        newStructure.splice(index, 0, newComponent);
        setHistory((prevHist) => [...prevHist.slice(0, historyIndex + 1), newStructure]);
        setHistoryIndex((prev) => prev + 1);
        return newStructure;
      });
      setQuickEditElement(null);
      setQuickEditPosition(null);
      toast.success('Đã thêm thành phần!');
    },
    [historyIndex]
  );

  // Function to move an existing element
  const moveElement = useCallback(
    (id, newIndex) => {
      const currentIndex = pageStructure.findIndex((el) => el.id === id);
      if (currentIndex === -1 || currentIndex === newIndex) return;

      setPageStructure((prev) => {
        const newStructure = [...prev];
        const [movedElement] = newStructure.splice(currentIndex, 1);
        newStructure.splice(newIndex, 0, movedElement);
        setHistory((prevHist) => [...prevHist.slice(0, historyIndex + 1), newStructure]);
        setHistoryIndex((prev) => prev + 1);
        return newStructure;
      });
      toast.info('Đã di chuyển thành phần!');
    },
    [pageStructure, historyIndex]
  );

  // Undo and Redo functions
  const undo = useCallback(() => {
    if (historyIndex > 0) {
      setHistoryIndex((prev) => prev - 1);
      setPageStructure(history[historyIndex - 1]);
      toast.info('Đã hoàn tác!');
    }
  }, [history, historyIndex]);

  const redo = useCallback(() => {
    if (historyIndex < history.length - 1) {
      setHistoryIndex((prev) => prev + 1);
      setPageStructure(history[historyIndex + 1]);
      toast.info('Đã khôi phục!');
    }
  }, [history, historyIndex]);

  // Handle select element
  const handleSelectElement = useCallback((element) => {
    setSelectedElement(element);
    setQuickEditElement(null);
    setQuickEditPosition(null);
  }, []);

  // Handle update element
  const handleUpdateElement = useCallback(
    (updatedElement) => {
      setPageStructure((prev) => {
        const newStructure = prev.map((el) => (el.id === updatedElement.id ? updatedElement : el));
        setHistory((prevHist) => [...prevHist.slice(0, historyIndex + 1), newStructure]);
        setHistoryIndex((prev) => prev + 1);
        return newStructure;
      });
      setSelectedElement(updatedElement);
      toast.success('Đã cập nhật thành phần!');
    },
    [historyIndex]
  );

  // Handle quick edit
  const handleQuickEdit = useCallback((element, position) => {
    setQuickEditElement(element);
    setQuickEditPosition(position);
  }, []);

  const handleCloseQuickEdit = useCallback(() => {
    setQuickEditElement(null);
    setQuickEditPosition(null);
  }, []);

  // Handle duplicate and delete
  const handleDuplicateElement = useCallback(
    (element) => {
      if (!element) return;
      const duplicated = {
        ...element,
        id: `${element.id}-copy-${Date.now()}`,
      };
      setPageStructure((prev) => {
        const newStructure = [...prev, duplicated];
        setHistory((prevHist) => [...prevHist.slice(0, historyIndex + 1), newStructure]);
        setHistoryIndex((prev) => prev + 1);
        return newStructure;
      });
      setQuickEditElement(null);
      setQuickEditPosition(null);
      toast.success('Đã sao chép thành phần!');
    },
    [historyIndex]
  );

  const handleDeleteElement = useCallback(
    (element) => {
      if (!element) return;
      setPageStructure((prev) => {
        const newStructure = prev.filter((el) => el.id !== element.id);
        setHistory((prevHist) => [...prevHist.slice(0, historyIndex + 1), newStructure]);
        setHistoryIndex((prev) => prev + 1);
        return newStructure;
      });
      if (selectedElement?.id === element.id) {
        setSelectedElement(null);
      }
      setQuickEditElement(null);
      setQuickEditPosition(null);
      toast.success('Đã xóa thành phần!');
    },
    [selectedElement, historyIndex]
  );

  // Handle move element up/down
  const handleMoveElement = useCallback(
    (element, direction) => {
      if (!element) return;
      const currentIndex = pageStructure.findIndex((el) => el.id === element.id);
      if (currentIndex === -1) return;

      const newIndex =
        direction === 'up' ? Math.max(0, currentIndex - 1) : Math.min(pageStructure.length - 1, currentIndex + 1);

      if (newIndex === currentIndex) return;
      moveElement(element.id, newIndex);
    },
    [pageStructure, moveElement]
  );

  // Handle zoom
  const handleZoom = useCallback(
    (direction) => {
      const newZoom = direction === 'in' ? Math.min(200, zoomLevel + 10) : Math.max(50, zoomLevel - 10);
      setZoomLevel(newZoom);
    },
    [zoomLevel]
  );

  // Handle generate code
  const handleGenerateCode = useCallback(() => {
    const pageConfig = {
      components: pageStructure.map((comp) => comp.json),
      meta: {
        title: 'Generated Landing Page',
        description: 'Page generated from JSON',
      },
      viewMode,
      zoomLevel,
    };
    console.log('Generated page config:', pageConfig);
    toast.success('Đã tạo mã! Kiểm tra console để xem chi tiết.');
  }, [pageStructure, viewMode, zoomLevel]);

  // Handle preview
  const handlePreview = useCallback(() => {
    const previewData = encodeURIComponent(
      JSON.stringify({
        pageStructure,
        viewMode,
      })
    );
    window.open(`/preview?data=${previewData}`, '_blank');
  }, [pageStructure, viewMode]);

  // Handle save
  const handleSave = useCallback(() => {
    try {
      const saveData = {
        pageStructure,
        viewMode,
        zoomLevel,
        savedAt: new Date().toISOString(),
      };
      localStorage.setItem('landing-page-builder', JSON.stringify(saveData));
      toast.success('Đã lưu trang thành công!');
    } catch (error) {
      console.error('Lỗi khi lưu:', error);
      toast.error('Lỗi khi lưu trang. Vui lòng thử lại.');
    }
  }, [pageStructure, viewMode, zoomLevel]);

  // Auto-save functionality
  useEffect(() => {
    const autoSave = setTimeout(() => {
      if (pageStructure.length > 0) {
        const autoSaveData = {
          pageStructure,
          viewMode,
          zoomLevel,
          autoSavedAt: new Date().toISOString(),
        };
        try {
          localStorage.setItem('landing-page-builder-autosave', JSON.stringify(autoSaveData));
          toast.info('Đã tự động lưu!');
        } catch (error) {
          console.error('Lỗi khi tự động lưu:', error);
        }
      }
    }, 30000); // Auto-save every 30 seconds

    return () => clearTimeout(autoSave);
  }, [pageStructure, viewMode, zoomLevel]);

  // Keyboard shortcuts
  useHotkeys('ctrl+s', handleSave, [handleSave]);
  useHotkeys('ctrl+z', undo, [undo]);
  useHotkeys('ctrl+shift+z', redo, [redo]);

  return (
    <DndProvider backend={HTML5Backend}>
      <style>{fontStyles}</style>
      <div className="h-screen flex flex-col bg-[#F4E4D4] text-gray-800 font-poppins">
        <link
          rel="stylesheet"
          href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.5.1/css/all.min.css"
          integrity="sha512-xxx" // Replace with actual integrity hash if needed
          crossOrigin="anonymous"
          referrerPolicy="no-referrer"
        />
        <ToastContainer
          position="top-right"
          autoClose={3000}
          hideProgressBar
          className="text-sm font-inter"
        />

        <Toolbar
          onSave={handleSave}
          onPreview={handlePreview}
          onGenerateCode={handleGenerateCode}
          viewMode={viewMode}
          onViewModeChange={setViewMode}
          onUndo={undo}
          onRedo={redo}
          canUndo={historyIndex > 0}
          canRedo={historyIndex < history.length - 1}
          className="bg-gradient-to-r from-[#D4E4E4] to-[#E6F3D6] text-gray-800 shadow-lg p-2"
        />

        <div className="flex-1 flex overflow-hidden">
          <div
            className={`transition-all duration-300 ${
    leftSidebarCollapsed ? 'w-0' : 'w-64'
} overflow-auto bg-white/90 dark:bg-gray-800 border-r border-[#FAD2E1]/50 shadow-md rounded-r-lg`}
          >
            <MemoizedComponentLibrary
              isCollapsed={leftSidebarCollapsed}
              onToggle={() => setLeftSidebarCollapsed(!leftSidebarCollapsed)}
            />
          </div>
          <div className="flex-1 overflow-auto bg-[#E6F3D6]/80">
            <MemoizedCanvas
              pageStructure={memoizedPageStructure}
              selectedElement={selectedElement}
              onSelectElement={handleSelectElement}
              quickEditElement={quickEditElement}
              quickEditPosition={quickEditPosition}
              onQuickEdit={handleQuickEdit}
              onCloseQuickEdit={handleCloseQuickEdit}
              onDuplicateElement={handleDuplicateElement}
              onDeleteElement={handleDeleteElement}
              onMoveElement={handleMoveElement}
              viewMode={viewMode}
              addComponent={addComponent}
              moveElement={moveElement}
              ItemTypes={ItemTypes}
              zoomLevel={zoomLevel}
              setPageStructure={setPageStructure}
            />
          </div>
          <div
            className={`transition-all duration-300 ${
    rightSidebarCollapsed ? 'w-0' : 'w-80'
} overflow-auto bg-white/90 dark:bg-gray-800 border-l border-[#FAD2E1]/50 shadow-md rounded-l-lg`}
          >
            <MemoizedPropertiesPanel
              selectedElement={selectedElement}
              onUpdateElement={handleUpdateElement}
              isCollapsed={rightSidebarCollapsed}
              onToggle={() => setRightSidebarCollapsed(!rightSidebarCollapsed)}
            />
          </div>
        </div>

        {/* Bottom Status Bar with View Mode Controls */}
        <div className="h-14 bg-white/90 border-t border-[#FAD2E1]/50 flex items-center justify-between px-6 shadow-lg rounded-t-lg">
          <div className="flex items-center gap-4 text-sm text-gray-700 font-inter">
            <span>Elements: {pageStructure.length}</span>
            <span className="text-[#FAD2E1]">|</span>
            <span>
              Selected: {selectedElement ? selectedElement.json?.type || 'None' : 'None'}
            </span>
          </div>

          <div className="flex items-center gap-4">
            <div className="flex items-center gap-2">
              <span className="text-sm text-gray-700 font-inter">View:</span>
              <div className="flex border border-[#D4E4E4]/50 rounded-md overflow-hidden">
                {['desktop', 'tablet', 'mobile', 'adaptive'].map((mode) => (
                  <button
                    key={mode}
                    onClick={() => setViewMode(mode)}
                    className={`px-3 py-1.5 text-xs font-inter font-medium transition-all duration-300 ${
    viewMode === mode
        ? 'bg-gradient-to-r from-[#D4E4E4] to-[#E6F3D6] text-gray-800 shadow-md'
        : 'bg-white text-gray-600 hover:bg-[#FAD2E1]/20'
}`}
                    data-tooltip-id="view-tooltip"
                    data-tooltip-content={`Chuyển sang ${mode}`}
                  >
                    {mode.charAt(0).toUpperCase() + mode.slice(1)}
                  </button>
                ))}
              </div>
            </div>

            <div className="flex items-center gap-2">
              <button
                className="p-1.5 hover:bg-[#FAD2E1]/20 rounded-full text-gray-500 transition-all duration-300"
                title="Zoom Out"
                onClick={() => handleZoom('out')}
                data-tooltip-id="zoom-tooltip"
                data-tooltip-content="Thu nhỏ"
              >
                <i className="fas fa-search-minus fa-xs"></i>
              </button>
              <input
                type="range"
                min="50"
                max="200"
                value={zoomLevel}
                onChange={(e) => setZoomLevel(Number(e.target.value))}
                className="w-24 h-1 bg-[#D4E4E4]/50 rounded-lg appearance-none cursor-pointer accent-[#E6F3D6]"
              />
              <span className="text-xs text-gray-600 font-inter px-2">{zoomLevel}%</span>
              <button
                className="p-1.5 hover:bg-[#FAD2E1]/20 rounded-full text-gray-500 transition-all duration-300"
                title="Zoom In"
                onClick={() => handleZoom('in')}
                data-tooltip-id="zoom-tooltip"
                data-tooltip-content="Phóng to"
              >
                <i className="fas fa-search-plus fa-xs"></i>
              </button>
            </div>
          </div>
        </div>
      </div>
    </DndProvider>
  );
};

export default CreateLandingPage;
