// F:\landinghub-iconic\apps\web\src\components\UserProfile.js
import React, { useState, useEffect, useContext } from 'react';
import { UserContext } from '../context/UserContext';
import api from '@landinghub/api';

const UserProfile = () => {
    const { user, setUser } = useContext(UserContext);
    const [form, setForm] = useState({ name: user?.name || '' });
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    useEffect(() => {
        const fetchData = async () => {
            try {
                const res = await api.get('/api/user/info');
                setUserData(res.data);
            } catch (err) {
                console.error('Error fetching user data:', err);
                setError('Không thể tải dữ liệu người dùng.');
            } finally {
                setLoading(false);
            }
        };
        fetchData();
    }, []);
    useEffect(() => {
        setForm({ name: user?.name || '' }); // Chỉ đồng bộ name để chỉnh sửa
    }, [user]);

    const handleChange = (e) => {
        setForm({ name: e.target.value }); // Chỉ cho phép thay đổi name
    };

    const handleUpdate = async (e) => {
        e.preventDefault();
        setError(null);
        try {
            const res = await api.put('/api/user/update', { name: form.name }); // Chỉ gửi name
            const newToken = res.data.token;
            localStorage.setItem('token', newToken);
            setUser({ ...user, name: form.name }); // Cập nhật name trong UserContext
            console.log('Thông tin người dùng được cập nhật thành công');
        } catch (err) {
            setError(err.response?.data?.msg || 'Cập nhật thất bại');
        }
    };

    return (
        <div className="user-profile">
            <h2>Thông tin người dùng</h2>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <form onSubmit={handleUpdate}>
                <div className="form-group">
                    <label>Tên</label>
                    <input
                        type="text"
                        name="name"
                        value={form.name}
                        onChange={handleChange}
                        disabled={loading}
                    />
                </div>
                <div className="form-group">
                    <label>Email</label>
                    <input
                        type="text"
                        value={user?.email || 'Chưa có thông tin'}
                        disabled
                    />
                </div>
                <div className="form-group">
                    <label>Vai trò</label>
                    <input
                        type="text"
                        value={user?.role || 'Chưa có thông tin'}
                        disabled
                    />
                </div>
                <div className="form-group">
                    <label>Đăng ký</label>
                    <input
                        type="text"
                        value={user?.subscription || 'Chưa có thông tin'}
                        disabled
                    />
                </div>
                <div className="form-group">
                    <label>Ngày tạo</label>
                    <input
                        type="text"
                        value={user?.createdAt ? new Date(user.createdAt).toLocaleDateString() : 'Chưa có thông tin'}
                        disabled
                    />
                </div>
                <button type="submit" disabled={loading}>
                    {loading ? 'Đang cập nhật...' : 'Cập nhật'}
                </button>
            </form>
        </div>
    );
};

export default UserProfile;
