import React, { useRef, useEffect, useCallback } from 'react';
import { useDrop, useDrag } from 'react-dnd';
import { getEmptyImage } from 'react-dnd-html5-backend';
import { toast } from 'react-toastify';
import QuickEditToolkit from './QuickEditToolkit';
import SectionToolkit from './toolkitqick/SectionToolkit';
import { ItemTypes, getCanvasPosition, snapToGrid, getElementBounds, renderComponentContent } from './helpers';

export const ChildElement = React.memo(
    ({
         parentId,
         id,
         type,
         componentData = {},
         styles = {},
         isSelected,
         onSelectChild,
         zoomLevel,
         gridSize,
         showGrid,
         onUpdateChildPosition,
         onMoveChild,
         position,
         size,
         viewMode,
         visible = true,
         locked = false,
         onDeleteChild,
     }) => {
        const dragRef = useRef(null);
        const childRef = useRef(null);

        const [{ isDragging }, drag, preview] = useDrag(() => ({
            type: ItemTypes.CHILD_ELEMENT,
            canDrag: () => !locked && !componentData.locked,
            item: () => {
                if (locked || componentData.locked) {
                    toast.warning('Child element đã bị khóa!');
                    return null;
                }
                return { childId: id, parentId, isExisting: true, position, size, type };
            },
            collect: (monitor) => ({
                isDragging: monitor.isDragging(),
            }),
        }), [id, parentId, position, size, locked, componentData.locked, type]);

        const handleClick = useCallback(
            (e) => {
                if (e && typeof e.stopPropagation === 'function') e.stopPropagation();
                if (typeof onSelectChild === 'function') {
                    onSelectChild(parentId, id);
                }
            },
            [parentId, id, onSelectChild]
        );

        const handleMouseDown = useCallback(
            (e) => {
                if (e && typeof e.stopPropagation === 'function') e.stopPropagation();
                if (locked || componentData.locked) return;

                handleClick(e);
                const startX = e.clientX;
                const startY = e.clientY;
                const initialPos = position[viewMode] || { x: 0, y: 0 };

                const handleMouseMove = (moveEvent) => {
                    if (!moveEvent || !childRef.current) return;
                    const deltaX = (moveEvent.clientX - startX) / (zoomLevel / 100);
                    const deltaY = (moveEvent.clientY - startY) / (zoomLevel / 100);
                    const newPos = {
                        x: Math.max(0, initialPos.x + deltaX),
                        y: Math.max(0, initialPos.y + deltaY),
                    };
                    const snapPoints = [];
                    const snapped = snapToGrid(newPos.x, newPos.y, showGrid ? gridSize : Infinity, snapPoints);
                    onUpdateChildPosition(parentId, id, { [viewMode]: snapped }, 'absolute');
                };

                const handleMouseUp = () => {
                    document.removeEventListener('mousemove', handleMouseMove);
                    document.removeEventListener('mouseup', handleMouseUp);
                };

                document.addEventListener('mousemove', handleMouseMove);
                document.addEventListener('mouseup', handleMouseUp);
            },
            [parentId, id, position, viewMode, zoomLevel, gridSize, showGrid, onUpdateChildPosition, locked, componentData.locked, handleClick]
        );

        useEffect(() => {
            preview(getEmptyImage(), { captureDraggingState: true });
            if (!locked && !componentData.locked) {
                drag(dragRef);
            }
        }, [drag, preview, locked, componentData.locked]);

        if (visible === false || componentData.visible === false) {
            return null;
        }

        const lockedStyles = locked || componentData.locked ? { opacity: 0.7, cursor: 'not-allowed', pointerEvents: 'none' } : {};

        return (
            <div
                ref={childRef}
                className={`lpb-child-element ${isSelected ? 'lpb-child-element-selected' : ''} ${isDragging ? 'lpb-child-element-dragging' : ''} ${
                    locked || componentData.locked ? 'lpb-element-locked' : ''
                } lpb-element-${type}`}
                style={{
                    position: 'absolute',
                    left: position[viewMode]?.x || 0,
                    top: position[viewMode]?.y || 0,
                    width: size?.width || 200,
                    height: size?.height || 50,
                    zIndex: isDragging ? 1000 : position.z || 10,
                    cursor: locked || componentData.locked ? 'not-allowed' : isDragging ? 'grabbing' : 'grab',
                    opacity: isDragging ? 0.5 : 1,
                    ...styles,
                    ...lockedStyles,
                }}
                onClick={handleClick}
                onMouseDown={handleMouseDown}
            >
                <div ref={dragRef}>{renderComponentContent(type, componentData, styles, [])}</div>

                {locked || componentData.locked ? (
                    <div
                        style={{
                            position: 'absolute',
                            top: '2px',
                            right: '2px',
                            zIndex: 10001,
                            background: 'rgba(0,0,0,0.7)',
                            color: '#fff',
                            padding: '2px 6px',
                            borderRadius: '3px',
                            fontSize: '10px',
                            pointerEvents: 'none',
                        }}
                    >
                        <i className="fas fa-lock"></i>
                    </div>
                ) : null}

                {isSelected && (
                    <QuickEditToolkit
                        element={{ id, type, styles }}
                        position={{ x: 0, y: -40 }}
                        onEdit={() => console.log('Edit child:', id)}
                        onDuplicate={() => console.log('Duplicate child:', id)}
                        onDelete={() => onDeleteChild(parentId, id)}
                        onMoveUp={() => console.log('Move up child:', id)}
                        onMoveDown={() => console.log('Move down child:', id)}
                        onClose={() => onSelectChild(parentId, null)}
                    />
                )}
            </div>
        );
    }
);

export const Element = React.memo(
    ({
         element,
         isSelected,
         onSelectElement,
         onUpdatePosition,
         viewMode,
         zoomLevel,
         gridSize,
         onContextMenu,
         canvasBounds,
         canvasRef,
         onSelectChild,
         selectedChildId,
         onAddChild,
         onUpdateChildPosition,
         onMoveChild,
         showGrid,
         setDragPreview,
         visiblePopups = [],
         onClosePopup,
         onSaveTemplate,
         onToggleVisibility,
         onEditElement,
         onDuplicateElement,
         onMoveElementUp,
         onMoveElementDown,
         onDeleteElement,
         onDeleteChild,
     }) => {
        const {
            id,
            type,
            componentData = {},
            position = {},
            size = { width: viewMode === 'mobile' ? 375 : 1200, height: 400 },
            styles = {},
            children = [],
            visible = true,
            locked = false,
        } = element;

        const elementRef = useRef(null);
        const containerRef = useRef(null);

        const handleMouseDown = useCallback(
            (e) => {
                if (e && typeof e.stopPropagation === 'function') e.stopPropagation();
                if (locked || type === 'section' || type === 'popup' || type === 'modal') {
                    if (locked) toast.warning('Element đã bị khóa!');
                    if (typeof onSelectElement === 'function') {
                        onSelectElement([id], e.ctrlKey);
                    }
                    onSelectChild(id, null);
                    return;
                }

                if (typeof onSelectElement === 'function') {
                    onSelectElement([id], e.ctrlKey);
                }
                onSelectChild(id, null);

                const startX = e.clientX;
                const startY = e.clientY;
                const initialPos = position[viewMode] || { x: 0, y: 0 };

                const handleMouseMove = (moveEvent) => {
                    if (!moveEvent || !elementRef.current) return;
                    const deltaX = (moveEvent.clientX - startX) / (zoomLevel / 100);
                    const deltaY = (moveEvent.clientY - startY) / (zoomLevel / 100);
                    const newPos = {
                        x: Math.max(0, initialPos.x + deltaX),
                        y: Math.max(0, initialPos.y + deltaY),
                    };
                    const snapPoints = children.map((child) => {
                        const bounds = getElementBounds(child);
                        return [
                            { x: bounds.left, y: bounds.top },
                            { x: bounds.right, y: bounds.bottom },
                            { x: bounds.centerX, y: bounds.centerY },
                        ];
                    }).flat();
                    const snapped = snapToGrid(newPos.x, newPos.y, showGrid ? gridSize : Infinity, snapPoints);
                    onUpdatePosition(id, { [viewMode]: snapped }, 'absolute');
                    setDragPreview({
                        id,
                        x: snapped.x,
                        y: snapped.y,
                        size,
                        type,
                        componentData,
                        styles,
                        children,
                    });
                };

                const handleMouseUp = () => {
                    document.removeEventListener('mousemove', handleMouseMove);
                    document.removeEventListener('mouseup', handleMouseUp);
                    setDragPreview(null);
                };

                document.addEventListener('mousemove', handleMouseMove);
                document.addEventListener('mouseup', handleMouseUp);
            },
            [id, type, locked, position, viewMode, zoomLevel, gridSize, showGrid, onUpdatePosition, onSelectElement, onSelectChild, setDragPreview, size, componentData, styles, children]
        );

        const handleContextMenu = useCallback(
            (e) => {
                if (e && typeof e.preventDefault === 'function') e.preventDefault();
                if (e && typeof e.stopPropagation === 'function') e.stopPropagation();
                const bounds = elementRef.current?.getBoundingClientRect();
                if (bounds) {
                    onContextMenu(id, { x: e.clientX - bounds.left, y: e.clientY - bounds.top });
                }
            },
            [id, onContextMenu]
        );

        const [{ isOverContainer, canDropContainer }, dropSection] = useDrop(() => ({
            accept: [ItemTypes.ELEMENT, ItemTypes.CHILD_ELEMENT],
            canDrop: (item, monitor) => {
                if (type !== 'section' || componentData?.structure !== 'ladi-standard') {
                    return false;
                }
                if (monitor.getItemType() === ItemTypes.CHILD_ELEMENT && item.parentId && item.parentId !== id) {
                    return true;
                }
                return monitor.getItemType() === ItemTypes.ELEMENT;
            },
            hover: (item, monitor) => {
                if (!monitor.canDrop() || !containerRef.current) return;
                const clientOffset = monitor.getClientOffset();
                if (!clientOffset) return;

                const pos = getCanvasPosition(clientOffset.x, clientOffset.y, containerRef.current, zoomLevel);
                const snapPoints = children.map((child) => {
                    const bounds = getElementBounds(child);
                    return [
                        { x: bounds.left, y: bounds.top },
                        { x: bounds.right, y: bounds.bottom },
                        { x: bounds.centerX, y: bounds.centerY },
                    ];
                }).flat();
                const snapped = snapToGrid(pos.x, pos.y, showGrid ? gridSize : Infinity, snapPoints);

                if (monitor.getItemType() === ItemTypes.ELEMENT && item.json) {
                    setDragPreview({
                        id: 'preview',
                        x: snapped.x,
                        y: snapped.y,
                        size: item.json.size || { width: 200, height: 50 },
                        type: item.json.type,
                        componentData: item.json.componentData || {},
                        styles: item.json.styles || {},
                        children: item.json.children || [],
                    });
                } else if (monitor.getItemType() === ItemTypes.CHILD_ELEMENT) {
                    setDragPreview({
                        id: item.childId,
                        x: snapped.x,
                        y: snapped.y,
                        size: item.size || { width: 200, height: 50 },
                        type: item.type,
                        componentData: item.componentData || {},
                        styles: item.styles || {},
                        children: [],
                    });
                }
            },
            drop: (item, monitor) => {
                if (!monitor.canDrop() || !containerRef.current) return { moved: false };

                const clientOffset = monitor.getClientOffset();
                if (!clientOffset) return { moved: false };

                const pos = getCanvasPosition(clientOffset.x, clientOffset.y, containerRef.current, zoomLevel);
                const snapPoints = children.map((child) => {
                    const bounds = getElementBounds(child);
                    return [
                        { x: bounds.left, y: bounds.top },
                        { x: bounds.right, y: bounds.bottom },
                        { x: bounds.centerX, y: bounds.centerY },
                    ];
                }).flat();
                const snapped = snapToGrid(pos.x, pos.y, showGrid ? gridSize : Infinity, snapPoints);

                if (monitor.getItemType() === ItemTypes.ELEMENT) {
                    const newId = `${item.json.type}-${Date.now()}`;
                    const newChild = {
                        id: newId,
                        type: item.json.type,
                        componentData: { ...item.json.componentData, title: item.json.componentData?.title || 'Thành phần mới' },
                        position: { [viewMode]: snapped, desktop: snapped, tablet: snapped, mobile: snapped },
                        size: item.json.size || { width: 200, height: 50 },
                        styles: item.json.styles || {},
                        children: item.json.children || [],
                        visible: true,
                        locked: false,
                    };
                    onAddChild(id, newChild);
                    toast.success('Đã thêm thành phần con vào section!');
                    setDragPreview(null);
                    return { moved: true, newPosition: snapped, parentId: id };
                } else if (monitor.getItemType() === ItemTypes.CHILD_ELEMENT) {
                    if (item.parentId === id) {
                        onUpdateChildPosition(id, item.childId, { [viewMode]: snapped });
                        toast.success('Đã di chuyển thành phần con trong section!');
                    } else {
                        onMoveChild(item.parentId, item.childId, id, snapped);
                        toast.success('Đã di chuyển thành phần con sang section khác!');
                    }
                    setDragPreview(null);
                    return { moved: true, newPosition: snapped, parentId: id };
                }
                setDragPreview(null);
                return { moved: false };
            },
            collect: (monitor) => ({
                isOverContainer: monitor.isOver(),
                canDropContainer: monitor.canDrop(),
            }),
        }), [id, type, componentData, viewMode, zoomLevel, gridSize, showGrid, onAddChild, onUpdateChildPosition, onMoveChild, children, setDragPreview]);

        const [{ isOverPopup, canDropPopup }, dropPopup] = useDrop(() => ({
            accept: [ItemTypes.ELEMENT, ItemTypes.CHILD_ELEMENT],
            canDrop: () => type === 'popup' || type === 'modal',
            hover: (item, monitor) => {
                if (!monitor.canDrop() || !containerRef.current) return;
                const clientOffset = monitor.getClientOffset();
                if (!clientOffset) return;

                const pos = getCanvasPosition(clientOffset.x, clientOffset.y, containerRef.current, zoomLevel);
                const snapPoints = children.map((child) => {
                    const bounds = getElementBounds(child);
                    return [
                        { x: bounds.left, y: bounds.top },
                        { x: bounds.right, y: bounds.bottom },
                        { x: bounds.centerX, y: bounds.centerY },
                    ];
                }).flat();
                const snapped = snapToGrid(pos.x, pos.y, showGrid ? gridSize : Infinity, snapPoints);

                if (monitor.getItemType() === ItemTypes.ELEMENT && item.json) {
                    setDragPreview({
                        id: 'preview',
                        x: snapped.x,
                        y: snapped.y,
                        size: item.json.size || { width: 200, height: 50 },
                        type: item.json.type,
                        componentData: item.json.componentData || {},
                        styles: item.json.styles || {},
                        children: item.json.children || [],
                    });
                } else if (monitor.getItemType() === ItemTypes.CHILD_ELEMENT) {
                    setDragPreview({
                        id: item.childId,
                        x: snapped.x,
                        y: snapped.y,
                        size: item.size || { width: 200, height: 50 },
                        type: item.type,
                        componentData: item.componentData || {},
                        styles: item.styles || {},
                        children: [],
                    });
                }
            },
            drop: (item, monitor) => {
                if (!monitor.canDrop() || !containerRef.current) return { moved: false };

                const clientOffset = monitor.getClientOffset();
                if (!clientOffset) return { moved: false };

                const pos = getCanvasPosition(clientOffset.x, clientOffset.y, containerRef.current, zoomLevel);
                const snapPoints = children.map((child) => {
                    const bounds = getElementBounds(child);
                    return [
                        { x: bounds.left, y: bounds.top },
                        { x: bounds.right, y: bounds.bottom },
                        { x: bounds.centerX, y: bounds.centerY },
                    ];
                }).flat();
                const snapped = snapToGrid(pos.x, pos.y, showGrid ? gridSize : Infinity, snapPoints);

                if (monitor.getItemType() === ItemTypes.ELEMENT) {
                    const newId = `${item.json.type}-${Date.now()}`;
                    const newChild = {
                        id: newId,
                        type: item.json.type,
                        componentData: { ...item.json.componentData, title: item.json.componentData?.title || 'Thành phần mới' },
                        position: { [viewMode]: snapped, desktop: snapped, tablet: snapped, mobile: snapped },
                        size: item.json.size || { width: 200, height: 50 },
                        styles: item.json.styles || {},
                        children: item.json.children || [],
                        visible: true,
                        locked: false,
                    };
                    onAddChild(id, newChild);
                    toast.success(`Đã thêm thành phần vào ${type}!`);
                    setDragPreview(null);
                    return { moved: true, newPosition: snapped, parentId: id };
                } else if (monitor.getItemType() === ItemTypes.CHILD_ELEMENT) {
                    if (item.parentId === id) {
                        onUpdateChildPosition(id, item.childId, { [viewMode]: snapped });
                        toast.success(`Đã di chuyển thành phần con trong ${type}!`);
                    } else {
                        onMoveChild(item.parentId, item.childId, id, snapped);
                        toast.success(`Đã di chuyển thành phần con sang ${type} khác!`);
                    }
                    setDragPreview(null);
                    return { moved: true, newPosition: snapped, parentId: id };
                }
                setDragPreview(null);
                return { moved: false };
            },
            collect: (monitor) => ({
                isOverPopup: monitor.isOver(),
                canDropPopup: monitor.canDrop(),
            }),
        }), [id, type, viewMode, zoomLevel, gridSize, showGrid, onAddChild, onUpdateChildPosition, onMoveChild, children, setDragPreview]);

        const [{ isDragging }, drag, preview] = useDrag(() => ({
            type: ItemTypes.EXISTING_ELEMENT,
            canDrag: () => !locked && type !== 'section' && type !== 'popup' && type !== 'modal',
            item: () => {
                if (locked) {
                    toast.warning('Element đã bị khóa!');
                    return null;
                }
                return {
                    id,
                    elementSize: size,
                    elementPosition: position[viewMode] || { x: 0, y: 0 },
                    isExisting: true,
                    type,
                    componentData,
                    styles,
                    children,
                };
            },
            collect: (monitor) => ({
                isDragging: monitor.isDragging(),
            }),
        }), [id, size, position, viewMode, locked, type, componentData, styles, children]);

        useEffect(() => {
            preview(getEmptyImage(), { captureDraggingState: true });
        }, [preview]);

        useEffect(() => {
            if (type === 'section' && componentData?.structure === 'ladi-standard') {
                dropSection(containerRef);
            } else if (type === 'popup' || type === 'modal') {
                dropPopup(containerRef);
            }
        }, [dropSection, dropPopup, type, componentData]);

        if (visible === false) {
            return null;
        }

        const isPopupOrModal = type === 'popup' || type === 'modal';
        const shouldShowPopup = isSelected || visiblePopups.includes(id);
        if (isPopupOrModal && !shouldShowPopup) {
            return null;
        }

        const animationStyles = {
            animation: componentData.animation?.onLoad
                ? `${componentData.animation.onLoad} ${componentData.animation?.duration || 1000}ms ease`
                : 'none',
        };

        const lockedStyles = locked ? { opacity: 0.7, cursor: 'not-allowed', pointerEvents: 'none' } : {};

        if (type === 'section' && componentData.structure === 'ladi-standard') {
            return (
                <div
                    ref={elementRef}
                    className={`lpb-canvas-element ${isSelected ? 'lpb-canvas-element-selected' : ''} ${locked ? 'lpb-element-locked' : ''} lpb-element-section`}
                    style={{
                        position: 'absolute',
                        top: position[viewMode]?.y || 0,
                        width: viewMode === 'mobile' ? '375px' : '1200px',
                        height: size.height,
                        left: '50%',
                        transform: `translateX(-50%)`,
                        zIndex: position.z || 1,
                        userSelect: 'none',
                        background: componentData.backgroundType === 'gradient' ? styles.background : 'none',
                        border: styles.border || 'none',
                        borderRadius: styles.borderRadius || '0px',
                        boxShadow: styles.boxShadow || 'none',
                        ...animationStyles,
                        ...lockedStyles,
                    }}
                    onMouseDown={handleMouseDown}
                    onContextMenu={handleContextMenu}
                >
                    {locked && (
                        <div
                            style={{
                                position: 'absolute',
                                top: '10px',
                                right: '10px',
                                zIndex: 10001,
                                background: 'rgba(0,0,0,0.7)',
                                color: '#fff',
                                padding: '6px 12px',
                                borderRadius: '6px',
                                fontSize: '12px',
                                pointerEvents: 'none',
                            }}
                        >
                            <i className="fas fa-lock"></i> Section Locked
                        </div>
                    )}

                    <div className="ladi-section" style={{ width: '100%', height: '100%' }}>
                        <div
                            className="ladi-section-background"
                            style={{
                                backgroundColor: componentData.backgroundType === 'color' ? styles.backgroundColor || 'transparent' : 'none',
                                backgroundImage: componentData.backgroundType === 'image' && componentData.backgroundImage ? `url(${componentData.backgroundImage})` : 'none',
                                backgroundSize: 'cover',
                                backgroundPosition: styles.backgroundPosition || 'center',
                                position: 'absolute',
                                top: 0,
                                left: 0,
                                right: 0,
                                bottom: 0,
                                zIndex: 0,
                            }}
                        ></div>
                        <div
                            className="ladi-overlay"
                            style={{
                                backgroundColor: componentData.overlayColor || 'transparent',
                                opacity: componentData.overlayOpacity || 0,
                                position: 'absolute',
                                top: 0,
                                left: 0,
                                right: 0,
                                bottom: 0,
                                zIndex: 0,
                            }}
                        ></div>
                        <div
                            ref={containerRef}
                            className={`ladi-container ${isOverContainer && canDropContainer ? 'lpb-child-element-hover' : ''}`}
                            style={{
                                position: 'relative',
                                zIndex: 1,
                                padding: styles.padding || '20px',
                                width: '100%',
                                height: '100%',
                                border: isOverContainer && canDropContainer ? '2px dashed #2563eb' : 'none',
                            }}
                        >
                            {children.map((child) => (
                                <ChildElement
                                    key={child.id}
                                    parentId={id}
                                    id={child.id}
                                    type={child.type}
                                    componentData={child.componentData || {}}
                                    styles={child.styles || {}}
                                    position={child.position || {}}
                                    size={child.size || { width: 200, height: 50 }}
                                    visible={child.visible !== false}
                                    locked={child.locked || false}
                                    isSelected={selectedChildId === child.id}
                                    onSelectChild={onSelectChild}
                                    zoomLevel={zoomLevel}
                                    gridSize={gridSize}
                                    showGrid={showGrid}
                                    onUpdateChildPosition={onUpdateChildPosition}
                                    onMoveChild={onMoveChild}
                                    viewMode={viewMode}
                                    onDeleteChild={onDeleteChild}
                                />
                            ))}
                        </div>
                    </div>

                    {isSelected && (
                        <SectionToolkit
                            element={element}
                            position={{ x: 0, y: 0 }}
                            onEdit={() => onEditElement({ json: element, isChild: false })}
                            onSaveTemplate={onSaveTemplate}
                            onMoveUp={() => onMoveElementUp(id)}
                            onMoveDown={() => onMoveElementDown(id)}
                            onToggleVisibility={() => onToggleVisibility(id)}
                            onDuplicate={() => onDuplicateElement(id)}
                            onDelete={() => onDeleteElement(id)}
                        />
                    )}
                </div>
            );
        }

        if (isPopupOrModal) {
            return (
                <>
                    <div
                        className="lpb-popup-backdrop"
                        style={{
                            position: 'fixed',
                            top: 0,
                            left: 0,
                            right: 0,
                            bottom: 0,
                            background: 'rgba(0, 0, 0, 0.4)',
                            zIndex: 1000,
                            pointerEvents: isSelected ? 'auto' : 'none',
                        }}
                        onClick={(e) => {
                            if (e && typeof e.stopPropagation === 'function') e.stopPropagation();
                            if (typeof onClosePopup === 'function') {
                                onClosePopup(id);
                            }
                        }}
                    />
                    <div
                        ref={elementRef}
                        className={`lpb-popup-container ${isSelected ? 'lpb-popup-selected' : ''} ${locked ? 'lpb-element-locked' : ''} lpb-element-${type}`}
                        style={{
                            position: 'fixed',
                            top: '50%',
                            left: '50%',
                            transform: 'translate(-50%, -50%)',
                            width: viewMode === 'mobile' ? '90%' : size?.width || 600,
                            maxWidth: viewMode === 'mobile' ? '90%' : '600px',
                            minHeight: size?.height || 400,
                            maxHeight: '90vh',
                            zIndex: 1001,
                            background: componentData.background || 'rgba(255, 255, 255, 0.95)',
                            borderRadius: componentData.borderRadius || '12px',
                            boxShadow: isSelected ? '0 20px 60px rgba(0, 0, 0, 0.3), 0 0 0 3px #3b82f6' : '0 8px 32px rgba(0, 0, 0, 0.25)',
                            overflow: 'hidden',
                            display: 'flex',
                            flexDirection: 'column',
                            transition: 'all 0.3s ease',
                            ...animationStyles,
                            ...styles,
                            ...lockedStyles,
                        }}
                        onClick={handleMouseDown}
                        onContextMenu={handleContextMenu}
                    >
                        <div
                            className="lpb-popup-header"
                            style={{
                                padding: '16px 20px',
                                borderBottom: '1px solid #e5e7eb',
                                background: 'linear-gradient(to bottom, #f9fafb, #ffffff)',
                                display: 'flex',
                                justifyContent: 'space-between',
                                alignItems: 'center',
                                flexShrink: 0,
                            }}
                        >
                            <h3 style={{ margin: 0, fontSize: '16px', fontWeight: '600', color: '#111827' }}>
                                {componentData.title || `${type === 'popup' ? 'Popup' : 'Modal'} Title`}
                            </h3>
                            <button
                                className="lpb-popup-close"
                                onClick={(e) => {
                                    if (e && typeof e.stopPropagation === 'function') e.stopPropagation();
                                    if (typeof onClosePopup === 'function') {
                                        onClosePopup(id);
                                    }
                                }}
                                style={{
                                    width: '28px',
                                    height: '28px',
                                    border: 'none',
                                    background: 'transparent',
                                    cursor: 'pointer',
                                    borderRadius: '6px',
                                    display: 'flex',
                                    alignItems: 'center',
                                    justifyContent: 'center',
                                    transition: 'all 0.15s ease',
                                    color: '#6b7280',
                                }}
                            >
                                <i className="fas fa-times" style={{ fontSize: '14px' }}></i>
                            </button>
                        </div>

                        <div
                            ref={containerRef}
                            className={`lpb-popup-content ${isOverPopup && canDropPopup ? 'lpb-popup-dropzone-active' : ''}`}
                            style={{
                                position: 'relative',
                                flex: 1,
                                padding: componentData.padding || '20px',
                                overflowY: 'auto',
                                overflowX: 'hidden',
                                minHeight: '200px',
                                border: isOverPopup && canDropPopup ? '2px dashed #2563eb' : 'none',
                                background: isOverPopup && canDropPopup ? 'rgba(37, 99, 235, 0.05)' : 'transparent',
                                transition: 'all 0.2s ease',
                            }}
                        >
                            {children.length === 0 && (
                                <div
                                    className="lpb-popup-empty"
                                    style={{
                                        display: 'flex',
                                        flexDirection: 'column',
                                        alignItems: 'center',
                                        justifyContent: 'center',
                                        minHeight: '200px',
                                        color: '#9ca3af',
                                        textAlign: 'center',
                                        padding: '40px 20px',
                                    }}
                                >
                                    <i className="fas fa-hand-pointer" style={{ fontSize: '48px', marginBottom: '16px', opacity: 0.3, color: '#d1d5db' }}></i>
                                    <p style={{ margin: '0 0 8px', fontSize: '14px', fontWeight: '500', color: '#6b7280' }}>
                                        Kéo thả elements vào đây
                                    </p>
                                    <small style={{ fontSize: '12px', color: '#9ca3af' }}>
                                        {type === 'popup' ? 'Popup' : 'Modal'} có thể chứa bất kỳ element nào
                                    </small>
                                </div>
                            )}
                            {children.map((child) => (
                                <ChildElement
                                    key={child.id}
                                    parentId={id}
                                    id={child.id}
                                    type={child.type}
                                    componentData={child.componentData || {}}
                                    styles={child.styles || {}}
                                    position={child.position || {}}
                                    size={child.size || { width: 200, height: 50 }}
                                    visible={child.visible !== false}
                                    locked={child.locked || false}
                                    isSelected={selectedChildId === child.id}
                                    onSelectChild={onSelectChild}
                                    zoomLevel={zoomLevel}
                                    gridSize={gridSize}
                                    showGrid={showGrid}
                                    onUpdateChildPosition={onUpdateChildPosition}
                                    onMoveChild={onMoveChild}
                                    viewMode={viewMode}
                                    onDeleteChild={onDeleteChild}
                                />
                            ))}
                        </div>

                        {locked && (
                            <div
                                style={{
                                    position: 'absolute',
                                    top: '5px',
                                    right: '5px',
                                    zIndex: 10001,
                                    background: 'rgba(0,0,0,0.7)',
                                    color: '#fff',
                                    padding: '4px 8px',
                                    borderRadius: '4px',
                                    fontSize: '11px',
                                    pointerEvents: 'none',
                                }}
                            >
                                <i className="fas fa-lock"></i> Locked
                            </div>
                        )}

                        {isSelected && (
                            <QuickEditToolkit
                                element={{ id, type, styles }}
                                position={{ x: 0, y: -40 }}
                                onEdit={() => onEditElement({ json: element, isChild: false })}
                                onDuplicate={() => onDuplicateElement(id)}
                                onDelete={() => onDeleteElement(id)}
                                onMoveUp={() => onMoveElementUp(id)}
                                onMoveDown={() => onMoveElementDown(id)}
                                onClose={() => onSelectElement([])}
                            />
                        )}
                    </div>
                </>
            );
        }

        return (
            <div
                ref={(node) => {
                    elementRef.current = node;
                    if (!locked && type !== 'section' && type !== 'popup' && type !== 'modal') {
                        drag(node);
                    }
                }}
                className={`lpb-canvas-element ${isSelected ? 'lpb-canvas-element-selected' : ''} ${isDragging ? 'lpb-canvas-element-dragging' : ''} ${
                    locked ? 'lpb-element-locked' : ''
                } lpb-element-${type}`}
                style={{
                    position: 'absolute',
                    left: position[viewMode]?.x || 0,
                    top: position[viewMode]?.y || 0,
                    width: size.width,
                    height: size.height,
                    zIndex: isDragging ? 1000 : position.z || 10,
                    opacity: isDragging ? 0.5 : 1,
                    cursor: locked ? 'not-allowed' : isDragging ? 'grabbing' : 'grab',
                    userSelect: 'none',
                    ...styles,
                    ...animationStyles,
                    ...lockedStyles,
                }}
                onMouseDown={handleMouseDown}
                onContextMenu={handleContextMenu}
            >
                {locked && (
                    <div
                        style={{
                            position: 'absolute',
                            top: '5px',
                            right: '5px',
                            zIndex: 10001,
                            background: 'rgba(0,0,0,0.7)',
                            color: '#fff',
                            padding: '4px 8px',
                            borderRadius: '4px',
                            fontSize: '11px',
                            pointerEvents: 'none',
                        }}
                    >
                        <i className="fas fa-lock"></i> Locked
                    </div>
                )}

                {renderComponentContent(type, componentData, styles, children)}

                {isSelected && type !== 'section' && (
                    <QuickEditToolkit
                        element={{ id, type, styles }}
                        position={{ x: 0, y: -40 }}
                        onEdit={() => onEditElement({ json: element, isChild: false })}
                        onDuplicate={() => onDuplicateElement(id)}
                        onDelete={() => onDeleteElement(id)}
                        onMoveUp={() => onMoveElementUp(id)}
                        onMoveDown={() => onMoveElementDown(id)}
                        onClose={() => onSelectElement([])}
                    />
                )}
            </div>
        );
    }
);
