import React from 'react';
import { useDrag } from 'react-dnd';
import EXTENDED_LIBRARY from '../../constants/ExtendedLibrary';
import '../../styles/ComponentLibrary.css.css';

const ComponentLibrary = ({ isCollapsed, onToggle, addSection, addElement }) => {
    const ItemTypes = {
        SECTION: 'section',
        ELEMENT: 'element',
    };

    const DraggableComponent = ({ component }) => {
        const [{ isDragging }, drag] = useDrag(() => ({
            type: component.type === 'section' ? ItemTypes.SECTION : ItemTypes.ELEMENT,
            item: {
                id: component.id,
                json: component.json,
            },
            collect: (monitor) => ({
                isDragging: !!monitor.isDragging(),
            }),
        }));

        return (
            <div
                ref={drag}
                className={`lpb-component-item ${isDragging ? 'lpb-component-dragging' : ''}`}
                style={{
                    padding: '10px',
                    margin: '5px 0',
                    background: '#f0f0f0',
                    border: '1px solid #ddd',
                    cursor: 'move',
                    opacity: isDragging ? 0.5 : 1,
                }}
            >
                {component.name}
            </div>
        );
    };

    return (
        <div className={`lpb-component-library ${isCollapsed ? 'lpb-collapsed' : ''}`}>
            <div className="lpb-component-header">
                <h3>Thư viện thành phần</h3>
                <button onClick={onToggle} className="lpb-toggle-button">
                    {isCollapsed ? <i className="fas fa-chevron-right"></i> : <i className="fas fa-chevron-left"></i>}
                </button>
            </div>
            {!isCollapsed && (
                <div className="lpb-component-content">
                    <h4>Sections</h4>
                    {EXTENDED_LIBRARY.filter((comp) => comp.type === 'section').map((component) => (
                        <DraggableComponent key={component.id} component={component} />
                    ))}
                    <h4>Elements</h4>
                    {EXTENDED_LIBRARY.filter((comp) => comp.type === 'element').map((component) => (
                        <DraggableComponent key={component.id} component={component} />
                    ))}
                </div>
            )}
        </div>
    );
};

export default ComponentLibrary;
