// F:\landinghub-iconic\apps\web\src\pages\AdminDashboard.js
import React, { useState, useEffect } from 'react';
import api from '@landinghub/api';

const AdminDashboard = () => {
    const [userData, setUserData] = useState({});
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const res = await api.get('/api/user/info');
                setUserData(res.data);
            } catch (err) {
                console.error('Error fetching admin data:', err);
                setError('Không thể tải dữ liệu admin.');
            } finally {
                setLoading(false);
            }
        };
        fetchData();
    }, []);

    if (loading) return <div>Đang tải...</div>;
    if (error) return <div style={{ color: 'red' }}>{error}</div>;

    return (
        <div className="admin-dashboard">
            <h1>Admin Dashboard</h1>
            <section>
                <h2>Thông tin Cá nhân</h2>
                <p>Tên: {userData.name || 'Chưa có thông tin'}</p>
                <p>Email: {userData.email || 'Chưa có thông tin'}</p>
            </section>
            {userData.users && (
                <section>
                    <h2>Danh sách Người dùng</h2>
                    <ul>
                        {userData.users.map((user) => (
                            <li key={user._id}>{user.name} ({user.email})</li>
                        ))}
                    </ul>
                </section>
            )}
            {userData.reports && (
                <section>
                    <h2>Tổng quan Hệ thống</h2>
                    <p>Số Nhà môi giới: {userData.reports.total_brokers || 0}</p>
                    <p>Số Leads Tổng: {userData.reports.total_leads || 0}</p>
                    <p>Doanh thu Tổng: {userData.reports.total_revenue || 0}</p>
                </section>
            )}
        </div>
    );
};

export default React.memo(AdminDashboard);
