import React, { useState } from 'react';
import {
    ChevronRight, ChevronLeft, MousePointer,
    Type, Zap, Sparkles, Bold, Italic, Underline,
    AlignLeft, AlignCenter, AlignRight, AlignJustify,
    Palette
} from 'lucide-react';

const ElementPropertiesPanel = ({ selectedElement, onUpdateElement, isCollapsed, onToggle }) => {
    const [activeTab, setActiveTab] = useState('design');

    if (isCollapsed) {
        return (
            <div style={{
                width: '48px',
                height: '100%',
                background: '#ffffff',
                borderLeft: '1px solid #e5e7eb',
                display: 'flex',
                alignItems: 'flex-start',
                padding: '16px 0'
            }}>
                <button
                    onClick={onToggle}
                    style={{
                        background: 'none',
                        border: 'none',
                        cursor: 'pointer',
                        padding: '8px',
                        color: '#64748b'
                    }}
                    title="M thuc tnh"
                >
                    <ChevronLeft size={18} />
                </button>
            </div>
        );
    }

    if (!selectedElement || !selectedElement.json) {
        return (
            <div style={{
                width: '320px',
                height: '100%',
                background: '#ffffff',
                borderLeft: '1px solid #e5e7eb',
                display: 'flex',
                flexDirection: 'column'
            }}>
                <div style={{
                    padding: '16px',
                    borderBottom: '1px solid #e5e7eb',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'space-between'
                }}>
                    <span style={{
                        fontSize: '14px',
                        fontWeight: '600',
                        color: '#1f2937'
                    }}>Thuc tnh</span>
                    <button
                        onClick={onToggle}
                        style={{
                            background: 'none',
                            border: 'none',
                            cursor: 'pointer',
                            padding: '4px',
                            color: '#64748b'
                        }}
                        title="ng"
                    >
                        <ChevronRight size={18} />
                    </button>
                </div>
                <div style={{
                    flex: 1,
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'center',
                    justifyContent: 'center',
                    padding: '32px',
                    color: '#94a3b8'
                }}>
                    <MousePointer size={48} strokeWidth={1.5} />
                    <p style={{
                        marginTop: '16px',
                        textAlign: 'center',
                        fontSize: '14px',
                        lineHeight: '1.6'
                    }}>
                        Chn mt element  chnh sa
                    </p>
                </div>
            </div>
        );
    }

    const { type, componentData = {}, styles = {}, size = {} } = selectedElement.json;
    const isHeading = type === 'heading';
    const isParagraph = type === 'paragraph';

    if (!isHeading && !isParagraph) {
        return (
            <div style={{
                width: '320px',
                height: '100%',
                background: '#ffffff',
                borderLeft: '1px solid #e5e7eb'
            }}>
                <div style={{
                    padding: '16px',
                    borderBottom: '1px solid #e5e7eb',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'space-between'
                }}>
                    <span style={{ fontSize: '14px', fontWeight: '600' }}>
                        Khng h tr element ny
                    </span>
                    <button onClick={onToggle} style={{
                        background: 'none',
                        border: 'none',
                        cursor: 'pointer',
                        padding: '4px'
                    }}>
                        <ChevronRight size={18} />
                    </button>
                </div>
            </div>
        );
    }

    const handleStyleChange = (property, value) => {
        const updated = {
            ...selectedElement,
            json: {
                ...selectedElement.json,
                styles: {
                    ...selectedElement.json.styles,
                    [property]: value,
                },
            },
        };
        onUpdateElement(updated);
    };

    const handleComponentDataChange = (key, value) => {
        const updated = {
            ...selectedElement,
            json: {
                ...selectedElement.json,
                componentData: {
                    ...selectedElement.json.componentData,
                    [key]: value,
                },
            },
        };
        onUpdateElement(updated);
    };

    const handleSizeChange = (dimension, value) => {
        const updated = {
            ...selectedElement,
            json: {
                ...selectedElement.json,
                size: {
                    ...selectedElement.json.size,
                    [dimension]: parseInt(value) || 0,
                },
            },
        };
        onUpdateElement(updated);
    };

    const toggleFontStyle = (property, value) => {
        const currentValue = styles[property];
        handleStyleChange(property, currentValue === value ? 'normal' : value);
    };

    const renderDesignTab = () => {
        return (
            <div style={{ padding: '16px' }}>
                {/* Thit lp ch */}
                <div style={{ marginBottom: '24px' }}>
                    <h4 style={{
                        fontSize: '13px',
                        fontWeight: '600',
                        color: '#1f2937',
                        marginBottom: '12px'
                    }}>Thit lp ch</h4>

                    {/* Font ch */}
                    <div style={{ marginBottom: '12px' }}>
                        <label style={{
                            display: 'block',
                            fontSize: '12px',
                            color: '#64748b',
                            marginBottom: '6px'
                        }}>Font ch</label>
                        <select
                            value={styles.fontFamily || 'Arial'}
                            onChange={(e) => handleStyleChange('fontFamily', e.target.value)}
                            style={{
                                width: '100%',
                                padding: '8px',
                                border: '1px solid #e5e7eb',
                                borderRadius: '6px',
                                fontSize: '13px',
                                background: '#ffffff'
                            }}
                        >
                            <option value="Arial, sans-serif">Arial</option>
                            <option value="Helvetica, sans-serif">Helvetica</option>
                            <option value="Times New Roman, serif">Times New Roman</option>
                            <option value="Georgia, serif">Georgia</option>
                            <option value="Courier New, monospace">Courier New</option>
                            <option value="Verdana, sans-serif">Verdana</option>
                            <option value="Roboto, sans-serif">Roboto</option>
                            <option value="Open Sans, sans-serif">Open Sans</option>
                            <option value="Poppins, sans-serif">Poppins</option>
                            <option value="Montserrat, sans-serif">Montserrat</option>
                            <option value="Inter, sans-serif">Inter</option>
                            <option value="'Dancing Script', cursive">Dancing Script</option>
                        </select>
                    </div>

                    {/* C ch */}
                    <div style={{ marginBottom: '12px' }}>
                        <label style={{
                            display: 'block',
                            fontSize: '12px',
                            color: '#64748b',
                            marginBottom: '6px'
                        }}>C ch</label>
                        <div style={{ display: 'flex', gap: '8px', alignItems: 'center' }}>
                            <input
                                type="number"
                                value={parseInt(styles.fontSize) || 16}
                                onChange={(e) => handleStyleChange('fontSize', e.target.value + 'px')}
                                style={{
                                    flex: 1,
                                    padding: '8px',
                                    border: '1px solid #e5e7eb',
                                    borderRadius: '6px',
                                    fontSize: '13px'
                                }}
                            />
                            <span style={{ fontSize: '12px', color: '#64748b' }}>px</span>
                        </div>
                    </div>

                    {/* Kiu ch */}
                    <div style={{ marginBottom: '12px' }}>
                        <label style={{
                            display: 'block',
                            fontSize: '12px',
                            color: '#64748b',
                            marginBottom: '6px'
                        }}>Kiu ch</label>
                        <select
                            value={styles.fontWeight || '400'}
                            onChange={(e) => handleStyleChange('fontWeight', e.target.value)}
                            style={{
                                width: '100%',
                                padding: '8px',
                                border: '1px solid #e5e7eb',
                                borderRadius: '6px',
                                fontSize: '13px',
                                background: '#ffffff'
                            }}
                        >
                            <option value="300">Light (H3)</option>
                            <option value="400">Regular (H4)</option>
                            <option value="500">Medium (H5)</option>
                            <option value="600">Semibold (H2)</option>
                            <option value="700">Bold (H1)</option>
                            <option value="800">Extra Bold</option>
                        </select>
                    </div>

                    {/* Nt nh dng */}
                    <div style={{
                        display: 'flex',
                        gap: '4px',
                        marginBottom: '12px',
                        flexWrap: 'wrap'
                    }}>
                        <button
                            onClick={() => toggleFontStyle('fontWeight', 'bold')}
                            style={{
                                padding: '8px 12px',
                                border: `1px solid ${styles.fontWeight === 'bold' || styles.fontWeight === '700' ? '#667eea' : '#e5e7eb'}`,
                                borderRadius: '6px',
                                background: styles.fontWeight === 'bold' || styles.fontWeight === '700' ? '#f0f4ff' : '#ffffff',
                                cursor: 'pointer',
                                display: 'flex',
                                alignItems: 'center',
                                justifyContent: 'center'
                            }}
                            title="Bold"
                        >
                            <Bold size={16} color={styles.fontWeight === 'bold' || styles.fontWeight === '700' ? '#667eea' : '#64748b'} />
                        </button>
                        <button
                            onClick={() => toggleFontStyle('fontStyle', 'italic')}
                            style={{
                                padding: '8px 12px',
                                border: `1px solid ${styles.fontStyle === 'italic' ? '#667eea' : '#e5e7eb'}`,
                                borderRadius: '6px',
                                background: styles.fontStyle === 'italic' ? '#f0f4ff' : '#ffffff',
                                cursor: 'pointer',
                                display: 'flex',
                                alignItems: 'center',
                                justifyContent: 'center'
                            }}
                            title="Italic"
                        >
                            <Italic size={16} color={styles.fontStyle === 'italic' ? '#667eea' : '#64748b'} />
                        </button>
                        <button
                            onClick={() => toggleFontStyle('textDecoration', 'underline')}
                            style={{
                                padding: '8px 12px',
                                border: `1px solid ${styles.textDecoration === 'underline' ? '#667eea' : '#e5e7eb'}`,
                                borderRadius: '6px',
                                background: styles.textDecoration === 'underline' ? '#f0f4ff' : '#ffffff',
                                cursor: 'pointer',
                                display: 'flex',
                                alignItems: 'center',
                                justifyContent: 'center'
                            }}
                            title="Underline"
                        >
                            <Underline size={16} color={styles.textDecoration === 'underline' ? '#667eea' : '#64748b'} />
                        </button>
                        <button
                            onClick={() => handleStyleChange('textTransform', styles.textTransform === 'uppercase' ? 'none' : 'uppercase')}
                            style={{
                                padding: '8px 12px',
                                border: `1px solid ${styles.textTransform === 'uppercase' ? '#667eea' : '#e5e7eb'}`,
                                borderRadius: '6px',
                                background: styles.textTransform === 'uppercase' ? '#f0f4ff' : '#ffffff',
                                cursor: 'pointer',
                                fontSize: '12px',
                                fontWeight: '600',
                                color: styles.textTransform === 'uppercase' ? '#667eea' : '#64748b'
                            }}
                            title="Uppercase"
                        >
                            TT
                        </button>
                    </div>

                    {/* Cn chnh */}
                    <div style={{
                        display: 'flex',
                        gap: '4px',
                        marginBottom: '12px'
                    }}>
                        <button
                            onClick={() => handleStyleChange('textAlign', 'left')}
                            style={{
                                flex: 1,
                                padding: '8px',
                                border: `1px solid ${styles.textAlign === 'left' ? '#667eea' : '#e5e7eb'}`,
                                borderRadius: '6px',
                                background: styles.textAlign === 'left' ? '#f0f4ff' : '#ffffff',
                                cursor: 'pointer',
                                display: 'flex',
                                alignItems: 'center',
                                justifyContent: 'center'
                            }}
                        >
                            <AlignLeft size={16} color={styles.textAlign === 'left' ? '#667eea' : '#64748b'} />
                        </button>
                        <button
                            onClick={() => handleStyleChange('textAlign', 'center')}
                            style={{
                                flex: 1,
                                padding: '8px',
                                border: `1px solid ${styles.textAlign === 'center' ? '#667eea' : '#e5e7eb'}`,
                                borderRadius: '6px',
                                background: styles.textAlign === 'center' ? '#f0f4ff' : '#ffffff',
                                cursor: 'pointer',
                                display: 'flex',
                                alignItems: 'center',
                                justifyContent: 'center'
                            }}
                        >
                            <AlignCenter size={16} color={styles.textAlign === 'center' ? '#667eea' : '#64748b'} />
                        </button>
                        <button
                            onClick={() => handleStyleChange('textAlign', 'right')}
                            style={{
                                flex: 1,
                                padding: '8px',
                                border: `1px solid ${styles.textAlign === 'right' ? '#667eea' : '#e5e7eb'}`,
                                borderRadius: '6px',
                                background: styles.textAlign === 'right' ? '#f0f4ff' : '#ffffff',
                                cursor: 'pointer',
                                display: 'flex',
                                alignItems: 'center',
                                justifyContent: 'center'
                            }}
                        >
                            <AlignRight size={16} color={styles.textAlign === 'right' ? '#667eea' : '#64748b'} />
                        </button>
                        <button
                            onClick={() => handleStyleChange('textAlign', 'justify')}
                            style={{
                                flex: 1,
                                padding: '8px',
                                border: `1px solid ${styles.textAlign === 'justify' ? '#667eea' : '#e5e7eb'}`,
                                borderRadius: '6px',
                                background: styles.textAlign === 'justify' ? '#f0f4ff' : '#ffffff',
                                cursor: 'pointer',
                                display: 'flex',
                                alignItems: 'center',
                                justifyContent: 'center'
                            }}
                        >
                            <AlignJustify size={16} color={styles.textAlign === 'justify' ? '#667eea' : '#64748b'} />
                        </button>
                    </div>

                    {/* Mu ch */}
                    <div style={{ marginBottom: '12px' }}>
                        <label style={{
                            display: 'block',
                            fontSize: '12px',
                            color: '#64748b',
                            marginBottom: '6px'
                        }}>Mu ch</label>
                        <div style={{ display: 'flex', gap: '8px', alignItems: 'center' }}>
                            <input
                                type="color"
                                value={styles.color || '#000000'}
                                onChange={(e) => handleStyleChange('color', e.target.value)}
                                style={{
                                    width: '40px',
                                    height: '40px',
                                    border: '1px solid #e5e7eb',
                                    borderRadius: '6px',
                                    cursor: 'pointer'
                                }}
                            />
                            <input
                                type="text"
                                value={styles.color || '#000000'}
                                onChange={(e) => handleStyleChange('color', e.target.value)}
                                style={{
                                    flex: 1,
                                    padding: '8px',
                                    border: '1px solid #e5e7eb',
                                    borderRadius: '6px',
                                    fontSize: '13px'
                                }}
                            />
                        </div>
                    </div>

                    {/* Line Height */}
                    <div style={{ marginBottom: '12px' }}>
                        <label style={{
                            display: 'block',
                            fontSize: '12px',
                            color: '#64748b',
                            marginBottom: '6px'
                        }}>Khong cch dng</label>
                        <input
                            type="number"
                            step="0.1"
                            value={parseFloat(styles.lineHeight) || 1.5}
                            onChange={(e) => handleStyleChange('lineHeight', e.target.value)}
                            style={{
                                width: '100%',
                                padding: '8px',
                                border: '1px solid #e5e7eb',
                                borderRadius: '6px',
                                fontSize: '13px'
                            }}
                        />
                    </div>

                    {/* Letter Spacing */}
                    <div style={{ marginBottom: '12px' }}>
                        <label style={{
                            display: 'block',
                            fontSize: '12px',
                            color: '#64748b',
                            marginBottom: '6px'
                        }}>Khong cch ch</label>
                        <div style={{ display: 'flex', gap: '8px', alignItems: 'center' }}>
                            <input
                                type="number"
                                step="0.1"
                                value={parseFloat(styles.letterSpacing) || 0}
                                onChange={(e) => handleStyleChange('letterSpacing', e.target.value + 'px')}
                                style={{
                                    flex: 1,
                                    padding: '8px',
                                    border: '1px solid #e5e7eb',
                                    borderRadius: '6px',
                                    fontSize: '13px'
                                }}
                            />
                            <span style={{ fontSize: '12px', color: '#64748b' }}>px</span>
                        </div>
                    </div>
                </div>

                {/* Kch thc */}
                <div style={{ marginBottom: '24px' }}>
                    <h4 style={{
                        fontSize: '13px',
                        fontWeight: '600',
                        color: '#1f2937',
                        marginBottom: '12px'
                    }}>Kch thc</h4>

                    <div style={{ display: 'flex', gap: '8px' }}>
                        <div style={{ flex: 1 }}>
                            <label style={{
                                display: 'block',
                                fontSize: '12px',
                                color: '#64748b',
                                marginBottom: '6px'
                            }}>W</label>
                            <input
                                type="number"
                                value={size.width || 400}
                                onChange={(e) => handleSizeChange('width', e.target.value)}
                                style={{
                                    width: '100%',
                                    padding: '8px',
                                    border: '1px solid #e5e7eb',
                                    borderRadius: '6px',
                                    fontSize: '13px'
                                }}
                            />
                        </div>
                        <div style={{ flex: 1 }}>
                            <label style={{
                                display: 'block',
                                fontSize: '12px',
                                color: '#64748b',
                                marginBottom: '6px'
                            }}>H</label>
                            <input
                                type="number"
                                value={size.height || 80}
                                onChange={(e) => handleSizeChange('height', e.target.value)}
                                style={{
                                    width: '100%',
                                    padding: '8px',
                                    border: '1px solid #e5e7eb',
                                    borderRadius: '6px',
                                    fontSize: '13px'
                                }}
                            />
                        </div>
                    </div>
                </div>

                {/* Text Shadow */}
                <div>
                    <h4 style={{
                        fontSize: '13px',
                        fontWeight: '600',
                        color: '#1f2937',
                        marginBottom: '12px'
                    }}> bng ch</h4>

                    <input
                        type="text"
                        value={styles.textShadow || 'none'}
                        onChange={(e) => handleStyleChange('textShadow', e.target.value)}
                        placeholder="2px 2px 4px rgba(0,0,0,0.3)"
                        style={{
                            width: '100%',
                            padding: '8px',
                            border: '1px solid #e5e7eb',
                            borderRadius: '6px',
                            fontSize: '13px'
                        }}
                    />
                </div>
            </div>
        );
    };

    const renderAnimationTab = () => {
        return (
            <div style={{ padding: '16px' }}>
                <div style={{ marginBottom: '24px' }}>
                    <h4 style={{
                        fontSize: '13px',
                        fontWeight: '600',
                        color: '#1f2937',
                        marginBottom: '12px'
                    }}>Hiu ng hin th</h4>

                    <div style={{ marginBottom: '12px' }}>
                        <label style={{
                            display: 'block',
                            fontSize: '12px',
                            color: '#64748b',
                            marginBottom: '6px'
                        }}>Hiu ng</label>
                        <select
                            value={componentData.animation?.type || 'none'}
                            onChange={(e) => handleComponentDataChange('animation', {
                                ...componentData.animation,
                                type: e.target.value
                            })}
                            style={{
                                width: '100%',
                                padding: '8px',
                                border: '1px solid #e5e7eb',
                                borderRadius: '6px',
                                fontSize: '13px',
                                background: '#ffffff'
                            }}
                        >
                            <option value="none">Khng chn</option>
                            <option value="bounce">bounce</option>
                            <option value="flash">flash</option>
                            <option value="pulse">pulse</option>
                            <option value="rubberBand">rubberBand</option>
                            <option value="shake">shake</option>
                            <option value="swing">swing</option>
                            <option value="tada">tada</option>
                            <option value="wobble">wobble</option>
                            <option value="bounceIn">bounceIn</option>
                            <option value="bounceInDown">bounceInDown</option>
                            <option value="bounceInLeft">bounceInLeft</option>
                            <option value="bounceInRight">bounceInRight</option>
                            <option value="bounceInUp">bounceInUp</option>
                            <option value="fadeIn">fadeIn</option>
                            <option value="fadeInDown">fadeInDown</option>
                            <option value="fadeInLeft">fadeInLeft</option>
                            <option value="fadeInRight">fadeInRight</option>
                            <option value="fadeInUp">fadeInUp</option>
                            <option value="slideInDown">slideInDown</option>
                            <option value="slideInLeft">slideInLeft</option>
                            <option value="slideInRight">slideInRight</option>
                            <option value="slideInUp">slideInUp</option>
                            <option value="zoomIn">zoomIn</option>
                            <option value="zoomInDown">zoomInDown</option>
                            <option value="zoomInLeft">zoomInLeft</option>
                            <option value="zoomInRight">zoomInRight</option>
                            <option value="zoomInUp">zoomInUp</option>
                        </select>
                    </div>

                    <div style={{ marginBottom: '12px' }}>
                        <label style={{
                            display: 'block',
                            fontSize: '12px',
                            color: '#64748b',
                            marginBottom: '6px'
                        }}>Thi gian (ms)</label>
                        <input
                            type="number"
                            value={componentData.animation?.duration || 1000}
                            onChange={(e) => handleComponentDataChange('animation', {
                                ...componentData.animation,
                                duration: parseInt(e.target.value)
                            })}
                            style={{
                                width: '100%',
                                padding: '8px',
                                border: '1px solid #e5e7eb',
                                borderRadius: '6px',
                                fontSize: '13px'
                            }}
                        />
                    </div>

                    <div style={{ marginBottom: '12px' }}>
                        <label style={{
                            display: 'block',
                            fontSize: '12px',
                            color: '#64748b',
                            marginBottom: '6px'
                        }}> tr (ms)</label>
                        <input
                            type="number"
                            value={componentData.animation?.delay || 0}
                            onChange={(e) => handleComponentDataChange('animation', {
                                ...componentData.animation,
                                delay: parseInt(e.target.value)
                            })}
                            style={{
                                width: '100%',
                                padding: '8px',
                                border: '1px solid #e5e7eb',
                                borderRadius: '6px',
                                fontSize: '13px'
                            }}
                        />
                    </div>

                    <div>
                        <label style={{
                            display: 'flex',
                            alignItems: 'center',
                            gap: '8px',
                            fontSize: '12px',
                            color: '#64748b',
                            cursor: 'pointer'
                        }}>
                            <input
                                type="checkbox"
                                checked={componentData.animation?.repeat || false}
                                onChange={(e) => handleComponentDataChange('animation', {
                                    ...componentData.animation,
                                    repeat: e.target.checked
                                })}
                                style={{ cursor: 'pointer' }}
                            />
                            Lp li hiu ng
                        </label>
                    </div>
                </div>
            </div>
        );
    };

    return (
        <div style={{
            width: '320px',
            height: '100%',
            background: '#ffffff',
            borderLeft: '1px solid #e5e7eb',
            display: 'flex',
            flexDirection: 'column'
        }}>
            {/* Header */}
            <div style={{
                padding: '16px',
                borderBottom: '1px solid #e5e7eb',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'space-between'
            }}>
                <input
                    type="text"
                    value={componentData.content || ''}
                    onChange={(e) => handleComponentDataChange('content', e.target.value)}
                    placeholder={isHeading ? 'Tiu ' : 'on vn'}
                    style={{
                        flex: 1,
                        border: 'none',
                        outline: 'none',
                        fontSize: '14px',
                        fontWeight: '600',
                        color: '#1f2937'
                    }}
                />
                <button
                    onClick={onToggle}
                    style={{
                        background: 'none',
                        border: 'none',
                        cursor: 'pointer',
                        padding: '4px',
                        color: '#64748b',
                        marginLeft: '8px'
                    }}
                    title="ng"
                >
                    <ChevronRight size={18} />
                </button>
            </div>

            {/* Tabs */}
            <div style={{
                display: 'flex',
                borderBottom: '1px solid #e5e7eb',
                background: '#f9fafb'
            }}>
                <button
                    onClick={() => setActiveTab('design')}
                    style={{
                        flex: 1,
                        padding: '12px',
                        border: 'none',
                        background: activeTab === 'design' ? '#ffffff' : 'transparent',
                        borderBottom: activeTab === 'design' ? '2px solid #667eea' : 'none',
                        cursor: 'pointer',
                        fontSize: '13px',
                        fontWeight: '500',
                        color: activeTab === 'design' ? '#667eea' : '#64748b',
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center',
                        gap: '6px'
                    }}
                >
                    <Type size={16} />
                    Thit k
                </button>
                <button
                    onClick={() => setActiveTab('animation')}
                    style={{
                        flex: 1,
                        padding: '12px',
                        border: 'none',
                        background: activeTab === 'animation' ? '#ffffff' : 'transparent',
                        borderBottom: activeTab === 'animation' ? '2px solid #667eea' : 'none',
                        cursor: 'pointer',
                        fontSize: '13px',
                        fontWeight: '500',
                        color: activeTab === 'animation' ? '#667eea' : '#64748b',
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center',
                        gap: '6px'
                    }}
                >
                    <Sparkles size={16} />
                    Hiu ng
                </button>
            </div>

            {/* Content */}
            <div style={{
                flex: 1,
                overflowY: 'auto',
                overflowX: 'hidden'
            }}>
                {activeTab === 'design' && renderDesignTab()}
                {activeTab === 'animation' && renderAnimationTab()}
            </div>
        </div>
    );
};

export default ElementPropertiesPanel;
