import React, { useState, useEffect } from 'react';
import { DndProvider } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
import { toast } from 'react-toastify';
import { parse } from 'node-html-parser';
import Canvas from '../components/create-page/Canvas';
import ComponentLibrary from '../components/create-page/ComponentLibrary';
import PropertiesPanel from '../components/create-page/PropertiesPanel';
import Toolbar from './create-page/Toolbar';
import api from '@landinghub/api';
import '../styles/CreateLanding.css';
import { useSearchParams, useNavigate } from 'react-router-dom';
import {jwtDecode} from 'jwt-decode';

// Định nghĩa các biến CSS tương ứng với CreateLanding.css
const CSS_VARIABLES = {
    '--primary': '#6366f1',
    '--primary-dark': '#4f46e5',
    '--primary-light': '#a5b4fc',
    '--secondary': '#f1f5f9',
    '--accent': '#06b6d4',
    '--success': '#10b981',
    '--warning': '#f59e0b',
    '--danger': '#ef4444',
    '--dark': '#1e293b',
    '--light': '#f8fafc',
    '--white': '#ffffff',
    '--text-primary': '#334155',
    '--text-secondary': '#64748b',
    '--text-light': '#94a3b8',
    '--text-inverse': '#ffffff',
    '--border-color': '#e2e8f0',
    '--border-radius': '8px',
    '--border-radius-lg': '12px',
    '--border-radius-xl': '16px',
    '--shadow-sm': '0 1px 2px 0 rgba(0, 0, 0, 0.05)',
    '--shadow': '0 4px 6px -1px rgba(0, 0, 0, 0.1)',
    '--shadow-lg': '0 10px 15px -3px rgba(0, 0, 0, 0.1)',
    '--shadow-xl': '0 20px 25px -5px rgba(0, 0, 0, 0.1)',
    '--canvas-desktop': '1200px',
};

// Hàm parse styles từ chuỗi style
const parseStyles = (styleString) => {
    if (!styleString) return {};
    return styleString.split(';').reduce((acc, style) => {
        const [key, value] = style.split(':').map(s => s.trim());
        if (key && value) acc[key] = value;
        return acc;
    }, {});
};

// Hàm parse HTML từ S3 thành pageData
const parseHTMLToPageData = (html) => {
    try {
        console.log('Parsing HTML from S3:', html); // Log HTML để debug
        const root = parse(html, { blockTextElements: { style: true } });
        const pageContainer = root.querySelector('.page-container');
        if (!pageContainer) {
            console.warn('No .page-container found in HTML');
            toast.warn('HTML không chứa .page-container. Khởi tạo trang trống.');
            return { canvas: { width: 1200, background: CSS_VARIABLES['--white'] }, elements: [] };
        }

        const elements = [];
        pageContainer.childNodes.forEach((node, index) => {
            if (node.tagName === 'DIV') {
                const styles = node.getAttribute('style') || '';
                const styleObj = parseStyles(styles);

                const position = {
                    desktop: { x: parseFloat(styleObj.left) || 0, y: parseFloat(styleObj.top) || 0 },
                    tablet: { x: parseFloat(styleObj.left) || 0, y: parseFloat(styleObj.top) || 0 },
                    mobile: { x: parseFloat(styleObj.left) || 0, y: parseFloat(styleObj.top) || 0 },
                };
                const size = {
                    width: parseFloat(styleObj.width) || 100,
                    height: parseFloat(styleObj.height) || 100,
                };

                let type = 'container';
                let children = [];

                node.childNodes.forEach((child, childIndex) => {
                    const childStyles = child.getAttribute('style') ? parseStyles(child.getAttribute('style')) : {};
                    if (child.tagName === 'H2') {
                        children.push({
                            id: `child-${index}-${childIndex}-${Date.now()}`,
                            type: 'heading',
                            content: child.textContent.trim() || 'Tiêu đề',
                            styles: { ...childStyles, 'font-size': childStyles['font-size'] || '18px', color: childStyles.color || CSS_VARIABLES['--text-primary'] },
                        });
                    } else if (child.tagName === 'P') {
                        children.push({
                            id: `child-${index}-${childIndex}-${Date.now()}`,
                            type: 'paragraph',
                            content: child.textContent.trim() || 'Đoạn văn',
                            styles: { ...childStyles, 'font-size': childStyles['font-size'] || '14px', color: childStyles.color || CSS_VARIABLES['--text-secondary'] },
                        });
                    } else if (child.tagName === 'BUTTON') {
                        children.push({
                            id: `child-${index}-${childIndex}-${Date.now()}`,
                            type: 'button',
                            content: child.textContent.trim() || 'Nút',
                            styles: {
                                ...childStyles,
                                'background-color': childStyles['background-color'] || CSS_VARIABLES['--primary'],
                                color: childStyles.color || CSS_VARIABLES['--text-inverse'],
                                'border-radius': childStyles['border-radius'] || CSS_VARIABLES['--border-radius'],
                                padding: childStyles.padding || '8px 16px',
                            },
                        });
                    } else if (child.tagName === 'IMG') {
                        children.push({
                            id: `child-${index}-${childIndex}-${Date.now()}`,
                            type: 'image',
                            content: child.getAttribute('src') || '',
                            styles: { ...childStyles, width: childStyles.width || '100px', height: childStyles.height || 'auto' },
                        });
                    }
                });

                elements.push({
                    id: `element-${index}-${Date.now()}`,
                    type: children.length > 0 ? 'container' : (node.firstChild?.tagName?.toLowerCase() || 'container'),
                    position,
                    size,
                    styles: {
                        ...styleObj,
                        'border-radius': styleObj['border-radius'] || CSS_VARIABLES['--border-radius'],
                        'background-color': styleObj['background-color'] || CSS_VARIABLES['--white'],
                        'box-shadow': styleObj['box-shadow'] || CSS_VARIABLES['--shadow'],
                    },
                    children: children.length > 0 ? children : undefined,
                    componentData: children.length === 0 ? { content: node.textContent.trim() || '' } : undefined,
                });
            }
        });

        const pageData = {
            canvas: { width: parseInt(CSS_VARIABLES['--canvas-desktop']), background: CSS_VARIABLES['--white'] },
            elements,
        };
        console.log('Parsed pageData:', JSON.stringify(pageData, null, 2)); // Log pageData để debug
        return pageData;
    } catch (error) {
        console.error('Error parsing HTML:', error);
        toast.error('Lỗi khi phân tích nội dung trang. Khởi tạo trang trống.');
        return { canvas: { width: parseInt(CSS_VARIABLES['--canvas-desktop']), background: CSS_VARIABLES['--white'] }, elements: [] };
    }
};

// Hàm render HTML tĩnh từ pageData
const renderStaticHTML = (pageData) => {
    console.log('Rendering static HTML with pageData:', JSON.stringify(pageData, null, 2));
    let html = `
        <!DOCTYPE html>
        <html lang="vi">
        <head>
            <meta charset="UTF-8">
            <meta name="viewport" content="width=device-width, initial-scale=1.0">
            <title>Landing Page</title>
            <style>
                :root {
                    --primary: ${CSS_VARIABLES['--primary']};
                    --primary-dark: ${CSS_VARIABLES['--primary-dark']};
                    --primary-light: ${CSS_VARIABLES['--primary-light']};
                    --secondary: ${CSS_VARIABLES['--secondary']};
                    --accent: ${CSS_VARIABLES['--accent']};
                    --success: ${CSS_VARIABLES['--success']};
                    --warning: ${CSS_VARIABLES['--warning']};
                    --danger: ${CSS_VARIABLES['--danger']};
                    --dark: ${CSS_VARIABLES['--dark']};
                    --light: ${CSS_VARIABLES['--light']};
                    --white: ${CSS_VARIABLES['--white']};
                    --text-primary: ${CSS_VARIABLES['--text-primary']};
                    --text-secondary: ${CSS_VARIABLES['--text-secondary']};
                    --text-light: ${CSS_VARIABLES['--text-light']};
                    --text-inverse: ${CSS_VARIABLES['--text-inverse']};
                    --border-color: ${CSS_VARIABLES['--border-color']};
                    --border-radius: ${CSS_VARIABLES['--border-radius']};
                    --shadow: ${CSS_VARIABLES['--shadow']};
                }
                body { font-family: 'Inter', Arial, sans-serif; margin: 0; padding: 0; }
                .page-container { 
                    position: relative; 
                    width: ${pageData.canvas.width}px; 
                    min-height: 600px; 
                    margin: 0 auto; 
                    background: ${pageData.canvas.background}; 
                    border-radius: ${CSS_VARIABLES['--border-radius-xl']};
                    box-shadow: ${CSS_VARIABLES['--shadow-xl']};
                }
                @media (max-width: 768px) {
                    .page-container { width: ${CSS_VARIABLES['--canvas-tablet']}; }
                }
                @media (max-width: 375px) {
                    .page-container { width: ${CSS_VARIABLES['--canvas-mobile']}; }
                }
            </style>
        </head>
        <body>
            <div class="page-container">
    `;
    pageData.elements.forEach((element) => {
        const { type, componentData, position, size, styles, children } = element;
        const positionStyles = position?.desktop ? `position: absolute; left: ${position.desktop.x}px; top: ${position.desktop.y}px;` : '';
        const sizeStyles = size ? `width: ${size.width}px; height: ${size.height}px;` : '';
        const cssStyles = styles ? Object.entries(styles).map(([key, value]) => `${key}: ${value};`).join(' ') : '';
        let elementHtml = `<div class="lpb-canvas-element" style="${positionStyles} ${sizeStyles} ${cssStyles}" data-type="${type}">`;

        if (type === 'section' || type === 'container' || type === 'group') {
            if (children && children.length > 0) {
                children.forEach((child) => {
                    const childStyles = child.styles ? Object.entries(child.styles).map(([key, value]) => `${key}: ${value};`).join(' ') : '';
                    if (child.type === 'heading') {
                        elementHtml += `<h2 class="lpb-child-element" style="${childStyles}">${child.content || 'Tiêu đề'}</h2>`;
                    } else if (child.type === 'paragraph') {
                        elementHtml += `<p class="lpb-child-element" style="${childStyles}">${child.content || 'Đoạn văn'}</p>`;
                    } else if (child.type === 'button') {
                        elementHtml += `<button class="lpb-child-element" style="${childStyles}">${child.content || 'Nút'}</button>`;
                    } else if (child.type === 'image') {
                        elementHtml += `<img class="lpb-child-element" src="${child.content}" style="${childStyles}" />`;
                    }
                });
            } else {
                elementHtml += componentData?.content || '';
            }
        } else if (type === 'heading') {
            elementHtml += `<h2 class="lpb-child-element" style="${cssStyles}">${componentData?.content || 'Tiêu đề'}</h2>`;
        } else if (type === 'paragraph') {
            elementHtml += `<p class="lpb-child-element" style="${cssStyles}">${componentData?.content || 'Đoạn văn'}</p>`;
        } else if (type === 'button') {
            elementHtml += `<button class="lpb-child-element" style="${cssStyles}">${componentData?.content || 'Nút'}</button>`;
        } else if (type === 'image') {
            elementHtml += `<img class="lpb-child-element" src="${componentData?.content}" style="${cssStyles}" />`;
        }
        elementHtml += `</div>`;
        html += elementHtml;
    });
    html += `
            </div>
        </body>
        </html>
    `;
    return html;
};

const CreateLanding = () => {
    const [searchParams] = useSearchParams();
    const navigate = useNavigate();
    const pageId = searchParams.get('id');

    const [pageData, setPageData] = useState({
        canvas: { width: parseInt(CSS_VARIABLES['--canvas-desktop']), background: CSS_VARIABLES['--white'] },
        elements: [],
    });
    const [selectedIds, setSelectedIds] = useState([]);
    const [selectedChildId, setSelectedChildId] = useState(null);
    const [viewMode, setViewMode] = useState('desktop');
    const [zoomLevel, setZoomLevel] = useState(100);
    const [gridSize, setGridSize] = useState(10);
    const [showGrid, setShowGrid] = useState(true);
    const [history, setHistory] = useState([]);
    const [historyIndex, setHistoryIndex] = useState(-1);
    const [isLoading, setIsLoading] = useState(true);
    const [isLibraryCollapsed, setIsLibraryCollapsed] = useState(false); // Thêm trạng thái cho sidebar
    // Kiểm tra token
    useEffect(() => {
        const initializeAuth = async () => {
            const token = localStorage.getItem('token');
            if (!token) {
                console.error('No token found in localStorage');
                toast.error('Vui lòng đăng nhập để tiếp tục');
                navigate('/auth');
                return;
            }

            let decoded;
            try {
                decoded = jwtDecode(token);
                const currentTime = Math.floor(Date.now() / 1000);
                if (decoded.exp && decoded.exp < currentTime) {
                    console.error('Token has expired');
                    localStorage.removeItem('token');
                    toast.error('Phiên đăng nhập đã hết hạn. Vui lòng đăng nhập lại.');
                    navigate('/auth');
                    return;
                }
                api.defaults.headers.common['Authorization'] = `Bearer ${token}`;
            } catch (err) {
                console.error('Error decoding token:', err);
                toast.error('Phiên đăng nhập không hợp lệ');
                navigate('/auth');
                return;
            }
        };
        initializeAuth();
    }, [navigate]);

    // Tải nội dung từ S3
    useEffect(() => {
        if (!pageId) {
            toast.error('Không tìm thấy ID trang. Vui lòng tạo trang mới từ dashboard.');
            navigate('/pages');
            return;
        }

        // Kiểm tra định dạng pageId
        if (!pageId.match(/^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i)) {
            toast.error('ID trang không hợp lệ. Vui lòng kiểm tra lại.');
            navigate('/pages');
            return;
        }

        const fetchPageContent = async () => {
            setIsLoading(true);
            try {
                console.log('Fetching content for pageId:', pageId);
                const response = await api.get(`/api/pages/${pageId}/content`);
                console.log('Page content response:', response.data);
                if (response.data.html) {
                    const parsedPageData = parseHTMLToPageData(response.data.html);
                    setPageData(parsedPageData);
                    setHistory([parsedPageData]);
                    setHistoryIndex(0);
                } else {
                    console.warn('No HTML content found in response');
                    toast.warn('Không tìm thấy nội dung trang. Khởi tạo trang trống.');
                    setPageData({ canvas: { width: parseInt(CSS_VARIABLES['--canvas-desktop']), background: CSS_VARIABLES['--white'] }, elements: [] });
                }
            } catch (error) {
                console.error('Lỗi khi lấy nội dung trang:', error);
                const errorMessage = error.response?.data?.error || error.message;
                toast.error('Lỗi khi lấy nội dung trang: ' + errorMessage);
                if (error.response?.status === 401) {
                    localStorage.removeItem('token');
                    navigate('/auth');
                } else if (error.response?.status === 400 && errorMessage.includes('Invalid page ID')) {
                    toast.error('ID trang không hợp lệ. Vui lòng kiểm tra lại.');
                    navigate('/pages');
                } else {
                    navigate('/pages');
                }
            } finally {
                setIsLoading(false);
            }
        };
        fetchPageContent();
    }, [pageId, navigate]);

    const handleSelectElement = (ids, append = false) => {
        if (Array.isArray(ids)) {
            setSelectedIds(append ? [...selectedIds, ...ids] : ids);
        } else {
            setSelectedIds(append ? [...selectedIds, ids] : [ids]);
        }
        setSelectedChildId(null);
    };

    const handleSelectChild = (parentId, childId) => {
        if (!parentId) {
            setSelectedIds([]);
            setSelectedChildId(null);
            return;
        }
        if (!pageData.elements.find((el) => el.id === parentId)) {
            console.warn(`Parent element with id ${parentId} not found`);
            setSelectedIds([]);
            setSelectedChildId(null);
            return;
        }
        setSelectedIds([parentId]);
        setSelectedChildId(childId);
    };

    const handleAddElement = (element) => {
        setPageData((prev) => {
            const newElement = {
                ...element,
                id: `${element.id}-${Date.now()}`,
                position: {
                    desktop: element.position?.desktop || { x: 0, y: 0 },
                    tablet: element.position?.tablet || { x: 0, y: 0 },
                    mobile: element.position?.mobile || { x: 0, y: 0 },
                },
                styles: {
                    ...element.styles,
                    'border-radius': element.styles?.['border-radius'] || CSS_VARIABLES['--border-radius'],
                    'background-color': element.styles?.['background-color'] || CSS_VARIABLES['--white'],
                    'box-shadow': element.styles?.['box-shadow'] || CSS_VARIABLES['--shadow'],
                },
            };
            const newPageData = {
                ...prev,
                elements: [...prev.elements, newElement],
            };
            setHistory([...history.slice(0, historyIndex + 1), newPageData]);
            setHistoryIndex(historyIndex + 1);
            return newPageData;
        });
    };

    const handleUpdatePosition = (id, position, mode) => {
        setPageData((prev) => {
            const newPageData = {
                ...prev,
                elements: prev.elements.map((el) =>
                    el.id === id
                        ? {
                            ...el,
                            position: mode === 'replace' ? position : { ...el.position, ...position },
                        }
                        : el
                ),
            };
            setHistory([...history.slice(0, historyIndex + 1), newPageData]);
            setHistoryIndex(historyIndex + 1);
            return newPageData;
        });
    };

    const handleUpdateSize = (id, size) => {
        setPageData((prev) => {
            const newPageData = {
                ...prev,
                elements: prev.elements.map((el) =>
                    el.id === id ? { ...el, size } : el
                ),
            };
            setHistory([...history.slice(0, historyIndex + 1), newPageData]);
            setHistoryIndex(historyIndex + 1);
            return newPageData;
        });
    };

    const handleDeleteElement = (id) => {
        setPageData((prev) => {
            const newPageData = {
                ...prev,
                elements: prev.elements.filter((el) => el.id !== id),
            };
            setHistory([...history.slice(0, historyIndex + 1), newPageData]);
            setHistoryIndex(historyIndex + 1);
            return newPageData;
        });
        setSelectedIds((prev) => prev.filter((selId) => selId !== id));
        setSelectedChildId(null);
    };

    const handleEditElement = (id) => {
        console.log('Edit element:', id);
    };

    const handleDuplicateElement = (id) => {
        const element = pageData.elements.find((el) => el.id === id);
        if (element) {
            const newElement = {
                ...element,
                id: `${element.id}-${Date.now()}`,
                position: {
                    desktop: {
                        x: (element.position.desktop?.x || 0) + 10,
                        y: (element.position.desktop?.y || 0) + 10,
                    },
                    tablet: {
                        x: (element.position.tablet?.x || 0) + 10,
                        y: (element.position.tablet?.y || 0) + 10,
                    },
                    mobile: {
                        x: (element.position.mobile?.x || 0) + 10,
                        y: (element.position.mobile?.y || 0) + 10,
                    },
                },
            };
            setPageData((prev) => {
                const newPageData = {
                    ...prev,
                    elements: [...prev.elements, newElement],
                };
                setHistory([...history.slice(0, historyIndex + 1), newPageData]);
                setHistoryIndex(historyIndex + 1);
                return newPageData;
            });
        }
    };

    const handleMoveElementUp = (id) => {
        setPageData((prev) => {
            const index = prev.elements.findIndex((el) => el.id === id);
            if (index <= 0) return prev;
            const newElements = [...prev.elements];
            [newElements[index - 1], newElements[index]] = [newElements[index], newElements[index - 1]];
            const newPageData = { ...prev, elements: newElements };
            setHistory([...history.slice(0, historyIndex + 1), newPageData]);
            setHistoryIndex(historyIndex + 1);
            return newPageData;
        });
    };

    const handleMoveElementDown = (id) => {
        setPageData((prev) => {
            const index = prev.elements.findIndex((el) => el.id === id);
            if (index >= prev.elements.length - 1) return prev;
            const newElements = [...prev.elements];
            [newElements[index], newElements[index + 1]] = [newElements[index + 1], newElements[index]];
            const newPageData = { ...prev, elements: newElements };
            setHistory([...history.slice(0, historyIndex + 1), newPageData]);
            setHistoryIndex(historyIndex + 1);
            return newPageData;
        });
    };

    const handleGroupElements = (ids) => {
        const groupId = `group-${Date.now()}`;
        setPageData((prev) => {
            const selectedElements = prev.elements.filter((el) => ids.includes(el.id));
            if (selectedElements.length === 0) return prev;
            const newPageData = {
                ...prev,
                elements: [
                    ...prev.elements.filter((el) => !ids.includes(el.id)),
                    {
                        id: groupId,
                        type: 'group',
                        position: {
                            desktop: { x: 0, y: 0 },
                            tablet: { x: 0, y: 0 },
                            mobile: { x: 0, y: 0 },
                        },
                        size: { width: 300, height: 200 },
                        styles: {
                            'border-radius': CSS_VARIABLES['--border-radius'],
                            'background-color': CSS_VARIABLES['--white'],
                            'box-shadow': CSS_VARIABLES['--shadow'],
                        },
                        children: selectedElements.map((el) => el.id),
                    },
                ],
            };
            setHistory([...history.slice(0, historyIndex + 1), newPageData]);
            setHistoryIndex(historyIndex + 1);
            return newPageData;
        });
        setSelectedIds([groupId]);
        toast.success('Đã nhóm các phần tử!');
    };

    const handleUpdateChildren = (parentId, dragIndex, dropIndex) => {
        setPageData((prev) => {
            const newPageData = {
                ...prev,
                elements: prev.elements.map((el) => {
                    if (el.id === parentId) {
                        const newChildren = [...(el.children || [])];
                        if (dragIndex < 0 || dragIndex >= newChildren.length || dropIndex < 0 || dropIndex >= newChildren.length) {
                            console.warn('Invalid dragIndex or dropIndex:', { dragIndex, dropIndex, childrenLength: newChildren.length });
                            return el;
                        }
                        const [movedChild] = newChildren.splice(dragIndex, 1);
                        newChildren.splice(dropIndex, 0, movedChild);
                        return { ...el, children: newChildren };
                    }
                    return el;
                }),
            };
            setHistory([...history.slice(0, historyIndex + 1), newPageData]);
            setHistoryIndex(historyIndex + 1);
            return newPageData;
        });
    };

    const handleUndo = () => {
        if (historyIndex >= 0) {
            setPageData(history[historyIndex]);
            setHistoryIndex(historyIndex - 1);
            toast.info('Đã hoàn tác!');
        }
    };

    const handleRedo = () => {
        if (historyIndex < history.length - 1) {
            setPageData(history[historyIndex + 1]);
            setHistoryIndex(historyIndex + 1);
            toast.info('Đã khôi phục!');
        }
    };

    const handleSave = async () => {
        if (!pageId) {
            toast.error('Không tìm thấy ID trang. Vui lòng tạo trang mới từ dashboard.');
            return;
        }

        try {
            const htmlContent = renderStaticHTML(pageData);
            console.log('Sending PUT request to /api/pages/:id with payload:', { html: htmlContent, pageId });
            const response = await api.put(`/api/pages/${pageId}`, { html: htmlContent });
            if (response.data.success) {
                toast.success('Lưu trang thành công!');
            } else {
                toast.error('Lưu trang thất bại: ' + (response.data.error || 'Lỗi không xác định'));
            }
        } catch (error) {
            console.error('Lỗi khi lưu trang:', error);
            const errorMessage = error.response?.data?.error || error.message;
            toast.error('Lỗi khi lưu trang: ' + errorMessage);
            if (error.response?.status === 401) {
                localStorage.removeItem('token');
                navigate('/auth');
            }
        }
    };

    const handlePreview = () => {
        const htmlContent = renderStaticHTML(pageData);
        const newWindow = window.open('', '_blank');
        newWindow.document.write(htmlContent);
        newWindow.document.close();
    };

    const handleGenerateCode = async () => {
        if (!pageId) {
            toast.error('Không tìm thấy ID trang để triển khai.');
            return;
        }

        try {
            const htmlContent = renderStaticHTML(pageData);
            console.log('Saving before publish, sending PUT request:', { html: htmlContent });
            await api.put(`/api/pages/${pageId}`, { html: htmlContent });
            console.log('Sending POST request to /api/pages/:id/publish');
            const response = await api.post(`/api/pages/${pageId}/publish`);
            console.log('Publish response:', response.data);
            if (response.data.success && response.data.page?.url) {
                toast.success(`Triển khai thành công! Truy cập tại: ${response.data.page.url}`);
            } else {
                toast.error('Triển khai thất bại: ' + (response.data.error || 'Lỗi không xác định'));
            }
        } catch (error) {
            console.error('Lỗi khi triển khai:', error);
            const errorMessage = error.response?.data?.error || error.message;
            toast.error('Lỗi khi triển khai trang: ' + errorMessage);
            if (error.response?.status === 401) {
                localStorage.removeItem('token');
                navigate('/auth');
            }
        }
    };

    const selectedElement = (() => {
        if (selectedChildId) {
            const parent = pageData.elements.find((el) => el.id === selectedIds[0]);
            if (!parent || !parent.children) return null;
            const child = parent.children.find((child) => child.id === selectedChildId);
            return child ? { json: child, isChild: true } : null;
        }
        const element = pageData.elements.find((el) => el.id === selectedIds[0]);
        return element ? { json: element, isChild: false } : null;
    })();

    if (isLoading) {
        return (
            <div className="lpb-loading">
                <div className="lpb-loading-spinner"></div>
                <p>Đang tải trang...</p>
            </div>
        );
    }

    return (
        <DndProvider backend={HTML5Backend}>
            <div className="lpb-container">
                <link
                    rel="stylesheet"
                    href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.5.1/css/all.min.css"
                    crossOrigin="anonymous"
                    referrerPolicy="no-referrer"
                />
                <Toolbar
                    onSave={handleSave}
                    onPreview={handlePreview}
                    onGenerateCode={handleGenerateCode}
                    viewMode={viewMode}
                    onViewModeChange={setViewMode}
                    onUndo={handleUndo}
                    onRedo={handleRedo}
                    canUndo={historyIndex >= 0}
                    canRedo={historyIndex < history.length - 1}
                    zoomLevel={zoomLevel}
                    onZoom={(direction) => setZoomLevel(direction === 'in' ? Math.min(200, zoomLevel + 10) : Math.max(50, zoomLevel - 10))}
                    onToggleGrid={() => setShowGrid(!showGrid)}
                    showGrid={showGrid}
                    gridSize={gridSize}
                    onSetGridSize={setGridSize}
                />
                <div className="lpb-main-content">
                  <ComponentLibrary
                        isCollapsed={false}
                        onToggle={() => {}}
                        onAddElement={handleAddElement}
                    />
                    <Canvas
                        pageData={pageData}
                        selectedIds={selectedIds}
                        onSelectElement={handleSelectElement}
                        onUpdatePosition={handleUpdatePosition}
                        onUpdateSize={handleUpdateSize}
                        onDeleteElement={handleDeleteElement}
                        onAddElement={handleAddElement}
                        onGroupElements={handleGroupElements}
                        viewMode={viewMode}
                        zoomLevel={zoomLevel}
                        gridSize={gridSize}
                        showGrid={showGrid}
                        onEditElement={handleEditElement}
                        onDuplicateElement={handleDuplicateElement}
                        onMoveElementUp={handleMoveElementUp}
                        onMoveElementDown={handleMoveElementDown}
                        onSelectChild={handleSelectChild}
                        selectedChildId={selectedChildId}
                        onUpdateChildren={handleUpdateChildren}
                    />
                    <PropertiesPanel
                        selectedElement={selectedElement}
                        onUpdateElement={(updatedElement) => {
                            setPageData((prev) => {
                                const newPageData = {
                                    ...prev,
                                    elements: prev.elements.map((el) =>
                                        el.id === selectedIds[0]
                                            ? updatedElement.isChild
                                                ? {
                                                    ...el,
                                                    children: el.children.map((child) =>
                                                        child.id === selectedChildId
                                                            ? { ...child, ...updatedElement.json }
                                                            : child
                                                    ),
                                                }
                                                : { ...el, ...updatedElement.json }
                                            : el
                                    ),
                                };
                                setHistory([...history.slice(0, historyIndex + 1), newPageData]);
                                setHistoryIndex(historyIndex + 1);
                                return newPageData;
                            });
                        }}
                        isCollapsed={false}
                        onToggle={() => {}}
                    />
                </div>
            </div>
        </DndProvider>
    );
};

export default CreateLanding;
