import React, { useState, useEffect, useContext } from 'react';
import { UserContext } from '../context/UserContext';
import Header from '../components/Header';
import Sidebar from '../components/Sidebar';
import { jwtDecode } from 'jwt-decode';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';
import InfiniteScroll from 'react-infinite-scroll-component';
import AOS from 'aos';
import 'aos/dist/aos.css';
import '../styles/Templates.css';
import DogLoader from '../components/Loader';

const Templates = () => {
    const { user } = useContext(UserContext);
    const [loading, setLoading] = useState(true);
    const [userRole, setUserRole] = useState(null);
    const [templates, setTemplates] = useState([]);
    const [error, setError] = useState('');
    const [selectedCategory, setSelectedCategory] = useState('T·∫•t c·∫£');
    const [usingTemplateId, setUsingTemplateId] = useState(null);
    const [previewTemplate, setPreviewTemplate] = useState(null);
    const [showPreview, setShowPreview] = useState(false);
    const [hasMore, setHasMore] = useState(true);
    const [page, setPage] = useState(1);

    const location = useLocation();
    const navigate = useNavigate();
    const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000';
    const categories = ['T·∫•t c·∫£', 'B·∫•t ƒë·ªông s·∫£n', 'Th∆∞∆°ng m·∫°i ƒëi·ªán t·ª≠', 'Nh√† h√†ng', 'S·ª©c kh·ªèe', 'Gi√°o d·ª•c', 'Kh√°c'];

    useEffect(() => {
        const initializeAuth = async () => {
            const token = localStorage.getItem('token');
            if (!token) {
                navigate('/auth');
                setLoading(false);
                return;
            }
            try {
                const decodedToken = jwtDecode(token);
                if (!decodedToken.role || !decodedToken.userId) {
                    navigate('/auth');
                    setLoading(false);
                    return;
                }
                setUserRole(decodedToken.role);
            } catch (err) {
                navigate('/auth');
            } finally {
                setLoading(false);
            }
        };

        if (user?.role) {
            setUserRole(user.role);
            setLoading(false);
        } else {
            initializeAuth();
        }
    }, [user, navigate]);

    useEffect(() => {
        AOS.init({ duration: 800, once: true, offset: 100 });
    }, []);

    useEffect(() => {
        if (userRole && ['user', 'admin'].includes(userRole)) {
            setTemplates([]);
            setPage(1);
            setHasMore(true);
            loadTemplates(1);
        }
    }, [userRole, selectedCategory]);

    // ‚úÖ FIX 2: category=undefined ‚Üí ''
    const loadTemplates = async (pageNum = 1) => {
        try {
            setLoading(pageNum === 1);
            const token = localStorage.getItem('token');
            const params = new URLSearchParams({
                page: pageNum,
                limit: 12,
                category: selectedCategory !== 'T·∫•t c·∫£' ? selectedCategory : '' // ‚úÖ FIX!
            });

            const response = await axios.get(`${API_BASE_URL}/api/templates?${params}`, {
                headers: { Authorization: `Bearer ${token}` }
            });

            const newTemplates = response.data.templates || [];
            setTemplates(prev => pageNum === 1 ? newTemplates : [...prev, ...newTemplates]);
            setHasMore(newTemplates.length === 12); // ‚úÖ FIX: Check exactly 12
            setError('');
        } catch (err) {
            console.error('Error loading templates:', err);
            setError('Kh√¥ng th·ªÉ t·∫£i danh s√°ch template');
        } finally {
            setLoading(false);
        }
    };

    const loadMore = () => {
        if (!loading && hasMore) {
            const nextPage = page + 1;
            setPage(nextPage);
            loadTemplates(nextPage);
        }
    };

    const handlePreview = async (templateId) => {
        try {
            const token = localStorage.getItem('token');
            const response = await axios.get(`${API_BASE_URL}/api/templates/${templateId}/preview`, {
                headers: { Authorization: `Bearer ${token}` }
            });
            setPreviewTemplate(response.data);
            setShowPreview(true);
        } catch (err) {
            alert('Kh√¥ng th·ªÉ xem tr∆∞·ªõc template');
        }
    };

    const handleUseTemplate = async (templateId, templateName) => {
        if (usingTemplateId) return;
        const confirmed = window.confirm(`T·∫°o landing page t·ª´ "${templateName}"?`);
        if (!confirmed) return;

        try {
            setUsingTemplateId(templateId);
            const token = localStorage.getItem('token');
            const response = await axios.post(
                `${API_BASE_URL}/api/templates/${templateId}/use`,
                {
                    name: `${templateName} - Copy`,
                    description: `T·ª´ template ${templateName}`
                },
                { headers: { Authorization: `Bearer ${token}` } }
            );

            if (response.data.success) {
                alert('‚úÖ T·∫°o th√†nh c√¥ng!');
                navigate(`/pages?id=${response.data.page.id}`);
            }
        } catch (err) {
            alert(`‚ùå ${err.response?.data?.error || 'L·ªói s·ª≠ d·ª•ng template'}`);
        } finally {
            setUsingTemplateId(null);
        }
    };

    // ‚úÖ FIX 3: S3 URL ‚Üí Placeholder
    const getSafeImageUrl = (url) => {
        if (!url || url.includes('s3://')) {
            return `https://via.placeholder.com/400x240/667eea/ffffff?text=T`;
        }
        return url;
    };

    // ‚úÖ FIX 1: UNIQUE KEY = index + id
    const getUniqueKey = (template, index) => `${template.id}-${index}-${page}`;

    const getPlaceholderImage = (templateName, category) => {
        const colors = {
            'B·∫•t ƒë·ªông s·∫£n': 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
            'Th∆∞∆°ng m·∫°i ƒëi·ªán t·ª≠': 'linear-gradient(135deg, #f093fb 0%, #f5576c 100%)',
            'Nh√† h√†ng': 'linear-gradient(135deg, #4facfe 0%, #00f2fe 100%)',
            'S·ª©c kh·ªèe': 'linear-gradient(135deg, #43e97b 0%, #38f9d7 100%)',
            'Gi√°o d·ª•c': 'linear-gradient(135deg, #fa709a 0%, #fee140 100%)',
            'Kh√°c': 'linear-gradient(135deg, #30cfd0 0%, #330867 100%)'
        };
        return {
            background: colors[category] || colors['Kh√°c'],
            text: templateName.charAt(0).toUpperCase()
        };
    };

    if (loading) return <DogLoader />;

    const isCompact = location.pathname === '/dashboard';

    return (
        <div className="templates-page">
            <Header role={userRole} />
            <div className="templates-main">
                <Sidebar role={userRole} isCompact={isCompact} />
                <div className="templates-content">
                    {/* HEADER PRO */}
                    <div className="templates-hero">
                        <div className="hero-content">
                            <h1>üé® Th∆∞ Vi·ªán Templates</h1>
                            <p>200+ m·∫´u thi·∫øt k·∫ø chuy√™n nghi·ªáp ‚Ä¢ T·∫°o landing page trong 30s</p>
                        </div>
                        <div className="hero-stats">
                            <span className="stat">{templates.length}/20</span>
                            <span className="stat">Templates</span>
                        </div>
                    </div>

                    {/* FILTERS PRO */}
                    <div className="templates-filters">
                        {categories.map(category => (
                            <button
                                key={category}
                                className={`filter-btn ${selectedCategory === category ? 'active' : ''}`}
                                onClick={() => setSelectedCategory(category)}
                            >
                                {category}
                            </button>
                        ))}
                    </div>

                    {error && (
                        <div className="error-alert">
                            <i className="fas fa-exclamation-triangle"></i>
                            {error}
                        </div>
                    )}

                    {/* INFINITE SCROLL */}
                    <InfiniteScroll
                        dataLength={templates.length}
                        next={loadMore}
                        hasMore={hasMore}
                        loader={<DogLoader small />}
                        endMessage={
                            <div className="end-message">
                                <i className="fas fa-check-circle"></i>
                                <p>ƒê√£ t·∫£i h·∫øt templates!</p>
                            </div>
                        }
                        className="templates-grid"
                    >
                        {templates.map((template, index) => {
                            const placeholder = getPlaceholderImage(template.name, template.category);
                            const safeImage = getSafeImageUrl(template.thumbnail_url);
                            return (
                                <div key={getUniqueKey(template, index)} className="template-card" data-aos="fade-up">
                                    <div className="card-media">
                                        <img
                                            src={safeImage}
                                            alt={template.name}
                                            className="template-image"
                                            onError={(e) => {
                                                e.target.style.display = 'none';
                                                e.target.nextSibling.style.display = 'flex';
                                            }}
                                        />
                                        <div className="image-placeholder" style={{ background: placeholder.background }}>
                                            <span>{placeholder.text}</span>
                                        </div>

                                        {/* OVERLAY BUTTONS */}
                                        <div className="card-overlay">
                                            <button
                                                className="btn btn-secondary"
                                                onClick={() => handlePreview(template.id)}
                                            >
                                                <i className="fas fa-eye"></i>
                                            </button>
                                            <button
                                                className="btn btn-primary"
                                                onClick={() => handleUseTemplate(template.id, template.name)}
                                                disabled={usingTemplateId === template.id}
                                            >
                                                {usingTemplateId === template.id ? (
                                                    <><i className="fas fa-spinner fa-spin"></i> T·∫°o</>
                                                ) : (
                                                    <><i className="fas fa-rocket"></i> D√πng</>
                                                )}
                                            </button>
                                        </div>

                                        {/* BADGES */}
                                        {template.is_featured && <div className="badge featured">‚≠ê N·ªïi b·∫≠t</div>}
                                        {template.price > 0 && (
                                            <div className="badge premium">{template.formatted_price}</div>
                                        )}
                                        {template.price === 0 && <div className="badge free">Mi·ªÖn ph√≠</div>}
                                    </div>

                                    {/* CONTENT */}
                                    <div className="card-content">
                                        <h3 className="card-title">{template.name}</h3>
                                        <p className="card-desc">{template.description}</p>
                                        <div className="card-meta">
                                            <span className="category">{template.category}</span>
                                            <span className="usage">
                                                <i className="fas fa-users"></i> {template.usage_count}
                                            </span>
                                        </div>
                                    </div>
                                </div>
                            );
                        })}
                    </InfiniteScroll>

                    {/* MODAL PREVIEW */}
                    {showPreview && previewTemplate && (
                        <div className="modal-overlay" onClick={() => setShowPreview(false)}>
                            <div className="modal-content" onClick={e => e.stopPropagation()}>
                                <div className="modal-header">
                                    <h2>{previewTemplate.template?.name}</h2>
                                    <button className="modal-close" onClick={() => setShowPreview(false)}>
                                        <i className="fas fa-times"></i>
                                    </button>
                                </div>
                                <iframe
                                    srcDoc={previewTemplate.html}
                                    title="Preview"
                                    className="modal-iframe"
                                />
                                <div className="modal-footer">
                                    <button
                                        className="btn btn-primary"
                                        onClick={() => {
                                            setShowPreview(false);
                                            handleUseTemplate(previewTemplate.template.id, previewTemplate.template.name);
                                        }}
                                    >
                                        <i className="fas fa-rocket"></i> S·ª≠ d·ª•ng template n√†y
                                    </button>
                                </div>
                            </div>
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

export default React.memo(Templates);
