import React, { useReducer, useCallback, useRef, useEffect } from 'react';
import { DndProvider } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
import { useHotkeys } from 'react-hotkeys-hook';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import Toolbar from './create-page/Toolbar';
import ComponentLibrary from './create-page/ComponentLibrary';
import Canvas from './create-page/Canvas';
import PropertiesPanel from './create-page/PropertiesPanel';
import '../styles/CreateLanding.css';

const initialState = {
    pageData: {
        canvas: { width: 1200, height: 'auto', background: '#ffffff' },
        elements: [],
    },
    selectedIds: [],
    history: [],
    historyIndex: -1,
    viewMode: 'desktop',
    zoomLevel: 100,
    gridSize: 8,
    showGrid: true,
    leftSidebarCollapsed: false,
    rightSidebarCollapsed: false,
};

const reducer = (state, action) => {
    switch (action.type) {
        case 'ADD_ELEMENT':
            const newElement = {
                ...action.payload,
                id: `${action.payload.id}-${Date.now()}`,
                position: {
                    desktop: action.payload.position.desktop,
                    tablet: action.payload.position.tablet,
                    mobile: action.payload.position.mobile,
                },
            };
            const newPageDataAdd = {
                ...state.pageData,
                elements: [...state.pageData.elements, newElement],
            };
            return {
                ...state,
                pageData: newPageDataAdd,
                history: [...state.history.slice(0, state.historyIndex + 1), newPageDataAdd],
                historyIndex: state.historyIndex + 1,
                selectedIds: [newElement.id],
            };
        case 'UPDATE_POSITION':
            const newPageDataPos = {
                ...state.pageData,
                elements: state.pageData.elements.map((el) =>
                    action.payload.ids.includes(el.id)
                        ? {
                            ...el,
                            position: {
                                ...el.position,
                                [state.viewMode]: {
                                    x: el.position[state.viewMode].x + (action.payload.position[state.viewMode].x || 0),
                                    y: el.position[state.viewMode].y + (action.payload.position[state.viewMode].y || 0),
                                },
                            },
                        }
                        : el
                ),
            };
            return {
                ...state,
                pageData: newPageDataPos,
                history: action.payload.commit ? [...state.history.slice(0, state.historyIndex + 1), newPageDataPos] : state.history,
                historyIndex: action.payload.commit ? state.historyIndex + 1 : state.historyIndex,
            };
        case 'UPDATE_SIZE':
            const newPageDataSize = {
                ...state.pageData,
                elements: state.pageData.elements.map((el) =>
                    el.id === action.payload.id ? { ...el, size: action.payload.size } : el
                ),
            };
            return {
                ...state,
                pageData: newPageDataSize,
                history: [...state.history.slice(0, state.historyIndex + 1), newPageDataSize],
                historyIndex: state.historyIndex + 1,
            };
        case 'DELETE_ELEMENT':
            const newPageDataDel = {
                ...state.pageData,
                elements: state.pageData.elements.filter((el) => !action.payload.includes(el.id)),
            };
            return {
                ...state,
                pageData: newPageDataDel,
                history: [...state.history.slice(0, state.historyIndex + 1), newPageDataDel],
                historyIndex: state.historyIndex + 1,
                selectedIds: state.selectedIds.filter((id) => !action.payload.includes(id)),
            };
        case 'SELECT_ELEMENT':
            return { ...state, selectedIds: action.payload };
        case 'GROUP_ELEMENTS':
            const groupId = `group-${Date.now()}`;
            const newPageDataGroup = {
                ...state.pageData,
                elements: [
                    ...state.pageData.elements.filter((el) => !action.payload.includes(el.id)),
                    {
                        id: groupId,
                        type: 'group',
                        position: {
                            desktop: { x: 0, y: 0 },
                            tablet: { x: 0, y: 0 },
                            mobile: { x: 0, y: 0 },
                        },
                        size: { width: 300, height: 200 },
                        children: action.payload,
                    },
                ],
            };
            return {
                ...state,
                pageData: newPageDataGroup,
                history: [...state.history.slice(0, state.historyIndex + 1), newPageDataGroup],
                historyIndex: state.historyIndex + 1,
                selectedIds: [groupId],
            };
        case 'UNDO':
            if (state.historyIndex <= 0) return state;
            return {
                ...state,
                pageData: state.history[state.historyIndex - 1],
                historyIndex: state.historyIndex - 1,
            };
        case 'REDO':
            if (state.historyIndex >= state.history.length - 1) return state;
            return {
                ...state,
                pageData: state.history[state.historyIndex + 1],
                historyIndex: state.historyIndex + 1,
            };
        case 'SET_VIEW_MODE':
            return { ...state, viewMode: action.payload };
        case 'SET_ZOOM':
            return { ...state, zoomLevel: action.payload };
        case 'TOGGLE_GRID':
            return { ...state, showGrid: !state.showGrid };
        case 'SET_GRID_SIZE':
            return { ...state, gridSize: action.payload };
        case 'TOGGLE_SIDEBAR':
            return {
                ...state,
                [action.payload.side === 'left' ? 'leftSidebarCollapsed' : 'rightSidebarCollapsed']:
                    !state[action.payload.side === 'left' ? 'leftSidebarCollapsed' : 'rightSidebarCollapsed'],
            };
        default:
            return state;
    }
};

const CreateLandingPage = () => {
    const [state, dispatch] = useReducer(reducer, initialState);
    const { pageData, selectedIds, history, historyIndex, viewMode, zoomLevel, gridSize, showGrid, leftSidebarCollapsed, rightSidebarCollapsed } = state;
    const fileInputRef = useRef(null);

    const addElement = useCallback((element) => {
        dispatch({ type: 'ADD_ELEMENT', payload: element });
        toast.success('Đã thêm phần tử!');
    }, []);

    const updatePosition = useCallback((ids, position, commit) => {
        dispatch({ type: 'UPDATE_POSITION', payload: { ids, position, commit } });
        if (commit) toast.success('Đã cập nhật vị trí!');
    }, []);

    const updateSize = useCallback((id, size) => {
        dispatch({ type: 'UPDATE_SIZE', payload: { id, size } });
        toast.success('Đã cập nhật kích thước!');
    }, []);

    const deleteElement = useCallback((ids) => {
        dispatch({ type: 'DELETE_ELEMENT', payload: ids });
        toast.success('Đã xóa phần tử!');
    }, []);

    const selectElement = useCallback((ids) => {
        dispatch({ type: 'SELECT_ELEMENT', payload: ids });
    }, []);

    const groupElements = useCallback((ids) => {
        dispatch({ type: 'GROUP_ELEMENTS', payload: ids });
        toast.success('Đã nhóm phần tử!');
    }, []);

    const undo = useCallback(() => {
        dispatch({ type: 'UNDO' });
        toast.info('Đã hoàn tác!');
    }, []);

    const redo = useCallback(() => {
        dispatch({ type: 'REDO' });
        toast.info('Đã khôi phục!');
    }, []);

    const handleZoom = useCallback((direction) => {
        dispatch({ type: 'SET_ZOOM', payload: direction === 'in' ? Math.min(200, zoomLevel + 10) : Math.max(50, zoomLevel - 10) });
    }, [zoomLevel]);

    const handleSave = useCallback(() => {
        localStorage.setItem('landing-page-builder', JSON.stringify({ pageData, viewMode, zoomLevel }));
        toast.success('Đã lưu trang!');
    }, [pageData, viewMode, zoomLevel]);

    useEffect(() => {
        const autoSave = setTimeout(() => {
            if (pageData.elements.length > 0) {
                localStorage.setItem('landing-page-builder-autosave', JSON.stringify({ pageData, viewMode, zoomLevel }));
                toast.info('Đã tự động lưu!');
            }
        }, 30000);
        return () => clearTimeout(autoSave);
    }, [pageData, viewMode, zoomLevel]);

    useHotkeys('ctrl+s', handleSave, [handleSave]);
    useHotkeys('ctrl+z', undo, [undo]);
    useHotkeys('ctrl+shift+z', redo, [redo]);
    useHotkeys('ctrl+d', () => {
        selectedIds.forEach((id) => {
            const element = pageData.elements.find((el) => el.id === id);
            if (element) {
                addElement({
                    ...element,
                    id: `${element.id}-copy-${Date.now()}`,
                    position: {
                        desktop: { x: element.position.desktop.x + 20, y: element.position.desktop.y + 20 },
                        tablet: { x: element.position.tablet.x + 20, y: element.position.tablet.y + 20 },
                        mobile: { x: element.position.mobile.x + 20, y: element.position.mobile.y + 20 },
                    },
                });
            }
        });
    }, [selectedIds, pageData.elements, addElement]);

    return (
        <DndProvider backend={HTML5Backend}>
            <div className="lpb-container">
                <link
                    rel="stylesheet"
                    href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.5.1/css/all.min.css"
                    crossOrigin="anonymous"
                    referrerPolicy="no-referrer"
                />
                <ToastContainer position="top-right" autoClose={3000} hideProgressBar />
                <Toolbar
                    onSave={handleSave}
                    onUndo={undo}
                    onRedo={redo}
                    canUndo={historyIndex > 0}
                    canRedo={historyIndex < history.length - 1}
                    viewMode={viewMode}
                    onViewModeChange={(mode) => dispatch({ type: 'SET_VIEW_MODE', payload: mode })}
                    onZoom={handleZoom}
                    onToggleGrid={() => dispatch({ type: 'TOGGLE_GRID' })}
                    showGrid={showGrid}
                    gridSize={gridSize}
                    onSetGridSize={(size) => dispatch({ type: 'SET_GRID_SIZE', payload: size })}
                />
                <div className="lpb-main-content">
                    <div className={`lpb-sidebar-left ${leftSidebarCollapsed ? 'lpb-sidebar-collapsed' : ''}`}>
                        <ComponentLibrary
                            isCollapsed={leftSidebarCollapsed}
                            onToggle={() => dispatch({ type: 'TOGGLE_SIDEBAR', payload: { side: 'left' } })}
                            onAddElement={addElement}
                        />
                    </div>
                    <div className="lpb-canvas-container">
                        <Canvas
                            pageData={pageData}
                            selectedIds={selectedIds}
                            onSelectElement={selectElement}
                            onUpdatePosition={updatePosition}
                            onUpdateSize={updateSize}
                            onDeleteElement={deleteElement}
                            onAddElement={addElement}
                            onGroupElements={groupElements}
                            viewMode={viewMode}
                            zoomLevel={zoomLevel}
                            gridSize={gridSize}
                            showGrid={showGrid}
                        />
                    </div>
                    <div className={`lpb-sidebar-right ${rightSidebarCollapsed ? 'lpb-sidebar-collapsed' : ''}`}>
                        <PropertiesPanel
                            selectedElements={pageData.elements.filter((el) => selectedIds.includes(el.id))}
                            onUpdateElement={(id, updates) => dispatch({ type: 'UPDATE_SIZE', payload: { id, size: updates.size } })}
                            isCollapsed={rightSidebarCollapsed}
                            onToggle={() => dispatch({ type: 'TOGGLE_SIDEBAR', payload: { side: 'right' } })}
                        />
                    </div>
                </div>
            </div>
        </DndProvider>
    );
};

export default CreateLandingPage;
