import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';
import api from '@landinghub/api';
import SidebarComponent from '../components/pages-content/SidebarComponent';
import HeaderComponent from '../components/pages-content/HeaderComponent';
import ControlsComponent from '../components/pages-content/ControlsComponent';
import LandingList from '../components/pages-content/LandingList';
import PaginationComponent from '../components/pages-content/PaginationComponent';
import DogLoader from '../components/DogLoader';
import '../styles/PageContent.css';

const PageContent = () => {
    const [userData, setUserData] = useState(null);
    const [userId, setUserId] = useState(null);
    const [landingPages, setLandingPages] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [statusFilter, setStatusFilter] = useState('Trạng thái');
    const [menuOpen, setMenuOpen] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const navigate = useNavigate();

    // Initialize token and userId
    useEffect(() => {
        const initializeAuth = async () => {
            const token = localStorage.getItem('token');
            if (!token) {
                console.error('No token found in localStorage');
                setError('Vui lòng đăng nhập để tiếp tục');
                setLoading(false);
                navigate('/auth');
                return;
            }

            let decoded;
            try {
                decoded = jwtDecode(token);
                if (!decoded.userId) {
                    throw new Error('Invalid token: userId not found');
                }
            } catch (err) {
                console.error('Error decoding token:', err);
                setError('Phiên đăng nhập không hợp lệ');
                setLoading(false);
                navigate('/auth');
                return;
            }

            // Set userId and Authorization header
            setUserId(decoded.userId);
            api.defaults.headers.common['Authorization'] = `Bearer ${token}`;

            // Fetch user data
            try {
                const res = await api.get('/api/user/info');
                setUserData(res.data);
            } catch (err) {
                console.error('Error fetching user data:', err);
            }
        };
        initializeAuth();
    }, [navigate]);

    // Fetch landing pages
    useEffect(() => {
        if (!userId) {
            return;
        }

        const fetchLandingPages = async () => {
            try {
                const response = await api.get('/api/pages');
                setLandingPages(response.data);
            } catch (err) {
                console.error('Fetch error:', err);
                setError('Không thể tải danh sách landing page: ' + (err.response?.data?.msg || err.message));
            } finally {
                setLoading(false);
            }
        };
        fetchLandingPages();
    }, [userId]);

    const filteredPages = landingPages.filter((page) => {
        const matchesSearch = page.name.toLowerCase().includes(searchTerm.toLowerCase());
        const matchesStatus = statusFilter === 'Trạng thái' || page.status === statusFilter;
        return matchesSearch && matchesStatus;
    });

    // Navigation handlers với state passing
    const handleCreatePage = () => {
        navigate('/pages/create');
    };

    const handleEditPage = (page) => {
        navigate(`/pages/edit/${page._id}`, { state: { page } });
    };

    const handlePreviewPage = (page) => {
        // Mở preview trong tab mới với URL đầy đủ
        if (page.url) {
            window.open(page.url, '_blank');
        } else {
            // Hoặc điều hướng đến trang preview nội bộ
            navigate(`/pages/preview/${page.id}`, { state: { page } });
        }
    };

    const handlePublishPage = async (pageId) => {
        try {
            setLoading(true);
            const response = await api.post(`/api/pages/${pageId}/publish`);
            setLandingPages(landingPages.map((p) => (p.id === pageId ? response.data : p)));
        } catch (err) {
            setError('Lỗi khi triển khai landing page: ' + (err.response?.data?.msg || err.message));
            console.error('Publish error:', err);
        } finally {
            setLoading(false);
        }
    };

    const handleDeletePage = async (pageId) => {
        if (!window.confirm('Bạn có chắc muốn xóa landing page này?')) return;
        try {
            setLoading(true);
            await api.delete(`/api/pages/${pageId}`);
            setLandingPages(landingPages.filter((p) => p.id !== pageId));
        } catch (err) {
            setError('Lỗi khi xóa landing page: ' + (err.response?.data?.msg || err.message));
            console.error('Delete error:', err);
        } finally {
            setLoading(false);
        }
    };

    const handleMoreClick = (pageId) => {
        setMenuOpen(menuOpen === pageId ? null : pageId);
    };

    if (loading) {
        return <DogLoader />;
    }

    return (
        <div className="page-content">
            {error && (
                <div className="error">
                    {error}
                    <button onClick={() => setError(null)} className="error-dismiss">
                        Đóng
                    </button>
                </div>
            )}
            <SidebarComponent />
            <div className="main-content">
                <HeaderComponent onCreateClick={handleCreatePage} />
                <ControlsComponent
                    searchTerm={searchTerm}
                    setSearchTerm={setSearchTerm}
                    statusFilter={statusFilter}
                    setStatusFilter={setStatusFilter}
                />
                <LandingList
                    landingPages={landingPages}
                    filteredPages={filteredPages}
                    handleMoreClick={handleMoreClick}
                    menuOpen={menuOpen}
                    handlePreviewPage={handlePreviewPage}
                    handleEditPage={handleEditPage}
                    handlePublishPage={handlePublishPage}
                    handleDeletePage={handleDeletePage}
                />
                <PaginationComponent filteredPages={filteredPages} landingPages={landingPages} />
            </div>
        </div>
    );
};

export default PageContent;
