                break;
            case 'button':
                const action = componentData.events?.onClick || {};
                const actionAttr = action.type === 'popup' && action.popupId
                    ? `onclick="togglePopup('${action.popupId}')"`
                    : action.type === 'navigate' && action.url
                        ? `onclick="handleButtonClick('navigate', { url: '${action.url}', newTab: ${action.newTab || false} }, '${id}')"`
                        : action.type === 'triggerApi' && action.apiUrl
                            ? `onclick="handleButtonClick('triggerApi', { apiUrl: '${action.apiUrl}', method: '${action.method || 'GET'}' }, '${id}')"`
                            : action.type === 'submitForm' && action.apiUrl
                                ? `onclick="handleButtonClick('submitForm', { apiUrl: '${action.apiUrl}', method: '${action.method || 'POST'}' }, '${id}')"`
                                : action.type === 'scrollToSection' && action.sectionId
                                    ? `onclick="handleButtonClick('scrollToSection', { sectionId: '${action.sectionId}', smooth: ${action.smooth || false} }, '${id}')"`
                                    : '';
                elementHtml += `<button class="lpb-button" ${actionAttr} style="padding: ${componentData.padding || '10px 20px'}; border-radius: ${componentData.borderRadius || '8px'}; background: ${componentData.background || '#2563eb'}; color: ${componentData.color || '#fff'}; border: ${componentData.border || 'none'}; cursor: pointer; font-size: ${componentData.fontSize || '1rem'}; font-weight: ${componentData.fontWeight || '600'}; text-align: ${componentData.textAlign || 'center'}; ${cssStyles}">${componentData.content || 'Nút'}</button>`;
                break;
            case 'image':
