                break;
            case 'grid':
                elementHtml += `<div class="ladi-grid" style="grid-template-columns: repeat(${componentData.columns || 2}, 1fr); gap: ${componentData.gap || '20px'}; ${cssStyles}" ondragover="handleDragOver(event)" ondrop="handleDrop(event)">`;
                if (Array.isArray(componentData.items)) {
                    componentData.items.forEach((item, index) => {
                        elementHtml += `<div style="padding: ${componentData.itemPadding || '16px'}; background: ${componentData.itemBackground || '#ffffff'}; border-radius: ${componentData.itemBorderRadius || '8px'}; box-shadow: ${componentData.itemBoxShadow || '0 2px 8px rgba(0,0,0,0.1)'}; ${cssStyles}" draggable="true" ondragstart="handleDragStart(event)">`;
                        if (item.image) {
