                    <div class="lpb-popup-overlay" style="position: fixed; top: 0; left: 0; right: 0; bottom: 0; background: rgba(0, 0, 0, 0.4); z-index: -1;" onclick="LPB.closePopup('${id}')"></div>
                    <div class="lpb-popup-content" style="padding: ${componentData.padding || '20px'};">
                        ${componentData.title ? `<h3 style="font-size: ${componentData.titleFontSize || '1.5rem'}; margin: ${componentData.titleMargin || '0'}; color: ${componentData.titleColor || '#1f2937'}; font-weight: ${componentData.titleFontWeight || '600'};">${componentData.title}</h3>` : ''}
                        ${componentData.content ? `<p style="font-size: ${componentData.contentFontSize || '1rem'}; margin: ${componentData.contentMargin || '0'}; color: ${componentData.contentColor || '#4b5563'};">${componentData.content}</p>` : ''}
