import React, { useState } from 'react';
import {
    ChevronRight, ChevronLeft, MousePointer,
    Palette, Zap, Upload, Settings
} from 'lucide-react';
import '../../styles/PropertiesPanel.css';

const COLOR_PRESETS = {
    '--primary': '#667eea',
    '--primary-dark': '#5568d3',
    '--primary-light': '#a5b4fc',
    '--secondary': '#f1f5f9',
    '--accent': '#764ba2',
    '--success': '#10b981',
    '--warning': '#f59e0b',
    '--danger': '#ef4444',
    '--dark': '#1e293b',
    '--light': '#f8fafc',
    '--white': '#ffffff',
    '--text-primary': '#334155',
    '--text-secondary': '#64748b',
    '--text-light': '#94a3b8',
    '--text-inverse': '#ffffff',
};

const PropertiesPanel = ({ selectedElement, onUpdateElement, isCollapsed, onToggle }) => {
    const [activeTab, setActiveTab] = useState('design');
    const [imagePreview, setImagePreview] = useState(null);

    if (isCollapsed) {
        return (
            <div className="properties-panel-collapsed">
                <button onClick={onToggle} className="toggle-button" title="Mở thuộc tính">
                    <ChevronLeft size={18} />
                </button>
            </div>
        );
    }

    if (!selectedElement || !selectedElement.json) {
        return (
            <div className="properties-panel">
                <div className="panel-header">
                    <input
                        type="text"
                        value=""
                        placeholder="Thuộc tính"
                        disabled
                        className="panel-title-input"
                    />
                    <button onClick={onToggle} className="toggle-button" title="Đóng">
                        <ChevronRight size={18} />
                    </button>
                </div>
                <div className="panel-empty">
                    <MousePointer className="empty-icon" size={48} strokeWidth={1.5} />
                    <p className="empty-text">Chọn một section, element hoặc popup để chỉnh sửa</p>
                </div>
            </div>
        );
    }

    const { type, componentData = {}, styles = {}, size = {}, position = {} } = selectedElement.json;

    const handleStyleChange = (property, value) => {
        const updated = {
            ...selectedElement,
            json: {
                ...selectedElement.json,
                styles: {
                    ...selectedElement.json.styles,
                    [property]: value,
                },
            },
        };
        onUpdateElement(updated);
    };

    const handleComponentDataChange = (key, value) => {
        const updated = {
            ...selectedElement,
            json: {
                ...selectedElement.json,
                componentData: {
                    ...selectedElement.json.componentData,
                    [key]: value,
                },
            },
        };
        onUpdateElement(updated);
    };

    const handleSizeChange = (dimension, value) => {
        const updated = {
            ...selectedElement,
            json: {
                ...selectedElement.json,
                size: {
                    ...selectedElement.json.size,
                    [dimension]: value,
                },
            },
        };
        onUpdateElement(updated);
    };

    const handlePositionChange = (property, value) => {
        const updated = {
            ...selectedElement,
            json: {
                ...selectedElement.json,
                position: {
                    ...selectedElement.json.position,
                    [property]: parseInt(value),
                },
            },
        };
        onUpdateElement(updated);
    };

    const handleImageUpload = (e) => {
        const file = e.target.files[0];
        if (file) {
            const reader = new FileReader();
            reader.onloadend = () => {
                setImagePreview(reader.result);
                handleComponentDataChange('backgroundImage', reader.result);
            };
            reader.readAsDataURL(file);
        }
    };

    const handleGradientChange = (gradient) => {
        handleStyleChange('background', gradient);
    };

    const renderDesignTab = () => {
        const isSection = type === 'section';
        const isPopup = type === 'popup';
        const isText = type === 'text';
        const isButton = type === 'button';
        const isLine = type === 'line';

        return (
            <div className="panel-section">
                <h4 className="section-title">
                    <Palette size={16} strokeWidth={1.5} />
                    Thiết kế
                </h4>
                <div className="input-group">
                    {(isSection || isPopup) && (
                        <>
                            <div>
                                <label className="input-label">Loại nền</label>
                                <select
                                    value={componentData.backgroundType || 'color'}
                                    onChange={(e) => handleComponentDataChange('backgroundType', e.target.value)}
                                    className="input-select"
                                >
                                    <option value="color">Màu đơn</option>
                                    <option value="image">Hình ảnh</option>
                                    <option value="gradient">Gradient</option>
                                </select>
                            </div>

                            {componentData.backgroundType === 'color' && (
                                <div>
                                    <label className="input-label">Màu nền</label>
                                    <input
                                        type="color"
                                        value={styles.backgroundColor || '#ffffff'}
                                        onChange={(e) => handleStyleChange('backgroundColor', e.target.value)}
                                        className="input-color"
                                    />
                                    <div className="color-preset-grid">
                                        {Object.entries(COLOR_PRESETS).map(([key, value]) => (
                                            <div
                                                key={key}
                                                className={`color-preset-item ${styles.backgroundColor === value ? 'active' : ''}`}
                                                style={{ backgroundColor: value }}
                                                onClick={() => handleStyleChange('backgroundColor', value)}
                                                title={key}
                                            />
                                        ))}
                                    </div>
                                </div>
                            )}

                            {componentData.backgroundType === 'image' && (
                                <>
                                    <div>
                                        <label className="input-label">Tải ảnh nền</label>
                                        <input
                                            type="file"
                                            accept="image/*"
                                            onChange={handleImageUpload}
                                            style={{ display: 'none' }}
                                            id="background-image-upload"
                                        />
                                        <label htmlFor="background-image-upload" className="upload-button">
                                            <Upload size={16} strokeWidth={1.5} />
                                            Chọn ảnh
                                        </label>
                                        {(imagePreview || componentData.backgroundImage) && (
                                            <div className="image-preview">
                                                <img
                                                    src={imagePreview || componentData.backgroundImage}
                                                    alt="Background Preview"
                                                    style={{ maxWidth: '100%', maxHeight: '80px' }}
                                                />
                                            </div>
                                        )}
                                    </div>
                                    <div>
                                        <label className="input-label">Vị trí ảnh nền</label>
                                        <select
                                            value={styles.backgroundPosition || 'center'}
                                            onChange={(e) => handleStyleChange('backgroundPosition', e.target.value)}
                                            className="input-select"
                                        >
                                            <option value="center">Giữa</option>
                                            <option value="top">Trên</option>
                                            <option value="bottom">Dưới</option>
                                            <option value="left">Trái</option>
                                            <option value="right">Phải</option>
                                        </select>
                                    </div>
                                </>
                            )}

                            {componentData.backgroundType === 'gradient' && (
                                <div>
                                    <label className="input-label">Gradient</label>
                                    <input
                                        type="text"
                                        value={styles.background || 'linear-gradient(90deg, #667eea, #764ba2)'}
                                        onChange={(e) => handleGradientChange(e.target.value)}
                                        className="input-field"
                                        placeholder="linear-gradient(90deg, #667eea, #764ba2)"
                                    />
                                </div>
                            )}

                            <div>
                                <label className="input-label">Lớp phủ (Overlay)</label>
                                <input
                                    type="color"
                                    value={componentData.overlayColor || '#000000'}
                                    onChange={(e) => handleComponentDataChange('overlayColor', e.target.value)}
                                    className="input-color"
                                />
                                <label className="input-label">Độ mờ lớp phủ</label>
                                <input
                                    type="range"
                                    min="0"
                                    max="1"
                                    step="0.1"
                                    value={componentData.overlayOpacity || 0}
                                    onChange={(e) => handleComponentDataChange('overlayOpacity', parseFloat(e.target.value))}
                                    className="input-range"
                                />
                            </div>
                        </>
                    )}

                    {isText && (
                        <>
                            <div>
                                <label className="input-label">Kích thước chữ</label>
                                <input
                                    type="text"
                                    value={styles.fontSize || '16px'}
                                    onChange={(e) => handleStyleChange('fontSize', e.target.value)}
                                    className="input-field"
                                    placeholder="16px"
                                />
                            </div>
                            <div>
                                <label className="input-label">Màu chữ</label>
                                <input
                                    type="color"
                                    value={styles.color || '#000000'}
                                    onChange={(e) => handleStyleChange('color', e.target.value)}
                                    className="input-color"
                                />
                            </div>
                            <div>
                                <label className="input-label">Phông chữ</label>
                                <select
                                    value={styles.fontFamily || 'Arial'}
                                    onChange={(e) => handleStyleChange('fontFamily', e.target.value)}
                                    className="input-select"
                                >
                                    <option value="Arial">Arial</option>
                                    <option value="Helvetica">Helvetica</option>
                                    <option value="Times New Roman">Times New Roman</option>
                                    <option value="Roboto">Roboto</option>
                                </select>
                            </div>
                        </>
                    )}

                    {isButton && (
                        <>
                            <div>
                                <label className="input-label">Màu nền nút</label>
                                <input
                                    type="color"
                                    value={styles.backgroundColor || '#667eea'}
                                    onChange={(e) => handleStyleChange('backgroundColor', e.target.value)}
                                    className="input-color"
                                />
                            </div>
                            <div>
                                <label className="input-label">Màu chữ</label>
                                <input
                                    type="color"
                                    value={styles.color || '#ffffff'}
                                    onChange={(e) => handleStyleChange('color', e.target.value)}
                                    className="input-color"
                                />
                            </div>
                            <div>
                                <label className="input-label">Kích thước chữ</label>
                                <input
                                    type="text"
                                    value={styles.fontSize || '14px'}
                                    onChange={(e) => handleStyleChange('fontSize', e.target.value)}
                                    className="input-field"
                                    placeholder="14px"
                                />
                            </div>
                        </>
                    )}

                    {isLine && (
                        <>
                            <div>
                                <label className="input-label">Độ dày đường kẻ</label>
                                <input
                                    type="text"
                                    value={styles.borderTopWidth || '2px'}
                                    onChange={(e) => handleStyleChange('borderTopWidth', e.target.value)}
                                    className="input-field"
                                    placeholder="2px"
                                />
                            </div>
                            <div>
                                <label className="input-label">Màu đường kẻ</label>
                                <input
                                    type="color"
                                    value={styles.borderTopColor || '#000000'}
                                    onChange={(e) => handleStyleChange('borderTopColor', e.target.value)}
                                    className="input-color"
                                />
                            </div>
                            <div>
                                <label className="input-label">Kiểu đường kẻ</label>
                                <select
                                    value={styles.borderTopStyle || 'solid'}
                                    onChange={(e) => handleStyleChange('borderTopStyle', e.target.value)}
                                    className="input-select"
                                >
                                    <option value="solid">Solid</option>
                                    <option value="dashed">Dashed</option>
                                    <option value="dotted">Dotted</option>
                                </select>
                            </div>
                        </>
                    )}

                    {(isSection || isPopup) && (
                        <>
                            <div>
                                <label className="input-label">Viền (Border)</label>
                                <input
                                    type="text"
                                    value={styles.border || 'none'}
                                    onChange={(e) => handleStyleChange('border', e.target.value)}
                                    className="input-field"
                                    placeholder="1px solid #000"
                                />
                            </div>
                            <div>
                                <label className="input-label">Bo góc (Border Radius)</label>
                                <input
                                    type="text"
                                    value={styles.borderRadius || '0px'}
                                    onChange={(e) => handleStyleChange('borderRadius', e.target.value)}
                                    className="input-field"
                                    placeholder="8px"
                                />
                            </div>
                            <div>
                                <label className="input-label">Đổ bóng (Box Shadow)</label>
                                <input
                                    type="text"
                                    value={styles.boxShadow || 'none'}
                                    onChange={(e) => handleStyleChange('boxShadow', e.target.value)}
                                    className="input-field"
                                    placeholder="0 4px 6px rgba(0,0,0,0.1)"
                                />
                            </div>
                            <div>
                                <label className="input-label">Padding</label>
                                <input
                                    type="text"
                                    value={styles.padding || '0'}
                                    onChange={(e) => handleStyleChange('padding', e.target.value)}
                                    className="input-field"
                                    placeholder="20px"
                                />
                            </div>
                            <div>
                                <label className="input-label">Chiều cao</label>
                                <input
                                    type="text"
                                    value={size.height || '400px'}
                                    onChange={(e) => handleSizeChange('height', e.target.value)}
                                    className="input-field"
                                    placeholder="400px"
                                />
                            </div>
                            {isPopup && (
                                <div>
                                    <label className="input-label">Chiều rộng</label>
                                    <input
                                        type="text"
                                        value={size.width || '600px'}
                                        onChange={(e) => handleSizeChange('width', e.target.value)}
                                        className="input-field"
                                        placeholder="600px"
                                    />
                                </div>
                            )}
                        </>
                    )}
                </div>
            </div>
        );
    };

    const renderAnimationTab = () => (
        <div className="panel-section">
            <h4 className="section-title">
                <Zap size={16} strokeWidth={1.5} />
                Hiệu ứng
            </h4>
            <div className="input-group">
                <div>
                    <label className="input-label">Hiệu ứng khi load</label>
                    <select
                        value={componentData.animation?.onLoad || 'none'}
                        onChange={(e) => handleComponentDataChange('animation', { ...componentData.animation, onLoad: e.target.value })}
                        className="input-select"
                    >
                        <option value="none">Không có</option>
                        <option value="fadeIn">Fade In</option>
                        <option value="slideUp">Slide Up</option>
                        <option value="zoomIn">Zoom In</option>
                    </select>
                </div>
                <div>
                    <label className="input-label">Hiệu ứng khi scroll</label>
                    <select
                        value={componentData.animation?.onScroll || 'none'}
                        onChange={(e) => handleComponentDataChange('animation', { ...componentData.animation, onScroll: e.target.value })}
                        className="input-select"
                    >
                        <option value="none">Không có</option>
                        <option value="fadeInUp">Fade In Up</option>
                        <option value="scaleUp">Scale Up</option>
                    </select>
                </div>
                <div>
                    <label className="input-label">Thời gian hiệu ứng (ms)</label>
                    <input
                        type="number"
                        value={componentData.animation?.duration || 1000}
                        onChange={(e) => handleComponentDataChange('animation', { ...componentData.animation, duration: parseInt(e.target.value) })}
                        className="input-field"
                        placeholder="1000"
                    />
                </div>
            </div>
        </div>
    );

    const renderAdvancedTab = () => (
        <div className="panel-section">
            <h4 className="section-title">
                <Settings size={16} strokeWidth={1.5} />
                Thuộc tính khác
            </h4>
            <div className="input-group">
                <div>
                    <label className="input-label">Margin</label>
                    <input
                        type="text"
                        value={styles.margin || '0'}
                        onChange={(e) => handleStyleChange('margin', e.target.value)}
                        className="input-field"
                        placeholder="0px"
                    />
                </div>
                <div>
                    <label className="input-label">Z-Index</label>
                    <input
                        type="number"
                        value={position.z || 1}
                        onChange={(e) => handlePositionChange('z', e.target.value)}
                        className="input-field"
                        placeholder="1"
                    />
                </div>
            </div>
        </div>
    );

    return (
        <div className="properties-panel">
            <div className="panel-header">
                <input
                    type="text"
                    value={componentData.title || ''}
                    onChange={(e) => handleComponentDataChange('title', e.target.value)}
                    className="panel-title-input"
                    placeholder={`Tên ${type === 'section' ? 'Section' : type === 'popup' ? 'Popup' : 'Element'}`}
                />
                <button onClick={onToggle} className="toggle-button" title="Đóng">
                    <ChevronRight size={18} />
                </button>
            </div>

            <div className="panel-tabs">
                <button
                    className={`tab-button ${activeTab === 'design' ? 'active' : ''}`}
                    onClick={() => setActiveTab('design')}
                >
                    Thiết kế
                </button>
                <button
                    className={`tab-button ${activeTab === 'animation' ? 'active' : ''}`}
                    onClick={() => setActiveTab('animation')}
                >
                    Hiệu ứng
                </button>
                <button
                    className={`tab-button ${activeTab === 'advanced' ? 'active' : ''}`}
                    onClick={() => setActiveTab('advanced')}
                >
                    Nâng cao
                </button>
            </div>

            <div className="panel-content">
                {activeTab === 'design' && renderDesignTab()}
                {activeTab === 'animation' && renderAnimationTab()}
                {activeTab === 'advanced' && renderAdvancedTab()}
            </div>
        </div>
    );
};

export default PropertiesPanel;
