import React from 'react';
import '../styles/CreateLanding.css';

const QuickEditToolkit = ({ element, position, onEdit, onDuplicate, onDelete, onMoveUp, onMoveDown, onClose }) => {
    if (!element || !position) return null;

    return (
        <div
            className="lpb-quick-edit-toolkit"
            style={{
                position: 'absolute',
                left: position.x,
                top: position.y,
                zIndex: 1000,
            }}
            onMouseEnter={(e) => e.stopPropagation()}
            onMouseLeave={onClose}
        >
            <button
                className="lpb-quick-edit-toolkit-btn"
                onClick={onEdit}
                title="Chỉnh sửa (Mở Properties Panel)"
            >
                <i className="fas fa-edit"></i>
            </button>
            <button
                className="lpb-quick-edit-toolkit-btn"
                onClick={onDuplicate}
                title="Sao chép phần tử"
            >
                <i className="fas fa-copy"></i>
            </button>
            <button
                className="lpb-quick-edit-toolkit-btn"
                onClick={onDelete}
                title="Xóa phần tử"
            >
                <i className="fas fa-trash"></i>
            </button>
            <button
                className="lpb-quick-edit-toolkit-btn"
                onClick={onMoveUp}
                title="Di chuyển lên"
            >
                <i className="fas fa-arrow-up"></i>
            </button>
            <button
                className="lpb-quick-edit-toolkit-btn"
                onClick={onMoveDown}
                title="Di chuyển xuống"
            >
                <i className="fas fa-arrow-down"></i>
            </button>
        </div>
    );
};

export default QuickEditToolkit;
