import React, { useState, useMemo, useCallback, useEffect } from 'react';
import { useDrag } from 'react-dnd';
import { useHotkeys } from 'react-hotkeys-hook';
import { toast } from 'react-toastify';
import COMPONENT_LIBRARY from '../../constants/componentLibrary';

const ItemTypes = {
  COMPONENT: 'component',
  TEMPLATE: 'template',
};

// Updated library structure with compact design


// Assume subCategories structure remains as in previous version, adjust as needed

// DraggableComponent with compact, professional style
const DraggableComponent = React.memo(({ item, isTemplate = false }) => {
  const [{ isDragging }, drag] = useDrag(() => ({
    type: isTemplate ? ItemTypes.TEMPLATE : ItemTypes.COMPONENT,
    item: { componentData: isTemplate ? item.structure : item },
    collect: (monitor) => ({
      isDragging: !!monitor.isDragging(),
    }),
  }));

  return (
    <div
      ref={drag}
      className="p-1.5 mb-1 border border-gray-200 rounded-md cursor-move hover:border-blue-500 hover:shadow-md bg-white flex items-center gap-1.5 text-xs transition-all duration-200"
      style={{ opacity: isDragging ? 0.5 : 1 }}
      onMouseDown={() => toast.info(`Bắt đầu kéo ${item.name}`)}
      data-tooltip-id="component-tooltip"
      data-tooltip-content={item.description || `Kéo để thêm ${item.name}`}
    >
      <i className={`${item.icon} text-gray-500 text-base`}></i>
      <span className="font-medium text-gray-700 truncate w-20">{item.name}</span>
      {item.level && <span className="text-[10px] text-blue-600 bg-blue-50 px-1 rounded">{item.level.toUpperCase()}</span>}
    </div>
  );
});

const ComponentLibrary = React.memo(({ isCollapsed, onToggle }) => {
  const [activeCategory, setActiveCategory] = useState('elements');
  const [searchQuery, setSearchQuery] = useState('');
  const [expandedSubs, setExpandedSubs] = useState({});
  const [isDragging, setIsDragging] = useState(false);

  const categories = Object.keys(EXTENDED_LIBRARY).map((key) => ({
    id: key,
    ...EXTENDED_LIBRARY[key],
  }));

  // Toggle sub-category expansion
  const toggleSub = useCallback((catId, subId) => {
    setExpandedSubs((prev) => ({
      ...prev,
      [catId]: { ...prev[catId], [subId]: !prev[catId]?.[subId] },
    }));
  }, []);

  // Filter items based on search query
  const filteredItems = useMemo(() => {
    const categoryData = EXTENDED_LIBRARY[activeCategory];
    if (!categoryData) return [];

    let items = [];
    categoryData.subCategories.forEach((sub) => {
      if (expandedSubs[activeCategory]?.[sub.id]) {
        items = [...items, ...sub.templates];
      }
    });

    if (!searchQuery) return items;
    return items.filter((item) =>
      item.name.toLowerCase().includes(searchQuery.toLowerCase())
    );
  }, [activeCategory, searchQuery, expandedSubs]);

  // Handle category change
  const handleCategoryChange = useCallback((categoryId) => {
    setActiveCategory(categoryId);
    setSearchQuery('');
    toast.info(`Đã chuyển sang danh mục ${EXTENDED_LIBRARY[categoryId]?.name}`);
  }, []);

  // Keyboard shortcuts for categories
  categories.forEach((cat, index) => {
    useHotkeys(`ctrl+${index + 1}`, () => handleCategoryChange(cat.id), [handleCategoryChange]);
  });

  // Reset to first category after drag
  useEffect(() => {
    if (!isDragging) {
      setActiveCategory('elements'); // Reset to 'elements' after dragging ends
    }
  }, [isDragging]);

  if (isCollapsed) {
    return (
      <div className="w-12 bg-white border-r border-gray-200 h-full flex flex-col items-center py-2 transition-all duration-300">
        <button
          onClick={onToggle}
          className="p-2 hover:bg-blue-100 rounded-full text-gray-600 hover:text-blue-600 transition-all duration-200"
          data-tooltip-id="component-tooltip"
          data-tooltip-content="Mở thư viện"
        >
          <i className="fas fa-chevron-right"></i>
        </button>
        {categories.map((cat) => (
          <button
            key={cat.id}
            className="p-2 hover:bg-blue-100 rounded-full text-gray-600 hover:text-blue-600 my-1 transition-all duration-200"
            data-tooltip-id="component-tooltip"
            data-tooltip-content={cat.name}
            onClick={() => {
              handleCategoryChange(cat.id);
              onToggle();
            }}
          >
            <i className={cat.icon}></i>
          </button>
        ))}
      </div>
    );
  }

  const renderSubCategories = (categoryData) => {
    return categoryData.subCategories.map((sub) => (
      <div key={sub.id} className="border-b border-gray-100">
        <button
          onClick={() => toggleSub(activeCategory, sub.id)}
          className="w-full p-2 text-left flex items-center justify-between hover:bg-gray-50 text-gray-700 text-sm font-medium transition-all duration-200"
        >
          <span>{sub.name}</span>
          <i className={`fas fa-chevron-${expandedSubs[activeCategory]?.[sub.id] ? 'down' : 'right'} text-gray-500`}></i>
        </button>
        {expandedSubs[activeCategory]?.[sub.id] && (
          <div className="pl-2 pt-1">
            {sub.templates.map((template) => (
              <DraggableComponent
                key={template.id}
                item={template}
                isTemplate={activeCategory === 'templates'}
                onDrag={() => setIsDragging(true)}
                onDragEnd={() => setIsDragging(false)}
              />
            ))}
          </div>
        )}
      </div>
    ));
  };

  return (
    <div className="w-64 bg-white border-r border-gray-200 h-full overflow-y-auto transition-all duration-300 shadow-lg">
      <div className="p-2 border-b flex items-center justify-between bg-gradient-to-r from-gray-100 to-white">
        <h2 className="font-semibold text-gray-800 text-lg">Thư viện</h2>
        <button
          onClick={onToggle}
          className="p-1.5 hover:bg-blue-100 rounded-full text-gray-600 hover:text-blue-600 transition-all duration-200"
          data-tooltip-id="component-tooltip"
          data-tooltip-content={isCollapsed ? "Mở thư viện" : "Thu gọn thư viện"}
        >
          <i className={`fas fa-chevron-${isCollapsed ? 'right' : 'left'}`}></i>
        </button>
      </div>

      <div className="flex justify-around p-1 bg-gray-50 border-b">
        {categories.map((cat) => (
          <button
            key={cat.id}
            onClick={() => handleCategoryChange(cat.id)}
            className={`flex items-center justify-center w-16 h-10 rounded-full text-sm font-medium transition-all duration-200 ${
    activeCategory === cat.id
        ? 'bg-gradient-to-r from-blue-500 to-blue-600 text-white shadow-md'
        : 'text-gray-600 hover:bg-blue-100'
}`}
            data-tooltip-id="component-tooltip"
            data-tooltip-content={`Chuyển sang ${cat.name}`}
          >
            {cat.name}
          </button>
        ))}
      </div>

      <div className="p-2 border-b">
        <input
          type="text"
          placeholder="Tìm kiếm thành phần..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          className="w-full p-2 border border-gray-200 rounded-lg text-sm focus:outline-none focus:border-blue-500 focus:ring-1 focus:ring-blue-500 transition-all duration-200"
        />
      </div>

      <div className="p-2">
        {renderSubCategories(EXTENDED_LIBRARY[activeCategory])}
        {filteredItems.length === 0 && (
          <div className="text-center text-gray-500 text-sm py-4">Không tìm thấy thành phần</div>
        )}
      </div>
    </div>
  );
});

export default ComponentLibrary;
