import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import '../styles/doashboard-sidebar.css'

const Sidebar = ({ role }) => {
    const [isCompact, setIsCompact] = useState(false);
    const location = useLocation();

    useEffect(() => {
        const saved = localStorage.getItem("sidebarCompact");
        if (saved !== null) {
            setIsCompact(JSON.parse(saved));
        } else {
            if (window.innerWidth < 1024) {
                setIsCompact(true);
            }
        }
    }, []);

    // ✅ Lắng nghe resize màn hình để auto toggle nếu không có saved state
    useEffect(() => {
        const handleResize = () => {
            const saved = localStorage.getItem("sidebarCompact");
            if (saved === null) {
                if (window.innerWidth < 1024) {
                    setIsCompact(true);
                } else {
                    setIsCompact(false);
                }
            }
        };
        window.addEventListener("resize", handleResize);
        return () => window.removeEventListener("resize", handleResize);
    }, []);

    // ✅ Toggle và lưu trạng thái vào localStorage
    const handleToggle = () => {
        setIsCompact(prev => {
            localStorage.setItem("sidebarCompact", JSON.stringify(!prev));
            return !prev;
        });
    };

    const userMenu = [
        { name: 'Tổng quan', path: '/dashboard', icon: (
                <svg className="sidebar-svg-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 12l2-2m0 0l7-7 7 7M5 10v10a1 1 0 001 1h3m10-11l2 2m-2-2v10a1 1 0 01-1 1h-3m-6 0a1 1 0 001-1v-4a1 1 0 011-1h2a1 1 0 011 1v4a1 1 0 001 1m-6 0h6" />
                </svg>
            )},
        { name: 'Quản lý Landing Page', path: '/pages', icon: (
                <svg className="sidebar-svg-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
                </svg>
            )},
        { name: 'Quản lý Leads', path: '/leads', icon: (
                <svg className="sidebar-svg-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17 20h5v-2a3 3 0 00-5.356-1.857M17 20H7m10 0v-2c0-.656-.126-1.283-.356-1.857M7 20H2v-2a3 3 0 015.356-1.857M7 20v-2c0-.656.126-1.283.356-1.857m0 0a5.002 5.002 0 019.288 0M15 7a3 3 0 11-6 0 3 3 0 016 0zm6 3a2 2 0 11-4 0 2 2 0zM7 10a2 2 0 11-4 0 2 2 0z" />
                </svg>
            )},
        { name: 'Thanh toán & Premium', path: '/payments', icon: (
                <svg className="sidebar-svg-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 10h18M7 15h1m4 0h1m-7 4h12a3 3 0 003-3V8a3 3 0 00-3-3H6a3 3 0 00-3 3v8a3 3 0 003 3z" />
                </svg>
            )},
        { name: 'Báo cáo', path: '/reports', icon: (
                <svg className="sidebar-svg-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z" />
                </svg>
            )},
        { name: 'Quảng cáo & Marketing', path: '/marketing', icon: (
                <svg className="sidebar-svg-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M11 5.882V19.24a1.76 1.76 0 01-3.417.592l-2.147-6.15M18 13a3 3 0 100-6M5.436 13.683A4.001 4.001 0 017 6h1.832c4.1 0 7.625-1.234 9.168-3v14c-1.543-1.766-5.067-3-9.168-3H7a3.988 3.988 0 01-1.564-.317z" />
                </svg>
            )},
        { name: 'Tương tác Khách hàng', path: '/chatbot', icon: (
                <svg className="sidebar-svg-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 12h.01M12 12h.01M16 12h.01M21 12c0 4.418-4.03 8-9 8a9.863 9.863 0 01-4.255-.949L3 20l1.395-3.72C3.512 15.042 3 13.574 3 12c0-4.418 4.03-8 9-8s9 3.582 9 8z" />
                </svg>
            )},
    ];

    const adminMenu = [
        { name: 'Tổng quan Hệ thống', path: '/dashboard', icon: (
                <svg className="sidebar-svg-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z" />
                </svg>
            )},
        { name: 'Quản lý Tài khoản', path: '/users', icon: (
                <svg className="sidebar-svg-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
                </svg>
            )},
        { name: 'Kiểm tra Thanh toán', path: '/payments', icon: (
                <svg className="sidebar-svg-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 10h18M7 15h1m4 0h1m-7 4h12a3 3 0 003-3V8a3 3 0 00-3-3H6a3 3 0 00-3 3v8a3 3 0 003 3z" />
                </svg>
            )},
        { name: 'Quản lý Mẫu Marketplace', path: '/templates', icon: (
                <svg className="sidebar-svg-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 11H5m14 0a2 2 0 012 2v6a2 2 0 01-2 2H5a2 2 0 01-2-2v-6a2 2 0 012-2m14 0V9a2 2 0 00-2-2M5 11V9a2 2 0 012-2m0 0V5a2 2 0 012-2h6a2 2 0 012 2v2M7 7h10" />
                </svg>
            )},
        { name: 'Báo cáo Hệ thống', path: '/reports', icon: (
                <svg className="sidebar-svg-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z" />
                </svg>
            )},
    ];

    const menu = role === 'admin' ? adminMenu : userMenu;

    return (
        <aside className={`sidebar ${isCompact ? 'compact' : ''}`}>
            {/* Background pattern */}
            <div className="sidebar-bg-pattern"></div>

            {/* Header */}
            <div className="sidebar-header">
                {!isCompact && (
                    <div className="sidebar-brand">
                        <div className="sidebar-brand-icon">
                            <svg fill="currentColor" viewBox="0 0 24 24">
                                <path d="M12 2L2 7l10 5 10-5-10-5zM2 17l10 5 10-5M2 12l10 5 10-5"/>
                            </svg>
                        </div>
                        <span className="sidebar-brand-text">Dashboard</span>
                    </div>
                )}
                <button className="sidebar-toggle" onClick={handleToggle}>
                    <svg className={`sidebar-toggle-icon ${isCompact ? 'rotated' : ''}`}
                         fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
                    </svg>
                </button>
            </div>

            {/* Menu Items */}
            <nav className="sidebar-nav">
                {menu.map((item) => {
                    const isActive = location.pathname === item.path;
                    return (
                        <Link
                            key={item.name}
                            to={item.path}
                            className={`sidebar-item ${isActive ? 'active' : ''} ${isCompact ? 'compact' : ''}`}
                            title={isCompact ? item.name : ''}
                        >
                            {isActive && <div className="sidebar-item-indicator"></div>}

                            <div className="sidebar-icon">
                                {item.icon}
                            </div>

                            {!isCompact && (
                                <span className="sidebar-text">{item.name}</span>
                            )}

                            <div className="sidebar-item-glow"></div>
                        </Link>
                    );
                })}
            </nav>

            {/* Footer */}
            {!isCompact && (
                <div className="sidebar-footer">
                    <div className="sidebar-status">
                        <div className="sidebar-status-icon">
                            <div className="sidebar-status-dot"></div>
                        </div>
                        <span className="sidebar-status-text">System Online</span>
                    </div>
                </div>
            )}
        </aside>
    );
};

export default React.memo(Sidebar);
