import React, { useState, useCallback } from 'react';
import { DndProvider } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
import Canvas from '../components/create-page/Canvas';
import '../styles/CreateLanding.css';

const ItemTypes = {
    SECTION: 'section',
    ELEMENT: 'element',
    EXISTING_ELEMENT: 'existingElement',
};

const CreateLandingPage = () => {
    const [pageStructure, setPageStructure] = useState({
        children: [],
        styles: { desktop: {}, mobile: {} },
    });
    const [selectedElement, setSelectedElement] = useState(null);
    const [quickEditElement, setQuickEditElement] = useState(null);
    const [quickEditPosition, setQuickEditPosition] = useState({ left: 0, top: 0 });
    const [viewMode, setViewMode] = useState('desktop');
    const [zoomLevel, setZoomLevel] = useState(100);

    const generateUniqueId = () => `id-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;

    const addSection = useCallback((section, index) => {
        setPageStructure((prev) => {
            const newChildren = [...(prev.children || [])];
            newChildren.splice(index, 0, { ...section, id: section.id || generateUniqueId() });
            return { ...prev, children: newChildren };
        });
    }, []);

    const addElement = useCallback((sectionId, element, index) => {
        setPageStructure((prev) => {
            const newChildren = [...(prev.children || [])];
            const sectionIndex = newChildren.findIndex((s) => s.id === sectionId);
            if (sectionIndex === -1) return prev;
            const newElements = [...(newChildren[sectionIndex].elements || [])];
            newElements.splice(index, 0, { ...element, id: element.id || generateUniqueId() });
            newChildren[sectionIndex] = { ...newChildren[sectionIndex], elements: newElements };
            return { ...prev, children: newChildren };
        });
    }, []);

    const updatePosition = useCallback((elementId, sectionId, position) => {
        setPageStructure((prev) => {
            const newChildren = [...(prev.children || [])];
            const sectionIndex = newChildren.findIndex((s) => s.id === sectionId);
            if (sectionIndex === -1) return prev;
            const newElements = [...(newChildren[sectionIndex].elements || [])];
            const elementIndex = newElements.findIndex((e) => e.id === elementId);
            if (elementIndex === -1) return prev;
            newElements[elementIndex] = {
                ...newElements[elementIndex],
                json: {
                    ...newElements[elementIndex].json,
                    styles: {
                        ...newElements[elementIndex].json.styles,
                        desktop: { ...newElements[elementIndex].json.styles.desktop, ...position.desktop },
                        mobile: { ...newElements[elementIndex].json.styles.mobile, ...position.mobile },
                    },
                },
            };
            newChildren[sectionIndex] = { ...newChildren[sectionIndex], elements: newElements };
            return { ...prev, children: newChildren };
        });
    }, []);

    const moveElement = useCallback((sectionId, elementId, newIndex) => {
        setPageStructure((prev) => {
            const newChildren = [...(prev.children || [])];
            const sectionIndex = newChildren.findIndex((s) => s.id === sectionId);
            if (sectionIndex === -1) return prev;
            const newElements = [...(newChildren[sectionIndex].elements || [])];
            const elementIndex = newElements.findIndex((e) => e.id === elementId);
            if (elementIndex === -1) return prev;
            const [element] = newElements.splice(elementIndex, 1);
            newElements.splice(newIndex, 0, element);
            newChildren[sectionIndex] = { ...newChildren[sectionIndex], elements: newElements };
            return { ...prev, children: newChildren };
        });
    }, []);

    const onSelectElement = useCallback((element) => {
        setSelectedElement(element);
    }, []);

    const onQuickEdit = useCallback((element, position) => {
        setQuickEditElement(element);
        setQuickEditPosition(position);
    }, []);

    const onCloseQuickEdit = useCallback(() => {
        setQuickEditElement(null);
    }, []);

    const onDuplicateElement = useCallback((element) => {
        const sectionId = pageStructure.children.find((s) => s.elements?.some((e) => e.id === element.id))?.id;
        if (!sectionId) return;
        addElement(sectionId, {
            ...element,
            id: generateUniqueId(),
            json: {
                ...element.json,
                styles: {
                    desktop: { ...element.json.styles.desktop, left: (element.json.styles.desktop.left || 0) + 20 },
                    mobile: { ...element.json.styles.mobile, left: (element.json.styles.mobile.left || 0) + 10 },
                },
            },
        }, pageStructure.children.find((s) => s.id === sectionId).elements.length);
    }, [addElement, pageStructure]);

    const onDeleteElement = useCallback((element) => {
        setPageStructure((prev) => {
            const newChildren = [...(prev.children || [])];
            const sectionIndex = newChildren.findIndex((s) => s.elements?.some((e) => e.id === element.id));
            if (sectionIndex === -1) return prev;
            const newElements = newChildren[sectionIndex].elements.filter((e) => e.id !== element.id);
            newChildren[sectionIndex] = { ...newChildren[sectionIndex], elements: newElements };
            return { ...prev, children: newChildren };
        });
        setSelectedElement(null);
        setQuickEditElement(null);
    }, []);

    const onMoveElement = useCallback((element, direction) => {
        const sectionId = pageStructure.children.find((s) => s.elements?.some((e) => e.id === element.id))?.id;
        if (!sectionId) return;
        const sectionIndex = pageStructure.children.findIndex((s) => s.id === sectionId);
        const elements = pageStructure.children[sectionIndex].elements;
        const elementIndex = elements.findIndex((e) => e.id === element.id);
        if (elementIndex === -1) return;
        const newIndex = direction === 'up' ? elementIndex - 1 : elementIndex + 1;
        if (newIndex >= 0 && newIndex < elements.length) {
            moveElement(sectionId, element.id, newIndex);
        }
    }, [pageStructure, moveElement]);

    const onInlineEdit = useCallback((elementId, sectionId, content) => {
        setPageStructure((prev) => {
            const newChildren = [...(prev.children || [])];
            const sectionIndex = newChildren.findIndex((s) => s.id === sectionId);
            if (sectionIndex === -1) return prev;
            const newElements = [...(newChildren[sectionIndex].elements || [])];
            const elementIndex = newElements.findIndex((e) => e.id === elementId);
            if (elementIndex === -1) return prev;
            newElements[elementIndex] = {
                ...newElements[elementIndex],
                json: {
                    ...newElements[elementIndex].json,
                    componentData: {
                        ...newElements[elementIndex].json.componentData,
                        content,
                    },
                },
            };
            newChildren[sectionIndex] = { ...newChildren[sectionIndex], elements: newElements };
            return { ...prev, children: newChildren };
        });
    }, []);

    return (
        <DndProvider backend={HTML5Backend}>
            <div className="lpb-container">
                <div className="lpb-toolbar-container">
                    <div className="lpb-toolbar-inner">
                        <div className="lpb-toolbar-left">
                            <span className="lpb-toolbar-title">Landing Page Builder</span>
                        </div>
                    </div>
                </div>
                <div className="lpb-main-content">
                    <Canvas
                        pageStructure={pageStructure}
                        selectedElement={selectedElement}
                        onSelectElement={onSelectElement}
                        quickEditElement={quickEditElement}
                        quickEditPosition={quickEditPosition}
                        onQuickEdit={onQuickEdit}
                        onCloseQuickEdit={onCloseQuickEdit}
                        onDuplicateElement={onDuplicateElement}
                        onDeleteElement={onDeleteElement}
                        onMoveElement={onMoveElement}
                        viewMode={viewMode}
                        addSection={addSection}
                        addElement={addElement}
                        moveElement={moveElement}
                        ItemTypes={ItemTypes}
                        zoomLevel={zoomLevel}
                        setPageStructure={setPageStructure}
                        updatePosition={updatePosition}
                        onInlineEdit={onInlineEdit}
                    />
                </div>
            </div>
        </DndProvider>
    );
};

export default CreateLandingPage;
