import React, { useState, useEffect, useRef } from 'react';
import { Search, Plus, ChevronDown, MoreHorizontal, Eye, Clock, Globe } from 'lucide-react';
import axios from 'axios';
import GrapesJS from 'grapesjs';
import 'grapesjs/dist/css/grapes.min.css';
import 'grapesjs-preset-webpage';
import '../styles/PageContent.css';

const PageContent = () => {
    const [landingPages, setLandingPages] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [statusFilter, setStatusFilter] = useState('Tr·∫°ng th√°i');
    const [menuOpen, setMenuOpen] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [showCreateModal, setShowCreateModal] = useState(false);
    const [showEditorModal, setShowEditorModal] = useState(false);
    const [pageDescription, setPageDescription] = useState('');
    const [editingPage, setEditingPage] = useState(null);
    const editorRef = useRef(null);

    // L·∫•y danh s√°ch landing pages
    useEffect(() => {
        const fetchLandingPages = async () => {
            try {
                setLoading(true);
                const response = await axios.get(`${process.env.REACT_APP_API_URL}/api/pages`, {
                    headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
                });
                setLandingPages(response.data);
            } catch (err) {
                setError('Kh√¥ng th·ªÉ t·∫£i danh s√°ch landing page: ' + err.message);
                console.error('Fetch error:', err);
            } finally {
                setLoading(false);
            }
        };
        fetchLandingPages();
    }, []);

    // Kh·ªüi t·∫°o GrapesJS khi m·ªü modal ch·ªânh s·ª≠a
    useEffect(() => {
        if (showEditorModal && editingPage) {
            const editor = GrapesJS.init({
                container: '#gjs-editor',
                fromElement: true,
                height: '600px',
                width: '100%',
                plugins: ['gjs-preset-webpage'],
                storageManager: false,
                assetManager: { upload: false },
            });

            // L·∫•y n·ªôi dung t·ª´ backend
            axios
                .get(`${process.env.REACT_APP_API_URL}/api/pages/${editingPage.id}/content`, {
                    headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
                })
                .then((response) => {
                    editor.setComponents(response.data.html);
                    editor.setStyle(response.data.css);
                })
                .catch((err) => {
                    setError('L·ªói t·∫£i n·ªôi dung page: ' + err.message);
                    console.error('Editor load error:', err);
                });

            editorRef.current = editor;

            return () => {
                editor.destroy();
                editorRef.current = null;
            };
        }
    }, [showEditorModal, editingPage]);

    // L·ªçc danh s√°ch
    const filteredPages = landingPages.filter((page) => {
        const matchesSearch = page.name.toLowerCase().includes(searchTerm.toLowerCase());
        const matchesStatus = statusFilter === 'Tr·∫°ng th√°i' || page.status === statusFilter;
        return matchesSearch && matchesStatus;
    });

    // T·∫°o landing page m·ªõi
    const handleCreatePage = async () => {
        if (!pageDescription) {
            setError('Vui l√≤ng nh·∫≠p m√¥ t·∫£ landing page');
            return;
        }
        try {
            setLoading(true);
            const response = await axios.post(
                `${process.env.REACT_APP_API_URL}/api/pages`,
                { description: pageDescription },
                { headers: { Authorization: `Bearer ${localStorage.getItem('token')}` } }
            );
            setLandingPages([...landingPages, response.data]);
            setShowCreateModal(false);
            setPageDescription('');
        } catch (err) {
            setError('L·ªói khi t·∫°o landing page: ' + err.response?.data?.error || err.message);
            console.error('Create error:', err);
        } finally {
            setLoading(false);
        }
    };

    // L∆∞u n·ªôi dung t·ª´ GrapesJS
    const handleSavePage = async () => {
        if (editorRef.current) {
            const html = editorRef.current.getHtml();
            const css = editorRef.current.getCss();
            try {
                const response = await axios.put(
                    `${process.env.REACT_APP_API_URL}/api/pages/${editingPage.id}`,
                    { html, css },
                    { headers: { Authorization: `Bearer ${localStorage.getItem('token')}` } }
                );
                setLandingPages(landingPages.map((p) => (p.id === editingPage.id ? response.data : p)));
                setShowEditorModal(false);
                setEditingPage(null);
            } catch (err) {
                setError('L·ªói khi l∆∞u landing page: ' + err.response?.data?.error || err.message);
                console.error('Save error:', err);
            }
        }
    };

    // Tri·ªÉn khai landing page
    const handlePublishPage = async (pageId) => {
        try {
            const response = await axios.post(
                `${process.env.REACT_APP_API_URL}/api/pages/${pageId}/publish`,
                {},
                { headers: { Authorization: `Bearer ${localStorage.getItem('token')}` } }
            );
            setLandingPages(landingPages.map((p) => (p.id === pageId ? response.data : p)));
        } catch (err) {
            setError('L·ªói khi tri·ªÉn khai landing page: ' + err.response?.data?.error || err.message);
            console.error('Publish error:', err);
        }
    };

    // X√≥a landing page
    const handleDeletePage = async (pageId) => {
        if (!window.confirm('B·∫°n c√≥ ch·∫Øc mu·ªën x√≥a landing page n√†y?')) return;
        try {
            await axios.delete(`${process.env.REACT_APP_API_URL}/api/pages/${pageId}`, {
                headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
            });
            setLandingPages(landingPages.filter((p) => p.id !== pageId));
        } catch (err) {
            setError('L·ªói khi x√≥a landing page: ' + err.response?.data?.error || err.message);
            console.error('Delete error:', err);
        }
    };

    // Xem tr∆∞·ªõc landing page
    const handlePreviewPage = (url) => {
        if (url) window.open(url, '_blank');
    };

    // M·ªü modal ch·ªânh s·ª≠a
    const handleEditPage = (page) => {
        setEditingPage(page);
        setShowEditorModal(true);
    };

    // X·ª≠ l√Ω menu ng·ªØ c·∫£nh
    const handleMoreClick = (pageId) => {
        setMenuOpen(menuOpen === pageId ? null : pageId);
    };

    // Sidebar items
    const sidebarItems = [
        { icon: 'üìÑ', label: 'Pages', active: true },
        { icon: 'üìß', label: 'Th∆∞ vi·ªán m·∫´u' },
        { icon: 'üë§', label: 'T√™n mi·ªÅn' },
    ];

    return (
        <div className="page-content">
            {loading && <div className="loading">ƒêang t·∫£i...</div>}
            {error && (
                <div className="error">
                    {error}
                    <button onClick={() => setError(null)} className="error-dismiss">ƒê√≥ng</button>
                </div>
            )}
            <div className="sidebar">
                <div className="sidebar-header">
                    <h3>Landing Pages</h3>
                </div>
                <nav className="sidebar-nav">
                    {sidebarItems.map((item, index) => (
                        <div key={index} className={`nav-item ${item.active ? 'active' : ''}`}>
                            <span className="nav-icon">{item.icon}</span>
                            <span className="nav-label">{item.label}</span>
                        </div>
                    ))}
                </nav>
                <div className="filter-section">
                    <div className="filter-header">
                        <span>L·ªçc theo Tags</span>
                        <div className="filter-actions">
                            <button className="filter-btn">+</button>
                            <button className="filter-btn">‚â°</button>
                        </div>
                    </div>
                    <div className="search-filter">
                        <Search className="search-icon" size={16} />
                        <input type="text" placeholder="T√¨m ki·∫øm" />
                    </div>
                    <button className="all-btn">T·∫•t c·∫£</button>
                </div>
            </div>

            <div className="main-content">
                <div className="content-header">
                    <div>
                        <h1>Landing Pages</h1>
                        <p>Qu·∫£n l√Ω danh s√°ch Landing Page c·ªßa b·∫°n ƒë·ªÉ d·ªÖ d√†ng h∆°n v·ªõi vi·ªác g·∫Øn Tag, theo d√µi hi·ªáu su·∫•t.</p>
                    </div>
                    <button className="create-btn" onClick={() => setShowCreateModal(true)}>
                        <Plus size={16} />
                        T·∫°o Landing Page
                    </button>
                </div>

                {showCreateModal && (
                    <div className="modal">
                        <div className="modal-content">
                            <h2>T·∫°o Landing Page M·ªõi</h2>
                            <textarea
                                value={pageDescription}
                                onChange={(e) => setPageDescription(e.target.value)}
                                placeholder="Nh·∫≠p m√¥ t·∫£ (v√≠ d·ª•: Trang b√°n cƒÉn h·ªô Qu·∫≠n 7, m√†u xanh, c√≥ form li√™n h·ªá)"
                                rows="5"
                                style={{ width: '100%' }}
                            />
                            <div className="modal-actions">
                                <button onClick={handleCreatePage}>T·∫°o</button>
                                <button onClick={() => setShowCreateModal(false)}>H·ªßy</button>
                            </div>
                        </div>
                    </div>
                )}

                {showEditorModal && (
                    <div className="modal">
                        <div className="modal-content" style={{ width: '90%', maxWidth: '1200px' }}>
                            <h2>Ch·ªânh s·ª≠a Landing Page: {editingPage?.name}</h2>
                            <div id="gjs-editor" style={{ height: '600px', border: '1px solid #ccc' }}></div>
                            <div className="modal-actions">
                                <button onClick={handleSavePage}>L∆∞u</button>
                                <button onClick={() => setShowEditorModal(false)}>H·ªßy</button>
                            </div>
                        </div>
                    </div>
                )}

                <div className="controls">
                    <div className="search-box">
                        <Search className="search-icon" size={16} />
                        <input
                            type="text"
                            placeholder="T√¨m ki·∫øm Landing Page"
                            value={searchTerm}
                            onChange={(e) => setSearchTerm(e.target.value)}
                        />
                    </div>
                    <div className="filters">
                        <select
                            value={statusFilter}
                            onChange={(e) => setStatusFilter(e.target.value)}
                            className="filter-select"
                        >
                            <option>Tr·∫°ng th√°i</option>
                            <option>ƒê√É XU·∫§T B·∫¢N</option>
                            <option>CH∆ØA XU·∫§T B·∫¢N</option>
                            <option>ARCHIVED</option>
                        </select>
                    </div>
                </div>

                <div className="table-container">
                    <table className="data-table">
                        <thead>
                        <tr>
                            <th><input type="checkbox" /></th>
                            <th>Landing Page</th>
                            <th>Tr·∫°ng th√°i</th>
                            <th>Truy c·∫≠p <ChevronDown size={14} className="sort-icon" /></th>
                            <th>Chuy·ªÉn ƒë·ªïi <ChevronDown size={14} className="sort-icon" /></th>
                            <th>Doanh thu <ChevronDown size={14} className="sort-icon" /></th>
                            <th></th>
                        </tr>
                        </thead>
                        <tbody>
                        {filteredPages.map((page) => (
                            <tr key={page.id}>
                                <td><input type="checkbox" /></td>
                                <td className="page-info">
                                    <div className="page-name">{page.name}</div>
                                    {page.url && (
                                        <div className="page-url">
                                            <a href={page.url} target="_blank" rel="noopener noreferrer">
                                                {page.url}
                                            </a>
                                        </div>
                                    )}
                                </td>
                                <td>
                                    <div className="status-container">
                      <span className={`status ${page.statusColor}`}>
                        {page.status}
                      </span>
                                        <div className="status-info">
                                            <Globe size={12} />
                                            <Eye size={12} />
                                            <span className="timestamp">{page.lastUpdated}</span>
                                        </div>
                                    </div>
                                </td>
                                <td className="metric">{page.views}</td>
                                <td className="metric">{page.conversions}</td>
                                <td className="metric">{page.revenue}</td>
                                <td>
                                    <button className="more-btn" onClick={() => handleMoreClick(page.id)}>
                                        <MoreHorizontal size={16} />
                                    </button>
                                    {menuOpen === page.id && (
                                        <div className="context-menu">
                                            <button onClick={() => handlePreviewPage(page.url)}>Xem tr∆∞·ªõc</button>
                                            <button onClick={() => handleEditPage(page)}>Ch·ªânh s·ª≠a</button>
                                            <button onClick={() => handlePublishPage(page.id)}>Tri·ªÉn khai</button>
                                            <button onClick={() => handleDeletePage(page.id)}>X√≥a</button>
                                            <button onClick={() => console.log('Xu·∫•t b√°o c√°o', page.id)}>Xu·∫•t b√°o c√°o</button>
                                            {page.zalo_chatbot_script_id && (
                                                <button onClick={() => window.open(`https://zalo.me/${page.zalo_chatbot_script_id}`, '_blank')}>
                                                    K√≠ch ho·∫°t Chatbot
                                                </button>
                                            )}
                                        </div>
                                    )}
                                </td>
                            </tr>
                        ))}
                        </tbody>
                    </table>
                </div>

                <div className="pagination">
                    <span>ƒêang hi·ªÉn th·ªã 1 ƒë·∫øn {filteredPages.length} c·ªßa {landingPages.length} b·∫£n ghi</span>
                </div>
            </div>
        </div>
    );
};

export default PageContent;
